"use client"
import { createContext, useContext, useState } from "react"

// Cart Context
const CartContext = createContext()

export const useCart = () => {
    const context = useContext(CartContext)
    if (!context) {
        throw new Error("useCart must be used within a CartProvider")
    }
    return context
}

export const CartProvider = ({ children }) => {
    const [cartItems, setCartItems] = useState([])

    const addToCart = (item) => {
        setCartItems((prevItems) => {
            const existingItem = prevItems.find((cartItem) => cartItem.id === item.id)
            if (existingItem) {
                return prevItems.map((cartItem) =>
                    cartItem.id === item.id ? { ...cartItem, quantity: cartItem.quantity + 1 } : cartItem,
                )
            } else {
                return [...prevItems, { ...item, quantity: 1 }]
            }
        })
    }

    const removeFromCart = (id) => {
        setCartItems((prevItems) => prevItems.filter((item) => item.id !== id))
    }

    const updateQuantity = (id, change) => {
        setCartItems((prevItems) =>
            prevItems
                .map((item) => {
                    if (item.id === id) {
                        const newQuantity = Math.max(0, item.quantity + change)
                        return newQuantity === 0 ? null : { ...item, quantity: newQuantity }
                    }
                    return item
                })
                .filter(Boolean),
        )
    }

    const clearCart = () => {
        setCartItems([])
    }

    const getCartCount = () => {
        return cartItems.reduce((total, item) => total + item.quantity, 0)
    }

    const getCartTotal = () => {
        return cartItems.reduce((total, item) => total + item.price * item.quantity, 0)
    }

    return (
        <CartContext.Provider
            value={{
                cartItems,
                addToCart,
                removeFromCart,
                updateQuantity,
                clearCart,
                getCartCount,
                getCartTotal,
            }}
        >
            {children}
        </CartContext.Provider>
    )
}

// Stationary Page Component
const StationaryPage = ({ setCurrentPage }) => {
    const [selectedCategory, setSelectedCategory] = useState("books-notebooks")
    const [showAlert, setShowAlert] = useState(false)
    const [alertMessage, setAlertMessage] = useState("")
    const { addToCart, getCartCount } = useCart()
    const cartCount = getCartCount()

    // Stationary categories
    const categories = [
        { id: "books-notebooks", name: "Books & Notebooks", icon: "üìö" },
        { id: "writing-instruments", name: "Writing Instruments", icon: "‚úèÔ∏è" },
        { id: "art-supplies", name: "Art Supplies", icon: "üé®" },
        { id: "office-supplies", name: "Office Supplies", icon: "üìé" },
        { id: "electronics", name: "Electronics & Accessories", icon: "üîå" },
    ]

    // Stationary items for each category
    const stationaryItems = {
        "books-notebooks": [
            {
                id: 1,
                name: "University Record Book",
                description: "Official university record book with 200 pages, ruled format",
                price: 45,
                category: "Books & Notebooks",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 2,
                name: "Blue Book (Exam)",
                description: "Standard blue book for examinations, 16 pages, college approved",
                price: 15,
                category: "Books & Notebooks",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 3,
                name: "Spiral Notebook A4",
                description: "A4 size spiral notebook, 300 pages, ruled, hard cover",
                price: 85,
                category: "Books & Notebooks",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 4,
                name: "Graph Book",
                description: "Graph notebook with grid lines, 200 pages, perfect for engineering",
                price: 55,
                category: "Books & Notebooks",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 5,
                name: "Lab Record Book",
                description: "Laboratory record book with blank pages, 100 pages, thick paper",
                price: 65,
                category: "Books & Notebooks",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 6,
                name: "Sticky Notes Pack",
                description: "Colorful sticky notes pack, 5 colors, 100 sheets each",
                price: 35,
                category: "Books & Notebooks",
                image: "/placeholder.svg?height=120&width=120",
            },
        ],
        "writing-instruments": [
            {
                id: 7,
                name: "Blue Ballpoint Pen (Pack of 10)",
                description: "Smooth writing blue ballpoint pens, reliable ink flow",
                price: 50,
                category: "Writing Instruments",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 8,
                name: "Black Gel Pen Set",
                description: "Premium black gel pens, pack of 5, smooth writing experience",
                price: 75,
                category: "Writing Instruments",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 9,
                name: "Mechanical Pencil 0.5mm",
                description: "Professional mechanical pencil with lead refills included",
                price: 40,
                category: "Writing Instruments",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 10,
                name: "Highlighter Set",
                description: "Fluorescent highlighters, 6 colors, perfect for studying",
                price: 60,
                category: "Writing Instruments",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 11,
                name: "Fountain Pen",
                description: "Classic fountain pen with ink cartridges, elegant writing",
                price: 120,
                category: "Writing Instruments",
                image: "/placeholder.svg?height=120&width=120",
            },
        ],
        "art-supplies": [
            {
                id: 12,
                name: "Colored Pencils Set",
                description: "Professional colored pencils, 24 colors, vibrant pigments",
                price: 180,
                category: "Art Supplies",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 13,
                name: "Sketch Pad A3",
                description: "High-quality sketch pad, 50 sheets, 200gsm paper",
                price: 95,
                category: "Art Supplies",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 14,
                name: "Watercolor Paint Set",
                description: "Student watercolor set, 12 colors with brushes included",
                price: 150,
                category: "Art Supplies",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 15,
                name: "Drawing Compass Set",
                description: "Geometry compass set with pencil and pen attachments",
                price: 85,
                category: "Art Supplies",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 16,
                name: "Eraser & Sharpener Kit",
                description: "Premium erasers and sharpeners, various sizes included",
                price: 45,
                category: "Art Supplies",
                image: "/placeholder.svg?height=120&width=120",
            },
        ],
        "office-supplies": [
            {
                id: 17,
                name: "Paper Clips & Pins Set",
                description: "Assorted paper clips, safety pins, and push pins",
                price: 30,
                category: "Office Supplies",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 18,
                name: "Stapler with Staples",
                description: "Heavy-duty stapler with 1000 staples included",
                price: 120,
                category: "Office Supplies",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 19,
                name: "File Folders Pack",
                description: "A4 file folders, pack of 10, assorted colors",
                price: 80,
                category: "Office Supplies",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 20,
                name: "Transparent Tape Roll",
                description: "Clear adhesive tape, 2 rolls, strong adhesion",
                price: 25,
                category: "Office Supplies",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 21,
                name: "Ruler & Scale Set",
                description: "Plastic rulers, 15cm and 30cm, with triangular scales",
                price: 35,
                category: "Office Supplies",
                image: "/placeholder.svg?height=120&width=120",
            },
        ],
        electronics: [
            {
                id: 22,
                name: "Scientific Calculator",
                description: "Advanced scientific calculator with 240+ functions",
                price: 450,
                category: "Electronics & Accessories",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 23,
                name: "USB Flash Drive 32GB",
                description: "High-speed USB 3.0 flash drive, 32GB storage capacity",
                price: 280,
                category: "Electronics & Accessories",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 24,
                name: "Power Bank 10000mAh",
                description: "Portable power bank with dual USB ports, fast charging",
                price: 850,
                category: "Electronics & Accessories",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 25,
                name: "Earphones with Mic",
                description: "Wired earphones with built-in microphone, good sound quality",
                price: 320,
                category: "Electronics & Accessories",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 26,
                name: "Phone Stand Holder",
                description: "Adjustable phone stand for desk, compatible with all phones",
                price: 150,
                category: "Electronics & Accessories",
                image: "/placeholder.svg?height=120&width=120",
            },
        ],
    }

    const handleAddToCart = (item) => {
        addToCart(item)
        setAlertMessage(`${item.name} added to cart!`)
        setShowAlert(true)
        setTimeout(() => {
            setShowAlert(false)
        }, 2000)
    }

    const styles = {
        container: {
            minHeight: "100vh",
            background: "linear-gradient(135deg, #0f172a, #1e293b)",
            fontFamily: "system-ui, -apple-system, sans-serif",
            color: "white",
        },
        header: {
            background: "linear-gradient(135deg, #3b82f6, #10b981)",
            padding: "1rem",
            display: "flex",
            alignItems: "center",
            justifyContent: "space-between",
            boxShadow: "0 4px 12px rgba(0, 0, 0, 0.3)",
            position: "sticky",
            top: 0,
            zIndex: 50,
        },
        headerTitle: {
            fontSize: "1.5rem",
            fontWeight: "bold",
            color: "white",
            margin: 0,
        },
        cartContainer: {
            position: "relative",
            background: "rgba(255, 255, 255, 0.2)",
            borderRadius: "50%",
            width: "40px",
            height: "40px",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            cursor: "pointer",
            transition: "all 0.3s ease",
        },
        cartIcon: {
            fontSize: "1.25rem",
            color: "white",
        },
        cartBadge: {
            position: "absolute",
            top: "-8px",
            right: "-8px",
            background: "#ef4444",
            color: "white",
            borderRadius: "50%",
            width: "20px",
            height: "20px",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            fontSize: "0.75rem",
            fontWeight: "bold",
        },
        mainContent: {
            display: "flex",
            minHeight: "calc(100vh - 80px)",
        },
        sidebar: {
            width: "280px",
            background: "rgba(15, 23, 42, 0.95)",
            backdropFilter: "blur(12px)",
            borderRight: "1px solid #475569",
            padding: "1rem 0",
            overflowY: "auto",
        },
        categoryItem: {
            padding: "1rem 1.5rem",
            cursor: "pointer",
            transition: "all 0.3s ease",
            borderLeft: "4px solid transparent",
            display: "flex",
            alignItems: "center",
            gap: "0.75rem",
        },
        categoryItemActive: {
            background: "linear-gradient(90deg, rgba(59, 130, 246, 0.2), rgba(16, 185, 129, 0.2))",
            borderLeftColor: "#60a5fa",
            color: "#60a5fa",
        },
        categoryIcon: {
            fontSize: "1.25rem",
        },
        categoryName: {
            fontSize: "0.95rem",
            fontWeight: 500,
        },
        contentArea: {
            flex: 1,
            padding: "2rem",
            overflowY: "auto",
        },
        itemsGrid: {
            display: "grid",
            gap: "1.5rem",
        },
        stationaryItem: {
            background: "rgba(30, 41, 59, 0.8)",
            backdropFilter: "blur(12px)",
            borderRadius: "1rem",
            padding: "1.5rem",
            border: "1px solid #475569",
            display: "flex",
            gap: "1.5rem",
            transition: "all 0.3s ease",
        },
        itemImage: {
            width: "120px",
            height: "120px",
            borderRadius: "0.75rem",
            objectFit: "cover",
            flexShrink: 0,
        },
        itemDetails: {
            flex: 1,
            display: "flex",
            flexDirection: "column",
            justifyContent: "space-between",
        },
        itemName: {
            fontSize: "1.25rem",
            fontWeight: "bold",
            color: "white",
            margin: 0,
            marginBottom: "0.5rem",
        },
        itemDescription: {
            color: "#cbd5e1",
            fontSize: "0.95rem",
            lineHeight: 1.5,
            marginBottom: "1rem",
        },
        itemFooter: {
            display: "flex",
            alignItems: "center",
            justifyContent: "space-between",
        },
        itemPrice: {
            fontSize: "1.5rem",
            fontWeight: "bold",
            color: "#60a5fa",
        },
        addButton: {
            background: "linear-gradient(135deg, #ef4444, #dc2626)",
            border: "none",
            borderRadius: "2rem",
            padding: "0.75rem 1.5rem",
            color: "white",
            fontSize: "1rem",
            fontWeight: "bold",
            cursor: "pointer",
            transition: "all 0.3s ease",
            textTransform: "uppercase",
            letterSpacing: "0.05em",
        },
        alert: {
            position: "fixed",
            top: "100px",
            left: "50%",
            transform: "translateX(-50%)",
            background: "linear-gradient(135deg, #10b981, #059669)",
            color: "white",
            padding: "1rem 2rem",
            borderRadius: "2rem",
            boxShadow: "0 8px 25px rgba(16, 185, 129, 0.4)",
            zIndex: 100,
            fontSize: "1rem",
            fontWeight: "bold",
            opacity: showAlert ? 1 : 0,
            visibility: showAlert ? "visible" : "hidden",
            transition: "all 0.3s ease",
        },
    }

    return (
        <div style={styles.container}>
            {/* Alert */}
            <div style={styles.alert}>{alertMessage}</div>

            {/* Header */}
            <header style={styles.header}>
                <button
                    style={{
                        background: "rgba(255, 255, 255, 0.2)",
                        border: "none",
                        borderRadius: "50%",
                        width: "40px",
                        height: "40px",
                        display: "flex",
                        alignItems: "center",
                        justifyContent: "center",
                        cursor: "pointer",
                        color: "white",
                        fontSize: "1.25rem",
                        transition: "all 0.3s ease",
                    }}
                    onClick={() => alert("Welcome to DSCE Stationary Store!")}
                >
                    üè†
                </button>
