                    <div style={styles.section}>
                        <h2 style={styles.sectionTitle}>
                            {selectedPaymentMethod === "upi" ? "üì± UPI Details" : "üí≥ Card Details"}
                        </h2>
                        {selectedPaymentMethod === "upi" && (
                            <div style={styles.inputGroup}>
                                <label style={styles.inputLabel}>UPI ID *</label>
                                <input
                                    type="text"
                                    name="upiId"
                                    value={paymentDetails.upiId}
                                    onChange={handleInputChange}
                                    style={styles.inputField}
                                    placeholder="yourname@upi"
                                    required
                                />
                            </div>
                        )}
                        {selectedPaymentMethod === "card" && (
                            <>
                                <div style={styles.inputGroup}>
                                    <label style={styles.inputLabel}>Cardholder Name *</label>
                                    <input
                                        type="text"
                                        name="cardName"
                                        value={paymentDetails.cardName}
                                        onChange={handleInputChange}
                                        style={styles.inputField}
                                        placeholder="Enter cardholder name"
                                        required
                                    />
                                </div>
                                <div style={styles.inputGroup}>
                                    <label style={styles.inputLabel}>Card Number *</label>
                                    <input
                                        type="text"
                                        name="cardNumber"
                                        value={paymentDetails.cardNumber}
                                        onChange={handleInputChange}
                                        style={styles.inputField}
                                        placeholder="1234 5678 9012 3456"
                                        maxLength="19"
                                        required
                                    />
                                </div>
                                <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: "1rem" }}>
                                    <div style={styles.inputGroup}>
                                        <label style={styles.inputLabel}>Expiry Date *</label>
                                        <input
                                            type="text"
                                            name="cardExpiry"
                                            value={paymentDetails.cardExpiry}
                                            onChange={handleInputChange}
                                            style={styles.inputField}
                                            placeholder="MM/YY"
                                            maxLength="5"
                                            required
                                        />
                                    </div>
                                    <div style={styles.inputGroup}>
                                        <label style={styles.inputLabel}>CVV *</label>
                                        <input
                                            type="text"
                                            name="cardCvv"
                                            value={paymentDetails.cardCvv}
                                            onChange={handleInputChange}
                                            style={styles.inputField}
                                            placeholder="123"
                                            maxLength="3"
                                            required
                                        />
                                    </div>
                                </div>
                            </>
                        )}
                    </div>
                )}

                {/* Pay Button */}
                <button style={styles.payButton} onClick={handlePayment} disabled={isProcessing}>
                    {selectedPaymentMethod === "cod" ? "Place Order" : `Pay ‚Çπ{orderData.total}/-`}
                    {selectedPaymentMethod !== "cod" && <span>üí≥</span>}
                </button>
            </div>

            {/* Processing Overlay */}
            {isProcessing && (
                <div style={styles.processingOverlay}>
                    <div style={styles.spinner}></div>
                    <div style={{ color: "white", fontSize: "1.25rem", fontWeight: "bold" }}>
                        {selectedPaymentMethod === "cod" ? "Placing your order..." : "Processing payment..."}
                    </div>
                    <div style={{ color: "#cbd5e1", textAlign: "center" }}>
                        Please wait while we {selectedPaymentMethod === "cod" ? "confirm your order" : "process your payment"}
                    </div>
                </div>
            )}

            <style>
                {`
          @keyframes spin {
            0% { transform: rotate(0deg); }
            100% { transform: rotate(360deg); }
          }
        `}
            </style>
        </div>
    )
}

// Order Confirmation Page Component (same as before)
const OrderConfirmationPage = ({ setCurrentPage, orderData }) => {
    const generateBillNumber = () => {
        return `DSCE${Date.now().toString().slice(-6)}`
    }

    const getCurrentDateTime = () => {
        return new Date().toLocaleString("en-IN", {
            timeZone: "Asia/Kolkata",
            year: "numeric",
            month: "2-digit",
            day: "2-digit",
            hour: "2-digit",
            minute: "2-digit",
            second: "2-digit",
        })
    }

    const styles = {
        container: {
            minHeight: "100vh",
            background: "linear-gradient(135deg, #0f172a, #1e293b)",
            fontFamily: "system-ui, -apple-system, sans-serif",
            color: "white",
        },
        header: {
            background: "linear-gradient(135deg, #10b981, #059669)",
            padding: "1rem",
            display: "flex",
            alignItems: "center",
            justifyContent: "space-between",
            boxShadow: "0 4px 12px rgba(0, 0, 0, 0.3)",
            position: "sticky",
            top: 0,
            zIndex: 50,
        },
        headerTitle: {
            fontSize: "1.5rem",
            fontWeight: "bold",
            color: "white",
            margin: 0,
        },
        mainContent: {
            padding: "2rem",
            maxWidth: "600px",
            margin: "0 auto",
        },
        successCard: {
            background: "linear-gradient(135deg, #10b981, #059669)",
            borderRadius: "1rem",
            padding: "3rem 2rem",
            textAlign: "center",
            marginBottom: "2rem",
            boxShadow: "0 8px 25px rgba(16, 185, 129, 0.3)",
        },
        successIcon: {
            fontSize: "4rem",
            marginBottom: "1rem",
        },
        successTitle: {
            fontSize: "2rem",
            fontWeight: "bold",
            marginBottom: "0.5rem",
        },
        successSubtitle: {
            color: "#d1fae5",
            fontSize: "1.1rem",
            marginBottom: "1rem",
        },
        orderNumber: {
            background: "rgba(255, 255, 255, 0.2)",
            borderRadius: "2rem",
            padding: "0.75rem 1.5rem",
            fontSize: "1.25rem",
            fontWeight: "bold",
            display: "inline-block",
        },
        detailsCard: {
            background: "rgba(30, 41, 59, 0.8)",
            backdropFilter: "blur(12px)",
            borderRadius: "1rem",
            padding: "2rem",
            border: "1px solid #475569",
            marginBottom: "2rem",
        },
        detailsTitle: {
            fontSize: "1.25rem",
            fontWeight: "bold",
            color: "#60a5fa",
            marginBottom: "1rem",
        },
        detailRow: {
            display: "flex",
            justifyContent: "space-between",
            padding: "0.5rem 0",
            borderBottom: "1px solid #334155",
        },
        actionButtons: {
            display: "grid",
            gridTemplateColumns: "1fr 1fr",
            gap: "1rem",
        },
        button: {
            padding: "1rem 2rem",
            borderRadius: "2rem",
            border: "none",
            fontSize: "1rem",
            fontWeight: "bold",
            cursor: "pointer",
            transition: "all 0.3s ease",
            textTransform: "uppercase",
            letterSpacing: "0.05em",
        },
        primaryButton: {
            background: "linear-gradient(135deg, #3b82f6, #1d4ed8)",
            color: "white",
        },
        secondaryButton: {
            background: "rgba(107, 114, 128, 0.8)",
            color: "white",
        },
    }

    return (
        <div style={styles.container}>
            <header style={styles.header}>
                <button
                    style={{
                        background: "rgba(255, 255, 255, 0.2)",
                        border: "none",
                        borderRadius: "50%",
                        width: "40px",
                        height: "40px",
                        display: "flex",
                        alignItems: "center",
                        justifyContent: "center",
                        cursor: "pointer",
                        color: "white",
                        fontSize: "1.25rem",
                        transition: "all 0.3s ease",
                    }}
                    onClick={() => setCurrentPage("stationary")}
                >
                    ‚Üê
                </button>
                <h1 style={styles.headerTitle}>Order Confirmed</h1>
                <div style={{ width: "40px" }}></div>
            </header>

            <div style={styles.mainContent}>
                {/* Success Message */}
                <div style={styles.successCard}>
                    <div style={styles.successIcon}>üéâ</div>
                    <div style={styles.successTitle}>Order Placed Successfully!</div>
                    <div style={styles.successSubtitle}>Thank you for your order. We're preparing your stationary items!</div>
                    <div style={styles.orderNumber}>Order #{generateBillNumber()}</div>
                </div>

                {/* Order Details */}
                <div style={styles.detailsCard}>
                    <div style={styles.detailsTitle}>üìã Order Details</div>
                    <div style={styles.detailRow}>
                        <span>Order Date & Time:</span>
                        <span>{getCurrentDateTime()}</span>
                    </div>
                    <div style={styles.detailRow}>
                        <span>Items:</span>
                        <span>{orderData.itemCount} items</span>
                    </div>
                    <div style={styles.detailRow}>
                        <span>Total Amount:</span>
                        <span style={{ color: "#10b981", fontWeight: "bold" }}>‚Çπ{orderData.total}/-</span>
                    </div>
                    <div style={styles.detailRow}>
                        <span>Payment Status:</span>
                        <span style={{ color: "#10b981", fontWeight: "bold" }}>‚úÖ Confirmed</span>
                    </div>
                    <div style={styles.detailRow}>
                        <span>Estimated Delivery:</span>
                        <span>1-2 hours</span>
                    </div>
                </div>

                {/* Customer Info */}
                <div style={styles.detailsCard}>
                    <div style={styles.detailsTitle}>üë§ Delivery Information</div>
                    <div style={styles.detailRow}>
                        <span>Name:</span>
                        <span>{orderData.customerInfo.name}</span>
                    </div>
                    <div style={styles.detailRow}>
                        <span>Student ID:</span>
                        <span>{orderData.customerInfo.studentId}</span>
                    </div>
                    <div style={styles.detailRow}>
                        <span>Phone:</span>
                        <span>{orderData.customerInfo.phone}</span>
                    </div>
                    {orderData.customerInfo.email && (
                        <div style={styles.detailRow}>
                            <span>Email:</span>
                            <span>{orderData.customerInfo.email}</span>
                        </div>
                    )}
                </div>

                {/* Action Buttons */}
                <div style={styles.actionButtons}>
                    <button style={{ ...styles.button, ...styles.secondaryButton }} onClick={() => setCurrentPage("stationary")}>
                        Order More
                    </button>
                    <button
                        style={{ ...styles.button, ...styles.primaryButton }}
                        onClick={() => alert("Order tracking feature coming soon!")}
                    >
                        Track Order
                    </button>
                </div>
            </div>
        </div>
    )
}

// Main App Component
const App = () => {
    const [currentPage, setCurrentPage] = useState("stationary")
    const [orderData, setOrderData] = useState(null)

    const handlePageChange = (page, data = null) => {
        setCurrentPage(page)
        if (data) {
            setOrderData(data)
        }
    }

    return (
        <CartProvider>
            <div>
                {currentPage === "stationary" && <StationaryPage setCurrentPage={handlePageChange} />}
                {currentPage === "cart" && <CartPage setCurrentPage={handlePageChange} />}
                {currentPage === "payment" && <PaymentPage setCurrentPage={handlePageChange} orderData={orderData} />}
                {currentPage === "confirmation" && (
                    <OrderConfirmationPage setCurrentPage={handlePageChange} orderData={orderData} />
                )}
            </div>
        </CartProvider>
    )
}

export default App
