"use client"
import DsceLogo from './Images/DsceLogo.jpg';
import { useState, useEffect } from "react"
import "./App.css"

const DSCEPortal = () => {
    const [dropdownOpen, setDropdownOpen] = useState(false)
    const [isLoaded, setIsLoaded] = useState(false)
    const [showLogin, setShowLogin] = useState(false)
    const [showTeacherLogin, setShowTeacherLogin] = useState(false)
    // Admin Login States
    const [showFoodCartLogin, setShowFoodCartLogin] = useState(false)
    const [showCanteenLogin, setShowCanteenLogin] = useState(false)
    const [showStationeryLogin, setShowStationeryLogin] = useState(false)
    const [showDepartmentLogin, setShowDepartmentLogin] = useState(false)
    
    // Login data states
    const [loginData, setLoginData] = useState({
        username: "",
        password: "",
    })
    const [teacherLoginData, setTeacherLoginData] = useState({
        email: "",
        password: "",
        remember: false,
    })
    const [foodCartLoginData, setFoodCartLoginData] = useState({
        email: "",
        password: "",
        remember: false,
    })
    const [canteenLoginData, setCanteenLoginData] = useState({
        email: "",
        password: "",
        remember: false,
    })
    const [stationeryLoginData, setStationeryLoginData] = useState({
        email: "",
        password: "",
        remember: false,
    })
    const [departmentLoginData, setDepartmentLoginData] = useState({
        email: "",
        password: "",
        remember: false,
    })
    
    // Password visibility states
    const [showPassword, setShowPassword] = useState(false)
    const [showTeacherPassword, setShowTeacherPassword] = useState(false)
    const [showFoodCartPassword, setShowFoodCartPassword] = useState(false)
    const [showCanteenPassword, setShowCanteenPassword] = useState(false)
    const [showStationeryPassword, setShowStationeryPassword] = useState(false)
    const [showDepartmentPassword, setShowDepartmentPassword] = useState(false)
    
    // Loading states
    const [isLoggingIn, setIsLoggingIn] = useState(false)

    // API Base URL - Update this to match your Tomcat deployment
    const API_BASE_URL = 'http://localhost:8080/dsce-portal/api'

    useEffect(() => {
        const timer = setTimeout(() => {
            setIsLoaded(true)
        }, 200)
        return () => clearTimeout(timer)
    }, [])

    // API Helper Functions
    const apiCall = async (endpoint, method = 'GET', data = null) => {
        try {
            const config = {
                method,
                headers: {
                    'Content-Type': 'application/json',
                },
            }
            
            if (data) {
                config.body = JSON.stringify(data)
            }
            
            const response = await fetch(`${API_BASE_URL}${endpoint}`, config)
            
            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`)
            }
            
            return await response.json()
        } catch (error) {
            console.error('API call failed:', error)
            throw error
        }
    }

    // Student Login Handler
    const handleLoginSubmit = async (e) => {
        e.preventDefault()
        if (!loginData.username || !loginData.password) {
            alert("Please enter both username and password")
            return
        }

        setIsLoggingIn(true)
        try {
            const response = await apiCall('/auth/student/login', 'POST', {
                username: loginData.username,
                password: loginData.password
            })
            
            if (response.token) {
                // Store token in localStorage
                localStorage.setItem('studentToken', response.token)
                localStorage.setItem('userType', 'student')
                localStorage.setItem('userData', JSON.stringify(response.user))
                
                alert(`Welcome ${response.user.name}! Redirecting to Student Dashboard...`)
                setShowLogin(false)
                setLoginData({ username: "", password: "" })
                
                // Redirect to student dashboard
                window.location.href = '/student-dashboard'
            }
        } catch (error) {
            alert("Login failed. Please check your credentials.")
        } finally {
            setIsLoggingIn(false)
        }
    }

    // Teacher Login Handler
    const handleTeacherLoginSubmit = async (e) => {
        e.preventDefault()
        if (!teacherLoginData.email || !teacherLoginData.password) {
            alert("Please enter both email and password")
            return
        }

        setIsLoggingIn(true)
        try {
            const response = await apiCall('/auth/teacher/login', 'POST', {
                username: teacherLoginData.email,
                password: teacherLoginData.password
            })
            
            if (response.token) {
                localStorage.setItem('teacherToken', response.token)
                localStorage.setItem('userType', 'teacher')
                localStorage.setItem('userData', JSON.stringify(response.user))
                
                alert(`Welcome ${response.user.name}! Redirecting to Faculty Dashboard...`)
                setShowTeacherLogin(false)
                setTeacherLoginData({ email: "", password: "", remember: false })
                
                // Redirect to teacher dashboard
                window.location.href = '/teacher-dashboard'
            }
        } catch (error) {
            alert("Login failed. Please check your credentials.")
        } finally {
            setIsLoggingIn(false)
        }
    }

    // Food Cart Admin Login Handler
    const handleFoodCartLoginSubmit = async (e) => {
        e.preventDefault()
        if (!foodCartLoginData.email || !foodCartLoginData.password) {
            alert("Please enter both email and password")
            return
        }

        setIsLoggingIn(true)
        try {
            const response = await apiCall('/auth/admin/login', 'POST', {
                username: foodCartLoginData.email,
                password: foodCartLoginData.password
            })
            
            if (response.token && response.user.adminType === 'Food Cart Admin') {
                localStorage.setItem('adminToken', response.token)
                localStorage.setItem('userType', 'admin')
                localStorage.setItem('adminType', 'foodcart')
                localStorage.setItem('userData', JSON.stringify(response.user))
                
                alert(`Welcome to Food Cart Management! Redirecting to Admin Dashboard...`)
                setShowFoodCartLogin(false)
                setFoodCartLoginData({ email: "", password: "", remember: false })
                
                // Redirect to food cart dashboard
                window.location.href = '/foodcart-dashboard'
            } else {
                alert("Invalid credentials for Food Cart Admin")
            }
        } catch (error) {
            alert("Login failed. Please check your credentials.")
        } finally {
            setIsLoggingIn(false)
        }
    }

    // Canteen Admin Login Handler
    const handleCanteenLoginSubmit = async (e) => {
        e.preventDefault()
        if (!canteenLoginData.email || !canteenLoginData.password) {
            alert("Please enter both email and password")
            return
        }

        setIsLoggingIn(true)
        try {
            const response = await apiCall('/auth/admin/login', 'POST', {
                username: canteenLoginData.email,
                password: canteenLoginData.password
            })
            
            if (response.token && response.user.adminType === 'Canteen Admin') {
                localStorage.setItem('adminToken', response.token)
                localStorage.setItem('userType', 'admin')
                localStorage.setItem('adminType', 'canteen')
                localStorage.setItem('userData', JSON.stringify(response.user))
                
                alert(`Welcome to Canteen Management! Redirecting to Admin Dashboard...`)
                setShowCanteenLogin(false)
                setCanteenLoginData({ email: "", password: "", remember: false })
                
                // Redirect to canteen dashboard
                window.location.href = '/canteen-dashboard'
            } else {
                alert("Invalid credentials for Canteen Admin")
            }
        } catch (error) {
            alert("Login failed. Please check your credentials.")
        } finally {
            setIsLoggingIn(false)
        }
    }

    // Stationery Admin Login Handler
    const handleStationeryLoginSubmit = async (e) => {
        e.preventDefault()
        if (!stationeryLoginData.email || !stationeryLoginData.password) {
            alert("Please enter both email and password")
            return
        }

        setIsLoggingIn(true)
        try {
            const response = await apiCall('/auth/admin/login', 'POST', {
                username: stationeryLoginData.email,
                password: stationeryLoginData.password
            })
            
            if (response.token && response.user.adminType === 'Stationery Admin') {
                localStorage.setItem('adminToken', response.token)
                localStorage.setItem('userType', 'admin')
                localStorage.setItem('adminType', 'stationery')
                localStorage.setItem('userData', JSON.stringify(response.user))
                
                alert(`Welcome to Stationery Management! Redirecting to Admin Dashboard...`)
                setShowStationeryLogin(false)
                setStationeryLoginData({ email: "", password: "", remember: false })
                
                // Redirect to stationery dashboard
                window.location.href = '/stationery-dashboard'
            } else {
                alert("Invalid credentials for Stationery Admin")
            }
        } catch (error) {
            alert("Login failed. Please check your credentials.")
        } finally {
            setIsLoggingIn(false)
        }
    }

    // Department Admin Login Handler
    const handleDepartmentLoginSubmit = async (e) => {
        e.preventDefault()
        if (!departmentLoginData.email || !departmentLoginData.password) {
            alert("Please enter both email and password")
            return
        }

        setIsLoggingIn(true)
        try {
            const response = await apiCall('/auth/department/login', 'POST', {
                username: departmentLoginData.email,
                password: departmentLoginData.password
            })
            
            if (response.token) {
                localStorage.setItem('departmentToken', response.token)
                localStorage.setItem('userType', 'department')
                localStorage.setItem('userData', JSON.stringify(response.department))
                
                alert(`Welcome to Department Management! Redirecting to Admin Dashboard...`)
                setShowDepartmentLogin(false)
                setDepartmentLoginData({ email: "", password: "", remember: false })
                
                // Redirect to department dashboard
                window.location.href = '/admin-dashboard'
            }
        } catch (error) {
            alert("Login failed. Please check your credentials.")
        } finally {
            setIsLoggingIn(false)
        }
    }

    // Event Handlers
    const handleStudentClick = () => {
        setShowLogin(true)
    }

    const handleTeacherClick = () => {
        setShowTeacherLogin(true)
    }

    const toggleAdminDropdown = () => {
        setDropdownOpen(!dropdownOpen)
    }

    const handleAdminOption = (option) => {
        setDropdownOpen(false)
        switch (option) {
            case "food-cart":
                setShowFoodCartLogin(true)
                break
            case "canteen":
                setShowCanteenLogin(true)
                break
            case "stationery":
                setShowStationeryLogin(true)
                break
            case "department":
                setShowDepartmentLogin(true)
                break
            default:
                break
        }
    }

    const handleClickOutside = (event) => {
        if (!event.target.closest(".dropdown-container")) {
            setDropdownOpen(false)
        }
    }

    // Form change handlers
    const handleLoginChange = (e) => {
        setLoginData({
            ...loginData,
            [e.target.name]: e.target.value,
        })
    }

    const handleTeacherLoginChange = (e) => {
        const { name, value, type, checked } = e.target
        setTeacherLoginData({
            ...teacherLoginData,
            [name]: type === "checkbox" ? checked : value,
        })
    }

    const handleFoodCartLoginChange = (e) => {
        const { name, value, type, checked } = e.target
        setFoodCartLoginData({
            ...foodCartLoginData,
            [name]: type === "checkbox" ? checked : value,
        })
    }

    const handleCanteenLoginChange = (e) => {
        const { name, value, type, checked } = e.target
        setCanteenLoginData({
            ...canteenLoginData,
            [name]: type === "checkbox" ? checked : value,
        })
    }

    const handleStationeryLoginChange = (e) => {
        const { name, value, type, checked } = e.target
        setStationeryLoginData({
            ...stationeryLoginData,
            [name]: type === "checkbox" ? checked : value,
        })
    }

    const handleDepartmentLoginChange = (e) => {
        const { name, value, type, checked } = e.target
        setDepartmentLoginData({
            ...departmentLoginData,
            [name]: type === "checkbox" ? checked : value,
        })
    }

    // Close handlers
    const closeLogin = () => {
        setShowLogin(false)
        setLoginData({ username: "", password: "" })
    }

    const closeTeacherLogin = () => {
        setShowTeacherLogin(false)
        setTeacherLoginData({ email: "", password: "", remember: false })
        setShowTeacherPassword(false)
    }

    const closeFoodCartLogin = () => {
        setShowFoodCartLogin(false)
        setFoodCartLoginData({ email: "", password: "", remember: false })
        setShowFoodCartPassword(false)
    }

    const closeCanteenLogin = () => {
        setShowCanteenLogin(false)
        setCanteenLoginData({ email: "", password: "", remember: false })
        setShowCanteenPassword(false)
    }

    const closeStationeryLogin = () => {
        setShowStationeryLogin(false)
        setStationeryLoginData({ email: "", password: "", remember: false })
        setShowStationeryPassword(false)
    }

    const closeDepartmentLogin = () => {
        setShowDepartmentLogin(false)
        setDepartmentLoginData({ email: "", password: "", remember: false })
        setShowDepartmentPassword(false)
    }

    // Password toggle handlers
    const toggleTeacherPassword = () => {
        setShowTeacherPassword(!showTeacherPassword)
    }

    const toggleFoodCartPassword = () => {
        setShowFoodCartPassword(!showFoodCartPassword)
    }

    const toggleCanteenPassword = () => {
        setShowCanteenPassword(!showCanteenPassword)
    }

    const toggleStationeryPassword = () => {
        setShowStationeryPassword(!showStationeryPassword)
    }

    const toggleDepartmentPassword = () => {
        setShowDepartmentPassword(!showDepartmentPassword)
    }

    useEffect(() => {
        document.addEventListener("click", handleClickOutside)
        return () => {
            document.removeEventListener("click", handleClickOutside)
        }
    }, [])

    // All your existing styles remain the same
    const styles = {
        container: {
            minHeight: "100vh",
            position: "relative",
            fontFamily: "system-ui, -apple-system, sans-serif",
        },
        backgroundImage: {
            position: "fixed",
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            backgroundImage:
                "url('https://ugcounselor-content.s3.ap-south-1.amazonaws.com/wp-content/uploads/2024/07/15161737/Dayananda-Sagar-College-Of-Engineering-1.jpg')",
            backgroundSize: "cover",
            backgroundPosition: "center",
            backgroundRepeat: "no-repeat",
            backgroundAttachment: "fixed",
        },
        overlay: {
            position: "fixed",
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
