                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 3,
                name: "Pomegranate Juice",
                description: "Antioxidant-rich pomegranate juice, fresh and healthy",
                price: 60,
                category: "Fresh Fruit Juices",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 4,
                name: "Watermelon Juice",
