                description: "Blend of seasonal fruits, packed with vitamins and minerals",
                price: 55,
                category: "Fresh Fruit Juices",
                image: "/placeholder.svg?height=120&width=120",
            },
        ],
        smoothies: [
            {
                id: 7,
                name: "Mango Smoothie",
                description: "Creamy mango smoothie with yogurt and honey",
                price: 70,
                category: "Smoothies & Shakes",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 8,
                name: "Banana Shake",
                description: "Rich banana shake with milk, nuts and a touch of vanilla",
                price: 65,
                category: "Smoothies & Shakes",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 9,
                name: "Strawberry Smoothie",
                description: "Fresh strawberry smoothie with yogurt and mint",
                price: 75,
                category: "Smoothies & Shakes",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 10,
                name: "Chocolate Milkshake",
                description: "Thick chocolate milkshake topped with whipped cream",
                price: 80,
                category: "Smoothies & Shakes",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 11,
                name: "Avocado Smoothie",
                description: "Healthy avocado smoothie with honey and lime",
                price: 85,
                category: "Smoothies & Shakes",
                image: "/placeholder.svg?height=120&width=120",
            },
        ],
        chinese: [
            {
                id: 12,
                name: "Veg Fried Rice Combo",
                description: "Veg Fried Rice + Manchurian + Spring Roll + Soup",
                price: 170,
                category: "Chinese Food",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 13,
                name: "Hakka Noodles Special",
                description: "Hakka Noodles + Chilli Paneer + Veg Soup + Fortune Cookie",
                price: 160,
                category: "Chinese Food",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 14,
                name: "Schezwan Rice Meal",
                description: "Schezwan Rice + Gobi Manchurian + Hot & Sour Soup",
                price: 180,
                category: "Chinese Food",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 15,
                name: "Chowmein Combo",
                description: "Veg Chowmein + Spring Roll + Manchow Soup + Cold Drink",
                price: 150,
                category: "Chinese Food",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 16,
                name: "Sweet & Sour Meal",
                description: "Sweet & Sour Vegetables + Fried Rice + Soup + Prawn Crackers",
                price: 190,
                category: "Chinese Food",
                image: "/placeholder.svg?height=120&width=120",
            },
        ],
        snacks: [
            {
                id: 17,
                name: "Samosa Chat",
                description: "2 Samosa + Chole + Chutney + Onion + Sev",
                price: 60,
                category: "Snacks and Chats",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 18,
                name: "Pav Bhaji",
                description: "2 Pav + Bhaji + Butter + Onion + Lemon + Pickle",
                price: 80,
                category: "Snacks and Chats",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 19,
                name: "Vada Pav Combo",
                description: "2 Vada Pav + Green Chutney + Fried Chilli + Tea",
                price: 50,
                category: "Snacks and Chats",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 20,
                name: "Momos Platter",
                description: "8 Veg Momos + Spicy Chutney + Soup + Salad",
                price: 90,
                category: "Snacks and Chats",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 21,
                name: "Sandwich Combo",
                description: "Grilled Sandwich + French Fries + Ketchup + Cold Drink",
                price: 70,
                category: "Snacks and Chats",
                image: "/placeholder.svg?height=120&width=120",
            },
        ],
        beverages: [
            {
                id: 22,
                name: "Fresh Lime Soda",
                description: "Fresh Lime + Soda + Mint + Salt/Sugar + Ice",
                price: 40,
                category: "Hot & Cold Beverages",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 23,
                name: "Mango Lassi",
                description: "Fresh Mango Pulp + Yogurt + Sugar + Cardamom + Ice",
                price: 60,
                category: "Hot & Cold Beverages",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 24,
                name: "Filter Coffee",
                description: "South Indian Filter Coffee + Milk + Sugar",
                price: 30,
                category: "Hot & Cold Beverages",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 25,
                name: "Masala Chai",
                description: "Indian Spiced Tea + Milk + Ginger + Cardamom",
                price: 25,
                category: "Hot & Cold Beverages",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 26,
                name: "Iced Tea",
                description: "Refreshing iced tea with lemon and mint",
                price: 45,
                category: "Hot & Cold Beverages",
                image: "/placeholder.svg?height=120&width=120",
            },
        ],
    }

    const handleAddToCart = (item) => {
        addToCart(item)
        setAlertMessage(`${item.name} added to cart!`)
        setShowAlert(true)
        setTimeout(() => {
            setShowAlert(false)
        }, 2000)
    }

    const styles = {
        container: {
            minHeight: "100vh",
            background: "linear-gradient(135deg, #0f172a, #1e293b)",
            fontFamily: "system-ui, -apple-system, sans-serif",
            color: "white",
        },
        header: {
            background: "linear-gradient(135deg, #3b82f6, #10b981)",
            padding: "1rem",
            display: "flex",
            alignItems: "center",
            justifyContent: "space-between",
            boxShadow: "0 4px 12px rgba(0, 0, 0, 0.3)",
            position: "sticky",
            top: 0,
            zIndex: 50,
        },
        headerTitle: {
            fontSize: "1.5rem",
            fontWeight: "bold",
            color: "white",
            margin: 0,
        },
        cartContainer: {
            position: "relative",
            background: "rgba(255, 255, 255, 0.2)",
            borderRadius: "50%",
            width: "40px",
            height: "40px",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            cursor: "pointer",
            transition: "all 0.3s ease",
        },
        cartIcon: {
            fontSize: "1.25rem",
            color: "white",
        },
        cartBadge: {
            position: "absolute",
            top: "-8px",
            right: "-8px",
            background: "#ef4444",
            color: "white",
            borderRadius: "50%",
            width: "20px",
            height: "20px",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            fontSize: "0.75rem",
            fontWeight: "bold",
        },
        mainContent: {
            display: "flex",
            minHeight: "calc(100vh - 80px)",
        },
        sidebar: {
            width: "280px",
            background: "rgba(15, 23, 42, 0.95)",
            backdropFilter: "blur(12px)",
            borderRight: "1px solid #475569",
            padding: "1rem 0",
            overflowY: "auto",
        },
        categoryItem: {
            padding: "1rem 1.5rem",
            cursor: "pointer",
            transition: "all 0.3s ease",
            borderLeft: "4px solid transparent",
            display: "flex",
            alignItems: "center",
            gap: "0.75rem",
        },
        categoryItemActive: {
            background: "linear-gradient(90deg, rgba(59, 130, 246, 0.2), rgba(16, 185, 129, 0.2))",
            borderLeftColor: "#60a5fa",
            color: "#60a5fa",
        },
        categoryIcon: {
            fontSize: "1.25rem",
        },
        categoryName: {
            fontSize: "0.95rem",
            fontWeight: 500,
        },
        contentArea: {
            flex: 1,
            padding: "2rem",
            overflowY: "auto",
        },
        itemsGrid: {
            display: "grid",
            gap: "1.5rem",
        },
        menuItem: {
            background: "rgba(30, 41, 59, 0.8)",
            backdropFilter: "blur(12px)",
            borderRadius: "1rem",
            padding: "1.5rem",
            border: "1px solid #475569",
            display: "flex",
            gap: "1.5rem",
            transition: "all 0.3s ease",
        },
        itemImage: {
            width: "120px",
            height: "120px",
            borderRadius: "0.75rem",
            objectFit: "cover",
            flexShrink: 0,
        },
        itemDetails: {
            flex: 1,
            display: "flex",
            flexDirection: "column",
            justifyContent: "space-between",
        },
        itemName: {
            fontSize: "1.25rem",
            fontWeight: "bold",
            color: "white",
            margin: 0,
            marginBottom: "0.5rem",
        },
        itemDescription: {
            color: "#cbd5e1",
            fontSize: "0.95rem",
            lineHeight: 1.5,
            marginBottom: "1rem",
        },
        itemFooter: {
            display: "flex",
            alignItems: "center",
            justifyContent: "space-between",
        },
        itemPrice: {
            fontSize: "1.5rem",
            fontWeight: "bold",
            color: "#60a5fa",
        },
        addButton: {
            background: "linear-gradient(135deg, #ef4444, #dc2626)",
            border: "none",
            borderRadius: "2rem",
            padding: "0.75rem 1.5rem",
            color: "white",
            fontSize: "1rem",
            fontWeight: "bold",
            cursor: "pointer",
            transition: "all 0.3s ease",
            textTransform: "uppercase",
            letterSpacing: "0.05em",
        },
        alert: {
            position: "fixed",
            top: "100px",
            left: "50%",
            transform: "translateX(-50%)",
            background: "linear-gradient(135deg, #10b981, #059669)",
            color: "white",
            padding: "1rem 2rem",
            borderRadius: "2rem",
            boxShadow: "0 8px 25px rgba(16, 185, 129, 0.4)",
            zIndex: 100,
            fontSize: "1rem",
            fontWeight: "bold",
            opacity: showAlert ? 1 : 0,
            visibility: showAlert ? "visible" : "hidden",
            transition: "all 0.3s ease",
        },
    }

    return (
        <div style={styles.container}>
            {/* Alert */}
            <div style={styles.alert}>{alertMessage}</div>
            {/* Header */}
            <header style={styles.header}>
                <button
                    style={{
                        background: "rgba(255, 255, 255, 0.2)",
                        border: "none",
                        borderRadius: "50%",
                        width: "40px",
                        height: "40px",
                        display: "flex",
                        alignItems: "center",
                        justifyContent: "center",
                        cursor: "pointer",
                        color: "white",
                        fontSize: "1.25rem",
                        transition: "all 0.3s ease",
                    }}
                    onClick={() => alert("Welcome to DSCE Food/Juice Cart!")}
                >
                    ðŸ 
                </button>
                <h1 style={styles.headerTitle}>DSCE Food/Juice Cart</h1>
                <div style={{ ...styles.cartContainer }} onClick={() => setCurrentPage("cart")}>
                    <span style={styles.cartIcon}>ðŸ›’</span>
                    {cartCount > 0 && <span style={styles.cartBadge}>{cartCount}</span>}
                </div>
            </header>
            {/* Main Content */}
            <div style={styles.mainContent}>
                {/* Sidebar */}
                <div style={styles.sidebar}>
                    {categories.map((category) => (
                        <div
                            key={category.id}
                            style={{
                                ...styles.categoryItem,
                                ...(selectedCategory === category.id ? styles.categoryItemActive : {}),
                            }}
                            onClick={() => setSelectedCategory(category.id)}
                        >
                            <span style={styles.categoryIcon}>{category.icon}</span>
                            <span style={styles.categoryName}>{category.name}</span>
                        </div>
                    ))}
                </div>
                {/* Content Area */}
                <div style={styles.contentArea}>
                    <div style={styles.itemsGrid}>
                        {menuItems[selectedCategory]?.map((item) => (
                            <div key={item.id} style={styles.menuItem}>
                                <img src={item.image || "/placeholder.svg"} alt={item.name} style={styles.itemImage} />
                                <div style={styles.itemDetails}>
                                    <div>
                                        <h3 style={styles.itemName}>{item.name}</h3>
                                        <p style={styles.itemDescription}>{item.description}</p>
                                    </div>
                                    <div style={styles.itemFooter}>
                                        <span style={styles.itemPrice}>â‚¹{item.price}/-</span>
                                        <button style={styles.addButton} onClick={() => handleAddToCart(item)}>
                                            Add +
                                        </button>
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        </div>
    )
}

