import React, { useState, useEffect } from "react";

// Top-level component managing all pages and cart state
const FoodJuiceCartApp = () => {
  // Current page: menu, cart, payment, confirmation
  const [currentPage, setCurrentPage] = useState("menu");
  const [orderData, setOrderData] = useState(null);

  // Cart state managed here (no context)
  const [cartItems, setCartItems] = useState([]);

  // Add to cart handler
  const addToCart = (item) => {
    setCartItems((prev) => {
      const existing = prev.find((i) => i.id === item.id);
      if (existing) {
        return prev.map((i) =>
          i.id === item.id ? { ...i, quantity: i.quantity + 1 } : i
        );
      } else {
        return [...prev, { ...item, quantity: 1 }];
      }
    });
  };

  // Remove from cart handler
  const removeFromCart = (itemId) => {
    setCartItems((prev) => prev.filter((i) => i.id !== itemId));
  };

  // Update quantity handler (+1 / -1)
  const updateQuantity = (itemId, change) => {
    setCartItems((prev) =>
      prev
        .map((i) => {
          if (i.id === itemId) {
            const newQty = i.quantity + change;
            if (newQty < 1) return null;
            return { ...i, quantity: newQty };
          }
          return i;
        })
        .filter(Boolean)
    );
  };

  // Get total number of items in cart
  const getCartCount = () => {
    return cartItems.reduce((sum, i) => sum + i.quantity, 0);
  };

  // Handler for changing page and optionally passing order data
  const handleSetCurrentPage = (page, data = null) => {
    setCurrentPage(page);
    if (data) {
      setOrderData(data);
    }
  };

  // Show current page component
  return (
    <>
      {currentPage === "menu" && (
        <FoodJuiceCartPage
          setCurrentPage={handleSetCurrentPage}
          addToCart={addToCart}
          getCartCount={getCartCount}
        />
      )}
      {currentPage === "cart" && (
        <CartPage
          setCurrentPage={handleSetCurrentPage}
          cartItems={cartItems}
          removeFromCart={removeFromCart}
          updateQuantity={updateQuantity}
        />
      )}
      {currentPage === "payment" && (
        <PaymentPage setCurrentPage={handleSetCurrentPage} orderData={orderData} />
      )}
      {currentPage === "confirmation" && (
        <OrderConfirmationPage
          setCurrentPage={handleSetCurrentPage}
          orderData={orderData}
        />
      )}
    </>
  );
};

// Food/Juice Menu Page Component
