      .card-row {
        display: grid;
        grid-template-columns: 1fr 1fr;
        gap: 1rem;
      }
      .pay-button {
        width: 100%;
        background: linear-gradient(135deg, #10b981, #059669);
        border: none;
        border-radius: 2rem;
        padding: 1rem 2rem;
        color: white;
        font-size: 1.125rem;
        font-weight: bold;
        cursor: pointer;
        transition: all 0.3s ease;
        text-transform: uppercase;
        letter-spacing: 0.05em;
        display: flex;
        align-items: center;
        justify-content: center;
        gap: 0.5rem;
      }
      .pay-button:hover {
        transform: translateY(-2px);
        box-shadow: 0 8px 25px rgba(16,185,129,0.4);
      }
      .pay-button:disabled {
        opacity: 0.6;
        cursor: not-allowed;
        transform: none;
      }
      .processing-overlay {
        position: fixed;
        top: 0; left: 0; right: 0; bottom: 0;
        background: rgba(0,0,0,0.8);
        z-index: 100;
        display: flex;
        align-items: center;
        justify-content: center;
        flex-direction: column;
        gap: 1rem;
      }
      .spinner {
        width: 50px;
        height: 50px;
        border: 4px solid #334155;
        border-top: 4px solid #10b981;
        border-radius: 50%;
        animation: spin 1s linear infinite;
      }
      @keyframes spin {
        0% {transform: rotate(0deg);}
        100% {transform: rotate(360deg);}
      }
      .bill-row {
        display: flex;
        justify-content: space-between;
        padding: 0.5rem 0;
        border-bottom: 1px solid #334155;
      }
      .bill-row-total {
        border-bottom: 2px solid #60a5fa;
        font-size: 1.25rem;
        font-weight: bold;
        color: #60a5fa;
      }
      @media (max-width: 768px) {
        .main-content {
          padding: 1rem;
        }
        .card-row {
          grid-template-columns: 1fr;
        }
        .payment-method {
          flex-direction: column;
          text-align: center;
        }
      }
      `}</style>

      <div className="container">
        <header className="header">
          <button className="back-button" onClick={() => setCurrentPage("cart")}>
            â†
          </button>
          <h1 className="header-title">Payment</h1>
          <div style={{ width: "40px" }}></div>
        </header>

        <div className="main-content">
          {/* Order Summary */}
          <div className="section">
            <h2 className="section-title">ðŸ“‹ Order Summary</h2>
            <div className="bill-row">
              <span>Items ({orderData.itemCount})</span>
              <span>â‚¹{orderData.subtotal}/-</span>
            </div>
            <div className="bill-row">
              <span>GST (5%)</span>
              <span>â‚¹{orderData.tax}/-</span>
            </div>

            <div className="bill-row bill-row-total">
              <span>Total Amount</span>
              <span>â‚¹{orderData.total}/-</span>
            </div>
          </div>

          {/* Payment Methods */}
          <div className="section">
            <h2 className="section-title">ðŸ’³ Select Payment Method</h2>
            {paymentMethods.map((method) => (
              <div
                key={method.id}
                className={`payment-method ${selectedPaymentMethod === method.id ? "active" : ""}`}
                onClick={() => setSelectedPaymentMethod(method.id)}
                tabIndex={0}
                onKeyDown={(e) => {
                  if (e.key === "Enter" || e.key === " ") {
                    setSelectedPaymentMethod(method.id);
                  }
                }}
                aria-pressed={selectedPaymentMethod === method.id}
                role="radio"
              >
                <span className="payment-icon">{method.icon}</span>
                <div className="payment-info">
                  <div className="payment-name">{method.name}</div>
                  <div className="payment-description">{method.description}</div>
                </div>
                <div
                  className="radio-indicator"
                  style={{ color: selectedPaymentMethod === method.id ? "#60a5fa" : "#6b7280" }}
                >
                  {selectedPaymentMethod === method.id ? "â—" : "â—‹"}
                </div>
              </div>
            ))}
          </div>

          {/* Payment Details */}
          {selectedPaymentMethod !== "cod" && (
            <div className="section">
              <h2 className="section-title">
                {selectedPaymentMethod === "upi" ? "ðŸ“± UPI Details" : "ðŸ’³ Card Details"}
              </h2>
              {selectedPaymentMethod === "upi" && (
                <div className="input-group">
                  <label className="input-label" htmlFor="upiId">
                    UPI ID *
                  </label>
                  <input
                    id="upiId"
                    type="text"
                    name="upiId"
                    value={paymentDetails.upiId}
                    onChange={handleInputChange}
                    className="input-field"
                    placeholder="yourname@upi"
                    required
                  />
                </div>
              )}

              {selectedPaymentMethod === "card" && (
                <>
                  <div className="input-group">
                    <label className="input-label" htmlFor="cardName">
                      Cardholder Name *
                    </label>
                    <input
                      id="cardName"
                      type="text"
                      name="cardName"
                      value={paymentDetails.cardName}
                      onChange={handleInputChange}
                      className="input-field"
                      placeholder="Enter cardholder name"
                      required
                    />
                  </div>
                  <div className="input-group">
                    <label className="input-label" htmlFor="cardNumber">
                      Card Number *
                    </label>
                    <input
                      id="cardNumber"
                      type="text"
                      name="cardNumber"
                      value={paymentDetails.cardNumber}
                      onChange={handleInputChange}
                      className="input-field"
                      placeholder="1234 5678 9012 3456"
                      maxLength="19"
                      required
                    />
                  </div>
                  <div className="card-row">
                    <div className="input-group">
                      <label className="input-label" htmlFor="cardExpiry">
                        Expiry Date *
                      </label>
                      <input
                        id="cardExpiry"
                        type="text"
                        name="cardExpiry"
                        value={paymentDetails.cardExpiry}
                        onChange={handleInputChange}
                        className="input-field"
                        placeholder="MM/YY"
                        maxLength="5"
                        required
                      />
                    </div>
                    <div className="input-group">
                      <label className="input-label" htmlFor="cardCvv">
                        CVV *
                      </label>
                      <input
                        id="cardCvv"
                        type="text"
                        name="cardCvv"
                        value={paymentDetails.cardCvv}
                        onChange={handleInputChange}
                        className="input-field"
                        placeholder="123"
                        maxLength="3"
                        required
                      />
                    </div>
                  </div>
                </>
              )}
            </div>
          )}

          {/* Pay Button */}
          <button className="pay-button" onClick={handlePayment} disabled={isProcessing}>
            {selectedPaymentMethod === "cod" ? "Place Order" : `Pay â‚¹${orderData.total}/-`}
            {selectedPaymentMethod !== "cod" && <span>ðŸ’³</span>}
          </button>
        </div>

        {/* Processing Overlay */}
        {isProcessing && (
          <div className="processing-overlay" role="alert" aria-live="assertive">
            <div className="spinner"></div>
            <div style={{ color: "white", fontSize: "1.25rem", fontWeight: "bold" }}>
              {selectedPaymentMethod === "cod" ? "Placing your order..." : "Processing payment..."}
            </div>
            <div style={{ color: "#cbd5e1", textAlign: "center" }}>
              Please wait while we {selectedPaymentMethod === "cod" ? "confirm your order" : "process your payment"}
            </div>
          </div>
        )}
      </div>
    </>
  );
};

// Order Confirmation Page Component
const OrderConfirmationPage = ({ setCurrentPage, orderData }) => {
