      }
      .success-card {
        background: linear-gradient(135deg, #10b981, #059669);
        border-radius: 1rem;
        padding: 3rem 2rem;
        text-align: center;
        margin-bottom: 2rem;
        box-shadow: 0 8px 25px rgba(16,185,129,0.3);
      }
