      }
      .details-card {
        background: rgba(30,41,59,0.8);
        backdrop-filter: blur(12px);
        border-radius: 1rem;
        padding: 2rem;
        border: 1px solid #475569;
        margin-bottom: 2rem;
      }
      .details-title {
        font-size: 1.25rem;
        font-weight: bold;
        color: #60a5fa;
        margin-bottom: 1rem;
      }
      .detail-row {
        display: flex;
        justify-content: space-between;
        padding: 0.5rem 0;
        border-bottom: 1px solid #334155;
      }
      .action-buttons {
        display: grid;
        grid-template-columns: 1fr 1fr;
        gap: 1rem;
      }
      .button {
        padding: 1rem 2rem;
        border-radius: 2rem;
