"use client"

import { useState, useEffect } from "react"

const DepartmentDashboard = () => {
    const [activeTab, setActiveTab] = useState("students")
    const [isLoaded, setIsLoaded] = useState(false)
    const [users, setUsers] = useState([])
    const [loading, setLoading] = useState(false)
    const [message, setMessage] = useState({ type: "", text: "" })

    // Form states for different user types
    const [studentForm, setStudentForm] = useState({
        firstName: "",
        lastName: "",
        email: "",
        department: "",
        semester: "",
        phoneNumber: "",
        address: "",
    })

    const [teacherForm, setTeacherForm] = useState({
        firstName: "",
        lastName: "",
        email: "",
        department: "",
        designation: "",
        specialization: "",
        phoneNumber: "",
    })

    const [adminForm, setAdminForm] = useState({
        firstName: "",
        lastName: "",
        email: "",
        phoneNumber: "",
        adminType: "FOOD_CART",
    })

    const departments = ["COMPUTER SCIENCE", "INFORMATION SCIENCE", "ELECTRONICS", "MECHANICAL", "CIVIL", "ELECTRICAL"]
    const semesters = ["1", "2", "3", "4", "5", "6", "7", "8"]
    const designations = ["Assistant Professor", "Associate Professor", "Professor", "HOD", "Principal"]
    const adminTypes = [
        { value: "FOOD_CART", label: "Food Cart Management" },
        { value: "CANTEEN", label: "Canteen Management" },
        { value: "STATIONERY", label: "Stationery Management" },
    ]

    useEffect(() => {
        const timer = setTimeout(() => {
            setIsLoaded(true)
        }, 200)
        return () => clearTimeout(timer)
    }, [])

    useEffect(() => {
        fetchUsers()
    }, [])

    const fetchUsers = async () => {
        try {
            const token = localStorage.getItem("departmentToken")
            const response = await fetch("/api/department/users", {
                headers: {
                    Authorization: `Bearer ${token}`,
                    "Content-Type": "application/json",
                },
            })

            if (response.ok) {
                const data = await response.json()
                setUsers(data)
            }
        } catch (error) {
            console.error("Error fetching users:", error)
        }
    }

    const showMessage = (type, text) => {
        setMessage({ type, text })
        setTimeout(() => setMessage({ type: "", text: "" }), 5000)
    }

    const handleStudentSubmit = async (e) => {
        e.preventDefault()
        setLoading(true)

        try {
            const token = localStorage.getItem("departmentToken")
            const response = await fetch("/api/department/create-student", {
                method: "POST",
                headers: {
                    Authorization: `Bearer ${token}`,
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(studentForm),
            })

            const data = await response.json()

            if (response.ok) {
                showMessage("success", `Student created successfully! USN: ${data.userId}, Password: ${data.password}`)
                setStudentForm({
                    firstName: "",
                    lastName: "",
                    email: "",
                    department: "",
                    semester: "",
                    phoneNumber: "",
                    address: "",
                })
                fetchUsers()
            } else {
                showMessage("error", data.message || "Failed to create student")
            }
        } catch (error) {
            showMessage("error", "Network error occurred")
        } finally {
            setLoading(false)
        }
    }

    const handleTeacherSubmit = async (e) => {
        e.preventDefault()
        setLoading(true)

        try {
            const token = localStorage.getItem("departmentToken")
            const response = await fetch("/api/department/create-teacher", {
                method: "POST",
                headers: {
                    Authorization: `Bearer ${token}`,
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(teacherForm),
            })

            const data = await response.json()

            if (response.ok) {
                showMessage("success", `Teacher created successfully! Employee ID: ${data.userId}, Password: ${data.password}`)
                setTeacherForm({
                    firstName: "",
                    lastName: "",
                    email: "",
                    department: "",
                    designation: "",
                    specialization: "",
                    phoneNumber: "",
                })
                fetchUsers()
            } else {
                showMessage("error", data.message || "Failed to create teacher")
            }
        } catch (error) {
            showMessage("error", "Network error occurred")
        } finally {
            setLoading(false)
        }
    }

    const handleAdminSubmit = async (e) => {
        e.preventDefault()
        setLoading(true)

        try {
            const token = localStorage.getItem("departmentToken")
            const response = await fetch("/api/department/create-admin", {
                method: "POST",
                headers: {
                    Authorization: `Bearer ${token}`,
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(adminForm),
            })

            const data = await response.json()

            if (response.ok) {
                showMessage("success", `Admin created successfully! Email: ${data.email}, Password: ${data.password}`)
                setAdminForm({
                    firstName: "",
                    lastName: "",
                    email: "",
                    phoneNumber: "",
                    adminType: "FOOD_CART",
                })
                fetchUsers()
            } else {
                showMessage("error", data.message || "Failed to create admin")
            }
        } catch (error) {
            showMessage("error", "Network error occurred")
        } finally {
            setLoading(false)
        }
    }

    const handleInputChange = (formType, field, value) => {
        switch (formType) {
            case "student":
                setStudentForm((prev) => ({ ...prev, [field]: value }))
                break
            case "teacher":
                setTeacherForm((prev) => ({ ...prev, [field]: value }))
                break
            case "admin":
                setAdminForm((prev) => ({ ...prev, [field]: value }))
                break
        }
    }

    const filteredUsers = users.filter((user) => {
        switch (activeTab) {
            case "students":
                return user.userType === "STUDENT"
            case "teachers":
                return user.userType === "TEACHER"
            case "admins":
                return ["FOOD_CART", "CANTEEN", "STATIONERY"].includes(user.userType)
            default:
                return true
        }
    })

    const tabs = [
        { id: "students", label: "👨‍🎓 Students", icon: "👨‍🎓" },
        { id: "teachers", label: "👩‍🏫 Teachers", icon: "👩‍🏫" },
        { id: "admins", label: "🔧 Admin Users", icon: "🔧" },
    ]

    // Inline Styles
    const styles = {
        dashboardContainer: {
            minHeight: "100vh",
            position: "relative",
            fontFamily: "system-ui, -apple-system, sans-serif",
        },
        dashboardBackground: {
            position: "fixed",
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            backgroundImage:
                "url('https://images.unsplash.com/photo-1562774053-701939374585?ixlib=rb-4.0.3&auto=format&fit=crop&w=1920&q=80')",
            backgroundSize: "cover",
            backgroundPosition: "center",
            backgroundRepeat: "no-repeat",
            backgroundAttachment: "fixed",
        },
        dashboardOverlay: {
            position: "fixed",
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            background: "rgba(15, 23, 42, 0.85)",
            zIndex: 10,
        },
        dashboardHeader: {
            position: "relative",
            zIndex: 20,
            background: "rgba(15, 23, 42, 0.95)",
            backdropFilter: "blur(12px)",
            borderBottom: "1px solid #475569",
            padding: "1rem 2rem",
            opacity: isLoaded ? 1 : 0,
            transform: isLoaded ? "translateY(0)" : "translateY(-20px)",
            transition: "all 1s ease-out",
        },
        headerContent: {
            maxWidth: "1400px",
            margin: "0 auto",
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            flexWrap: "wrap",
            gap: "1rem",
        },
        headerLeft: {
            display: "flex",
            alignItems: "center",
        },
        logoSection: {
            display: "flex",
            alignItems: "center",
            gap: "1rem",
        },
        logoCircle: {
            width: "60px",
            height: "60px",
            borderRadius: "50%",
            background: "linear-gradient(135deg, #3b82f6, #10b981)",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            boxShadow: "0 8px 20px rgba(59, 130, 246, 0.4)",
        },
        logoText: {
            color: "white",
            fontWeight: "bold",
            fontSize: "1rem",
        },
        headerText: {
            display: "flex",
            flexDirection: "column",
        },
        headerTitle: {
            color: "white",
            fontSize: "1.5rem",
            fontWeight: "bold",
            margin: 0,
            background: "linear-gradient(135deg, #60a5fa, #34d399)",
            WebkitBackgroundClip: "text",
            WebkitTextFillColor: "transparent",
            backgroundClip: "text",
        },
        headerSubtitle: {
            color: "#cbd5e1",
            fontSize: "0.875rem",
            margin: 0,
        },
        logoutBtn: {
            background: "linear-gradient(135deg, #ef4444, #dc2626)",
            color: "white",
            border: "none",
            padding: "0.75rem 1.5rem",
            borderRadius: "0.75rem",
            fontWeight: 600,
            cursor: "pointer",
            transition: "all 0.3s ease",
        },
        dashboardMain: {
            position: "relative",
            zIndex: 20,
            padding: "2rem",
            opacity: isLoaded ? 1 : 0,
            transform: isLoaded ? "translateY(0)" : "translateY(20px)",
            transition: "all 1s ease-out 0.3s",
        },
        dashboardContent: {
            maxWidth: "1400px",
            margin: "0 auto",
        },
        message: {
            background: "rgba(15, 23, 42, 0.95)",
            backdropFilter: "blur(12px)",
            borderRadius: "1rem",
            padding: "1rem 1.5rem",
            marginBottom: "2rem",
            border: `1px solid ${message.type === "success" ? "#10b981" : "#ef4444"}`,
            color: message.type === "success" ? "#10b981" : "#ef4444",
            display: message.text ? "flex" : "none",
            alignItems: "center",
            gap: "0.75rem",
            animation: "slideIn 0.3s ease-out",
        },
        messageIcon: {
            fontSize: "1.25rem",
        },
        tabNavigation: {
            display: "flex",
            gap: "1rem",
            marginBottom: "2rem",
            background: "rgba(15, 23, 42, 0.95)",
            backdropFilter: "blur(12px)",
            borderRadius: "1rem",
            padding: "0.5rem",
            border: "1px solid #475569",
            flexWrap: "wrap",
        },
        tabButton: {
            flex: 1,
            minWidth: "150px",
            background: "transparent",
            border: "none",
            color: "#cbd5e1",
            padding: "1rem 1.5rem",
            borderRadius: "0.75rem",
            fontWeight: 600,
            cursor: "pointer",
            transition: "all 0.3s ease",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            gap: "0.5rem",
        },
        tabButtonActive: {
            background: "linear-gradient(135deg, #3b82f6, #10b981)",
            color: "white",
            boxShadow: "0 4px 12px rgba(59, 130, 246, 0.3)",
        },
        tabIcon: {
            fontSize: "1.25rem",
        },
        contentSection: {
            animation: "fadeIn 0.5s ease-out",
        },
        sectionGrid: {
            display: "grid",
            gridTemplateColumns: window.innerWidth > 1200 ? "1fr 1fr" : "1fr",
            gap: "2rem",
        },
        formCard: {
            background: "rgba(15, 23, 42, 0.95)",
            backdropFilter: "blur(12px)",
            borderRadius: "1.5rem",
            border: "1px solid #475569",
            padding: "2rem",
            boxShadow: "0 25px 50px rgba(0, 0, 0, 0.3)",
        },
        formTitle: {
            color: "white",
            fontSize: "1.5rem",
            fontWeight: "bold",
            marginBottom: "2rem",
            display: "flex",
            alignItems: "center",
            gap: "0.75rem",
            flexWrap: "wrap",
        },
        formIcon: {
            fontSize: "1.75rem",
        },
        userForm: {
            display: "flex",
            flexDirection: "column",
            gap: "1.5rem",
        },
        formRow: {
            display: "grid",
            gridTemplateColumns: window.innerWidth > 768 ? "1fr 1fr" : "1fr",
            gap: "1rem",
        },
        formGroup: {
            display: "flex",
            flexDirection: "column",
            gap: "0.5rem",
        },
        formLabel: {
            color: "#cbd5e1",
            fontWeight: 500,
            fontSize: "0.875rem",
        },
        formInput: {
            background: "rgba(30, 41, 59, 0.8)",
            border: "1px solid #475569",
            borderRadius: "0.75rem",
            padding: "1rem",
            color: "white",
            fontSize: "1rem",
            transition: "all 0.3s ease",
            outline: "none",
        },
        formTextarea: {
            background: "rgba(30, 41, 59, 0.8)",
            border: "1px solid #475569",
            borderRadius: "0.75rem",
            padding: "1rem",
            color: "white",
            fontSize: "1rem",
            transition: "all 0.3s ease",
            outline: "none",
            resize: "vertical",
            minHeight: "80px",
            fontFamily: "inherit",
        },
        formSelect: {
            background: "rgba(30, 41, 59, 0.8)",
            border: "1px solid #475569",
            borderRadius: "0.75rem",
            padding: "1rem",
            color: "white",
            fontSize: "1rem",
            transition: "all 0.3s ease",
            outline: "none",
        },
        submitBtn: {
            background: "linear-gradient(135deg, #3b82f6, #10b981)",
            color: "white",
            border: "none",
            padding: "1rem 2rem",
            borderRadius: "0.75rem",
            fontWeight: 600,
            fontSize: "1.125rem",
            cursor: "pointer",
            transition: "all 0.3s ease",
            textTransform: "uppercase",
            letterSpacing: "0.05em",
            marginTop: "1rem",
            opacity: loading ? 0.6 : 1,
        },
        listCard: {
            background: "rgba(15, 23, 42, 0.95)",
            backdropFilter: "blur(12px)",
            borderRadius: "1.5rem",
            border: "1px solid #475569",
            padding: "2rem",
            boxShadow: "0 25px 50px rgba(0, 0, 0, 0.3)",
        },
        listTitle: {
            color: "white",
            fontSize: "1.5rem",
            fontWeight: "bold",
            marginBottom: "2rem",
            display: "flex",
            alignItems: "center",
            gap: "0.75rem",
            flexWrap: "wrap",
        },
        listIcon: {
            fontSize: "1.75rem",
        },
        usersList: {
            display: "flex",
            flexDirection: "column",
            gap: "1rem",
            maxHeight: "600px",
            overflowY: "auto",
        },
        userItem: {
            background: "rgba(30, 41, 59, 0.6)",
            border: "1px solid #475569",
            borderRadius: "1rem",
            padding: "1.5rem",
            display: "flex",
            alignItems: "center",
            gap: "1rem",
            transition: "all 0.3s ease",
            flexWrap: "wrap",
        },
        userAvatar: {
            width: "50px",
            height: "50px",
            borderRadius: "50%",
            background: "linear-gradient(135deg, #3b82f6, #10b981)",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            fontSize: "1.5rem",
            flexShrink: 0,
        },
        userInfo: {
            flex: 1,
            minWidth: "200px",
        },
        userName: {
            color: "white",
            fontWeight: 600,
            fontSize: "1rem",
            marginBottom: "0.25rem",
        },
        userEmail: {
            color: "#cbd5e1",
            fontSize: "0.875rem",
            marginBottom: "0.25rem",
        },
        userType: {
            color: "#94a3b8",
            fontSize: "0.75rem",
            textTransform: "uppercase",
            letterSpacing: "0.05em",
        },
        userStatus: {
            flexShrink: 0,
        },
        statusBadge: {
            padding: "0.5rem 1rem",
            borderRadius: "2rem",
            fontSize: "0.75rem",
            fontWeight: 600,
            textTransform: "uppercase",
            letterSpacing: "0.05em",
            background: "rgba(16, 185, 129, 0.2)",
            color: "#10b981",
            border: "1px solid #10b981",
        },
        emptyState: {
            textAlign: "center",
            padding: "3rem 2rem",
            color: "#94a3b8",
        },
        emptyIcon: {
            fontSize: "3rem",
            display: "block",
            marginBottom: "1rem",
        },
        emptyText: {
            fontSize: "1.125rem",
            margin: 0,
        },
    }

    const handleInputFocus = (e) => {
        e.target.style.borderColor = "#60a5fa"
        e.target.style.boxShadow = "0 0 0 3px rgba(96, 165, 250, 0.2)"
    }

    const handleInputBlur = (e) => {
        e.target.style.borderColor = "#475569"
        e.target.style.boxShadow = "none"
    }

    const handleButtonHover = (e, isHover) => {
        if (isHover && !loading) {
            e.target.style.transform = "translateY(-2px)"
            e.target.style.boxShadow = "0 8px 20px rgba(59, 130, 246, 0.4)"
        } else {
            e.target.style.transform = "translateY(0)"
            e.target.style.boxShadow = "none"
        }
    }

    const handleLogoutHover = (e, isHover) => {
        if (isHover) {
            e.target.style.transform = "translateY(-2px)"
            e.target.style.boxShadow = "0 8px 20px rgba(239, 68, 68, 0.4)"
        } else {
            e.target.style.transform = "translateY(0)"
            e.target.style.boxShadow = "none"
        }
    }

    const handleTabHover = (e, isHover, isActive) => {
        if (!isActive && isHover) {
            e.target.style.background = "rgba(59, 130, 246, 0.1)"
            e.target.style.color = "#60a5fa"
        } else if (!isActive && !isHover) {
            e.target.style.background = "transparent"
            e.target.style.color = "#cbd5e1"
        }
    }

    const handleUserItemHover = (e, isHover) => {
        if (isHover) {
            e.currentTarget.style.background = "rgba(30, 41, 59, 0.8)"
            e.currentTarget.style.borderColor = "#60a5fa"
            e.currentTarget.style.transform = "translateY(-2px)"
        } else {
            e.currentTarget.style.background = "rgba(30, 41, 59, 0.6)"
            e.currentTarget.style.borderColor = "#475569"
            e.currentTarget.style.transform = "translateY(0)"
        }
    }

    return (
        <div style={styles.dashboardContainer}>
            {/* Background */}
            <div style={styles.dashboardBackground} />
            <div style={styles.dashboardOverlay} />

            {/* Header */}
            <header style={styles.dashboardHeader}>
                <div style={styles.headerContent}>
                    <div style={styles.headerLeft}>
                        <div style={styles.logoSection}>
                            <div style={styles.logoCircle}>
                                <span style={styles.logoText}>DSCE</span>
                            </div>
                            <div style={styles.headerText}>
                                <h1 style={styles.headerTitle}>Department Admin Panel</h1>
                                <p style={styles.headerSubtitle}>Manage Users & Credentials</p>
                            </div>
                        </div>
                    </div>
                    <div>
                        <button
                            style={styles.logoutBtn}
                            onMouseEnter={(e) => handleLogoutHover(e, true)}
                            onMouseLeave={(e) => handleLogoutHover(e, false)}
                            onClick={() => {
                                localStorage.removeItem("departmentToken")
                                window.location.href = "/"
                            }}
                        >
                            Logout
                        </button>
                    </div>
                </div>
            </header>

            {/* Main Content */}
            <main style={styles.dashboardMain}>
                <div style={styles.dashboardContent}>
                    {/* Message Display */}
                    {message.text && (
                        <div style={styles.message}>
                            <span style={styles.messageIcon}>{message.type === "success" ? "✅" : "❌"}</span>
                            {message.text}
                        </div>
                    )}

                    {/* Tab Navigation */}
                    <div style={styles.tabNavigation}>
                        {tabs.map((tab) => (
                            <button
                                key={tab.id}
                                style={{
                                    ...styles.tabButton,
                                    ...(activeTab === tab.id ? styles.tabButtonActive : {}),
                                }}
                                onMouseEnter={(e) => handleTabHover(e, true, activeTab === tab.id)}
                                onMouseLeave={(e) => handleTabHover(e, false, activeTab === tab.id)}
                                onClick={() => setActiveTab(tab.id)}
                            >
                                <span style={styles.tabIcon}>{tab.icon}</span>
                                {tab.label}
                            </button>
                        ))}
                    </div>

                    {/* Content Sections */}
                    <div style={styles.contentSection}>
                        {/* Students Section */}
                        {activeTab === "students" && (
                            <div style={styles.sectionGrid}>
                                {/* Add Student Form */}
                                <div style={styles.formCard}>
                                    <h2 style={styles.formTitle}>
                                        <span style={styles.formIcon}>👨‍🎓</span>
                                        Add New Student
                                    </h2>
                                    <form onSubmit={handleStudentSubmit} style={styles.userForm}>
                                        <div style={styles.formRow}>
                                            <div style={styles.formGroup}>
                                                <label style={styles.formLabel}>First Name</label>
                                                <input
                                                    type="text"
                                                    value={studentForm.firstName}
                                                    onChange={(e) => handleInputChange("student", "firstName", e.target.value)}
                                                    style={styles.formInput}
                                                    onFocus={handleInputFocus}
                                                    onBlur={handleInputBlur}
                                                    required
                                                    placeholder="Enter first name"
                                                />
                                            </div>
                                            <div style={styles.formGroup}>
                                                <label style={styles.formLabel}>Last Name</label>
                                                <input
                                                    type="text"
                                                    value={studentForm.lastName}
                                                    onChange={(e) => handleInputChange("student", "lastName", e.target.value)}
                                                    style={styles.formInput}
                                                    onFocus={handleInputFocus}
                                                    onBlur={handleInputBlur}
                                                    required
                                                    placeholder="Enter last name"
                                                />
                                            </div>
                                        </div>
                                        <div style={styles.formRow}>
                                            <div style={styles.formGroup}>
                                                <label style={styles.formLabel}>Email Address</label>
                                                <input
                                                    type="email"
                                                    value={studentForm.email}
                                                    onChange={(e) => handleInputChange("student", "email", e.target.value)}
                                                    style={styles.formInput}
                                                    onFocus={handleInputFocus}
                                                    onBlur={handleInputBlur}
                                                    required
                                                    placeholder="student@dsce.edu.in"
                                                />
                                            </div>
                                            <div style={styles.formGroup}>
                                                <label style={styles.formLabel}>Phone Number</label>
                                                <input
                                                    type="tel"
                                                    value={studentForm.phoneNumber}
                                                    onChange={(e) => handleInputChange("student", "phoneNumber", e.target.value)}
                                                    style={styles.formInput}
                                                    onFocus={handleInputFocus}
                                                    onBlur={handleInputBlur}
                                                    placeholder="9876543210"
                                                />
                                            </div>
                                        </div>
                                        <div style={styles.formRow}>
                                            <div style={styles.formGroup}>
                                                <label style={styles.formLabel}>Department</label>
                                                <select
                                                    value={studentForm.department}
                                                    onChange={(e) => handleInputChange("student", "department", e.target.value)}
                                                    style={styles.formSelect}
                                                    onFocus={handleInputFocus}
                                                    onBlur={handleInputBlur}
                                                    required
                                                >
                                                    <option value="">Select Department</option>
                                                    {departments.map((dept) => (
                                                        <option key={dept} value={dept} style={{ background: "#1e293b", color: "white" }}>
                                                            {dept}
                                                        </option>
                                                    ))}
                                                </select>
                                            </div>
                                            <div style={styles.formGroup}>
                                                <label style={styles.formLabel}>Semester</label>
                                                <select
                                                    value={studentForm.semester}
                                                    onChange={(e) => handleInputChange("student", "semester", e.target.value)}
                                                    style={styles.formSelect}
                                                    onFocus={handleInputFocus}
                                                    onBlur={handleInputBlur}
                                                    required
                                                >
                                                    <option value="">Select Semester</option>
                                                    {semesters.map((sem) => (
                                                        <option key={sem} value={sem} style={{ background: "#1e293b", color: "white" }}>
                                                            {sem}
                                                        </option>
                                                    ))}
                                                </select>
                                            </div>
                                        </div>
                                        <div style={styles.formGroup}>
                                            <label style={styles.formLabel}>Address</label>
                                            <textarea
                                                value={studentForm.address}
                                                onChange={(e) => handleInputChange("student", "address", e.target.value)}
                                                style={styles.formTextarea}
                                                onFocus={handleInputFocus}
                                                onBlur={handleInputBlur}
                                                placeholder="Enter student address"
                                                rows="3"
                                            />
                                        </div>
                                        <button
                                            type="submit"
                                            style={styles.submitBtn}
                                            onMouseEnter={(e) => handleButtonHover(e, true)}
                                            onMouseLeave={(e) => handleButtonHover(e, false)}
                                            disabled={loading}
                                        >
                                            {loading ? "Creating..." : "Create Student Account"}
                                        </button>
                                    </form>
                                </div>

                                {/* Students List */}
                                <div style={styles.listCard}>
                                    <h2 style={styles.listTitle}>
                                        <span style={styles.listIcon}>📋</span>
                                        Students List ({filteredUsers.length})
                                    </h2>
                                    <div style={styles.usersList}>
                                        {filteredUsers.length === 0 ? (
                                            <div style={styles.emptyState}>
                                                <span style={styles.emptyIcon}>👨‍🎓</span>
                                                <p style={styles.emptyText}>No students created yet</p>
                                            </div>
                                        ) : (
                                            filteredUsers.map((user, index) => (
                                                <div
                                                    key={index}
                                                    style={styles.userItem}
                                                    onMouseEnter={(e) => handleUserItemHover(e, true)}
                                                    onMouseLeave={(e) => handleUserItemHover(e, false)}
                                                >
                                                    <div style={styles.userAvatar}>👨‍🎓</div>
                                                    <div style={styles.userInfo}>
                                                        <div style={styles.userName}>USN: {user.userId}</div>
                                                        <div style={styles.userEmail}>{user.email}</div>
                                                        <div style={styles.userType}>{user.userType}</div>
                                                    </div>
                                                    <div style={styles.userStatus}>
                                                        <span style={styles.statusBadge}>Active</span>
                                                    </div>
                                                </div>
                                            ))
                                        )}
                                    </div>
                                </div>
                            </div>
                        )}

                        {/* Teachers Section */}
                        {activeTab === "teachers" && (
                            <div style={styles.sectionGrid}>
                                {/* Add Teacher Form */}
                                <div style={styles.formCard}>
                                    <h2 style={styles.formTitle}>
                                        <span style={styles.formIcon}>👩‍🏫</span>
                                        Add New Teacher
                                    </h2>
                                    <form onSubmit={handleTeacherSubmit} style={styles.userForm}>
                                        <div style={styles.formRow}>
                                            <div style={styles.formGroup}>
                                                <label style={styles.formLabel}>First Name</label>
                                                <input
                                                    type="text"
                                                    value={teacherForm.firstName}
                                                    onChange={(e) => handleInputChange("teacher", "firstName", e.target.value)}
                                                    style={styles.formInput}
                                                    onFocus={handleInputFocus}
                                                    onBlur={handleInputBlur}
                                                    required
                                                    placeholder="Enter first name"
                                                />
                                            </div>
                                            <div style={styles.formGroup}>
                                                <label style={styles.formLabel}>Last Name</label>
                                                <input
                                                    type="text"
                                                    value={teacherForm.lastName}
                                                    onChange={(e) => handleInputChange("teacher", "lastName", e.target.value)}
                                                    style={styles.formInput}
                                                    onFocus={handleInputFocus}
                                                    onBlur={handleInputBlur}
                                                    required
                                                    placeholder="Enter last name"
                                                />
                                            </div>
                                        </div>
                                        <div style={styles.formRow}>
                                            <div style={styles.formGroup}>
                                                <label style={styles.formLabel}>Email Address</label>
                                                <input
                                                    type="email"
                                                    value={teacherForm.email}
                                                    onChange={(e) => handleInputChange("teacher", "email", e.target.value)}
                                                    style={styles.formInput}
                                                    onFocus={handleInputFocus}
                                                    onBlur={handleInputBlur}
                                                    required
                                                    placeholder="teacher@dsce.edu.in"
                                                />
                                            </div>
                                            <div style={styles.formGroup}>
                                                <label style={styles.formLabel}>Phone Number</label>
                                                <input
                                                    type="tel"
                                                    value={teacherForm.phoneNumber}
                                                    onChange={(e) => handleInputChange("teacher", "phoneNumber", e.target.value)}
                                                    style={styles.formInput}
                                                    onFocus={handleInputFocus}
                                                    onBlur={handleInputBlur}
                                                    placeholder="9876543210"
                                                />
                                            </div>
                                        </div>
                                        <div style={styles.formRow}>
                                            <div style={styles.formGroup}>
                                                <label style={styles.formLabel}>Department</label>
                                                <select
                                                    value={teacherForm.department}
                                                    onChange={(e) => handleInputChange("teacher", "department", e.target.value)}
                                                    style={styles.formSelect}
                                                    onFocus={handleInputFocus}
                                                    onBlur={handleInputBlur}
                                                    required
                                                >
                                                    <option value="">Select Department</option>
                                                    {departments.map((dept) => (
                                                        <option key={dept} value={dept} style={{ background: "#1e293b", color: "white" }}>
                                                            {dept}
                                                        </option>
                                                    ))}
                                                </select>
                                            </div>
                                            <div style={styles.formGroup}>
                                                <label style={styles.formLabel}>Designation</label>
                                                <select
                                                    value={teacherForm.designation}
                                                    onChange={(e) => handleInputChange("teacher", "designation", e.target.value)}
                                                    style={styles.formSelect}
                                                    onFocus={handleInputFocus}
                                                    onBlur={handleInputBlur}
                                                    required
                                                >
                                                    <option value="">Select Designation</option>
                                                    {designations.map((des) => (
                                                        <option key={des} value={des} style={{ background: "#1e293b", color: "white" }}>
                                                            {des}
                                                        </option>
                                                    ))}
                                                </select>
                                            </div>
                                        </div>
                                        <div style={styles.formGroup}>
                                            <label style={styles.formLabel}>Specialization</label>
                                            <input
                                                type="text"
                                                value={teacherForm.specialization}
                                                onChange={(e) => handleInputChange("teacher", "specialization", e.target.value)}
                                                style={styles.formInput}
                                                onFocus={handleInputFocus}
                                                onBlur={handleInputBlur}
                                                placeholder="e.g., Data Structures, Machine Learning"
                                            />
                                        </div>
                                        <button
                                            type="submit"
                                            style={styles.submitBtn}
                                            onMouseEnter={(e) => handleButtonHover(e, true)}
                                            onMouseLeave={(e) => handleButtonHover(e, false)}
                                            disabled={loading}
                                        >
                                            {loading ? "Creating..." : "Create Teacher Account"}
                                        </button>
                                    </form>
                                </div>

                                {/* Teachers List */}
                                <div style={styles.listCard}>
                                    <h2 style={styles.listTitle}>
                                        <span style={styles.listIcon}>📋</span>
                                        Teachers List ({filteredUsers.length})
                                    </h2>
                                    <div style={styles.usersList}>
                                        {filteredUsers.length === 0 ? (
                                            <div style={styles.emptyState}>
                                                <span style={styles.emptyIcon}>👩‍🏫</span>
                                                <p style={styles.emptyText}>No teachers created yet</p>
                                            </div>
                                        ) : (
                                            filteredUsers.map((user, index) => (
                                                <div
                                                    key={index}
                                                    style={styles.userItem}
                                                    onMouseEnter={(e) => handleUserItemHover(e, true)}
                                                    onMouseLeave={(e) => handleUserItemHover(e, false)}
                                                >
                                                    <div style={styles.userAvatar}>👩‍🏫</div>
                                                    <div style={styles.userInfo}>
                                                        <div style={styles.userName}>ID: {user.userId}</div>
                                                        <div style={styles.userEmail}>{user.email}</div>
                                                        <div style={styles.userType}>{user.userType}</div>
                                                    </div>
                                                    <div style={styles.userStatus}>
                                                        <span style={styles.statusBadge}>Active</span>
                                                    </div>
                                                </div>
                                            ))
                                        )}
                                    </div>
                                </div>
                            </div>
                        )}

                        {/* Admin Users Section */}
                        {activeTab === "admins" && (
                            <div style={styles.sectionGrid}>
                                {/* Add Admin Form */}
                                <div style={styles.formCard}>
                                    <h2 style={styles.formTitle}>
                                        <span style={styles.formIcon}>🔧</span>
                                        Add New Admin User
                                    </h2>
                                    <form onSubmit={handleAdminSubmit} style={styles.userForm}>
                                        <div style={styles.formRow}>
                                            <div style={styles.formGroup}>
                                                <label style={styles.formLabel}>First Name</label>
                                                <input
                                                    type="text"
                                                    value={adminForm.firstName}
                                                    onChange={(e) => handleInputChange("admin", "firstName", e.target.value)}
                                                    style={styles.formInput}
                                                    onFocus={handleInputFocus}
                                                    onBlur={handleInputBlur}
                                                    required
                                                    placeholder="Enter first name"
                                                />
                                            </div>
                                            <div style={styles.formGroup}>
                                                <label style={styles.formLabel}>Last Name</label>
                                                <input
                                                    type="text"
                                                    value={adminForm.lastName}
                                                    onChange={(e) => handleInputChange("admin", "lastName", e.target.value)}
                                                    style={styles.formInput}
                                                    onFocus={handleInputFocus}
                                                    onBlur={handleInputBlur}
                                                    required
                                                    placeholder="Enter last name"
                                                />
                                            </div>
                                        </div>
                                        <div style={styles.formRow}>
                                            <div style={styles.formGroup}>
                                                <label style={styles.formLabel}>Email Address</label>
                                                <input
                                                    type="email"
                                                    value={adminForm.email}
                                                    onChange={(e) => handleInputChange("admin", "email", e.target.value)}
                                                    style={styles.formInput}
                                                    onFocus={handleInputFocus}
                                                    onBlur={handleInputBlur}
                                                    required
                                                    placeholder="admin@dsce.edu.in"
                                                />
                                            </div>
                                            <div style={styles.formGroup}>
                                                <label style={styles.formLabel}>Phone Number</label>
                                                <input
                                                    type="tel"
                                                    value={adminForm.phoneNumber}
                                                    onChange={(e) => handleInputChange("admin", "phoneNumber", e.target.value)}
                                                    style={styles.formInput}
                                                    onFocus={handleInputFocus}
                                                    onBlur={handleInputBlur}
                                                    placeholder="9876543210"
                                                />
                                            </div>
                                        </div>
                                        <div style={styles.formGroup}>
                                            <label style={styles.formLabel}>Admin Type</label>
                                            <select
                                                value={adminForm.adminType}
                                                onChange={(e) => handleInputChange("admin", "adminType", e.target.value)}
                                                style={styles.formSelect}
                                                onFocus={handleInputFocus}
                                                onBlur={handleInputBlur}
                                                required
                                            >
                                                {adminTypes.map((type) => (
                                                    <option key={type.value} value={type.value} style={{ background: "#1e293b", color: "white" }}>
                                                        {type.label}
                                                    </option>
                                                ))}
                                            </select>
                                        </div>
                                        <button
                                            type="submit"
                                            style={styles.submitBtn}
                                            onMouseEnter={(e) => handleButtonHover(e, true)}
                                            onMouseLeave={(e) => handleButtonHover(e, false)}
                                            disabled={loading}
                                        >
                                            {loading ? "Creating..." : "Create Admin Account"}
                                        </button>
                                    </form>
                                </div>

                                {/* Admin Users List */}
                                <div style={styles.listCard}>
                                    <h2 style={styles.listTitle}>
                                        <span style={styles.listIcon}>📋</span>
                                        Admin Users List ({filteredUsers.length})
                                    </h2>
                                    <div style={styles.usersList}>
                                        {filteredUsers.length === 0 ? (
                                            <div style={styles.emptyState}>
                                                <span style={styles.emptyIcon}>🔧</span>
                                                <p style={styles.emptyText}>No admin users created yet</p>
                                            </div>
                                        ) : (
                                            filteredUsers.map((user, index) => (
                                                <div
                                                    key={index}
                                                    style={styles.userItem}
                                                    onMouseEnter={(e) => handleUserItemHover(e, true)}
                                                    onMouseLeave={(e) => handleUserItemHover(e, false)}
                                                >
                                                    <div style={styles.userAvatar}>
                                                        {user.userType === "FOOD_CART"
                                                            ? "🍕"
                                                            : user.userType === "CANTEEN"
                                                                ? "🍽"
                                                                : user.userType === "STATIONERY"
                                                                    ? "📚"
                                                                    : "🔧"}
                                                    </div>
                                                    <div style={styles.userInfo}>
                                                        <div style={styles.userName}>{user.email}</div>
                                                        <div style={styles.userEmail}>{user.email}</div>
                                                        <div style={styles.userType}>{user.userType.replace("_", " ")}</div>
                                                    </div>
                                                    <div style={styles.userStatus}>
                                                        <span style={styles.statusBadge}>Active</span>
                                                    </div>
                                                </div>
                                            ))
                                        )}
                                    </div>
                                </div>
                            </div>
                        )}
                    </div>
                </div>
            </main>
        </div>
    )
}

export default DepartmentDashboard
