"use client"
import DsceLogo from './Images/DsceLogo.jpg';

import { useState, useEffect } from "react"
import "./App.css"

const DSCEPortal = () => {
    const [dropdownOpen, setDropdownOpen] = useState(false)
    const [isLoaded, setIsLoaded] = useState(false)
    const [showLogin, setShowLogin] = useState(false)
    const [showTeacherLogin, setShowTeacherLogin] = useState(false)

    // Admin Login States
    const [showFoodCartLogin, setShowFoodCartLogin] = useState(false)
    const [showCanteenLogin, setShowCanteenLogin] = useState(false)
    const [showStationeryLogin, setShowStationeryLogin] = useState(false)
    const [showDepartmentLogin, setShowDepartmentLogin] = useState(false)

    const [loginData, setLoginData] = useState({
        username: "",
        password: "",
    })
    const [teacherLoginData, setTeacherLoginData] = useState({
        email: "",
        password: "",
        remember: false,
    })

    // Admin Login Data States
    const [foodCartLoginData, setFoodCartLoginData] = useState({
        email: "",
        password: "",
        remember: false,
    })
    const [canteenLoginData, setCanteenLoginData] = useState({
        email: "",
        password: "",
        remember: false,
    })
    const [stationeryLoginData, setStationeryLoginData] = useState({
        email: "",
        password: "",
        remember: false,
    })
    const [departmentLoginData, setDepartmentLoginData] = useState({
        email: "",
        password: "",
        remember: false,
    })

    const [showPassword, setShowPassword] = useState(false)
    const [showTeacherPassword, setShowTeacherPassword] = useState(false)
    const [showFoodCartPassword, setShowFoodCartPassword] = useState(false)
    const [showCanteenPassword, setShowCanteenPassword] = useState(false)
    const [showStationeryPassword, setShowStationeryPassword] = useState(false)
    const [showDepartmentPassword, setShowDepartmentPassword] = useState(false)

    useEffect(() => {
        const timer = setTimeout(() => {
            setIsLoaded(true)
        }, 200)
        return () => clearTimeout(timer)
    }, [])

    const handleStudentClick = () => {
        setShowLogin(true)
    }

    const handleTeacherClick = () => {
        setShowTeacherLogin(true)
    }

    const toggleAdminDropdown = () => {
        setDropdownOpen(!dropdownOpen)
    }

    const handleAdminOption = (option) => {
        setDropdownOpen(false)
        switch (option) {
            case "food-cart":
                setShowFoodCartLogin(true)
                break
            case "canteen":
                setShowCanteenLogin(true)
                break
            case "stationery":
                setShowStationeryLogin(true)
                break
            case "department":
                setShowDepartmentLogin(true)
                break
            default:
                break
        }
    }

    const handleClickOutside = (event) => {
        if (!event.target.closest(".dropdown-container")) {
            setDropdownOpen(false)
        }
    }

    // Student Login Handlers
    const handleLoginSubmit = (e) => {
        e.preventDefault()
        if (loginData.username && loginData.password) {
            alert(`Welcome ${loginData.username}! Redirecting to Student Dashboard...`)
            setShowLogin(false)
            setLoginData({ username: "", password: "" })
        } else {
            alert("Please enter both username and password")
        }
    }

    const handleLoginChange = (e) => {
        setLoginData({
            ...loginData,
            [e.target.name]: e.target.value,
        })
    }

    const closeLogin = () => {
        setShowLogin(false)
        setLoginData({ username: "", password: "" })
    }

    // Teacher Login Handlers
    const handleTeacherLoginSubmit = (e) => {
        e.preventDefault()
        if (teacherLoginData.email && teacherLoginData.password) {
            alert(`Welcome Teacher! Redirecting to Faculty Dashboard...`)
            setShowTeacherLogin(false)
            setTeacherLoginData({ email: "", password: "", remember: false })
        } else {
            alert("Please enter both email and password")
        }
    }

    const handleTeacherLoginChange = (e) => {
        const { name, value, type, checked } = e.target
        setTeacherLoginData({
            ...teacherLoginData,
            [name]: type === "checkbox" ? checked : value,
        })
    }

    const closeTeacherLogin = () => {
        setShowTeacherLogin(false)
        setTeacherLoginData({ email: "", password: "", remember: false })
        setShowTeacherPassword(false)
    }

    const toggleTeacherPassword = () => {
        setShowTeacherPassword(!showTeacherPassword)
    }

    // Food Cart Admin Login Handlers
    const handleFoodCartLoginSubmit = (e) => {
        e.preventDefault()
        if (foodCartLoginData.email && foodCartLoginData.password) {
            alert(`Welcome to Food Cart Management! Redirecting to Admin Dashboard...`)
            setShowFoodCartLogin(false)
            setFoodCartLoginData({ email: "", password: "", remember: false })
        } else {
            alert("Please enter both email and password")
        }
    }

    const handleFoodCartLoginChange = (e) => {
        const { name, value, type, checked } = e.target
        setFoodCartLoginData({
            ...foodCartLoginData,
            [name]: type === "checkbox" ? checked : value,
        })
    }

    const closeFoodCartLogin = () => {
        setShowFoodCartLogin(false)
        setFoodCartLoginData({ email: "", password: "", remember: false })
        setShowFoodCartPassword(false)
    }

    const toggleFoodCartPassword = () => {
        setShowFoodCartPassword(!showFoodCartPassword)
    }

    // Canteen Admin Login Handlers
    const handleCanteenLoginSubmit = (e) => {
        e.preventDefault()
        if (canteenLoginData.email && canteenLoginData.password) {
            alert(`Welcome to Canteen Management! Redirecting to Admin Dashboard...`)
            setShowCanteenLogin(false)
            setCanteenLoginData({ email: "", password: "", remember: false })
        } else {
            alert("Please enter both email and password")
        }
    }

    const handleCanteenLoginChange = (e) => {
        const { name, value, type, checked } = e.target
        setCanteenLoginData({
            ...canteenLoginData,
            [name]: type === "checkbox" ? checked : value,
        })
    }

    const closeCanteenLogin = () => {
        setShowCanteenLogin(false)
        setCanteenLoginData({ email: "", password: "", remember: false })
        setShowCanteenPassword(false)
    }

    const toggleCanteenPassword = () => {
        setShowCanteenPassword(!showCanteenPassword)
    }

    // Stationery Admin Login Handlers
    const handleStationeryLoginSubmit = (e) => {
        e.preventDefault()
        if (stationeryLoginData.email && stationeryLoginData.password) {
            alert(`Welcome to Stationery Management! Redirecting to Admin Dashboard...`)
            setShowStationeryLogin(false)
            setStationeryLoginData({ email: "", password: "", remember: false })
        } else {
            alert("Please enter both email and password")
        }
    }

    const handleStationeryLoginChange = (e) => {
        const { name, value, type, checked } = e.target
        setStationeryLoginData({
            ...stationeryLoginData,
            [name]: type === "checkbox" ? checked : value,
        })
    }

    const closeStationeryLogin = () => {
        setShowStationeryLogin(false)
        setStationeryLoginData({ email: "", password: "", remember: false })
        setShowStationeryPassword(false)
    }

    const toggleStationeryPassword = () => {
        setShowStationeryPassword(!showStationeryPassword)
    }

    // Department Admin Login Handlers
    const handleDepartmentLoginSubmit = (e) => {
        e.preventDefault()
        if (departmentLoginData.email && departmentLoginData.password) {
            alert(`Welcome to Department Management! Redirecting to Admin Dashboard...`)
            setShowDepartmentLogin(false)
            setDepartmentLoginData({ email: "", password: "", remember: false })
        } else {
            alert("Please enter both email and password")
        }
    }

    const handleDepartmentLoginChange = (e) => {
        const { name, value, type, checked } = e.target
        setDepartmentLoginData({
            ...departmentLoginData,
            [name]: type === "checkbox" ? checked : value,
        })
    }

    const closeDepartmentLogin = () => {
        setShowDepartmentLogin(false)
        setDepartmentLoginData({ email: "", password: "", remember: false })
        setShowDepartmentPassword(false)
    }

    const toggleDepartmentPassword = () => {
        setShowDepartmentPassword(!showDepartmentPassword)
    }

    useEffect(() => {
        document.addEventListener("click", handleClickOutside)
        return () => {
            document.removeEventListener("click", handleClickOutside)
        }
    }, [])

    const styles = {
        container: {
            minHeight: "100vh",
            position: "relative",
            fontFamily: "system-ui, -apple-system, sans-serif",
        },
        backgroundImage: {
            position: "fixed",
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            backgroundImage:
                "url('https://images.unsplash.com/photo-1562774053-701939374585?ixlib=rb-4.0.3&auto=format&fit=crop&w=1920&q=80')",
            backgroundSize: "cover",
            backgroundPosition: "center",
            backgroundRepeat: "no-repeat",
            backgroundAttachment: "fixed",
        },
        overlay: {
            position: "fixed",
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            background: "rgba(15, 23, 42, 0.8)",
            zIndex: 10,
        },
        portalContainer: {
            position: "relative",
            zIndex: 20,
            minHeight: "100vh",
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            justifyContent: "center",
            padding: window.innerWidth >= 1024 ? "4rem 2rem" : "2rem 1rem",
        },
        welcomeSection: {
            width: "100%",
            maxWidth: "1280px",
            margin: "0 auto",
            marginBottom: window.innerWidth >= 1024 ? "4rem" : "3rem",
            opacity: isLoaded ? 1 : 0,
            transform: isLoaded ? "translateY(0)" : "translateY(-48px)",
            transition: "all 1s ease-out",
        },
        welcomeTitle: {
            color: "white",
            fontSize:
                window.innerWidth >= 1280
                    ? "4rem"
                    : window.innerWidth >= 1024
                        ? "3.5rem"
                        : window.innerWidth >= 768
                            ? "2.5rem"
                            : "2rem",
            fontWeight: "bold",
            textAlign: "center",
            textShadow: "2px 2px 8px rgba(0, 0, 0, 0.7)",
            margin: 0,
            background: "linear-gradient(135deg, #60a5fa, #34d399)",
            WebkitBackgroundClip: "text",
            WebkitTextFillColor: "transparent",
            backgroundClip: "text",
        },
        welcomeSubtitle: {
            color: "#cbd5e1",
            fontSize: window.innerWidth >= 1024 ? "1.25rem" : "1rem",
            textAlign: "center",
            marginTop: "1rem",
            maxWidth: "600px",
            margin: "1rem auto 0",
        },
        mainContent: {
            width: "100%",
            maxWidth: "1280px",
            margin: "0 auto",
        },
        desktopLayout: {
            display: window.innerWidth >= 1024 ? "flex" : "none",
            alignItems: "center",
            justifyContent: "space-between",
            gap: window.innerWidth >= 1280 ? "5rem" : "4rem",
            width: "100%",
        },
        logoSectionDesktop: {
            flexShrink: 0,
            opacity: isLoaded ? 1 : 0,
            transform: isLoaded ? "translateX(0)" : "translateX(-96px)",
            transition: "all 1s ease-out",
        },
        logoCircleDesktop: {
            width: window.innerWidth >= 1280 ? "320px" : "256px",
            height: window.innerWidth >= 1280 ? "320px" : "256px",
            borderRadius: "50%",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            backgroundImage: `url(${DsceLogo}), linear-gradient(135deg, #3b82f6, #10b981)`,
            backgroundSize: "cover",
            backgroundPosition: "center",
            backgroundRepeat: "no-repeat",
            boxShadow: "0 20px 40px rgba(59, 130, 246, 0.4)",
            transition: "transform 0.3s ease-in-out",
            overflow: "hidden",
            cursor: "pointer",
        },

        logoTextDesktop: {
            color: "white",
            fontSize: window.innerWidth >= 1280 ? "2.25rem" : "1.875rem",
            fontWeight: "bold",
            textAlign: "center",
            textShadow: "0 2px 4px rgba(0, 0, 0, 0.3)",
            lineHeight: 1.2,
        },
        buttonsSectionDesktop: {
            flexShrink: 0,
            width: "100%",
            maxWidth: "512px",
            background: "rgba(15, 23, 42, 0.95)",
            backdropFilter: "blur(12px)",
            borderRadius: "1.5rem",
            padding: window.innerWidth >= 1280 ? "2.5rem" : "2rem",
            border: "1px solid #475569",
            boxShadow: "0 25px 50px rgba(0, 0, 0, 0.3)",
            opacity: isLoaded ? 1 : 0,
            transform: isLoaded ? "translateX(0)" : "translateX(96px)",
            transition: "all 1s ease-out",
        },
        cardTitle: {
            color: "white",
            fontSize: window.innerWidth >= 1280 ? "1.875rem" : "1.5rem",
            fontWeight: 600,
            marginBottom: "2rem",
            textAlign: "center",
            margin: "0 0 2rem 0",
        },
        buttonsContainer: {
            display: "flex",
            flexDirection: "column",
            gap: "1.25rem",
        },
        portalButton: {
            position: "relative",
            overflow: "hidden",
            padding: window.innerWidth >= 1280 ? "1.5rem" : "1.25rem",
            fontSize: window.innerWidth >= 1280 ? "1.25rem" : "1.125rem",
            fontWeight: 600,
            border: "none",
            borderRadius: "1rem",
            cursor: "pointer",
            transition: "all 0.3s ease-in-out",
            color: "white",
            textTransform: "uppercase",
            letterSpacing: "0.05em",
            minHeight: window.innerWidth >= 1280 ? "80px" : "72px",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            width: "100%",
        },
        studentButton: {
            background: "linear-gradient(135deg, #3b82f6, #10b981)",
            boxShadow: "0 8px 20px rgba(59, 130, 246, 0.4)",
        },
        teacherButton: {
            background: "linear-gradient(135deg, #10b981, #3b82f6)",
            boxShadow: "0 8px 20px rgba(16, 185, 129, 0.4)",
        },
        adminButton: {
            background: "linear-gradient(135deg, #3b82f6, #10b981)",
            boxShadow: "0 8px 20px rgba(59, 130, 246, 0.4)",
        },
        buttonShine: {
            position: "absolute",
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            background: "linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.3), transparent)",
            transform: "translateX(-100%)",
            transition: "transform 0.5s ease",
        },
        dropdownContainer: {
            position: "relative",
            width: "100%",
        },
        dropdownArrow: {
            marginLeft: "0.75rem",
            transition: "transform 0.3s ease",
            transform: dropdownOpen ? "rotate(180deg)" : "rotate(0deg)",
        },
        dropdownMenu: {
            position: "absolute",
            top: "100%",
            left: 0,
            right: 0,
            background: "#1e293b",
            borderRadius: "1rem",
            border: "1px solid #475569",
            marginTop: "0.5rem",
            zIndex: 50,
            boxShadow: "0 15px 35px rgba(0, 0, 0, 0.3)",
            opacity: dropdownOpen ? 1 : 0,
            visibility: dropdownOpen ? "visible" : "hidden",
            transform: dropdownOpen ? "translateY(0)" : "translateY(-12px)",
            transition: "all 0.3s ease",
        },
        dropdownItem: {
            padding: window.innerWidth >= 1280 ? "1.25rem 1.5rem" : "1rem 1.5rem",
            color: "#cbd5e1",
            cursor: "pointer",
            transition: "all 0.3s ease",
            fontWeight: 500,
            fontSize: window.innerWidth >= 1280 ? "1.125rem" : "1rem",
        },
        mobileLayout: {
            display: window.innerWidth >= 1024 ? "none" : "flex",
            flexDirection: "column",
            alignItems: "center",
            gap: window.innerWidth >= 768 ? "3rem" : "2rem",
        },
        logoSectionMobile: {
            opacity: isLoaded ? 1 : 0,
            transform: isLoaded ? "translateY(0)" : "translateY(-96px)",
            transition: "all 1s ease-out",
        },
        logoCircleMobile: {
            width: window.innerWidth >= 768 ? "224px" : "160px",
            height: window.innerWidth >= 768 ? "224px" : "160px",
            borderRadius: "50%",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            background: "linear-gradient(135deg, #3b82f6, #10b981)",
            boxShadow: "0 20px 40px rgba(59, 130, 246, 0.4)",
            transition: "transform 0.3s ease-in-out",
            overflow: "hidden",
            cursor: "pointer",
        },
        logoTextMobile: {
            color: "white",
            fontSize: window.innerWidth >= 768 ? "1.5rem" : "1.25rem",
            fontWeight: "bold",
            textAlign: "center",
            textShadow: "0 2px 4px rgba(0, 0, 0, 0.3)",
            lineHeight: 1.2,
        },
        buttonsSectionMobile: {
            width: "100%",
            maxWidth: "448px",
            margin: "0 auto",
            background: "rgba(15, 23, 42, 0.95)",
            backdropFilter: "blur(12px)",
            borderRadius: "1.5rem",
            padding: window.innerWidth >= 768 ? "2rem" : "1.5rem",
            border: "1px solid #475569",
            boxShadow: "0 25px 50px rgba(0, 0, 0, 0.3)",
            opacity: isLoaded ? 1 : 0,
            transform: isLoaded ? "translateY(0)" : "translateY(96px)",
            transition: "all 1s ease-out",
        },
        cardTitleMobile: {
            color: "white",
            fontSize: window.innerWidth >= 768 ? "1.5rem" : "1.25rem",
            fontWeight: 600,
            marginBottom: window.innerWidth >= 768 ? "2rem" : "1.5rem",
            textAlign: "center",
            margin: `0 0 ${window.innerWidth >= 768 ? "2rem" : "1.5rem"} 0`,
        },
        buttonsContainerMobile: {
            display: "flex",
            flexDirection: "column",
            gap: window.innerWidth >= 768 ? "1.25rem" : "1rem",
        },
        portalButtonMobile: {
            position: "relative",
            overflow: "hidden",
            padding: window.innerWidth >= 768 ? "1.25rem" : "1rem",
            fontSize: window.innerWidth >= 768 ? "1.125rem" : "1rem",
            fontWeight: 600,
            border: "none",
            borderRadius: "1rem",
            cursor: "pointer",
            transition: "all 0.3s ease-in-out",
            color: "white",
            textTransform: "uppercase",
            letterSpacing: "0.05em",
            minHeight: window.innerWidth >= 768 ? "68px" : "60px",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            width: "100%",
        },
        dropdownItemMobile: {
            padding: window.innerWidth >= 768 ? "1rem 1.5rem" : "0.75rem 1rem",
            color: "#cbd5e1",
            cursor: "pointer",
            transition: "all 0.3s ease",
            fontWeight: 500,
            fontSize: window.innerWidth >= 768 ? "1rem" : "0.875rem",
        },
        additionalContent: {
            position: "relative",
            zIndex: 20,
            background: "rgba(15, 23, 42, 0.95)",
            backdropFilter: "blur(12px)",
            margin: "4rem auto",
            maxWidth: "1280px",
            borderRadius: "1.5rem",
            border: "1px solid #475569",
            boxShadow: "0 25px 50px rgba(0, 0, 0, 0.3)",
            padding: window.innerWidth >= 768 ? "3rem" : "2rem",
        },
        sectionTitle: {
            color: "white",
            fontSize: window.innerWidth >= 768 ? "2rem" : "1.5rem",
            fontWeight: "bold",
            textAlign: "center",
            marginBottom: "2rem",
            background: "linear-gradient(135deg, #60a5fa, #34d399)",
            WebkitBackgroundClip: "text",
            WebkitTextFillColor: "transparent",
            backgroundClip: "text",
        },
        sectionText: {
            color: "#cbd5e1",
            fontSize: window.innerWidth >= 768 ? "1.125rem" : "1rem",
            lineHeight: 1.6,
            textAlign: "center",
            marginBottom: "1.5rem",
        },
        // Base Login Modal Styles
        loginOverlay: {
            position: "fixed",
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            background: "rgba(0, 0, 0, 0.8)",
            zIndex: 100,
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            padding: "1rem",
            transition: "all 0.3s ease",
        },
        loginModal: {
            background: "rgba(15, 23, 42, 0.98)",
            backdropFilter: "blur(20px)",
            borderRadius: "1.5rem",
            border: "1px solid #475569",
            boxShadow: "0 25px 50px rgba(0, 0, 0, 0.5)",
            padding: window.innerWidth >= 768 ? "3rem" : "2rem",
            width: "100%",
            maxWidth: "450px",
            transition: "all 0.3s ease",
        },
        loginTitle: {
            color: "white",
            fontSize: window.innerWidth >= 768 ? "2rem" : "1.5rem",
            fontWeight: "bold",
            textAlign: "center",
            marginBottom: "2rem",
        },
        loginForm: {
            display: "flex",
            flexDirection: "column",
            gap: "1.5rem",
        },
        inputGroup: {
            display: "flex",
            flexDirection: "column",
            gap: "0.5rem",
        },
        inputLabel: {
            color: "#cbd5e1",
            fontSize: "1rem",
            fontWeight: 500,
        },
        inputField: {
            background: "rgba(30, 41, 59, 0.8)",
            border: "1px solid #475569",
            borderRadius: "0.75rem",
            padding: "1rem",
            color: "white",
            fontSize: "1rem",
            transition: "all 0.3s ease",
            outline: "none",
        },
        loginButton: {
            border: "none",
            borderRadius: "0.75rem",
            padding: "1rem",
            color: "white",
            fontSize: "1.125rem",
            fontWeight: 600,
            cursor: "pointer",
            transition: "all 0.3s ease",
            textTransform: "uppercase",
            letterSpacing: "0.05em",
            marginTop: "1rem",
        },
        closeButton: {
            position: "absolute",
            top: "1rem",
            right: "1rem",
            background: "rgba(239, 68, 68, 0.2)",
            border: "1px solid #ef4444",
            borderRadius: "50%",
            width: "40px",
            height: "40px",
            color: "#ef4444",
            fontSize: "1.25rem",
            cursor: "pointer",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            transition: "all 0.3s ease",
        },
        loginSubtitle: {
            color: "#94a3b8",
            fontSize: "0.875rem",
            textAlign: "center",
            marginBottom: "1.5rem",
        },
        passwordContainer: {
            position: "relative",
        },
        passwordToggle: {
            position: "absolute",
            right: "1rem",
            top: "50%",
            transform: "translateY(-50%)",
            background: "transparent",
            border: "none",
            color: "#94a3b8",
            cursor: "pointer",
            fontSize: "0.875rem",
            padding: "0.25rem",
        },
        checkboxContainer: {
            display: "flex",
            alignItems: "center",
            gap: "0.5rem",
            marginTop: "1rem",
        },
        checkbox: {
            width: "1rem",
            height: "1rem",
            cursor: "pointer",
        },
        checkboxLabel: {
            color: "#cbd5e1",
            fontSize: "0.875rem",
            cursor: "pointer",
        },
    }

    // Color themes for different admin sections
    const adminThemes = {
        foodCart: {
            gradient: "linear-gradient(135deg, #f59e0b, #ef4444)",
            title: "linear-gradient(135deg, #fbbf24, #f87171)",
            focus: "#f59e0b",
        },
        canteen: {
            gradient: "linear-gradient(135deg, #8b5cf6, #a855f7)",
            title: "linear-gradient(135deg, #a78bfa, #c084fc)",
            focus: "#8b5cf6",
        },
        stationery: {
            gradient: "linear-gradient(135deg, #06b6d4, #0891b2)",
            title: "linear-gradient(135deg, #22d3ee, #0ea5e9)",
            focus: "#06b6d4",
        },
        department: {
            gradient: "linear-gradient(135deg, #dc2626, #b91c1c)",
            title: "linear-gradient(135deg, #f87171, #ef4444)",
            focus: "#dc2626",
        },
    }

    const handleButtonHover = (e, isHover, shadowColor) => {
        if (isHover) {
            e.currentTarget.style.transform = "translateY(-3px)"
            e.currentTarget.style.boxShadow = `0 12px 25px ${shadowColor}`
            const shine = e.currentTarget.querySelector(".button-shine")
            if (shine) shine.style.transform = "translateX(100%)"
        } else {
            e.currentTarget.style.transform = "translateY(0)"
            e.currentTarget.style.boxShadow = `0 8px 20px ${shadowColor}`
            const shine = e.currentTarget.querySelector(".button-shine")
            if (shine) shine.style.transform = "translateX(-100%)"
        }
    }

    const handleLogoHover = (e, isHover) => {
        if (isHover) {
            e.currentTarget.style.transform = "scale(1.05) rotate(6deg)"
            e.currentTarget.style.boxShadow = "0 25px 50px rgba(59, 130, 246, 0.6)"
        } else {
            e.currentTarget.style.transform = "scale(1) rotate(0deg)"
            e.currentTarget.style.boxShadow = "0 20px 40px rgba(59, 130, 246, 0.4)"
        }
    }

    const handleDropdownItemHover = (e, isHover) => {
        if (isHover) {
            e.currentTarget.style.background = "linear-gradient(90deg, #3b82f6, #10b981)"
            e.currentTarget.style.color = "white"
            e.currentTarget.style.transform = "translateX(4px)"
        } else {
            e.currentTarget.style.background = "transparent"
            e.currentTarget.style.color = "#cbd5e1"
            e.currentTarget.style.transform = "translateX(0)"
        }
    }

    const handleInputFocus = (e, focusColor = "#60a5fa") => {
        e.target.style.borderColor = focusColor
        e.target.style.boxShadow = `0 0 0 3px ${focusColor}20`
    }

    const handleInputBlur = (e) => {
        e.target.style.borderColor = "#475569"
        e.target.style.boxShadow = "none"
    }

    const handleLoginButtonHover = (e, isHover, shadowColor) => {
        if (isHover) {
            e.target.style.transform = "translateY(-2px)"
            e.target.style.boxShadow = `0 8px 20px ${shadowColor}`
        } else {
            e.target.style.transform = "translateY(0)"
            e.target.style.boxShadow = "none"
        }
    }

    const handleCloseButtonHover = (e, isHover) => {
        if (isHover) {
            e.target.style.background = "rgba(239, 68, 68, 0.3)"
            e.target.style.transform = "scale(1.1)"
        } else {
            e.target.style.background = "rgba(239, 68, 68, 0.2)"
            e.target.style.transform = "scale(1)"
        }
    }

    const adminOptions = [
        { key: "food-cart", label: "🍕 Food Cart Management" },
        { key: "canteen", label: "🍽 Canteen Management" },
        { key: "stationery", label: "📚 Stationery Management" },
        { key: "department", label: "🏢 Department Management" },
    ]

    return (
        <div style={styles.container}>
            {/* Background Image */}
            <div style={styles.backgroundImage} />
            {/* Dark Overlay */}
            <div style={styles.overlay} />
            {/* Portal Container */}
            <div style={styles.portalContainer}>
                {/* Welcome Message */}
                <div style={styles.welcomeSection}>
                    <h1 style={styles.welcomeTitle}>Welcome to DSCE Campus</h1>
                    <p style={styles.welcomeSubtitle}>Access your portal and manage your academic journey with ease</p>
                </div>
                {/* Main Content Container */}
                <div style={styles.mainContent}>
                    {/* Desktop Layout */}
                    <div style={styles.desktopLayout}>
                        {/* Logo Section - Desktop */}
                        <div style={styles.logoSectionDesktop}>
                            <div
                                style={styles.logoCircleDesktop}
                                onMouseEnter={(e) => handleLogoHover(e, true)}
                                onMouseLeave={(e) => handleLogoHover(e, false)}
                            >
                                <div style={styles.logoTextDesktop}>

                                    <br />

                                </div>
                            </div>
                        </div>
                        {/* Buttons Section - Desktop */}
                        <div style={styles.buttonsSectionDesktop}>
                            <h2 style={styles.cardTitle}>Portal Access System</h2>
                            <div style={styles.buttonsContainer}>
                                {/* Student Button */}
                                <button
                                    onClick={handleStudentClick}
                                    style={{ ...styles.portalButton, ...styles.studentButton }}
                                    onMouseEnter={(e) => handleButtonHover(e, true, "rgba(59, 130, 246, 0.6)")}
                                    onMouseLeave={(e) => handleButtonHover(e, false, "rgba(59, 130, 246, 0.4)")}
                                >
                                    <span className="button-shine" style={styles.buttonShine} />
                                    👨‍🎓 Student Portal
                                </button>
                                {/* Teacher Button */}
                                <button
                                    onClick={handleTeacherClick}
                                    style={{ ...styles.portalButton, ...styles.teacherButton }}
                                    onMouseEnter={(e) => handleButtonHover(e, true, "rgba(16, 185, 129, 0.6)")}
                                    onMouseLeave={(e) => handleButtonHover(e, false, "rgba(16, 185, 129, 0.4)")}
                                >
                                    <span className="button-shine" style={styles.buttonShine} />
                                    👩‍🏫 Teacher Portal
                                </button>
                                {/* Admin Dropdown */}
                                <div className="dropdown-container" style={styles.dropdownContainer}>
                                    <button
                                        onClick={toggleAdminDropdown}
                                        style={{ ...styles.portalButton, ...styles.adminButton }}
                                        onMouseEnter={(e) => handleButtonHover(e, true, "rgba(59, 130, 246, 0.6)")}
                                        onMouseLeave={(e) => handleButtonHover(e, false, "rgba(59, 130, 246, 0.4)")}
                                    >
                                        <span className="button-shine" style={styles.buttonShine} />🔧 Admin Panel
                                        <span style={styles.dropdownArrow}>▼</span>
                                    </button>
                                    {/* Dropdown Menu */}
                                    <div style={styles.dropdownMenu}>
                                        {adminOptions.map((item, index, array) => (
                                            <div
                                                key={item.key}
                                                onClick={() => handleAdminOption(item.key)}
                                                style={{
                                                    ...styles.dropdownItem,
                                                    borderBottom: index === array.length - 1 ? "none" : "1px solid #475569",
                                                    borderRadius:
                                                        index === 0 ? "1rem 1rem 0 0" : index === array.length - 1 ? "0 0 1rem 1rem" : "0",
                                                }}
                                                onMouseEnter={(e) => handleDropdownItemHover(e, true)}
                                                onMouseLeave={(e) => handleDropdownItemHover(e, false)}
                                            >
                                                {item.label}
                                            </div>
                                        ))}
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                    {/* Mobile/Tablet Layout */}
                    <div style={styles.mobileLayout}>
                        {/* Logo Section - Mobile */}
                        <div style={styles.logoSectionMobile}>
                            <div
                                style={styles.logoCircleMobile}
                                onMouseEnter={(e) => handleLogoHover(e, true)}
                                onMouseLeave={(e) => handleLogoHover(e, false)}
                            >
                                <div style={styles.logoTextMobile}>

                                    <br />

                                </div>
                            </div>
                        </div>
                        {/* Buttons Section - Mobile */}
                        <div style={styles.buttonsSectionMobile}>
                            <h2 style={styles.cardTitleMobile}>Portal Access System</h2>
                            <div style={styles.buttonsContainerMobile}>
                                {/* Student Button - Mobile */}
                                <button onClick={handleStudentClick} style={{ ...styles.portalButtonMobile, ...styles.studentButton }}>
                                    <span style={styles.buttonShine} />
                                    👨‍🎓 Student Portal
                                </button>
                                {/* Teacher Button - Mobile */}
                                <button onClick={handleTeacherClick} style={{ ...styles.portalButtonMobile, ...styles.teacherButton }}>
                                    <span style={styles.buttonShine} />
                                    👩‍🏫 Teacher Portal
                                </button>
                                {/* Admin Dropdown - Mobile */}
                                <div className="dropdown-container" style={styles.dropdownContainer}>
                                    <button onClick={toggleAdminDropdown} style={{ ...styles.portalButtonMobile, ...styles.adminButton }}>
                                        <span style={styles.buttonShine} />🔧 Admin Panel
                                        <span style={styles.dropdownArrow}>▼</span>
                                    </button>
                                    {/* Dropdown Menu - Mobile */}
                                    <div style={styles.dropdownMenu}>
                                        {adminOptions.map((item, index, array) => (
                                            <div
                                                key={item.key}
                                                onClick={() => handleAdminOption(item.key)}
                                                style={{
                                                    ...styles.dropdownItemMobile,
                                                    borderBottom: index === array.length - 1 ? "none" : "1px solid #475569",
                                                    borderRadius:
                                                        index === 0 ? "1rem 1rem 0 0" : index === array.length - 1 ? "0 0 1rem 1rem" : "0",
                                                }}
                                                onMouseEnter={(e) => handleDropdownItemHover(e, true)}
                                                onMouseLeave={(e) => handleDropdownItemHover(e, false)}
                                            >
                                                {item.label}
                                            </div>
                                        ))}
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            {/* Student Login Modal */}
            <div
                style={{
                    ...styles.loginOverlay,
                    opacity: showLogin ? 1 : 0,
                    visibility: showLogin ? "visible" : "hidden",
                }}
                onClick={(e) => e.target === e.currentTarget && closeLogin()}
            >
                <div
                    style={{
                        ...styles.loginModal,
                        transform: showLogin ? "scale(1) translateY(0)" : "scale(0.9) translateY(-20px)",
                    }}
                >
                    <button
                        style={styles.closeButton}
                        onClick={closeLogin}
                        onMouseEnter={(e) => handleCloseButtonHover(e, true)}
                        onMouseLeave={(e) => handleCloseButtonHover(e, false)}
                    >
                        ×
                    </button>
                    <h2
                        style={{
                            ...styles.loginTitle,
                            background: "linear-gradient(135deg, #60a5fa, #34d399)",
                            WebkitBackgroundClip: "text",
                            WebkitTextFillColor: "transparent",
                            backgroundClip: "text",
                        }}
                    >
                        Student Login
                    </h2>
                    <p style={styles.loginSubtitle}>Enter your credentials to access the student portal</p>
                    <form style={styles.loginForm} onSubmit={handleLoginSubmit}>
                        <div style={styles.inputGroup}>
                            <label style={styles.inputLabel}>Username / Student ID</label>
                            <input
                                type="text"
                                name="username"
                                value={loginData.username}
                                onChange={handleLoginChange}
                                style={styles.inputField}
                                onFocus={(e) => handleInputFocus(e, "#60a5fa")}
                                onBlur={handleInputBlur}
                                placeholder="Enter your student ID"
                                required
                            />
                        </div>
                        <div style={styles.inputGroup}>
                            <label style={styles.inputLabel}>Password</label>
                            <input
                                type="password"
                                name="password"
                                value={loginData.password}
                                onChange={handleLoginChange}
                                style={styles.inputField}
                                onFocus={(e) => handleInputFocus(e, "#60a5fa")}
                                onBlur={handleInputBlur}
                                placeholder="Enter your password"
                                required
                            />
                        </div>
                        <button
                            type="submit"
                            style={{
                                ...styles.loginButton,
                                background: "linear-gradient(135deg, #3b82f6, #10b981)",
                            }}
                            onMouseEnter={(e) => handleLoginButtonHover(e, true, "rgba(59, 130, 246, 0.4)")}
                            onMouseLeave={(e) => handleLoginButtonHover(e, false, "transparent")}
                        >
                            Login to Student Portal
                        </button>
                    </form>
                </div>
            </div>

            {/* Teacher Login Modal */}
            <div
                style={{
                    ...styles.loginOverlay,
                    opacity: showTeacherLogin ? 1 : 0,
                    visibility: showTeacherLogin ? "visible" : "hidden",
                }}
                onClick={(e) => e.target === e.currentTarget && closeTeacherLogin()}
            >
                <div
                    style={{
                        ...styles.loginModal,
                        transform: showTeacherLogin ? "scale(1) translateY(0)" : "scale(0.9) translateY(-20px)",
                    }}
                >
                    <button
                        style={styles.closeButton}
                        onClick={closeTeacherLogin}
                        onMouseEnter={(e) => handleCloseButtonHover(e, true)}
                        onMouseLeave={(e) => handleCloseButtonHover(e, false)}
                    >
                        ×
                    </button>
                    <h2
                        style={{
                            ...styles.loginTitle,
                            background: "linear-gradient(135deg, #10b981, #60a5fa)",
                            WebkitBackgroundClip: "text",
                            WebkitTextFillColor: "transparent",
                            backgroundClip: "text",
                        }}
                    >
                        Teacher Login
                    </h2>
                    <p style={styles.loginSubtitle}>Sign in to your faculty account</p>
                    <form style={styles.loginForm} onSubmit={handleTeacherLoginSubmit}>
                        <div style={styles.inputGroup}>
                            <label style={styles.inputLabel}>Faculty Email Address</label>
                            <input
                                type="email"
                                name="email"
                                value={teacherLoginData.email}
                                onChange={handleTeacherLoginChange}
                                style={styles.inputField}
                                onFocus={(e) => handleInputFocus(e, "#10b981")}
                                onBlur={handleInputBlur}
                                placeholder="Enter your faculty email"
                                required
                            />
                        </div>
                        <div style={styles.inputGroup}>
                            <label style={styles.inputLabel}>Password</label>
                            <div style={styles.passwordContainer}>
                                <input
                                    type={showTeacherPassword ? "text" : "password"}
                                    name="password"
                                    value={teacherLoginData.password}
                                    onChange={handleTeacherLoginChange}
                                    style={styles.inputField}
                                    onFocus={(e) => handleInputFocus(e, "#10b981")}
                                    onBlur={handleInputBlur}
                                    placeholder="Enter your password"
                                    required
                                />
                                <button type="button" onClick={toggleTeacherPassword} style={styles.passwordToggle}>
                                    {showTeacherPassword ? "Hide" : "Show"}
                                </button>
                            </div>
                        </div>
                        <div style={styles.checkboxContainer}>
                            <input
                                type="checkbox"
                                id="teacherRemember"
                                name="remember"
                                checked={teacherLoginData.remember}
                                onChange={handleTeacherLoginChange}
                                style={styles.checkbox}
                            />
                            <label htmlFor="teacherRemember" style={styles.checkboxLabel}>
                                Remember me
                            </label>
                        </div>
                        <button
                            type="submit"
                            style={{
                                ...styles.loginButton,
                                background: "linear-gradient(135deg, #10b981, #3b82f6)",
                            }}
                            onMouseEnter={(e) => handleLoginButtonHover(e, true, "rgba(16, 185, 129, 0.4)")}
                            onMouseLeave={(e) => handleLoginButtonHover(e, false, "transparent")}
                        >
                            Sign In to Faculty Portal
                        </button>
                    </form>
                </div>
            </div>

            {/* Food Cart Admin Login Modal */}
            <div
                style={{
                    ...styles.loginOverlay,
                    opacity: showFoodCartLogin ? 1 : 0,
                    visibility: showFoodCartLogin ? "visible" : "hidden",
                }}
                onClick={(e) => e.target === e.currentTarget && closeFoodCartLogin()}
            >
                <div
                    style={{
                        ...styles.loginModal,
                        transform: showFoodCartLogin ? "scale(1) translateY(0)" : "scale(0.9) translateY(-20px)",
                    }}
                >
                    <button
                        style={styles.closeButton}
                        onClick={closeFoodCartLogin}
                        onMouseEnter={(e) => handleCloseButtonHover(e, true)}
                        onMouseLeave={(e) => handleCloseButtonHover(e, false)}
                    >
                        ×
                    </button>
                    <h2
                        style={{
                            ...styles.loginTitle,
                            background: adminThemes.foodCart.title,
                            WebkitBackgroundClip: "text",
                            WebkitTextFillColor: "transparent",
                            backgroundClip: "text",
                        }}
                    >
                        🍕 Food Cart Admin
                    </h2>
                    <p style={styles.loginSubtitle}>Access Food Cart Management System</p>
                    <form style={styles.loginForm} onSubmit={handleFoodCartLoginSubmit}>
                        <div style={styles.inputGroup}>
                            <label style={styles.inputLabel}>Admin Email</label>
                            <input
                                type="email"
                                name="email"
                                value={foodCartLoginData.email}
                                onChange={handleFoodCartLoginChange}
                                style={styles.inputField}
                                onFocus={(e) => handleInputFocus(e, adminThemes.foodCart.focus)}
                                onBlur={handleInputBlur}
                                placeholder="Enter admin email"
                                required
                            />
                        </div>
                        <div style={styles.inputGroup}>
                            <label style={styles.inputLabel}>Password</label>
                            <div style={styles.passwordContainer}>
                                <input
                                    type={showFoodCartPassword ? "text" : "password"}
                                    name="password"
                                    value={foodCartLoginData.password}
                                    onChange={handleFoodCartLoginChange}
                                    style={styles.inputField}
                                    onFocus={(e) => handleInputFocus(e, adminThemes.foodCart.focus)}
                                    onBlur={handleInputBlur}
                                    placeholder="Enter your password"
                                    required
                                />
                                <button type="button" onClick={toggleFoodCartPassword} style={styles.passwordToggle}>
                                    {showFoodCartPassword ? "Hide" : "Show"}
                                </button>
                            </div>
                        </div>
                        <div style={styles.checkboxContainer}>
                            <input
                                type="checkbox"
                                id="foodCartRemember"
                                name="remember"
                                checked={foodCartLoginData.remember}
                                onChange={handleFoodCartLoginChange}
                                style={styles.checkbox}
                            />
                            <label htmlFor="foodCartRemember" style={styles.checkboxLabel}>
                                Remember me
                            </label>
                        </div>
                        <button
                            type="submit"
                            style={{
                                ...styles.loginButton,
                                background: adminThemes.foodCart.gradient,
                            }}
                            onMouseEnter={(e) => handleLoginButtonHover(e, true, "rgba(245, 158, 11, 0.4)")}
                            onMouseLeave={(e) => handleLoginButtonHover(e, false, "transparent")}
                        >
                            Access Food Cart System
                        </button>
                    </form>
                </div>
            </div>

            {/* Canteen Admin Login Modal */}
            <div
                style={{
                    ...styles.loginOverlay,
                    opacity: showCanteenLogin ? 1 : 0,
                    visibility: showCanteenLogin ? "visible" : "hidden",
                }}
                onClick={(e) => e.target === e.currentTarget && closeCanteenLogin()}
            >
                <div
                    style={{
                        ...styles.loginModal,
                        transform: showCanteenLogin ? "scale(1) translateY(0)" : "scale(0.9) translateY(-20px)",
                    }}
                >
                    <button
                        style={styles.closeButton}
                        onClick={closeCanteenLogin}
                        onMouseEnter={(e) => handleCloseButtonHover(e, true)}
                        onMouseLeave={(e) => handleCloseButtonHover(e, false)}
                    >
                        ×
                    </button>
                    <h2
                        style={{
                            ...styles.loginTitle,
                            background: adminThemes.canteen.title,
                            WebkitBackgroundClip: "text",
                            WebkitTextFillColor: "transparent",
                            backgroundClip: "text",
                        }}
                    >
                        🍽 Canteen Admin
                    </h2>
                    <p style={styles.loginSubtitle}>Access Canteen Management System</p>
                    <form style={styles.loginForm} onSubmit={handleCanteenLoginSubmit}>
                        <div style={styles.inputGroup}>
                            <label style={styles.inputLabel}>Admin Email</label>
                            <input
                                type="email"
                                name="email"
                                value={canteenLoginData.email}
                                onChange={handleCanteenLoginChange}
                                style={styles.inputField}
                                onFocus={(e) => handleInputFocus(e, adminThemes.canteen.focus)}
                                onBlur={handleInputBlur}
                                placeholder="Enter admin email"
                                required
                            />
                        </div>
                        <div style={styles.inputGroup}>
                            <label style={styles.inputLabel}>Password</label>
                            <div style={styles.passwordContainer}>
                                <input
                                    type={showCanteenPassword ? "text" : "password"}
                                    name="password"
                                    value={canteenLoginData.password}
                                    onChange={handleCanteenLoginChange}
                                    style={styles.inputField}
                                    onFocus={(e) => handleInputFocus(e, adminThemes.canteen.focus)}
                                    onBlur={handleInputBlur}
                                    placeholder="Enter your password"
                                    required
                                />
                                <button type="button" onClick={toggleCanteenPassword} style={styles.passwordToggle}>
                                    {showCanteenPassword ? "Hide" : "Show"}
                                </button>
                            </div>
                        </div>
                        <div style={styles.checkboxContainer}>
                            <input
                                type="checkbox"
                                id="canteenRemember"
                                name="remember"
                                checked={canteenLoginData.remember}
                                onChange={handleCanteenLoginChange}
                                style={styles.checkbox}
                            />
                            <label htmlFor="canteenRemember" style={styles.checkboxLabel}>
                                Remember me
                            </label>
                        </div>
                        <button
                            type="submit"
                            style={{
                                ...styles.loginButton,
                                background: adminThemes.canteen.gradient,
                            }}
                            onMouseEnter={(e) => handleLoginButtonHover(e, true, "rgba(139, 92, 246, 0.4)")}
                            onMouseLeave={(e) => handleLoginButtonHover(e, false, "transparent")}
                        >
                            Access Canteen System
                        </button>
                    </form>
                </div>
            </div>

            {/* Stationery Admin Login Modal */}
            <div
                style={{
                    ...styles.loginOverlay,
                    opacity: showStationeryLogin ? 1 : 0,
                    visibility: showStationeryLogin ? "visible" : "hidden",
                }}
                onClick={(e) => e.target === e.currentTarget && closeStationeryLogin()}
            >
                <div
                    style={{
                        ...styles.loginModal,
                        transform: showStationeryLogin ? "scale(1) translateY(0)" : "scale(0.9) translateY(-20px)",
                    }}
                >
                    <button
                        style={styles.closeButton}
                        onClick={closeStationeryLogin}
                        onMouseEnter={(e) => handleCloseButtonHover(e, true)}
                        onMouseLeave={(e) => handleCloseButtonHover(e, false)}
                    >
                        ×
                    </button>
                    <h2
                        style={{
                            ...styles.loginTitle,
                            background: adminThemes.stationery.title,
                            WebkitBackgroundClip: "text",
                            WebkitTextFillColor: "transparent",
                            backgroundClip: "text",
                        }}
                    >
                        📚 Stationery Admin
                    </h2>
                    <p style={styles.loginSubtitle}>Access Stationery Management System</p>
                    <form style={styles.loginForm} onSubmit={handleStationeryLoginSubmit}>
                        <div style={styles.inputGroup}>
                            <label style={styles.inputLabel}>Admin Email</label>
                            <input
                                type="email"
                                name="email"
                                value={stationeryLoginData.email}
                                onChange={handleStationeryLoginChange}
                                style={styles.inputField}
                                onFocus={(e) => handleInputFocus(e, adminThemes.stationery.focus)}
                                onBlur={handleInputBlur}
                                placeholder="Enter admin email"
                                required
                            />
                        </div>
                        <div style={styles.inputGroup}>
                            <label style={styles.inputLabel}>Password</label>
                            <div style={styles.passwordContainer}>
                                <input
                                    type={showStationeryPassword ? "text" : "password"}
                                    name="password"
                                    value={stationeryLoginData.password}
                                    onChange={handleStationeryLoginChange}
                                    style={styles.inputField}
                                    onFocus={(e) => handleInputFocus(e, adminThemes.stationery.focus)}
                                    onBlur={handleInputBlur}
                                    placeholder="Enter your password"
                                    required
                                />
                                <button type="button" onClick={toggleStationeryPassword} style={styles.passwordToggle}>
                                    {showStationeryPassword ? "Hide" : "Show"}
                                </button>
                            </div>
                        </div>
                        <div style={styles.checkboxContainer}>
                            <input
                                type="checkbox"
                                id="stationeryRemember"
                                name="remember"
                                checked={stationeryLoginData.remember}
                                onChange={handleStationeryLoginChange}
                                style={styles.checkbox}
                            />
                            <label htmlFor="stationeryRemember" style={styles.checkboxLabel}>
                                Remember me
                            </label>
                        </div>
                        <button
                            type="submit"
                            style={{
                                ...styles.loginButton,
                                background: adminThemes.stationery.gradient,
                            }}
                            onMouseEnter={(e) => handleLoginButtonHover(e, true, "rgba(6, 182, 212, 0.4)")}
                            onMouseLeave={(e) => handleLoginButtonHover(e, false, "transparent")}
                        >
                            Access Stationery System
                        </button>
                    </form>
                </div>
            </div>

            {/* Department Admin Login Modal */}
            <div
                style={{
                    ...styles.loginOverlay,
                    opacity: showDepartmentLogin ? 1 : 0,
                    visibility: showDepartmentLogin ? "visible" : "hidden",
                }}
                onClick={(e) => e.target === e.currentTarget && closeDepartmentLogin()}
            >
                <div
                    style={{
                        ...styles.loginModal,
                        transform: showDepartmentLogin ? "scale(1) translateY(0)" : "scale(0.9) translateY(-20px)",
                    }}
                >
                    <button
                        style={styles.closeButton}
                        onClick={closeDepartmentLogin}
                        onMouseEnter={(e) => handleCloseButtonHover(e, true)}
                        onMouseLeave={(e) => handleCloseButtonHover(e, false)}
                    >
                        ×
                    </button>
                    <h2
                        style={{
                            ...styles.loginTitle,
                            background: adminThemes.department.title,
                            WebkitBackgroundClip: "text",
                            WebkitTextFillColor: "transparent",
                            backgroundClip: "text",
                        }}
                    >
                        🏢 Department Admin
                    </h2>
                    <p style={styles.loginSubtitle}>Access Department Management System</p>
                    <form style={styles.loginForm} onSubmit={handleDepartmentLoginSubmit}>
                        <div style={styles.inputGroup}>
                            <label style={styles.inputLabel}>Admin Email</label>
                            <input
                                type="email"
                                name="email"
                                value={departmentLoginData.email}
                                onChange={handleDepartmentLoginChange}
                                style={styles.inputField}
                                onFocus={(e) => handleInputFocus(e, adminThemes.department.focus)}
                                onBlur={handleInputBlur}
                                placeholder="Enter admin email"
                                required
                            />
                        </div>
                        <div style={styles.inputGroup}>
                            <label style={styles.inputLabel}>Password</label>
                            <div style={styles.passwordContainer}>
                                <input
                                    type={showDepartmentPassword ? "text" : "password"}
                                    name="password"
                                    value={departmentLoginData.password}
                                    onChange={handleDepartmentLoginChange}
                                    style={styles.inputField}
                                    onFocus={(e) => handleInputFocus(e, adminThemes.department.focus)}
                                    onBlur={handleInputBlur}
                                    placeholder="Enter your password"
                                    required
                                />
                                <button type="button" onClick={toggleDepartmentPassword} style={styles.passwordToggle}>
                                    {showDepartmentPassword ? "Hide" : "Show"}
                                </button>
                            </div>
                        </div>
                        <div style={styles.checkboxContainer}>
                            <input
                                type="checkbox"
                                id="departmentRemember"
                                name="remember"
                                checked={departmentLoginData.remember}
                                onChange={handleDepartmentLoginChange}
                                style={styles.checkbox}
                            />
                            <label htmlFor="departmentRemember" style={styles.checkboxLabel}>
                                Remember me
                            </label>
                        </div>
                        <button
                            type="submit"
                            style={{
                                ...styles.loginButton,
                                background: adminThemes.department.gradient,
                            }}
                            onMouseEnter={(e) => handleLoginButtonHover(e, true, "rgba(220, 38, 38, 0.4)")}
                            onMouseLeave={(e) => handleLoginButtonHover(e, false, "transparent")}
                        >
                            Access Department System
                        </button>
                    </form>
                </div>
            </div>

            <br></br>
            <br></br>
            <br></br>
            <br></br>
            <br></br>
            {/* Additional Content Sections for Scrolling */}
            <div style={styles.additionalContent}>
                <h2 style={styles.sectionTitle}>About DSCE</h2>
                <p style={styles.sectionText}>
                    Dayananda Sagar College of Engineering is a premier institution dedicated to excellence in education,
                    research, and innovation. Our state-of-the-art facilities and experienced faculty provide students with the
                    best learning environment.
                </p>
                <p style={styles.sectionText}>
                    We offer undergraduate and postgraduate programs in various engineering disciplines, fostering creativity and
                    technical expertise.
                </p>
            </div>
            <div style={styles.additionalContent}>
                <h2 style={styles.sectionTitle}>Academic Excellence</h2>
                <p style={styles.sectionText}>
                    Our comprehensive curriculum is designed to meet industry standards and prepare students for successful
                    careers. We emphasize practical learning through hands-on projects and industry collaborations.
                </p>
                <p style={styles.sectionText}>
                    With modern laboratories, research facilities, and a vibrant campus life, DSCE provides a holistic educational
                    experience.
                </p>
            </div>
            <div style={styles.additionalContent}>
                <h2 style={styles.sectionTitle}>Campus Facilities</h2>
                <p style={styles.sectionText}>
                    Our campus features modern classrooms, well-equipped laboratories, a comprehensive library, sports facilities,
                    and comfortable hostel accommodations for students.
                </p>
                <p style={styles.sectionText}>
                    We also provide various student services including career counseling, placement assistance, and
                    extracurricular activities to ensure overall development of our students.
                </p>
            </div>
        </div>
    )
}

export default DSCEPortal
