"use client";

import React, { useState, useEffect, useRef } from "react";

// Stationary Store Component (all features inside one file)
const Stationary = () => {
    // Application state (current page and cart)
    const [currentPage, setCurrentPage] = useState("stationary");
    const [orderData, setOrderData] = useState(null);
    const [cartItems, setCartItems] = useState([]);

    // ------- Stationary Page -------

    const [selectedCategory, setSelectedCategory] = useState("books-notebooks");
    const [showAlert, setShowAlert] = useState(false);
    const [alertMessage, setAlertMessage] = useState("");

    // Categories with icons (emojis here)
    const categories = [
        { id: "books-notebooks", name: "Books & Notebooks", icon: "📚" },
        { id: "writing-instruments", name: "Writing Instruments", icon: "✏️" },
        { id: "art-supplies", name: "Art Supplies", icon: "🎨" },
        { id: "office-supplies", name: "Office Supplies", icon: "📎" },
        { id: "electronics", name: "Electronics & Accessories", icon: "🔌" },
    ];

    // All stationary items, categorized
    const stationaryItems = {
        "books-notebooks": [
            {
                id: 1,
                name: "University Record Book",
                description: "Official university record book with 200 pages, ruled format",
                price: 45,
                category: "Books & Notebooks",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 2,
                name: "Blue Book (Exam)",
                description: "Standard blue book for examinations, 16 pages, college approved",
                price: 15,
                category: "Books & Notebooks",
                image: "/placeholder.svg?height=120&width=120",
            },
            // Other items as in your previous list...
            // ...
        ],
        "writing-instruments": [
            {
                id: 7,
                name: "Blue Ballpoint Pen (Pack of 10)",
                description: "Smooth writing blue ballpoint pens, reliable ink flow",
                price: 50,
                category: "Writing Instruments",
                image: "/placeholder.svg?height=120&width=120",
            },
            // ...
        ],
        // add remaining categories and items here exactly as you want, omitted here for brevity
    };

    // Add to cart handler
    const addToCart = (item) => {
        setCartItems((prev) => {
            const existing = prev.find((i) => i.id === item.id);
            if (existing) {
                return prev.map((i) =>
                    i.id === item.id ? { ...i, quantity: i.quantity + 1 } : i
                );
            } else {
                return [...prev, { ...item, quantity: 1 }];
            }
        });
        setAlertMessage(`${item.name} added to cart!`);
        setShowAlert(true);
        setTimeout(() => setShowAlert(false), 2000);
    };

    // ------- Cart Page -------

    // Remove from cart
    const removeFromCart = (id) => {
        setCartItems((prev) => prev.filter((item) => item.id !== id));
    };

    // Update item quantity
    const updateQuantity = (id, change) => {
        setCartItems((prev) =>
            prev
                .map((item) => {
                    if (item.id === id) {
                        const newQty = Math.max(0, item.quantity + change);
                        return newQty === 0 ? null : { ...item, quantity: newQty };
                    }
                    return item;
                })
                .filter(Boolean)
        );
    };

    // Proceed to payment
    const proceedToPayment = (customerInfo) => {
        if (!customerInfo.name || !customerInfo.studentId || !customerInfo.phone) {
            alert("Please fill all required fields!");
            return;
        }
        const subtotal = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);
        const tax = Math.round(subtotal * 0.05); // 5% GST
        const total = subtotal + tax;

        const order = {
            subtotal,
            tax,
            total,
            itemCount: cartItems.reduce((sum, i) => sum + i.quantity, 0),
            customerInfo,
            items: cartItems,
        };

        setOrderData(order);
        setCurrentPage("payment");
    };

    // ------- Payment Page -------

    const [selectedPaymentMethod, setSelectedPaymentMethod] = useState("upi");
    const [paymentDetails, setPaymentDetails] = useState({
        upiId: "",
        cardNumber: "",
        cardExpiry: "",
        cardCvv: "",
        cardName: "",
    });
    const [isProcessing, setIsProcessing] = useState(false);

    const paymentMethods = [
        { id: "upi", name: "UPI Payment", icon: "📱", description: "Pay using UPI ID" },
        { id: "card", name: "Credit/Debit Card", icon: "💳", description: "Pay using your card" },
        { id: "cod", name: "Cash on Delivery", icon: "💵", description: "Pay when you receive" },
    ];

    const handlePaymentInputChange = (e) => {
        setPaymentDetails({ ...paymentDetails, [e.target.name]: e.target.value });
    };

    const handleMakePayment = () => {
        if (selectedPaymentMethod === "upi" && !paymentDetails.upiId) {
            alert("Please enter your UPI ID");
            return;
        }
        if (selectedPaymentMethod === "card") {
            if (
                !paymentDetails.cardNumber ||
                !paymentDetails.cardExpiry ||
                !paymentDetails.cardCvv ||
                !paymentDetails.cardName
            ) {
                alert("Please provide all card details");
                return;
            }
        }

        setIsProcessing(true);
        setTimeout(() => {
            setIsProcessing(false);
            setCartItems([]);
            setCurrentPage("confirmation");
        }, 3000);
    };

    // ------- Order Confirmation Page -------

    const generateOrderNumber = () => `DSCE${Date.now().toString().slice(-6)}`;

    const getFormattedDateTime = () =>
        new Date().toLocaleString("en-IN", {
            timeZone: "Asia/Kolkata",
            year: "numeric",
            month: "2-digit",
            day: "2-digit",
            hour: "2-digit",
            minute: "2-digit",
            second: "2-digit",
        });

    //
    // Styles and rendering for all pages...
    // Due to size, below you'll find minimal styles inline, feel free to integrate with your CSS as needed.
    //

    // --- Stationary Page UI ---
    const StationaryPage = () => {
        const styles = {
            container: {
                minHeight: "100vh",
                background: "linear-gradient(135deg, #0f172a, #1e293b)",
                color: "white",
                fontFamily: "system-ui, -apple-system, sans-serif",
                padding: "1rem",
            },
            header: {
                display: "flex",
                justifyContent: "space-between",
                alignItems: "center",
                marginBottom: "1rem",
            },
            title: { fontSize: "1.5rem", fontWeight: "bold" },
            categories: { display: "flex", gap: "1rem", marginBottom: "1rem", flexWrap: "wrap" },
            categoryItem: (active) => ({
                cursor: "pointer",
                padding: "0.5rem 1rem",
                borderRadius: "1rem",
                background: active ? "#2563eb" : "#1e293b",
                display: "flex",
                alignItems: "center",
                gap: "0.5rem",
                userSelect: "none",
            }),
            itemsGrid: {
                display: "grid",
                gridTemplateColumns: "repeat(auto-fill,minmax(300px,1fr))",
                gap: "1rem",
            },
            itemCard: {
                background: "#1e293b",
                borderRadius: "1rem",
                padding: "1rem",
                display: "flex",
                gap: "1rem",
            },
            itemImage: { width: "120px", height: "120px", objectFit: "cover", borderRadius: "1rem" },
            itemDetails: { flex: 1, display: "flex", flexDirection: "column", justifyContent: "space-between" },
            itemName: { fontSize: "1.25rem", fontWeight: "bold", marginBottom: "0.5rem" },
            itemDesc: { fontSize: "0.9rem", color: "#94a3b8", marginBottom: "1rem" },
            itemFooter: { display: "flex", justifyContent: "space-between", alignItems: "center" },
            addButton: {
                background: "#ef4444",
                border: "none",
                borderRadius: "9999px",
                padding: "0.5rem 1rem",
                color: "white",
                cursor: "pointer",
                fontWeight: "bold",
                userSelect: "none",
            },
            alert: {
                position: "fixed",
                top: 20,
                left: "50%",
                transform: "translateX(-50%)",
                background: "#10b981",
                padding: "1rem 2rem",
                borderRadius: "2rem",
                boxShadow: "0 5px 15px rgba(16, 185, 129, 0.5)",
                opacity: showAlert ? 1 : 0,
                pointerEvents: showAlert ? "auto" : "none",
                transition: "opacity 0.3s ease",
                zIndex: 1000,
            },
        };

        return (
            <div style={styles.container}>
                <header style={styles.header}>
                    <h1 style={styles.title}>DSCE Stationary Store</h1>
                    <div
                        role="button"
                        tabIndex={0}
                        onClick={() => setCurrentPage("cart")}
                        style={{ cursor: "pointer", fontSize: "1.5rem" }}
                        aria-label={`View cart with ${cartItems.reduce((a, b) => a + b.quantity, 0)} items`}
                    >
                        🛒 ({cartItems.reduce((a, b) => a + b.quantity, 0)})
                    </div>
                </header>

                <div style={styles.categories}>
                    {categories.map(({ id, name, icon }) => (
                        <div
                            key={id}
                            style={styles.categoryItem(selectedCategory === id)}
                            onClick={() => setSelectedCategory(id)}
                            role="button"
                            tabIndex={0}
                            onKeyPress={(e) => {
                                if (e.key === "Enter" || e.key === " ") setSelectedCategory(id);
                            }}
                        >
                            <span>{icon}</span> <span>{name}</span>
                        </div>
                    ))}
                </div>

                <div style={styles.itemsGrid}>
                    {(stationaryItems[selectedCategory] || []).map((item) => (
                        <div key={item.id} style={styles.itemCard}>
                            <img src={item.image || "/placeholder.svg"} alt={item.name} style={styles.itemImage} />
                            <div style={styles.itemDetails}>
                                <div>
                                    <h3 style={styles.itemName}>{item.name}</h3>
                                    <p style={styles.itemDesc}>{item.description}</p>
                                </div>
                                <div style={styles.itemFooter}>
                                    <strong>₹{item.price}/-</strong>
                                    <button style={styles.addButton} onClick={() => addToCart(item)}>
                                        Add +
                                    </button>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>

                {/* Alert */}
                <div style={styles.alert}>{alertMessage}</div>
            </div>
        );
    };

    // ------- CART PAGE UI -------

    const CartPage = () => {
        const [customerInfo, setCustomerInfo] = useState({
            name: "",
            studentId: "",
            phone: "",
            email: "",
        });

        const handleInputChange = (e) => {
            setCustomerInfo({ ...customerInfo, [e.target.name]: e.target.value });
        };

        const subtotal = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);
        const tax = Math.round(subtotal * 0.05);
        const total = subtotal + tax;

        const styles = {
            container: {
                minHeight: "100vh",
                fontFamily: "system-ui, -apple-system, sans-serif",
                background: "linear-gradient(135deg, #0f172a, #1e293b)",
                color: "white",
                padding: "1rem",
            },
            header: {
                display: "flex",
                alignItems: "center",
                justifyContent: "space-between",
                marginBottom: "1rem",
            },
            title: { fontSize: "1.5rem", fontWeight: "bold" },
            backButton: {
                cursor: "pointer",
                fontSize: "2rem",
                userSelect: "none",
            },
            cartList: { marginBottom: "2rem" },
            cartItem: {
                background: "#1e293b",
                padding: "1rem",
                borderRadius: "1rem",
                marginBottom: "1rem",
                display: "flex",
                alignItems: "center",
                gap: "1rem",
            },
            itemImage: { width: "80px", height: "80px", borderRadius: "1rem", objectFit: "cover" },
            itemDetails: { flex: 1 },
            itemName: { fontWeight: "bold", fontSize: "1.2rem" },
            itemQtyControls: {
                display: "flex",
                alignItems: "center",
                gap: "0.5rem",
            },
            qtyButton: {
                background: "#3b82f6",
                border: "none",
                borderRadius: "50%",
                color: "white",
                width: "30px",
                height: "30px",
                cursor: "pointer",
                fontWeight: "bold",
                fontSize: "1.2rem",
            },
            removeButton: {
                background: "#ef4444",
                border: "none",
                padding: "0.5rem 1rem",
                borderRadius: "0.5rem",
                cursor: "pointer",
                fontWeight: "bold",
                color: "white",
            },
            customerForm: {
                display: "grid",
                gridTemplateColumns: "repeat(auto-fit, minmax(220px,1fr))",
                gap: "1rem",
                marginBottom: "2rem",
            },
            inputField: {
                padding: "0.75rem",
                borderRadius: "0.5rem",
                border: "1px solid #475569",
                background: "#1e293b",
                color: "white",
                fontSize: "1rem",
            },
            sectionTitle: { fontSize: "1.25rem", fontWeight: "bold", marginBottom: "1rem", color: "#60a5fa" },
            billSummary: {
                background: "#1e293b",
                padding: "1rem",
                borderRadius: "1rem",
                marginBottom: "2rem",
            },
            billRow: {
                display: "flex",
                justifyContent: "space-between",
                marginBottom: "0.5rem",
            },
            totalRow: {
                fontWeight: "bold",
                fontSize: "1.25rem",
                borderTop: "1px solid #334155",
                paddingTop: "1rem",
            },
            proceedButton: {
                width: "100%",
                background: "#10b981",
                padding: "1rem",
                borderRadius: "9999px",
                border: "none",
                fontWeight: "bold",
                fontSize: "1.2rem",
                cursor: "pointer",
                color: "white",
                textTransform: "uppercase",
            },
        };

        if (cartItems.length === 0)
            return (
                <div style={styles.container}>
                    <div style={styles.header}>
                        <span style={styles.backButton} onClick={() => setCurrentPage("stationary")} role="button" tabIndex={0}>
                            ←
                        </span>
                        <h1 style={styles.title}>Your Cart is Empty</h1>
                        <div></div>
                    </div>
                    <p>Add some stationary items from the store to your cart.</p>
                </div>
            );

        return (
            <div style={styles.container}>
                <div style={styles.header}>
                    <span style={styles.backButton} onClick={() => setCurrentPage("stationary")} role="button" tabIndex={0}>
                        ←
                    </span>
                    <h1 style={styles.title}>Your Cart</h1>
                    <div></div>
                </div>

                <div style={styles.cartList}>
                    {cartItems.map((item) => (
                        <div key={item.id} style={styles.cartItem}>
                            <img src={item.image || "/placeholder.svg"} alt={item.name} style={styles.itemImage} />
                            <div style={styles.itemDetails}>
                                <div style={styles.itemName}>{item.name}</div>
                                <div>₹{item.price} x {item.quantity}</div>
                                <div>{item.category}</div>
                            </div>
                            <div style={styles.itemQtyControls}>
                                <button
                                    style={styles.qtyButton}
                                    onClick={() => updateQuantity(item.id, -1)}
                                    aria-label={`Decrease quantity of ${item.name}`}
                                >
                                    −
                                </button>
                                <span>{item.quantity}</span>
                                <button
                                    style={styles.qtyButton}
                                    onClick={() => updateQuantity(item.id, 1)}
                                    aria-label={`Increase quantity of ${item.name}`}
                                >
                                    +
                                </button>
                            </div>
                            <button style={styles.removeButton} onClick={() => removeFromCart(item.id)} aria-label={`Remove ${item.name} from cart`}>
                                Remove
                            </button>
                        </div>
                    ))}
                </div>

                <div>
                    <h2 style={styles.sectionTitle}>Customer Information</h2>
                    <form style={styles.customerForm} onSubmit={(e) => { e.preventDefault(); proceedToPayment(customerInfo); }}>
                        <input
                            type="text"
                            placeholder="Full Name *"
                            name="name"
                            value={customerInfo.name}
                            onChange={handleInputChange}
                            style={styles.inputField}
                            required
                        />
                        <input
                            type="text"
                            placeholder="Student ID *"
                            name="studentId"
                            value={customerInfo.studentId}
                            onChange={handleInputChange}
                            style={styles.inputField}
                            required
                        />
                        <input
                            type="tel"
                            placeholder="Phone Number *"
                            name="phone"
                            value={customerInfo.phone}
                            onChange={handleInputChange}
                            style={styles.inputField}
                            required
                        />
                        <input
                            type="email"
                            placeholder="Email (Optional)"
                            name="email"
                            value={customerInfo.email}
                            onChange={handleInputChange}
                            style={styles.inputField}
                        />
                        <button type="submit" style={styles.proceedButton}>Proceed to Payment</button>
                    </form>
                </div>

                <div style={styles.billSummary}>
                    <div style={styles.billRow}>
                        <span>Subtotal ({cartItems.reduce((a, b) => a + b.quantity, 0)} items)</span><span>₹{subtotal}/-</span>
                    </div>
                    <div style={styles.billRow}>
                        <span>GST (5%)</span><span>₹{tax}/-</span>
                    </div>
                    <div style={{ ...styles.billRow, ...styles.totalRow }}>
                        <span>Total</span><span>₹{total}/-</span>
                    </div>
                </div>
            </div>
        );
    };

    // ------- PAYMENT PAGE UI -------

    const PaymentPage = () => {
        const styles = {
            container: {
                minHeight: "100vh",
                fontFamily: "system-ui, -apple-system, sans-serif",
                background: "linear-gradient(135deg, #0f172a, #1e293b)",
                color: "white",
            },
            header: {
                display: "flex",
                alignItems: "center",
                justifyContent: "space-between",
                padding: "1rem",
                boxShadow: "0 2px 6px rgba(0,0,0,0.3)",
                position: "sticky",
                top: 0,
                background: "#134e4a",
                zIndex: 10,
            },
            backButton: {
                cursor: "pointer",
                fontSize: "2rem",
                userSelect: "none",
                color: "white",
            },
            title: {
                fontSize: "1.5rem",
                fontWeight: "bold",
            },
            containerInner: {
                maxWidth: "600px",
                margin: "1rem auto",
                padding: "0 1rem",
            },
            paymentMethods: {
                display: "flex",
                flexDirection: "column",
                gap: "1rem",
                marginBottom: "1rem",
            },
            paymentMethod: (active) => ({
                display: "flex",
                alignItems: "center",
                gap: "1rem",
                padding: "1rem",
                borderRadius: "1rem",
                cursor: "pointer",
                background: active ? "#065f46" : "#134e4a",
                border: active ? "2px solid #22c55e" : "none",
                userSelect: "none",
            }),
            paymentIcon: { fontSize: "2rem" },
            paymentInfo: { flex: 1 },
            payButton: {
                width: "100%",
                background: "#10b981",
                padding: "1rem",
                borderRadius: "9999px",
                border: "none",
                fontWeight: "bold",
                fontSize: "1.2rem",
                cursor: "pointer",
                color: "white",
                textTransform: "uppercase",
            },
            inputField: {
                width: "100%",
                padding: "0.75rem",
                marginBottom: "1rem",
                borderRadius: "0.5rem",
                border: "1px solid #475569",
                fontSize: "1rem",
                color: "white",
                background: "#134e4a",
            },
            processingOverlay: {
                position: "fixed",
                top: 0,
                left: 0,
                right: 0,
                bottom: 0,
                background: "rgba(0, 0, 0, 0.7)",
                zIndex: 1000,
                display: "flex",
                flexDirection: "column",
                justifyContent: "center",
                alignItems: "center",
                color: "white",
                fontSize: "1.5rem",
            },
        };

        return (
            <div style={styles.container}>
                <header style={styles.header}>
                    <span style={styles.backButton} onClick={() => setCurrentPage("cart")} role="button" tabIndex={0} aria-label="Back to cart">
                        ←
                    </span>
                    <h1 style={styles.title}>Payment</h1>
                    <div style={{ width: "2rem" }} /> {/* Placeholder */}
                </header>

                <div style={styles.containerInner}>
                    {/* Order Summary */}
                    <h2 style={{ marginBottom: "1rem", color: "#60a5fa" }}>Order Summary</h2>
                    <div style={{ marginBottom: "1rem" }}>
                        <div>Items: {orderData?.itemCount || 0}</div>
                        <div>Subtotal: ₹{orderData?.subtotal || 0}/-</div>
                        <div>GST (5%): ₹{orderData?.tax || 0}/-</div>
                        <div style={{ fontWeight: "bold", fontSize: "1.2rem" }}>Total: ₹{orderData?.total || 0}/-</div>
                    </div>

                    {/* Payment Method Selection */}
                    <h2 style={{ marginBottom: "1rem", color: "#60a5fa" }}>Select Payment Method</h2>
                    <div style={styles.paymentMethods}>
                        {paymentMethods.map((method) => (
                            <div
                                key={method.id}
                                style={styles.paymentMethod(selectedPaymentMethod === method.id)}
                                onClick={() => setSelectedPaymentMethod(method.id)}
                                role="button"
                                tabIndex={0}
                                onKeyPress={(e) => {
                                    if (e.key === "Enter" || e.key === " ") setSelectedPaymentMethod(method.id);
                                }}
                                aria-selected={selectedPaymentMethod === method.id}
                            >
                                <span style={styles.paymentIcon}>{method.icon}</span>
                                <div style={styles.paymentInfo}>
                                    <div>{method.name}</div>
                                    <div style={{ fontSize: "0.85rem", color: "#94a3b8" }}>{method.description}</div>
                                </div>
                            </div>
                        ))}
                    </div>

                    {/* Payment Details Inputs */}
                    {selectedPaymentMethod === "upi" && (
                        <input
                            type="text"
                            name="upiId"
                            placeholder="Enter UPI ID"
                            value={paymentDetails.upiId}
                            onChange={handlePaymentInputChange}
                            style={styles.inputField}
                            required
                        />
                    )}
                    {selectedPaymentMethod === "card" && (
                        <>
                            <input
                                type="text"
                                name="cardName"
                                placeholder="Cardholder Name"
                                value={paymentDetails.cardName}
                                onChange={handlePaymentInputChange}
                                style={styles.inputField}
                            />
                            <input
                                type="text"
                                name="cardNumber"
                                placeholder="Card Number"
                                value={paymentDetails.cardNumber}
                                onChange={handlePaymentInputChange}
                                maxLength={19}
                                style={styles.inputField}
                            />
                            <input
                                type="text"
                                name="cardExpiry"
                                placeholder="Expiry MM/YY"
                                value={paymentDetails.cardExpiry}
                                onChange={handlePaymentInputChange}
                                maxLength={5}
                                style={styles.inputField}
                            />
                            <input
                                type="text"
                                name="cardCvv"
                                placeholder="CVV"
                                value={paymentDetails.cardCvv}
                                onChange={handlePaymentInputChange}
                                maxLength={3}
                                style={styles.inputField}
                            />
                        </>
                    )}

                    <button
                        style={styles.payButton}
                        onClick={handleMakePayment}
                        disabled={isProcessing}
                        aria-label={selectedPaymentMethod === "cod" ? "Place Order" : "Pay"}
                    >
                        {selectedPaymentMethod === "cod"
                            ? "Place Order"
                            : `Pay ₹${orderData?.total || 0}/-`}
                    </button>

                    {/* Processing Overlay */}
                    {isProcessing && (
                        <div style={styles.processingOverlay} role="alert" aria-live="assertive">
                            Processing... Please wait.
                        </div>
                    )}
                </div>
            </div>
        );
    };

    // ------- ORDER CONFIRMATION UI -------

    const OrderConfirmationPage = () => {
        const styles = {
            container: {
                minHeight: "100vh",
                fontFamily: "system-ui, -apple-system, sans-serif",
                background: "linear-gradient(135deg, #0f172a, #1e293b)",
                color: "white",
                padding: "1rem",
                textAlign: "center",
            },
            header: {
                display: "flex",
                justifyContent: "space-between",
                alignItems: "center",
                marginBottom: "2rem",
            },
            title: { fontSize: "1.5rem", fontWeight: "bold" },
            orderNum: {
                fontSize: "2rem",
                fontWeight: "bold",
                margin: "1rem 0",
                color: "#22c55e",
            },
            button: {
                padding: "1rem 3rem",
                margin: "0.5rem",
                fontWeight: "bold",
                fontSize: "1.1rem",
                borderRadius: "9999px",
                cursor: "pointer",
                border: "none",
                userSelect: "none",
            },
            primaryButton: {
                backgroundColor: "#2563eb",
                color: "white",
            },
            secondaryButton: {
                backgroundColor: "#475569",
                color: "white",
            },
        };

        return (
            <div style={styles.container}>
                <div style={styles.header}>
                    <button onClick={() => setCurrentPage("stationary")} style={{ ...styles.button, ...styles.secondaryButton }} aria-label="Order more stationary">
                        ← Order More
                    </button>
                    <h1 style={styles.title}>Order Confirmed 🎉</h1>
                    <div style={{ width: "7rem" }} />
                </div>

                <p>Thank you for your order! We're preparing your stationary items.</p>

                <div style={styles.orderNum}>Order #{generateOrderNumber()}</div>

                <p>Order Date &amp; Time: {getFormattedDateTime()}</p>

                <p>Items Ordered: {orderData?.itemCount || 0}</p>

                <p style={{ fontWeight: "bold", fontSize: "1.25rem", color: "#22c55e" }}>
                    Total Amount Paid: ₹{orderData?.total || 0}/-
                </p>

                <p>Payment Status: ✅ Confirmed</p>

                {/* No delivery charge or delivery time mentioned per your requirement */}

                <button onClick={() => alert("Order tracking coming soon!")} style={{ ...styles.button, ...styles.primaryButton }} aria-label="Track Order">
                    Track Order
                </button>
            </div>
        );
    };

    // ------- Main render logic -------
    switch (currentPage) {
        case "stationary":
            return <StationaryPage />;
        case "cart":
            return <CartPage />;
        case "payment":
            return <PaymentPage />;
        case "confirmation":
            return <OrderConfirmationPage />;
        default:
            return <StationaryPage />;
    }
};

export default Stationary;
