import { useNavigate } from "react-router-dom"
import { useState, useEffect } from "react"
// ... your imports

const StudentDashboard = () => {
    const [sidebarOpen, setSidebarOpen] = useState(true)
    const [activeItem, setActiveItem] = useState("Home")
    const [isMobile, setIsMobile] = useState(false)

    const navigate = useNavigate()  // React Router navigate

    useEffect(() => {
        const handleResize = () => {
            const mobile = window.innerWidth <= 768
            setIsMobile(mobile)
            if (mobile) {
                setSidebarOpen(false)
            }
        }

        handleResize()
        window.addEventListener("resize", handleResize)
        return () => window.removeEventListener("resize", handleResize)
    }, [])

    const handleSidebarToggle = () => {
        setSidebarOpen(!sidebarOpen)
    }

    const handleItemClick = (item) => {
        setActiveItem(item)
        if (isMobile) {
            setSidebarOpen(false)
        }

        // Use navigate() instead of window.location.href
        if (item === "Placements") {
            navigate("/StudentPlace")
        } else if (item === "Canteen") {
            navigate("/Food")
        } else if (item === "Food Cart") {
            navigate("/cart")
        } else if (item === "Home") {
            navigate("/") // example home route
        }
    }

    return (
        <div className="dashboard-container">
            {isMobile && sidebarOpen && <div className="overlay" onClick={() => setSidebarOpen(false)} />}
            <Sidebar isOpen={sidebarOpen} activeItem={activeItem} onItemClick={handleItemClick} />
            <div className="main-content">
                <Header onSidebarToggle={handleSidebarToggle} sidebarOpen={sidebarOpen} />
                <MainContent />
            </div>
        </div>
    )
}
