import { useState } from 'react';
import { Eye, EyeOff, User, Lock, AlertCircle, CheckCircle } from 'lucide-react';

export default function FoodCourt() {
    const [formData, setFormData] = useState({
        email: '',
        password: ''
    });
    const [showPassword, setShowPassword] = useState(false);

    const [message, setMessage] = useState({ type: '', text: '' });
    const [isLoading, setIsLoading] = useState(false);

    const handleInputChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    const showMessage = (type, text) => {
        setMessage({ type, text });
        setTimeout(() => setMessage({ type: '', text: '' }), 3000);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setIsLoading(true);

        // Simulate API call
        setTimeout(() => {
            if (formData.email === 'admin@dsce.edu' && formData.password === 'admin123') {
                showMessage('success', 'Login successful! Redirecting to dashboard...');
                setTimeout(() => {
                    alert('Welcome to DSCE Food Court Admin Dashboard!\n\nDashboard Features:\n‚Ä¢ View & manage food orders\n‚Ä¢ Update menu items\n‚Ä¢ Track order analytics\n‚Ä¢ Customer feedback\n‚Ä¢ Revenue reports');
                }, 2000);
            } else {
                showMessage('error', 'Invalid credentials. Please check your email and password.');
            }
            setIsLoading(false);
        }, 1500);
    };

    const handleForgotPassword = () => {
        alert('Password Reset Request\n\nPlease contact DSCE IT Support:\nüìß support@dsce.edu\nüìû +91-80-28440384\n\nInclude your admin ID for faster assistance.');
    };

    return (
        <div className="min-h-screen flex items-center justify-center p-4" style={{ background: '#0f172a' }}>
            {/* Background Pattern */}
            <div className="absolute inset-0 opacity-5">
                <div className="absolute inset-0" style={{
                    backgroundImage: `radial-gradient(circle at 25% 25%, #3b82f6 0%, transparent 50%), 
                           radial-gradient(circle at 75% 75%, #10b981 0%, transparent 50%)`
                }}></div>
            </div>

            <div className="w-full max-w-md relative z-10">
                {/* Main Form Container */}
                <div className="rounded-2xl shadow-2xl overflow-hidden" style={{ background: '#0f172a' }}>
                    {/* Header Section */}
                    <div className="px-8 pt-8 pb-6 text-center">
                        <div className="flex items-center justify-center mb-4">
                            <div className="w-16 h-16 rounded-full flex items-center justify-center text-2xl"
                                style={{ background: 'linear-gradient(135deg, #3b82f6 0%, #10b981 100%)' }}>
                                üçΩ
                            </div>
                        </div>
                        <h1 className="text-2xl font-bold text-white mb-2">DSCE E-Campus</h1>
                        <p className="text-slate-400 text-sm">Food Court Admin Portal</p>
                    </div>

                    {/* Message Display */}
                    {message.text && (
                        <div className={`mx-8 mb-6 p-4 rounded-lg flex items-center gap-3 ${message.type === 'success'
                            ? 'bg-green-500/20 border border-green-500/30 text-green-400'
                            : 'bg-red-500/20 border border-red-500/30 text-red-400'
                            }`}>
                            {message.type === 'success' ? <CheckCircle size={18} /> : <AlertCircle size={18} />}
                            <span className="text-sm">{message.text}</span>
                        </div>
                    )}

                    {/* Form */}
                    <form onSubmit={handleSubmit} className="px-8 pb-8">
                        <div className="space-y-6">
                            {/* Email Field */}
                            <div className="space-y-2">
                                <label className="text-sm font-medium text-slate-300 block">
                                    Admin Email
                                </label>
                                <div className="relative">
                                    <User className="absolute left-4 top-1/2 transform -translate-y-1/2 text-slate-400" size={18} />
                                    <input
                                        type="email"
                                        name="email"
                                        value={formData.email}
                                        onChange={handleInputChange}
                                        placeholder="Enter your admin email"
                                        required
                                        className="w-full pl-12 pr-4 py-4 rounded-xl text-white placeholder-slate-400 transition-all duration-300 focus:ring-2 focus:ring-blue-500/50 focus:outline-none"
                                        style={{
                                            background: '#1e293b',
                                            border: '1px solid #334155'
                                        }}
                                    />
                                </div>
                            </div>

                            {/* Password Field */}
                            <div className="space-y-2">
                                <label className="text-sm font-medium text-slate-300 block">
                                    Password
                                </label>
                                <div className="relative">
                                    <Lock className="absolute left-4 top-1/2 transform -translate-y-1/2 text-slate-400" size={18} />
                                    <input
                                        type={showPassword ? 'text' : 'password'}
                                        name="password"
                                        value={formData.password}
                                        onChange={handleInputChange}
                                        placeholder="Enter your password"
                                        required
                                        className="w-full pl-12 pr-12 py-4 rounded-xl text-white placeholder-slate-400 transition-all duration-300 focus:ring-2 focus:ring-blue-500/50 focus:outline-none"
                                        style={{
                                            background: '#1e293b',
                                            border: '1px solid #334155'
                                        }}
                                    />
                                    <button
                                        type="button"
                                        onClick={() => setShowPassword(!showPassword)}
                                        className="absolute right-4 top-1/2 transform -translate-y-1/2 text-slate-400 hover:text-slate-300 transition-colors"
                                    >
                                        {showPassword ? <EyeOff size={18} /> : <Eye size={18} />}
                                    </button>
                                </div>
                            </div>

                            {/* Submit Button */}
                            <button
                                type="submit"
                                disabled={isLoading}
                                className="w-full py-4 rounded-xl text-white font-semibold text-lg transition-all duration-300 hover:shadow-lg hover:scale-[1.02] active:scale-[0.98] disabled:opacity-70 disabled:cursor-not-allowed"
                                style={{
                                    background: 'linear-gradient(135deg, #3b82f6 0%, #10b981 100%)'
                                }}
                            >
                                {isLoading ? (
                                    <div className="flex items-center justify-center gap-2">
                                        <div className="w-5 h-5 border-2 border-white/30 border-t-white rounded-full animate-spin"></div>
                                        Signing In...
                                    </div>
                                ) : (
                                    'Sign In to Food Court Admin'
                                )}
                            </button>

                            {/* Forgot Password */}
                            <div className="text-center">
                                <button
                                    type="button"
                                    onClick={handleForgotPassword}
                                    className="text-sm text-slate-400 hover:text-blue-400 transition-colors"
                                >
                                    Forgot your password?
                                </button>
                            </div>

                            {/* Demo Credentials */}
                            <div className="p-4 rounded-lg border border-slate-700/50" style={{ background: '#1e293b/50' }}>
                                <p className="text-xs text-slate-400 mb-2">Demo Credentials:</p>
                                <p className="text-xs text-slate-300">üìß admin@dsce.edu</p>
                                <p className="text-xs text-slate-300">üîí admin123</p>
                            </div>
                        </div>
                    </form>
                </div>

                {/* Footer */}
                <div className="text-center mt-6">
                    <p className="text-xs text-slate-500">
                        ¬© 2024 DSCE E-Campus. All rights reserved.
                    </p>
                </div>
            </div>
        </div>
    );
}
