import React, { useState, useEffect, useRef } from 'react';

const ContactAdminForm = () => {
    const [formData, setFormData] = useState({
        firstName: '',
        lastName: '',
        email: '',
        department: '',
        queryType: '',
        priority: 'low',
        subject: '',
        message: ''
    });

    const [charCount, setCharCount] = useState(0);
    const [isLoading, setIsLoading] = useState(false);
    const [errors, setErrors] = useState({});
    const [isVisible, setIsVisible] = useState(false);
    const messageRef = useRef(null);

    useEffect(() => {
        const timer = setTimeout(() => {
            setIsVisible(true);
        }, 100);

        return () => clearTimeout(timer);
    }, []);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));

        if (errors[name]) {
            setErrors(prev => ({
                ...prev,
                [name]: false
            }));
        }

        if (name === 'message') {
            setCharCount(value.length);
            if (messageRef.current) {
                messageRef.current.style.height = 'auto';
                messageRef.current.style.height = Math.min(messageRef.current.scrollHeight, 200) + 'px';
            }
        }
    };

    const validateForm = () => {
        const newErrors = {};
        const requiredFields = ['firstName', 'lastName', 'email', 'queryType', 'subject', 'message'];

        requiredFields.forEach(field => {
            if (!formData[field].trim()) {
                newErrors[field] = true;
            }
        });

        if (formData.email && !/\S+@\S+\.\S+/.test(formData.email)) {
            newErrors.email = true;
        }

        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };

    const handleSubmit = () => {
        if (!validateForm()) {
            alert('Please fill in all required fields correctly.');
            return;
        }

        setIsLoading(true);

        setTimeout(() => {
            setIsLoading(false);
            alert('Query submitted successfully! Our admin team will review your request and respond within 24-48 hours.');

            setFormData({
                firstName: '',
                lastName: '',
                email: '',
                department: '',
                queryType: '',
                priority: 'low',
                subject: '',
                message: ''
            });
            setCharCount(0);
            setErrors({});

            if (messageRef.current) {
                messageRef.current.style.height = 'auto';
            }
        }, 2000);
    };

    const getCharCountColor = () => {
        if (charCount > 900) return '#ff6b6b';
        if (charCount > 750) return '#ffa500';
        return '#888888';
    };

    // STYLES consistent with previous theme
    const containerStyle = {
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
        background: 'linear-gradient(135deg, #0f0f0f 0%, #1a1a1a 50%, #000000 100%)',
        minHeight: '100vh',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        padding: '20px',
        margin: 0,
        transition: 'opacity 0.6s ease, transform 0.6s ease',
        opacity: isVisible ? 1 : 0,
        transform: isVisible ? 'translateY(0)' : 'translateY(30px)',
    };

    const formContainerStyle = {
        background: 'rgba(25, 25, 25, 0.95)',
        backdropFilter: 'blur(10px)',
        border: '1px solid rgba(255, 255, 255, 0.1)',
        borderRadius: '20px',
        boxShadow: '0 20px 40px rgba(0, 0, 0, 0.5)',
        padding: '40px 30px',
        width: '100%',
        maxWidth: '520px',
        pointerEvents: isLoading ? 'none' : 'auto',
        opacity: isLoading ? 0.7 : 1,
    };

    const inputStyle = {
        width: '100%',
        padding: '15px 20px',
        border: '2px solid #333333',
        borderRadius: '12px',
        fontSize: '1rem',
        background: '#1a1a1a',
        color: '#eee',
        fontFamily: 'inherit',
        outline: 'none',
        transition: 'border-color 0.3s ease, box-shadow 0.3s ease',
    };

    const inputFocusStyle = {
        borderColor: '#667eea',
        boxShadow: '0 0 8px 0 #667eea',
    };

    const errorStyle = {
        borderColor: '#ff6b6b',
    };

    const labelStyle = {
        display: 'block',
        color: '#dddddd',
        fontWeight: 600,
        marginBottom: '8px',
        fontSize: '0.9rem',
    };

    const textareaStyle = {
        minHeight: '120px',
        resize: 'vertical',
        ...inputStyle,
    };

    const charCountStyle = {
        textAlign: 'right',
        fontSize: '0.8rem',
        marginTop: '6px',
        color: getCharCountColor(),
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    };

    const radioContainerStyle = {
        display: 'flex',
        gap: '15px',
        flexWrap: 'wrap',
        marginTop: '8px',
    };

    const radioItemStyle = (checked) => ({
        display: 'flex',
        alignItems: 'center',
        gap: '8px',
        cursor: 'pointer',
        padding: '8px 14px',
        borderRadius: '10px',
        userSelect: 'none',
        backgroundColor: checked ? '#667eea' : 'transparent',
        color: checked ? 'white' : '#dddddd',
        fontWeight: checked ? '700' : '500',
        transition: 'background-color 0.3s',
    });

    const submitButtonStyle = {
        width: '100%',
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        color: 'white',
        border: 'none',
        padding: '15px',
        borderRadius: '12px',
        fontSize: '1rem',
        fontWeight: '600',
        cursor: isLoading ? 'not-allowed' : 'pointer',
        opacity: isLoading ? 0.7 : 1,
        transition: 'background 0.3s ease, opacity 0.3s ease',
    };

    const headerStyle = {
        textAlign: 'center',
        marginBottom: '30px',
    };

    const headerTitleStyle = {
        color: '#ffffff',
        fontSize: '2rem',
        fontWeight: '600',
        marginBottom: '8px',
    };

    const headerSubtitleStyle = {
        color: '#cccccc',
        fontSize: '0.95rem',
        lineHeight: 1.5,
    };

    const linkStyle = {
        color: '#667eea',
        textDecoration: 'none',
        fontSize: '0.9rem',
        cursor: 'pointer',
    };

    return (
        <div style={containerStyle}>
            <form style={formContainerStyle} onSubmit={e => e.preventDefault()} noValidate>
                <div style={headerStyle}>
                    <h1 style={headerTitleStyle}>Contact Admin</h1>
                    <p style={headerSubtitleStyle}>
                        Submit your query or request for account access. Our administrators will review and respond to your request.
                    </p>
                </div>

                {/* Names */}
                <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '15px', marginBottom: '20px' }}>
                    <div>
                        <label style={labelStyle}>
                            First Name <span style={{ color: '#ff6b6b' }}>*</span>
                        </label>
                        <input
                            type="text"
                            name="firstName"
                            value={formData.firstName}
                            onChange={handleInputChange}
                            placeholder="Enter your first name"
                            style={{
                                ...inputStyle,
                                ...(errors.firstName ? errorStyle : {}),
                            }}
                            disabled={isLoading}
                        />
                    </div>
                    <div>
                        <label style={labelStyle}>
                            Last Name <span style={{ color: '#ff6b6b' }}>*</span>
                        </label>
                        <input
                            type="text"
                            name="lastName"
                            value={formData.lastName}
                            onChange={handleInputChange}
                            placeholder="Enter your last name"
                            style={{
                                ...inputStyle,
                                ...(errors.lastName ? errorStyle : {}),
                            }}
                            disabled={isLoading}
                        />
                    </div>
                </div>

                {/* Email */}
                <div style={{ marginBottom: '20px' }}>
                    <label style={labelStyle}>
                        Email Address <span style={{ color: '#ff6b6b' }}>*</span>
                    </label>
                    <input
                        type="email"
                        name="email"
                        value={formData.email}
                        onChange={handleInputChange}
                        placeholder="Enter your email address"
                        style={{
                            ...inputStyle,
                            ...(errors.email ? errorStyle : {}),
                        }}
                        disabled={isLoading}
                    />
                </div>

                {/* Department */}
                <div style={{ marginBottom: '20px' }}>
                    <label style={labelStyle}>Department/Organization</label>
                    <input
                        type="text"
                        name="department"
                        value={formData.department}
                        onChange={handleInputChange}
                        placeholder="Enter your department or organization"
                        style={inputStyle}
                        disabled={isLoading}
                    />
                </div>

                {/* Query Type */}
                <div style={{ marginBottom: '20px' }}>
                    <label style={labelStyle}>
                        Query Type <span style={{ color: '#ff6b6b' }}>*</span>
                    </label>
                    <select
                        name="queryType"
                        value={formData.queryType}
                        onChange={handleInputChange}
                        style={{
                            ...inputStyle,
                            cursor: 'pointer',
                            ...(errors.queryType ? errorStyle : {}),
                        }}
                        disabled={isLoading}
                    >
                        <option value="" disabled>
                            Select query type
                        </option>
                        <option value="account-access">Account Access Request</option>
                        <option value="technical-support">Technical Support</option>
                        <option value="feature-request">Feature Request</option>
                        <option value="bug-report">Bug Report</option>
                        <option value="general-inquiry">General Inquiry</option>
                        <option value="other">Other</option>
                    </select>
                </div>

                {/* Priority */}
                <div style={{ marginBottom: '20px' }}>
                    <label style={labelStyle}>Priority Level</label>
                    <div style={radioContainerStyle}>
                        {['low', 'medium', 'high', 'urgent'].map(priority => {
                            const checked = formData.priority === priority;
                            return (
                                <label
                                    key={priority}
                                    htmlFor={priority}
                                    style={radioItemStyle(checked)}
                                >
                                    <input
                                        type="radio"
                                        id={priority}
                                        name="priority"
                                        value={priority}
                                        checked={checked}
                                        onChange={handleInputChange}
                                        style={{ cursor: 'pointer' }}
                                        disabled={isLoading}
                                    />
                                    {priority.charAt(0).toUpperCase() + priority.slice(1)}
                                </label>
                            );
                        })}
                    </div>
                </div>

                {/* Subject */}
                <div style={{ marginBottom: '20px' }}>
                    <label style={labelStyle}>
                        Subject <span style={{ color: '#ff6b6b' }}>*</span>
                    </label>
                    <input
                        type="text"
                        name="subject"
                        value={formData.subject}
                        onChange={handleInputChange}
                        placeholder="Brief subject of your query"
                        maxLength={100}
                        style={{
                            ...inputStyle,
                            ...(errors.subject ? errorStyle : {}),
                        }}
                        disabled={isLoading}
                    />
                </div>

                {/* Message */}
                <div style={{ marginBottom: '30px' }}>
                    <label style={labelStyle}>
                        Message <span style={{ color: '#ff6b6b' }}>*</span>
                    </label>
                    <textarea
                        ref={messageRef}
                        name="message"
                        value={formData.message}
                        onChange={handleInputChange}
                        placeholder="Please describe your query in detail..."
                        maxLength={1000}
                        style={{
                            ...textareaStyle,
                            ...(errors.message ? errorStyle : {}),
                        }}
                        disabled={isLoading}
                    />
                    <div style={charCountStyle}>
                        {charCount} / 1000 characters
                    </div>
                </div>

                {/* Submit Button */}
                <button
                    onClick={handleSubmit}
                    disabled={isLoading}
                    style={submitButtonStyle}
                    aria-busy={isLoading}
                >
                    {isLoading ? 'Submitting Query...' : 'Submit Query'}
                </button>

                {/* Back to Login Link */}
                <div style={{ textAlign: 'center', marginTop: '20px' }}>
                    <a
                        href="#"
                        onClick={(e) => {
                            e.preventDefault();
                            alert('Redirecting to login page... (This is a demo)');
                        }}
                        style={linkStyle}
                    >
                        ‚Üê Back to Login
                    </a>
                </div>
            </form>
        </div>
    );
};

export default ContactAdminForm;
