import React, { useState, useEffect } from 'react';

const DSCEPortal = () => {
    const [dropdownOpen, setDropdownOpen] = useState(false);
    const [isLoaded, setIsLoaded] = useState(false);

    useEffect(() => {
        const timer = setTimeout(() => {
            setIsLoaded(true);
        }, 200);
        return () => clearTimeout(timer);
    }, []);

    const handleStudentClick = () => {
        alert('Redirecting to Student Portal...');
    };

    const handleTeacherClick = () => {
        alert('Redirecting to Teacher Portal...');
    };

    const toggleAdminDropdown = () => {
        setDropdownOpen(!dropdownOpen);
    };

    const handleAdminOption = (option) => {
        const optionNames = {
            'food-cart': 'Food Cart Management',
            'canteen': 'Canteen Management',
            'stationery': 'Stationery Management',
            'department': 'Department Management'
        };

        alert(`Redirecting to ${optionNames[option]}...`);
        setDropdownOpen(false);
    };

    const handleClickOutside = (event) => {
        if (!event.target.closest('.dropdown-container')) {
            setDropdownOpen(false);
        }
    };

    useEffect(() => {
        document.addEventListener('click', handleClickOutside);
        return () => {
            document.removeEventListener('click', handleClickOutside);
        };
    }, []);

    return (
        <div className="min-h-screen relative font-sans overflow-hidden">
            {/* Background Image */}
            <div
                className="fixed inset-0 bg-cover bg-center bg-no-repeat"
                style={{
                    backgroundImage: "url('https://images.unsplash.com/photo-1562774053-701939374585?ixlib=rb-4.0.3&auto=format&fit=crop&w=1920&q=80')"
                }}
            />

            {/* Dark Overlay */}
            <div className="fixed inset-0 bg-slate-900/80 z-10" />

            {/* Portal Container */}
            <div className="relative z-20 min-h-screen flex flex-col items-center justify-center p-4 lg:p-8">

                {/* Welcome Message */}
                <div className={`w-full max-w-7xl mx-auto mb-8 lg:mb-12 transition-all duration-1000 ease-out ${isLoaded ? 'opacity-100 translate-y-0' : 'opacity-0 -translate-y-12'
                    }`}>
                    <h1 className="text-white text-3xl md:text-4xl lg:text-5xl xl:text-6xl font-bold text-center"
                        style={{ textShadow: '2px 2px 8px rgba(0, 0, 0, 0.7)' }}>
                        Welcome to DSCE Campus
                    </h1>
                </div>

                {/* Main Content Container */}
                <div className="w-full max-w-7xl mx-auto">

                    {/* Desktop Layout */}
                    <div className="hidden lg:flex items-center justify-center gap-16 xl:gap-20">

                        {/* Logo Section - Desktop */}
                        <div className={`flex-shrink-0 transition-all duration-1000 ease-out ${isLoaded ? 'opacity-100 translate-x-0' : 'opacity-0 -translate-x-24'
                            }`}>
                            <div
                                className="w-64 xl:w-80 h-64 xl:h-80 rounded-full flex items-center justify-center shadow-2xl transition-transform duration-300 ease-in-out hover:scale-105 hover:rotate-6 overflow-hidden cursor-pointer"
                                style={{
                                    background: 'linear-gradient(135deg, #3b82f6, #10b981)',
                                    boxShadow: '0 20px 40px rgba(59, 130, 246, 0.4)'
                                }}
                            >
                                <div className="text-white text-3xl xl:text-4xl font-bold text-center"
                                    style={{ textShadow: '0 2px 4px rgba(0, 0, 0, 0.3)' }}>
