import React, { useState, useEffect } from 'react';

const DSCEPortal = () => {
    const [dropdownOpen, setDropdownOpen] = useState(false);
    const [isLoaded, setIsLoaded] = useState(false);

    useEffect(() => {
        // Trigger entrance animation
        const timer = setTimeout(() => {
            setIsLoaded(true);
        }, 200);

        return () => clearTimeout(timer);
    }, []);

    const handleStudentClick = () => {
        alert('Redirecting to Student Portal...');
        console.log('Student button clicked');
    };

    const handleTeacherClick = () => {
        alert('Redirecting to Teacher Portal...');
        console.log('Teacher button clicked');
    };

    const toggleAdminDropdown = () => {
        setDropdownOpen(!dropdownOpen);
    };

    const handleAdminOption = (option) => {
        const optionNames = {
            'food-cart': 'Food Cart Management',
            'canteen': 'Canteen Management',
            'stationery': 'Stationery Management',
            'department': 'Department Management'
        };

        alert(`Redirecting to ${optionNames[option]}...`);
        console.log(`Admin ${option} selected`);

        // Close dropdown after selection
        setDropdownOpen(false);
    };

    const handleClickOutside = (event) => {
        if (!event.target.closest('.dropdown-container')) {
            setDropdownOpen(false);
        }
    };

    useEffect(() => {
        document.addEventListener('click', handleClickOutside);
        return () => {
            document.removeEventListener('click', handleClickOutside);
        };
    }, []);

    return (
        <div className="min-h-screen relative font-sans">
            {/* Background Image */}
            <div
                className="fixed inset-0 bg-cover bg-center bg-no-repeat"
                style={{
                    backgroundImage: "url('https://images.unsplash.com/photo-1562774053-701939374585?ixlib=rb-4.0.3&auto=format&fit=crop&w=1920&q=80')"
                }}
            />

            {/* Dark Overlay */}
            <div className="fixed inset-0 bg-slate-900/80 z-10" />

            {/* Portal Container */}
            <div className="relative z-20 min-h-screen flex flex-col items-center justify-center p-5 gap-8">
                {/* Welcome Message */}
                <h1 className={`text-white text-5xl font-bold text-center transition-all duration-1000 ease-out ${isLoaded ? 'opacity-100 translate-y-0' : 'opacity-0 -translate-y-12'
                    }`}
                    style={{ textShadow: '2px 2px 8px rgba(0, 0, 0, 0.7)' }}>
                    Welcome to DSCE Campus
                </h1>

                {/* Main Content */}
                <div className="flex items-center justify-between gap-20 max-w-6xl w-full">
                    {/* Logo Section */}
                    <div className={`flex items-center justify-center flex-shrink-0 transition-all duration-1000 ease-out ${isLoaded ? 'opacity-100 translate-x-0' : 'opacity-0 -translate-x-24'
                        }`}>
                        <div
                            className="w-64 h-64 rounded-full flex items-center justify-center shadow-2xl transition-transform duration-300 ease-in-out hover:scale-105 hover:rotate-6 overflow-hidden cursor-pointer"
                            style={{
                                background: 'linear-gradient(135deg, #3b82f6, #10b981)',
                                boxShadow: '0 20px 40px rgba(59, 130, 246, 0.4)'
                            }}
                        >
                            <div className="text-white text-4xl font-bold text-center"
                                style={{ textShadow: '0 2px 4px rgba(0, 0, 0, 0.3)' }}>
                                DSCE<br />LOGO
                            </div>
                        </div>
                    </div>

                    {/* Buttons Section */}
                    <div className={`flex-1 bg-slate-900/95 backdrop-blur-md rounded-3xl p-10 border border-slate-600 transition-all duration-1000 ease-out ${isLoaded ? 'opacity-100 translate-x-0' : 'opacity-0 translate-x-24'
                        }`}
                        style={{ boxShadow: '0 25px 50px rgba(0, 0, 0, 0.3)' }}>
                        <h2 className="text-white text-3xl font-semibold mb-8 text-center">
                            Portal Access System
                        </h2>

                        <div className="flex flex-col gap-5">
                            {/* Student Button */}
                            <button
                                onClick={handleStudentClick}
                                className="relative overflow-hidden p-5 text-xl font-semibold border-none rounded-2xl cursor-pointer transition-all duration-300 ease-in-out text-white uppercase tracking-wide min-h-18 flex items-center justify-center w-full group"
                                style={{
                                    background: 'linear-gradient(135deg, #3b82f6, #10b981)',
                                    boxShadow: '0 8px 20px rgba(59, 130, 246, 0.4)'
                                }}
                                onMouseEnter={(e) => {
                                    e.target.style.transform = 'translateY(-3px)';
                                    e.target.style.boxShadow = '0 12px 25px rgba(59, 130, 246, 0.6)';
                                }}
                                onMouseLeave={(e) => {
                                    e.target.style.transform = 'translateY(0)';
                                    e.target.style.boxShadow = '0 8px 20px rgba(59, 130, 246, 0.4)';
                                }}
                            >
                                <span className="absolute inset-0 bg-gradient-to-r from-transparent via-white/30 to-transparent -translate-x-full group-hover:translate-x-full transition-transform duration-500" />
                                👨‍🎓 Student Portal
                            </button>

                            {/* Teacher Button */}
                            <button
                                onClick={handleTeacherClick}
                                className="relative overflow-hidden p-5 text-xl font-semibold border-none rounded-2xl cursor-pointer transition-all duration-300 ease-in-out text-white uppercase tracking-wide min-h-18 flex items-center justify-center w-full group"
                                style={{
                                    background: 'linear-gradient(135deg, #10b981, #3b82f6)',
                                    boxShadow: '0 8px 20px rgba(16, 185, 129, 0.4)'
                                }}
                                onMouseEnter={(e) => {
                                    e.target.style.transform = 'translateY(-3px)';
                                    e.target.style.boxShadow = '0 12px 25px rgba(16, 185, 129, 0.6)';
                                }}
                                onMouseLeave={(e) => {
                                    e.target.style.transform = 'translateY(0)';
                                    e.target.style.boxShadow = '0 8px 20px rgba(16, 185, 129, 0.4)';
                                }}
                            >
                                <span className="absolute inset-0 bg-gradient-to-r from-transparent via-white/30 to-transparent -translate-x-full group-hover:translate-x-full transition-transform duration-500" />
                                👩‍🏫 Teacher Portal
                            </button>

                            {/* Admin Dropdown */}
                            <div className="dropdown-container relative w-full">
                                <button
                                    onClick={toggleAdminDropdown}
                                    className="relative overflow-hidden p-5 text-xl font-semibold border-none rounded-2xl cursor-pointer transition-all duration-300 ease-in-out text-white uppercase tracking-wide min-h-18 flex items-center justify-center w-full group"
                                    style={{
                                        background: 'linear-gradient(135deg, #3b82f6, #10b981)',
                                        boxShadow: '0 8px 20px rgba(59, 130, 246, 0.4)'
                                    }}
                                    onMouseEnter={(e) => {
                                        e.target.style.transform = 'translateY(-3px)';
                                        e.target.style.boxShadow = '0 12px 25px rgba(59, 130, 246, 0.6)';
                                    }}
                                    onMouseLeave={(e) => {
                                        e.target.style.transform = 'translateY(0)';
                                        e.target.style.boxShadow = '0 8px 20px rgba(59, 130, 246, 0.4)';
                                    }}
                                >
                                    <span className="absolute inset-0 bg-gradient-to-r from-transparent via-white/30 to-transparent -translate-x-full group-hover:translate-x-full transition-transform duration-500" />
                                    🔧 Admin Panel
                                    <span className={`ml-3 transition-transform duration-300 ${dropdownOpen ? 'rotate-180' : ''}`}>
                                        ▼
                                    </span>
                                </button>

                                {/* Dropdown Menu */}
                                <div className={`absolute top-full left-0 right-0 bg-slate-800 rounded-2xl border border-slate-600 mt-2 z-50 transition-all duration-300 ${dropdownOpen
                                        ? 'opacity-100 visible translate-y-0'
                                        : 'opacity-0 invisible -translate-y-3'
                                    }`}
                                    style={{ boxShadow: '0 15px 35px rgba(0, 0, 0, 0.3)' }}>
                                    {[
                                        { key: 'food-cart', label: '🍕 Food Cart Management' },
                                        { key: 'canteen', label: '🍽️ Canteen Management' },
                                        { key: 'stationery', label: '📚 Stationery Management' },
                                        { key: 'department', label: '🏢 Department Management' }
                                    ].map((item, index, array) => (
                                        <div
                                            key={item.key}
                                            onClick={() => handleAdminOption(item.key)}
                                            className={`px-6 py-5 text-slate-300 cursor-pointer transition-all duration-300 font-medium text-lg hover:bg-gradient-to-r hover:from-blue-500 hover:to-emerald-500 hover:text-white hover:translate-x-1 ${index === 0 ? 'rounded-t-2xl' : ''
                                                } ${index === array.length - 1 ? 'rounded-b-2xl' : 'border-b border-slate-600'
                                                }`}
                                        >
                                            {item.label}
                                        </div>
                                    ))}
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            {/* Responsive Styles */}
            <style jsx>{`
        @media (max-width: 768px) {
          .portal-container {
            flex-direction: column;
            gap: 30px;
          }
          
          .main-content {
            flex-direction: column;
            align-items: center;
            gap: 30px;
          }
          
          .logo-container {
            width: 200px;
            height: 200px;
          }
          
          .logo-text {
            font-size: 1.8rem;
          }
          
          .welcome-message {
            font-size: 2.5rem;
          }
          
          .section-title {
            font-size: 1.6rem;
          }
          
          .btn {
            min-height: 60px;
            font-size: 1.1rem;
          }
        }
        
        @media (max-width: 480px) {
          .logo-container {
            width: 150px;
            height: 150px;
          }
          
          .logo-text {
            font-size: 1.6rem;
          }
          
          .welcome-message {
            font-size: 2rem;
          }
          
          .section-title {
            font-size: 1.4rem;
          }
          
          .btn {
            padding: 16px 20px;
            font-size: 1rem;
            min-height: 55px;
          }
        }
      `}</style>
        </div>
    );
};

export default DSCEPortal;
