import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, Link } from 'react-router-dom';
import LoginComponent from './Login';
import home from './Home';
// Home Page with basic styling and navigation buttons
const Home = () => (
  <div className="min-h-screen flex flex-col justify-center items-center bg-gradient-to-r from-indigo-500 via-purple-600 to-pink-500 text-white px-4">
    <h1 className="text-5xl font-extrabold mb-6 drop-shadow-lg">Home Page</h1>
    <p className="text-lg mb-8 max-w-lg text-center drop-shadow-md">
      Welcome to the app! Use the navigation below to explore more.
    </p>
    <div className="space-x-4">
      <Link to="/login" className="bg-white bg-opacity-20 hover:bg-opacity-40 transition px-6 py-3 rounded-xl font-semibold shadow-lg">
        Login
      </Link>
      <Link to="/signup" className="bg-white bg-opacity-20 hover:bg-opacity-40 transition px-6 py-3 rounded-xl font-semibold shadow-lg">
        Sign Up
      </Link>
    </div>
  </div>
);

// SignUp Page styled similarly
const SignUp = () => (
  <div className="min-h-screen flex flex-col justify-center items-center bg-gradient-to-r from-yellow-400 via-red-500 to-pink-500 text-white px-4">
    <h1 className="text-5xl font-extrabold mb-6 drop-shadow-lg">Sign Up Page</h1>
    <p className="text-lg mb-8 max-w-lg text-center drop-shadow-md">Sign up form goes here. Build it up with your preferred UI components.</p>
    <Link to="/login" className="bg-white bg-opacity-20 hover:bg-opacity-40 transition px-6 py-3 rounded-xl font-semibold shadow-lg">
      Back to Login
    </Link>
  </div>
);

// Simple 404 with styled message and home link
const NotFound = () => (
  <div className="min-h-screen flex flex-col justify-center items-center bg-gray-900 text-red-400 px-4">
    <h2 className="text-6xl font-bold mb-4 drop-shadow-lg">404</h2>
    <p className="text-xl mb-8 drop-shadow-md">Page Not Found</p>
    <Link to="/" className="text-white underline hover:text-gray-300 transition">
      Go Home
    </Link>
  </div>
);

function App() {
  return (
    <Router>
      <Routes>
        {/* Redirect root to /login */}
        {/* Login Route */}
        <Route path="/login" element={<LoginComponent />} />

        {/* Other routes */}
        <Route path="/" element={<home />} />
        <Route path="/signup" element={<SignUp />} />

        {/* Catch-all route for 404 */}
        <Route path="*" element={<NotFound />} />
      </Routes>
    </Router>
  );
}

export default App;
