import React, { useState } from 'react';

const LoginComponent = () => {
    const [formData, setFormData] = useState({
        email: '',
        password: '',
        remember: false
    });
    const [showPassword, setShowPassword] = useState(false);
    const [isLoading, setIsLoading] = useState(false);
    const [errors, setErrors] = useState({});

    const handleInputChange = (e) => {
        const { name, value, type, checked } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: type === 'checkbox' ? checked : value
        }));

        // Clear error when user starts typing
        if (errors[name]) {
            setErrors(prev => ({ ...prev, [name]: '' }));
        }
    };

    const validateForm = () => {
        const newErrors = {};

        if (!formData.email.trim()) {
            newErrors.email = 'Email is required';
        } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
            newErrors.email = 'Email is invalid';
        }

        if (!formData.password.trim()) {
            newErrors.password = 'Password is required';
        } else if (formData.password.length < 6) {
            newErrors.password = 'Password must be at least 6 characters';
        }
        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!validateForm()) return;

        setIsLoading(true);

        // Simulate API call
        setTimeout(() => {
            alert('Login successful! (This is a demo)');
            setIsLoading(false);
        }, 2000);
    };

    const togglePassword = () => {
        setShowPassword(!showPassword);
    };

    const containerStyle = {
        minHeight: '100vh',
        backgroundColor: '#0a0a0a',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        padding: '20px',
        position: 'relative',
        overflow: 'hidden'
    };

    const navStyle = {
        position: 'absolute',
        top: 0,
        left: 0,
        right: 0,
        backgroundColor: '#1e293b',
        padding: '20px 40px',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        zIndex: 10
    };

    const formContainerStyle = {
        backgroundColor: '#0f172a',
        borderRadius: '20px',
        padding: '40px',
        width: '100%',
        maxWidth: '450px',
        boxShadow: '0 25px 50px -12px rgba(0, 0, 0, 0.5)',
        position: 'relative',
        zIndex: 1
    };

    const titleStyle = {
        fontSize: '32px',
        fontWeight: '300',
        color: '#ffffff',
        marginBottom: '8px',
        textAlign: 'center'
    };

    const subtitleStyle = {
        color: '#cbd5e1',
        fontSize: '14px',
        textAlign: 'center',
        marginBottom: '32px'
    };

    const labelStyle = {
        display: 'block',
        color: '#e2e8f0',
        marginBottom: '8px',
        fontSize: '14px',
        fontWeight: '500'
    };

    const inputStyle = {
        width: '100%',
        padding: '12px 16px',
        backgroundColor: '#1e293b',
        border: '1px solid #334155',
        borderRadius: '8px',
        color: '#ffffff',
        fontSize: '16px',
        outline: 'none',
        transition: 'all 0.3s ease'
    };

    const inputErrorStyle = {
        ...inputStyle,
        borderColor: '#ef4444'
    };

    const buttonStyle = {
        width: '100%',
        padding: '14px',
        background: 'linear-gradient(to right, #3b82f6, #10b981)',
        color: '#ffffff',
        border: 'none',
        borderRadius: '8px',
        fontSize: '16px',
        fontWeight: '600',
        cursor: isLoading ? 'not-allowed' : 'pointer',
        transition: 'all 0.3s ease',
        marginBottom: '24px',
        opacity: isLoading ? 0.7 : 1,
        position: 'relative',
        overflow: 'hidden'
    };

    const checkboxContainerStyle = {
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        marginBottom: '24px',
        fontSize: '14px'
    };

    const linkStyle = {
        color: '#3b82f6',
        textDecoration: 'none',
        transition: 'color 0.3s ease'
    };

    const dividerStyle = {
        position: 'relative',
        marginBottom: '24px'
    };

    const dividerLineStyle = {
        position: 'absolute',
        top: '50%',
        left: 0,
        right: 0,
        height: '1px',
        backgroundColor: '#334155'
    };

    const dividerTextStyle = {
        position: 'relative',
        display: 'inline-block',
        padding: '0 16px',
        backgroundColor: '#0f172a',
        color: '#64748b',
        fontSize: '14px'
    };

    return (
        <div style={containerStyle}>
            {/* Navigation Bar */}
            <div style={navStyle}>
                <div style={{ fontSize: '24px', fontWeight: '600', color: '#ffffff' }}>
                    Logo
                </div>
                <div style={{ display: 'flex', gap: '20px' }}>
                    <button
                        style={{
                            backgroundColor: 'transparent',
                            color: '#cbd5e1',
                            border: 'none',
                            padding: '8px 16px',
                            cursor: 'pointer',
                            fontSize: '14px'
                        }}
                    >
                        Home
                    </button>
                    <button
                        style={{
                            backgroundColor: '#3b82f6',
                            color: '#ffffff',
                            border: 'none',
                            padding: '8px 20px',
                            borderRadius: '6px',
                            cursor: 'pointer',
                            fontSize: '14px',
                            fontWeight: '500'
                        }}
                    >
                        Login
                    </button>
                    <button
                        style={{
                            backgroundColor: 'transparent',
                            color: '#cbd5e1',
                            border: '1px solid #334155',
                            padding: '8px 20px',
                            borderRadius: '6px',
                            cursor: 'pointer',
                            fontSize: '14px'
                        }}
                    >
                        Register
                    </button>
                </div>
            </div>

            {/* Background Pattern */}
            <div style={{
                position: 'absolute',
                inset: 0,
                opacity: 0.03,
                backgroundImage: `radial-gradient(circle at 25% 25%, #3b82f6 0%, transparent 50%),
                                  radial-gradient(circle at 75% 75%, #10b981 0%, transparent 50%)`
            }}></div>

            {/* Login Form Container */}
            <div style={formContainerStyle}>
                <h1 style={titleStyle}>Welcome Back</h1>
                <p style={subtitleStyle}>Sign in to your account</p>

                {/* Email Field */}
                <div style={{ marginBottom: '20px' }}>
                    <label htmlFor="email" style={labelStyle}>
                        Email Address
                    </label>
                    <input
                        type="email"
                        id="email"
                        name="email"
                        value={formData.email}
                        onChange={handleInputChange}
                        placeholder="Enter your email"
                        style={errors.email ? inputErrorStyle : inputStyle}
                        onFocus={(e) => {
                            e.target.style.borderColor = '#3b82f6';
                            e.target.style.backgroundColor = '#1a202c';
                        }}
                        onBlur={(e) => {
                            if (!errors.email) {
                                e.target.style.borderColor = '#334155';
                                e.target.style.backgroundColor = '#1e293b';
                            }
                        }}
                        required
                    />
                    {errors.email && <p style={{ color: '#ef4444', fontSize: '12px', marginTop: '4px' }}>{errors.email}</p>}
                </div>

                {/* Password Field */}
                <div style={{ marginBottom: '20px' }}>
                    <label htmlFor="password" style={labelStyle}>
                        Password
                    </label>
                    <div style={{ position: 'relative' }}>
                        <input
                            type={showPassword ? 'text' : 'password'}
                            id="password"
                            name="password"
                            value={formData.password}
                            onChange={handleInputChange}
                            placeholder="Enter your password"
                            style={{
                                ...(errors.password ? inputErrorStyle : inputStyle),
                                paddingRight: '60px'
                            }}
                            onFocus={(e) => {
                                e.target.style.borderColor = '#3b82f6';
                                e.target.style.backgroundColor = '#1a202c';
                            }}
                            onBlur={(e) => {
                                if (!errors.password) {
                                    e.target.style.borderColor = '#334155';
                                    e.target.style.backgroundColor = '#1e293b';
                                }
                            }}
                            required
                        />
                        <button
                            type="button"
                            onClick={togglePassword}
                            style={{
                                position: 'absolute',
                                right: '16px',
                                top: '50%',
                                transform: 'translateY(-50%)',
                                backgroundColor: 'transparent',
                                border: 'none',
                                color: '#64748b',
                                fontSize: '14px',
                                cursor: 'pointer',
                                padding: '4px 8px'
                            }}
                            onMouseEnter={(e) => e.target.style.color = '#3b82f6'}
                            onMouseLeave={(e) => e.target.style.color = '#64748b'}
                        >
                            {showPassword ? 'Hide' : 'Show'}
                        </button>
                    </div>
                    {errors.password && <p style={{ color: '#ef4444', fontSize: '12px', marginTop: '4px' }}>{errors.password}</p>}
                </div>

                {/* Remember Me & Forgot Password */}
                <div style={checkboxContainerStyle}>
                    <div style={{ display: 'flex', alignItems: 'center' }}>
                        <input
                            type="checkbox"
                            id="remember"
                            name="remember"
                            checked={formData.remember}
                            onChange={handleInputChange}
                            style={{ marginRight: '8px', cursor: 'pointer' }}
                        />
                        <label htmlFor="remember" style={{ color: '#cbd5e1', cursor: 'pointer' }}>
                            Remember me
                        </label>
                    </div>
                    <a
                        href="#"
                        style={linkStyle}
                        onMouseEnter={(e) => e.target.style.color = '#2563eb'}
                        onMouseLeave={(e) => e.target.style.color = '#3b82f6'}
                    >
                        Forgot password?
                    </a>
                </div>

                {/* Login Button */}
                <button
                    onClick={handleSubmit}
                    disabled={isLoading}
                    style={buttonStyle}
                    onMouseEnter={(e) => {
                        if (!isLoading) {
                            e.target.style.transform = 'translateY(-2px)';
                            e.target.style.boxShadow = '0 10px 20px rgba(59, 130, 246, 0.3)';
                        }
                    }}
                    onMouseLeave={(e) => {
                        e.target.style.transform = 'translateY(0)';
                        e.target.style.boxShadow = 'none';
                    }}
                >
                    {isLoading ? 'Signing In...' : 'Sign In'}
                </button>

                {/* Divider */}
                <div style={dividerStyle}>
                    <div style={dividerLineStyle}></div>
                    <div style={{ textAlign: 'center' }}>
                        <span style={dividerTextStyle}>or</span>
                    </div>
                </div>

                {/* Sign Up Link */}
                <div style={{ textAlign: 'center', fontSize: '14px', color: '#cbd5e1' }}>
                    Don't have an account?{' '}
                    <a
                        href="#"
                        style={{ ...linkStyle, fontWeight: '600' }}
                        onMouseEnter={(e) => e.target.style.color = '#2563eb'}
                        onMouseLeave={(e) => e.target.style.color = '#3b82f6'}
                    >
                        Sign up
                    </a>
                </div>
            </div>
        </div>
    );
};

export default LoginComponent;
