)

const Input = ({ className = "", type = "text", ...props }) => (
    <input
        type={type}
        className={`flex h-10 w-full rounded-md border border-input bg-background px-3 py-2 text-sm ring-offset-background file:border-0 file:bg-transparent file:text-sm file:font-medium placeholder:text-muted-foreground focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:cursor-not-allowed disabled:opacity-50 ${className}`}
        {...props}
    />
)

const Label = ({ children, className = "", htmlFor, ...props }) => (
    <label
        htmlFor={htmlFor}
        className={`text-sm font-medium leading-none peer-disabled:cursor-not-allowed peer-disabled:opacity-70 ${className}`}
        {...props}
    >
        {children}
    </label>
)

const Select = ({ children, value, onValueChange, ...props }) => {
    const [isOpen, setIsOpen] = useState(false)
    const handleSelect = (newValue) => {
        onValueChange?.(newValue)
        setIsOpen(false)
    }

    return (
        <div className="relative">
            <button
                type="button"
                onClick={() => setIsOpen(!isOpen)}
                className="flex h-10 w-full items-center justify-between rounded-md border border-slate-600 bg-slate-700 px-3 py-2 text-sm text-white ring-offset-background placeholder:text-muted-foreground focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 disabled:cursor-not-allowed disabled:opacity-50"
            >
                <span className="truncate">{value || "Select..."}</span>
                <svg className="h-4 w-4 opacity-50 flex-shrink-0 ml-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                </svg>
            </button>
            {isOpen && (
                <div className="absolute top-full z-50 w-full rounded-md border border-slate-600 bg-slate-700 p-1 text-white shadow-lg max-h-60 overflow-y-scroll scrollbar-thin scrollbar-track-slate-700 scrollbar-thumb-slate-600">
                    {typeof children === "function" ? children(handleSelect) : children}
                </div>
            )}
        </div>
    )
}

const SelectTrigger = ({ children, className = "", ...props }) => children
const SelectValue = ({ placeholder }) => placeholder
const SelectContent = ({ children, className = "", onSelect, ...props }) =>
    typeof children === "function" ? children(onSelect) : children
const SelectItem = ({ children, value, className = "", onSelect, ...props }) => (
    <div
        onClick={() => onSelect?.(value)}
        className={`relative flex w-full cursor-pointer select-none items-center rounded-sm py-2 px-3 text-sm outline-none hover:bg-slate-600 focus:bg-slate-600 transition-colors ${className}`}
        {...props}
    >
        {children}
    </div>
)

const Textarea = ({ className = "", ...props }) => (
    <textarea
        className={`flex min-h-[80px] w-full rounded-md border border-input bg-background px-3 py-2 text-sm ring-offset-background placeholder:text-muted-foreground focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:cursor-not-allowed disabled:opacity-50 resize-none ${className}`}
        {...props}
    />
)

const Tabs = ({ children, value, onValueChange, className = "", ...props }) => (
    <div className={className} {...props}>
        {children}
    </div>
)

const TabsList = ({ children, className = "", ...props }) => (
    <div
        className={`inline-flex h-auto sm:h-10 w-full sm:w-auto items-center justify-center rounded-md bg-slate-800 p-1 text-muted-foreground border-slate-700 flex-col sm:flex-row gap-1 sm:gap-0 ${className}`}
        {...props}
    >
        {children}
    </div>
)

const TabsTrigger = ({ children, value, className = "", onClick, isActive, ...props }) => (
    <button
        type="button"
        onClick={() => onClick?.(value)}
        className={`inline-flex items-center justify-center whitespace-nowrap rounded-sm px-3 py-2 sm:py-1.5 text-sm font-medium ring-offset-background transition-all focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:pointer-events-none disabled:opacity-50 w-full sm:w-auto ${isActive ? "bg-blue-600 text-white shadow-sm" : "text-slate-300 hover:bg-slate-700 hover:text-white"
            } ${className}`}
        {...props}
    >
        {children}
    </button>
)

const TabsContent = ({ children, value, className = "", ...props }) => (
    <div
        className={`mt-4 sm:mt-2 ring-offset-background focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 ${className}`}
        {...props}
    >
        {children}
    </div>
)

const Badge = ({ children, variant = "default", className = "", ...props }) => {
    const variants = {
        default: "bg-primary text-primary-foreground hover:bg-primary/80",
        secondary: "bg-secondary text-secondary-foreground hover:bg-secondary/80",
    }
    return (
        <div
            className={`inline-flex items-center rounded-full border px-2.5 py-0.5 text-xs font-semibold transition-colors focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2 ${variants[variant]} ${className}`}
            {...props}
        >
            {children}
        </div>
    )
}

const Alert = ({ children, className = "", ...props }) => (
    <div className={`relative w-full rounded-lg border p-4 ${className}`} {...props}>
        {children}
    </div>
)

const AlertDescription = ({ children, className = "", ...props }) => (
    <div className={`text-sm ${className}`} {...props}>
        {children}
    </div>
)

// Icons as simple SVG components
const Trash2 = ({ className = "", ...props }) => (
