)

// Main Component
export default function DepartmentDashboard() {
    const [activeTab, setActiveTab] = useState("students")
    const [users, setUsers] = useState([])
    const [loading, setLoading] = useState(false)
    const [message, setMessage] = useState({ type: "", text: "" })
    const [studentForm, setStudentForm] = useState({
        firstName: "",
        lastName: "",
        email: "",
        password: "",
        usn: "",
        department: "",
        semester: "",
        phoneNumber: "",
        address: "",
    })
    const [teacherForm, setTeacherForm] = useState({
        firstName: "",
        lastName: "",
        email: "",
        password: "",
        department: "",
        designation: "",
        specialization: "",
        phoneNumber: "",
    })
    const [adminForm, setAdminForm] = useState({
        firstName: "",
        lastName: "",
        email: "",
        password: "",
        phoneNumber: "",
        adminType: "FOOD_CART_ADMIN",
    })

    // Updated departments list
    const departments = [
        "MCA",
        "MBA",
        "BCom",
        "BCA",
        "BBA",
        "Computer Science",
        "Civil Engineering",
        "Electrical/Electronics",
        "Mechanical Engineering",
    ]

    // Updated semesters list (1-8)
    const semesters = ["1", "2", "3", "4", "5", "6", "7", "8"]

    const designations = ["Assistant Professor", "Associate Professor", "Professor", "HOD", "Principal"]

    // Updated admin types
    const adminTypes = [
        { value: "FOOD_CART_ADMIN", label: "Food Cart Admin" },
        { value: "CANTEEN_ADMIN", label: "Canteen Admin" },
        { value: "STATIONARY_ADMIN", label: "Stationary Admin" },
        { value: "DEPARTMENT_ADMIN", label: "Department Admin" },
    ]

    const showMessage = (type, text) => {
        setMessage({ type, text })
        setTimeout(() => setMessage({ type: "", text: "" }), 5000)
    }

    const generateId = (type, department) => {
        const timestamp = Date.now()
        const random = Math.floor(Math.random() * 1000)
        if (type === "TEACHER") {
            const deptCode = getDepartmentCode(department)
            return `EMP${deptCode}${random.toString().padStart(4, "0")}`
        }
        return `${timestamp}${random}`
    }

    const getDepartmentCode = (department) => {
        const codes = {
            MCA: "MCA",
            MBA: "MBA",
            BCom: "BCM",
            BCA: "BCA",
            BBA: "BBA",
            "Computer Science": "CS",
            "Civil Engineering": "CE",
            "Electrical/Electronics": "EE",
            "Mechanical Engineering": "ME",
        }
        return codes[department] || "GEN"
    }

    const handleStudentSubmit = (e) => {
        e.preventDefault()
        setLoading(true)
        setTimeout(() => {
            try {
                const newUser = {
                    id: Date.now(),
                    userId: studentForm.usn,
                    email: studentForm.email,
                    firstName: studentForm.firstName,
                    lastName: studentForm.lastName,
                    department: studentForm.department,
                    semester: studentForm.semester,
                    phoneNumber: studentForm.phoneNumber,
                    address: studentForm.address,
                    userType: "STUDENT",
                    createdAt: new Date().toISOString(),
                }
                setUsers((prev) => [...prev, newUser])
                showMessage("success", `Student created successfully! USN: ${newUser.userId}`)
                setStudentForm({
                    firstName: "",
                    lastName: "",
                    email: "",
                    password: "",
                    usn: "",
                    department: "",
                    semester: "",
                    phoneNumber: "",
                    address: "",
                })
            } catch (error) {
                showMessage("error", "Error creating student account")
            } finally {
                setLoading(false)
            }
        }, 1000)
    }

    const handleTeacherSubmit = (e) => {
        e.preventDefault()
        setLoading(true)
        setTimeout(() => {
            try {
                const newUser = {
                    id: Date.now(),
                    userId: generateId("TEACHER", teacherForm.department),
                    email: teacherForm.email,
                    firstName: teacherForm.firstName,
                    lastName: teacherForm.lastName,
                    department: teacherForm.department,
                    designation: teacherForm.designation,
                    specialization: teacherForm.specialization,
                    phoneNumber: teacherForm.phoneNumber,
                    userType: "TEACHER",
                    createdAt: new Date().toISOString(),
                }
                setUsers((prev) => [...prev, newUser])
                showMessage("success", `Teacher created successfully! Employee ID: ${newUser.userId}`)
                setTeacherForm({
                    firstName: "",
                    lastName: "",
                    email: "",
                    password: "",
                    department: "",
                    designation: "",
                    specialization: "",
                    phoneNumber: "",
                })
            } catch (error) {
                showMessage("error", "Error creating teacher account")
            } finally {
                setLoading(false)
            }
        }, 1000)
    }

    const handleAdminSubmit = (e) => {
        e.preventDefault()
        setLoading(true)
        setTimeout(() => {
            try {
                const newUser = {
                    id: Date.now(),
                    userId: adminForm.email,
                    email: adminForm.email,
                    firstName: adminForm.firstName,
                    lastName: adminForm.lastName,
                    phoneNumber: adminForm.phoneNumber,
                    userType: adminForm.adminType,
                    createdAt: new Date().toISOString(),
                }
                setUsers((prev) => [...prev, newUser])
                showMessage("success", `Admin created successfully! Email: ${newUser.email}`)
                setAdminForm({
                    firstName: "",
                    lastName: "",
                    email: "",
                    password: "",
                    phoneNumber: "",
                    adminType: "FOOD_CART_ADMIN",
                })
            } catch (error) {
                showMessage("error", "Error creating admin account")
            } finally {
                setLoading(false)
            }
        }, 1000)
    }

    const handleDeleteUser = (userId, userType) => {
        if (!window.confirm(`Are you sure you want to delete this ${userType.toLowerCase()}?`)) {
            return
        }
        setLoading(true)
        setTimeout(() => {
            try {
                setUsers((prev) => prev.filter((user) => user.userId !== userId))
                showMessage("success", `${userType.toLowerCase()} deleted successfully!`)
            } catch (error) {
                showMessage("error", "Error deleting user")
            } finally {
                setLoading(false)
            }
        }, 500)
    }

    const filteredUsers = users.filter((user) => {
        switch (activeTab) {
            case "students":
                return user.userType === "STUDENT"
            case "teachers":
                return user.userType === "TEACHER"
            case "admins":
                return ["FOOD_CART_ADMIN", "CANTEEN_ADMIN", "STATIONARY_ADMIN", "DEPARTMENT_ADMIN"].includes(user.userType)
            default:
                return true
        }
    })

    const getUserIcon = (userType) => {
        switch (userType) {
            case "STUDENT":
                return <GraduationCap className="h-5 w-5 sm:h-6 sm:w-6" />
            case "TEACHER":
                return <UserCheck className="h-5 w-5 sm:h-6 sm:w-6" />
            case "FOOD_CART_ADMIN":
                return "ðŸ•"
            case "CANTEEN_ADMIN":
                return "ðŸ½"
            case "STATIONARY_ADMIN":
                return "ðŸ“š"
            case "DEPARTMENT_ADMIN":
                return "ðŸ¢"
            default:
                return <Settings className="h-5 w-5 sm:h-6 sm:w-6" />
        }
    }

    return (
        <div className="min-h-screen bg-gradient-to-br from-slate-900 via-blue-900 to-slate-900">
            {/* Header */}
            <header className="border-b border-slate-700 bg-slate-900/95 backdrop-blur-sm">
                <div className="container mx-auto px-4 py-4">
                    <div className="flex items-center justify-between">
                        <div className="flex items-center space-x-3 sm:space-x-4">
                            <div className="flex h-10 w-10 sm:h-12 sm:w-12 items-center justify-center rounded-full bg-gradient-to-r from-blue-500 to-green-500">
                                <span className="text-xs sm:text-sm font-bold text-white">DSCE</span>
                            </div>
                            <div>
                                <h1 className="text-lg sm:text-xl font-bold text-white">Department Admin Panel</h1>
                                <p className="text-xs sm:text-sm text-slate-400 hidden sm:block">Manage Users & Credentials</p>
                            </div>
                        </div>
                        <Button variant="destructive" size="sm" onClick={() => (window.location.href = "/")}>
                            <span className="hidden sm:inline">Logout</span>
                            <span className="sm:hidden">Exit</span>
                        </Button>
                    </div>
                </div>
            </header>

            <main className="container mx-auto px-4 py-4 sm:py-8">
                {/* Message Display */}
                {message.text && (
                    <Alert
                        className={`mb-4 sm:mb-6 ${message.type === "success" ? "border-green-500 bg-green-500/10" : "border-red-500 bg-red-500/10"}`}
                    >
                        <AlertDescription className={message.type === "success" ? "text-green-400" : "text-red-400"}>
                            {message.text}
                        </AlertDescription>
                    </Alert>
                )}

                <Tabs value={activeTab} onValueChange={setActiveTab} className="space-y-4 sm:space-y-6">
                    <TabsList className="grid w-full grid-cols-1 sm:grid-cols-3 bg-slate-800 border-slate-700">
                        <TabsTrigger value="students" isActive={activeTab === "students"} onClick={() => setActiveTab("students")}>
                            <GraduationCap className="mr-2 h-4 w-4" />
                            Students
                        </TabsTrigger>
                        <TabsTrigger value="teachers" isActive={activeTab === "teachers"} onClick={() => setActiveTab("teachers")}>
                            <UserCheck className="mr-2 h-4 w-4" />
                            Teachers
                        </TabsTrigger>
                        <TabsTrigger value="admins" isActive={activeTab === "admins"} onClick={() => setActiveTab("admins")}>
                            <Settings className="mr-2 h-4 w-4" />
                            Admin Users
                        </TabsTrigger>
                    </TabsList>

                    {/* Students Tab */}
                    {activeTab === "students" && (
                        <TabsContent value="students" className="space-y-4 sm:space-y-6">
                            <div className="grid gap-4 sm:gap-6 lg:grid-cols-2">
                                {/* Add Student Form */}
                                <Card className="bg-slate-800/50 border-slate-700">
                                    <CardHeader>
                                        <CardTitle className="flex items-center space-x-2 text-white">
                                            <GraduationCap className="h-4 w-4 sm:h-5 sm:w-5" />
                                            <span>Add New Student</span>
                                        </CardTitle>
                                    </CardHeader>
                                    <CardContent>
                                        <form onSubmit={handleStudentSubmit} className="space-y-4">
                                            <div className="grid gap-4 sm:grid-cols-2">
                                                <div className="space-y-2">
                                                    <Label htmlFor="firstName" className="text-slate-300">
                                                        First Name
                                                    </Label>
                                                    <Input
                                                        id="firstName"
                                                        value={studentForm.firstName}
                                                        onChange={(e) => setStudentForm((prev) => ({ ...prev, firstName: e.target.value }))}
                                                        className="bg-slate-700 border-slate-600 text-white"
                                                        placeholder="Enter first name"
                                                        required
                                                    />
                                                </div>
                                                <div className="space-y-2">
                                                    <Label htmlFor="lastName" className="text-slate-300">
                                                        Last Name
                                                    </Label>
                                                    <Input
                                                        id="lastName"
                                                        value={studentForm.lastName}
                                                        onChange={(e) => setStudentForm((prev) => ({ ...prev, lastName: e.target.value }))}
                                                        className="bg-slate-700 border-slate-600 text-white"
                                                        placeholder="Enter last name"
                                                        required
                                                    />
                                                </div>
                                            </div>
                                            <div className="grid gap-4 sm:grid-cols-2">
                                                <div className="space-y-2">
                                                    <Label htmlFor="email" className="text-slate-300">
                                                        Email Address
                                                    </Label>
                                                    <Input
                                                        id="email"
                                                        type="email"
                                                        value={studentForm.email}
                                                        onChange={(e) => setStudentForm((prev) => ({ ...prev, email: e.target.value }))}
                                                        className="bg-slate-700 border-slate-600 text-white"
                                                        placeholder="student@dsce.edu.in"
                                                        required
                                                    />
                                                </div>
                                                <div className="space-y-2">
                                                    <Label htmlFor="password" className="text-slate-300">
                                                        Password
                                                    </Label>
                                                    <Input
                                                        id="password"
                                                        type="password"
                                                        value={studentForm.password}
                                                        onChange={(e) => setStudentForm((prev) => ({ ...prev, password: e.target.value }))}
                                                        className="bg-slate-700 border-slate-600 text-white"
                                                        placeholder="Set student password"
                                                        required
                                                    />
                                                </div>
                                            </div>
                                            <div className="space-y-2">
                                                <Label htmlFor="usn" className="text-slate-300">
                                                    USN (University Seat Number)
                                                </Label>
                                                <Input
                                                    id="usn"
                                                    value={studentForm.usn}
                                                    onChange={(e) => setStudentForm((prev) => ({ ...prev, usn: e.target.value.toUpperCase() }))}
                                                    className="bg-slate-700 border-slate-600 text-white"
                                                    placeholder="e.g., 1DS21CS001"
                                                    required
                                                />
                                            </div>
                                            <div className="grid gap-4 sm:grid-cols-2">
                                                <div className="space-y-2">
                                                    <Label className="text-slate-300">Department</Label>
                                                    <Select
                                                        value={studentForm.department}
                                                        onValueChange={(value) => setStudentForm((prev) => ({ ...prev, department: value }))}
                                                    >
                                                        {(onSelect) =>
                                                            departments.map((dept) => (
                                                                <SelectItem
                                                                    key={dept}
                                                                    value={dept}
                                                                    className="text-white hover:bg-slate-600"
                                                                    onSelect={onSelect}
                                                                >
                                                                    {dept}
                                                                </SelectItem>
                                                            ))
                                                        }
                                                    </Select>
                                                </div>
                                                <div className="space-y-2">
                                                    <Label className="text-slate-300">Semester</Label>
                                                    <Select
                                                        value={studentForm.semester}
                                                        onValueChange={(value) => setStudentForm((prev) => ({ ...prev, semester: value }))}
                                                    >
                                                        {(onSelect) =>
                                                            semesters.map((sem) => (
                                                                <SelectItem
                                                                    key={sem}
                                                                    value={sem}
                                                                    className="text-white hover:bg-slate-600"
                                                                    onSelect={onSelect}
                                                                >
                                                                    Semester {sem}
                                                                </SelectItem>
                                                            ))
                                                        }
                                                    </Select>
                                                </div>
                                            </div>
                                            <div className="grid gap-4 sm:grid-cols-2">
                                                <div className="space-y-2">
                                                    <Label htmlFor="phone" className="text-slate-300">
                                                        Phone Number
                                                    </Label>
                                                    <Input
                                                        id="phone"
                                                        type="tel"
                                                        value={studentForm.phoneNumber}
                                                        onChange={(e) => setStudentForm((prev) => ({ ...prev, phoneNumber: e.target.value }))}
                                                        className="bg-slate-700 border-slate-600 text-white"
                                                        placeholder="9876543210"
                                                    />
                                                </div>
                                                <div className="space-y-2">
                                                    <Label htmlFor="address" className="text-slate-300">
                                                        Address
                                                    </Label>
                                                    <Textarea
                                                        id="address"
                                                        value={studentForm.address}
                                                        onChange={(e) => setStudentForm((prev) => ({ ...prev, address: e.target.value }))}
                                                        className="bg-slate-700 border-slate-600 text-white"
                                                        placeholder="Enter student address"
                                                        rows={3}
                                                    />
                                                </div>
                                            </div>
                                            <Button
                                                type="submit"
                                                className="w-full bg-gradient-to-r from-blue-500 to-green-500 hover:from-blue-600 hover:to-green-600"
                                                disabled={loading}
                                            >
                                                {loading ? "Creating..." : "Create Student Account"}
                                            </Button>
                                        </form>
                                    </CardContent>
                                </Card>

                                {/* Students List */}
                                <Card className="bg-slate-800/50 border-slate-700">
                                    <CardHeader>
                                        <CardTitle className="flex items-center space-x-2 text-white">
                                            <Users className="h-4 w-4 sm:h-5 sm:w-5" />
                                            <span>Students List ({filteredUsers.length})</span>
                                        </CardTitle>
                                    </CardHeader>
                                    <CardContent>
                                        <div className="space-y-4 max-h-80 sm:max-h-96 overflow-y-scroll scrollbar-thin scrollbar-track-slate-800 scrollbar-thumb-slate-600">
                                            {filteredUsers.length === 0 ? (
                                                <div className="text-center py-8 text-slate-400">
                                                    <GraduationCap className="mx-auto h-12 w-12 mb-4 opacity-50" />
                                                    <p>No students created yet</p>
                                                </div>
                                            ) : (
                                                filteredUsers.map((user) => (
                                                    <div key={user.id} className="bg-slate-700/50 rounded-lg p-3 sm:p-4 border border-slate-600">
                                                        <div className="flex items-start sm:items-center justify-between mb-3 flex-col sm:flex-row gap-2 sm:gap-0">
                                                            <div className="flex items-center space-x-3">
                                                                <div className="flex h-8 w-8 sm:h-10 sm:w-10 items-center justify-center rounded-full bg-gradient-to-r from-blue-500 to-green-500 flex-shrink-0">
                                                                    {getUserIcon(user.userType)}
                                                                </div>
                                                                <div className="min-w-0">
                                                                    <h3 className="font-semibold text-white text-sm sm:text-base truncate">{`${user.firstName} ${user.lastName}`}</h3>
                                                                    <p className="text-xs sm:text-sm text-slate-400 truncate">{user.email}</p>
                                                                </div>
                                                            </div>
                                                            <div className="flex items-center space-x-2 self-end sm:self-auto">
                                                                <Badge variant="secondary" className="bg-green-500/20 text-green-400 text-xs">
                                                                    Active
                                                                </Badge>
                                                                <Button
                                                                    variant="destructive"
                                                                    size="sm"
                                                                    onClick={() => handleDeleteUser(user.userId, user.userType)}
                                                                    disabled={loading}
                                                                >
                                                                    <Trash2 className="h-3 w-3 sm:h-4 sm:w-4" />
                                                                </Button>
                                                            </div>
                                                        </div>
                                                        <div className="grid gap-2 grid-cols-1 sm:grid-cols-2 text-xs sm:text-sm">
                                                            <div className="truncate">
                                                                <span className="text-slate-400">USN:</span>
                                                                <span className="ml-2 text-white">{user.userId}</span>
                                                            </div>
                                                            <div className="truncate">
                                                                <span className="text-slate-400">Department:</span>
                                                                <span className="ml-2 text-white">{user.department || "N/A"}</span>
                                                            </div>
                                                            <div className="truncate">
                                                                <span className="text-slate-400">Semester:</span>
                                                                <span className="ml-2 text-white">
                                                                    {user.semester ? `Semester ${user.semester}` : "N/A"}
                                                                </span>
                                                            </div>
                                                            <div className="truncate">
                                                                <span className="text-slate-400">Phone:</span>
                                                                <span className="ml-2 text-white">{user.phoneNumber || "N/A"}</span>
                                                            </div>
                                                        </div>
                                                    </div>
                                                ))
                                            )}
                                        </div>
                                    </CardContent>
                                </Card>
                            </div>
                        </TabsContent>
                    )}

                    {/* Teachers Tab */}
                    {activeTab === "teachers" && (
                        <TabsContent value="teachers" className="space-y-4 sm:space-y-6">
                            <div className="grid gap-4 sm:gap-6 lg:grid-cols-2">
                                {/* Add Teacher Form */}
                                <Card className="bg-slate-800/50 border-slate-700">
                                    <CardHeader>
                                        <CardTitle className="flex items-center space-x-2 text-white">
                                            <UserCheck className="h-4 w-4 sm:h-5 sm:w-5" />
                                            <span>Add New Teacher</span>
                                        </CardTitle>
                                    </CardHeader>
                                    <CardContent>
                                        <form onSubmit={handleTeacherSubmit} className="space-y-4">
                                            <div className="grid gap-4 sm:grid-cols-2">
                                                <div className="space-y-2">
                                                    <Label htmlFor="teacherFirstName" className="text-slate-300">
                                                        First Name
                                                    </Label>
                                                    <Input
                                                        id="teacherFirstName"
                                                        value={teacherForm.firstName}
                                                        onChange={(e) => setTeacherForm((prev) => ({ ...prev, firstName: e.target.value }))}
                                                        className="bg-slate-700 border-slate-600 text-white"
                                                        placeholder="Enter first name"
                                                        required
                                                    />
                                                </div>
                                                <div className="space-y-2">
                                                    <Label htmlFor="teacherLastName" className="text-slate-300">
                                                        Last Name
                                                    </Label>
                                                    <Input
                                                        id="teacherLastName"
                                                        value={teacherForm.lastName}
                                                        onChange={(e) => setTeacherForm((prev) => ({ ...prev, lastName: e.target.value }))}
                                                        className="bg-slate-700 border-slate-600 text-white"
                                                        placeholder="Enter last name"
                                                        required
                                                    />
                                                </div>
                                            </div>
                                            <div className="grid gap-4 sm:grid-cols-2">
                                                <div className="space-y-2">
                                                    <Label htmlFor="teacherEmail" className="text-slate-300">
                                                        Email Address
                                                    </Label>
                                                    <Input
                                                        id="teacherEmail"
                                                        type="email"
                                                        value={teacherForm.email}
                                                        onChange={(e) => setTeacherForm((prev) => ({ ...prev, email: e.target.value }))}
                                                        className="bg-slate-700 border-slate-600 text-white"
                                                        placeholder="teacher@dsce.edu.in"
                                                        required
                                                    />
                                                </div>
                                                <div className="space-y-2">
                                                    <Label htmlFor="teacherPassword" className="text-slate-300">
                                                        Password
                                                    </Label>
                                                    <Input
                                                        id="teacherPassword"
                                                        type="password"
                                                        value={teacherForm.password}
                                                        onChange={(e) => setTeacherForm((prev) => ({ ...prev, password: e.target.value }))}
                                                        className="bg-slate-700 border-slate-600 text-white"
                                                        placeholder="Set teacher password"
                                                        required
                                                    />
                                                </div>
                                            </div>
                                            <div className="grid gap-4 sm:grid-cols-2">
                                                <div className="space-y-2">
                                                    <Label className="text-slate-300">Department</Label>
                                                    <Select
                                                        value={teacherForm.department}
                                                        onValueChange={(value) => setTeacherForm((prev) => ({ ...prev, department: value }))}
                                                    >
                                                        {(onSelect) =>
                                                            departments.map((dept) => (
                                                                <SelectItem
                                                                    key={dept}
                                                                    value={dept}
                                                                    className="text-white hover:bg-slate-600"
                                                                    onSelect={onSelect}
                                                                >
                                                                    {dept}
                                                                </SelectItem>
                                                            ))
                                                        }
                                                    </Select>
                                                </div>
                                                <div className="space-y-2">
                                                    <Label className="text-slate-300">Designation</Label>
                                                    <Select
                                                        value={teacherForm.designation}
                                                        onValueChange={(value) => setTeacherForm((prev) => ({ ...prev, designation: value }))}
                                                    >
                                                        {(onSelect) =>
                                                            designations.map((designation) => (
                                                                <SelectItem
                                                                    key={designation}
                                                                    value={designation}
                                                                    className="text-white hover:bg-slate-600"
                                                                    onSelect={onSelect}
                                                                >
                                                                    {designation}
                                                                </SelectItem>
                                                            ))
                                                        }
                                                    </Select>
                                                </div>
                                            </div>
                                            <div className="grid gap-4 sm:grid-cols-2">
                                                <div className="space-y-2">
                                                    <Label htmlFor="specialization" className="text-slate-300">
                                                        Specialization
                                                    </Label>
                                                    <Input
                                                        id="specialization"
                                                        value={teacherForm.specialization}
                                                        onChange={(e) => setTeacherForm((prev) => ({ ...prev, specialization: e.target.value }))}
                                                        className="bg-slate-700 border-slate-600 text-white"
                                                        placeholder="e.g., Data Structures, Machine Learning"
                                                    />
                                                </div>
                                                <div className="space-y-2">
                                                    <Label htmlFor="teacherPhone" className="text-slate-300">
                                                        Phone Number
                                                    </Label>
                                                    <Input
                                                        id="teacherPhone"
                                                        type="tel"
                                                        value={teacherForm.phoneNumber}
                                                        onChange={(e) => setTeacherForm((prev) => ({ ...prev, phoneNumber: e.target.value }))}
                                                        className="bg-slate-700 border-slate-600 text-white"
                                                        placeholder="9876543210"
                                                    />
                                                </div>
                                            </div>
                                            <Button
                                                type="submit"
                                                className="w-full bg-gradient-to-r from-blue-500 to-green-500 hover:from-blue-600 hover:to-green-600"
                                                disabled={loading}
                                            >
                                                {loading ? "Creating..." : "Create Teacher Account"}
                                            </Button>
                                        </form>
                                    </CardContent>
                                </Card>

                                {/* Teachers List */}
                                <Card className="bg-slate-800/50 border-slate-700">
                                    <CardHeader>
                                        <CardTitle className="flex items-center space-x-2 text-white">
                                            <Users className="h-4 w-4 sm:h-5 sm:w-5" />
                                            <span>Teachers List ({filteredUsers.length})</span>
                                        </CardTitle>
                                    </CardHeader>
                                    <CardContent>
                                        <div className="space-y-4 max-h-80 sm:max-h-96 overflow-y-scroll scrollbar-thin scrollbar-track-slate-800 scrollbar-thumb-slate-600">
                                            {filteredUsers.length === 0 ? (
                                                <div className="text-center py-8 text-slate-400">
                                                    <UserCheck className="mx-auto h-12 w-12 mb-4 opacity-50" />
                                                    <p>No teachers created yet</p>
                                                </div>
                                            ) : (
                                                filteredUsers.map((user) => (
                                                    <div key={user.id} className="bg-slate-700/50 rounded-lg p-3 sm:p-4 border border-slate-600">
                                                        <div className="flex items-start sm:items-center justify-between mb-3 flex-col sm:flex-row gap-2 sm:gap-0">
                                                            <div className="flex items-center space-x-3">
                                                                <div className="flex h-8 w-8 sm:h-10 sm:w-10 items-center justify-center rounded-full bg-gradient-to-r from-blue-500 to-green-500 flex-shrink-0">
                                                                    {getUserIcon(user.userType)}
                                                                </div>
                                                                <div className="min-w-0">
                                                                    <h3 className="font-semibold text-white text-sm sm:text-base truncate">{`${user.firstName} ${user.lastName}`}</h3>
                                                                    <p className="text-xs sm:text-sm text-slate-400 truncate">{user.email}</p>
                                                                </div>
                                                            </div>
                                                            <div className="flex items-center space-x-2 self-end sm:self-auto">
                                                                <Badge variant="secondary" className="bg-green-500/20 text-green-400 text-xs">
                                                                    Active
                                                                </Badge>
                                                                <Button
                                                                    variant="destructive"
                                                                    size="sm"
                                                                    onClick={() => handleDeleteUser(user.userId, user.userType)}
                                                                    disabled={loading}
                                                                >
                                                                    <Trash2 className="h-3 w-3 sm:h-4 sm:w-4" />
                                                                </Button>
                                                            </div>
                                                        </div>
                                                        <div className="grid gap-2 grid-cols-1 sm:grid-cols-2 text-xs sm:text-sm">
                                                            <div className="truncate">
                                                                <span className="text-slate-400">Employee ID:</span>
                                                                <span className="ml-2 text-white">{user.userId}</span>
                                                            </div>
                                                            <div className="truncate">
                                                                <span className="text-slate-400">Department:</span>
                                                                <span className="ml-2 text-white">{user.department || "N/A"}</span>
                                                            </div>
                                                            <div className="truncate">
                                                                <span className="text-slate-400">Designation:</span>
                                                                <span className="ml-2 text-white">{user.designation || "N/A"}</span>
                                                            </div>
                                                            <div className="truncate">
                                                                <span className="text-slate-400">Specialization:</span>
                                                                <span className="ml-2 text-white">{user.specialization || "N/A"}</span>
                                                            </div>
                                                        </div>
                                                    </div>
                                                ))
                                            )}
                                        </div>
                                    </CardContent>
                                </Card>
                            </div>
                        </TabsContent>
                    )}

                    {/* Admin Users Tab */}
                    {activeTab === "admins" && (
                        <TabsContent value="admins" className="space-y-4 sm:space-y-6">
                            <div className="grid gap-4 sm:gap-6 lg:grid-cols-2">
                                {/* Add Admin Form */}
                                <Card className="bg-slate-800/50 border-slate-700">
                                    <CardHeader>
                                        <CardTitle className="flex items-center space-x-2 text-white">
                                            <Settings className="h-4 w-4 sm:h-5 sm:w-5" />
                                            <span>Add New Admin User</span>
                                        </CardTitle>
                                    </CardHeader>
                                    <CardContent>
                                        <form onSubmit={handleAdminSubmit} className="space-y-4">
                                            <div className="grid gap-4 sm:grid-cols-2">
                                                <div className="space-y-2">
                                                    <Label htmlFor="adminFirstName" className="text-slate-300">
                                                        First Name
                                                    </Label>
                                                    <Input
                                                        id="adminFirstName"
                                                        value={adminForm.firstName}
                                                        onChange={(e) => setAdminForm((prev) => ({ ...prev, firstName: e.target.value }))}
                                                        className="bg-slate-700 border-slate-600 text-white"
                                                        placeholder="Enter first name"
                                                        required
                                                    />
                                                </div>
                                                <div className="space-y-2">
                                                    <Label htmlFor="adminLastName" className="text-slate-300">
                                                        Last Name
                                                    </Label>
                                                    <Input
                                                        id="adminLastName"
                                                        value={adminForm.lastName}
                                                        onChange={(e) => setAdminForm((prev) => ({ ...prev, lastName: e.target.value }))}
                                                        className="bg-slate-700 border-slate-600 text-white"
                                                        placeholder="Enter last name"
                                                        required
                                                    />
                                                </div>
                                            </div>
                                            <div className="grid gap-4 sm:grid-cols-2">
                                                <div className="space-y-2">
                                                    <Label htmlFor="adminEmail" className="text-slate-300">
                                                        Email Address
                                                    </Label>
                                                    <Input
                                                        id="adminEmail"
                                                        type="email"
                                                        value={adminForm.email}
                                                        onChange={(e) => setAdminForm((prev) => ({ ...prev, email: e.target.value }))}
                                                        className="bg-slate-700 border-slate-600 text-white"
                                                        placeholder="admin@dsce.edu.in"
                                                        required
                                                    />
                                                </div>
                                                <div className="space-y-2">
                                                    <Label htmlFor="adminPassword" className="text-slate-300">
                                                        Password
                                                    </Label>
                                                    <Input
                                                        id="adminPassword"
                                                        type="password"
                                                        value={adminForm.password}
                                                        onChange={(e) => setAdminForm((prev) => ({ ...prev, password: e.target.value }))}
                                                        className="bg-slate-700 border-slate-600 text-white"
                                                        placeholder="Set admin password"
                                                        required
                                                    />
                                                </div>
                                            </div>
                                            <div className="grid gap-4 sm:grid-cols-2">
                                                <div className="space-y-2">
                                                    <Label className="text-slate-300">Admin Type</Label>
                                                    <Select
                                                        value={adminForm.adminType}
                                                        onValueChange={(value) => setAdminForm((prev) => ({ ...prev, adminType: value }))}
                                                    >
                                                        {(onSelect) =>
                                                            adminTypes.map((type) => (
                                                                <SelectItem
                                                                    key={type.value}
                                                                    value={type.value}
                                                                    className="text-white hover:bg-slate-600"
                                                                    onSelect={onSelect}
                                                                >
                                                                    {type.label}
                                                                </SelectItem>
                                                            ))
                                                        }
                                                    </Select>
                                                </div>
                                                <div className="space-y-2">
                                                    <Label htmlFor="adminPhone" className="text-slate-300">
                                                        Phone Number
                                                    </Label>
                                                    <Input
                                                        id="adminPhone"
                                                        type="tel"
                                                        value={adminForm.phoneNumber}
                                                        onChange={(e) => setAdminForm((prev) => ({ ...prev, phoneNumber: e.target.value }))}
                                                        className="bg-slate-700 border-slate-600 text-white"
                                                        placeholder="9876543210"
                                                    />
                                                </div>
                                            </div>
                                            <Button
                                                type="submit"
                                                className="w-full bg-gradient-to-r from-blue-500 to-green-500 hover:from-blue-600 hover:to-green-600"
                                                disabled={loading}
                                            >
                                                {loading ? "Creating..." : "Create Admin Account"}
                                            </Button>
                                        </form>
                                    </CardContent>
                                </Card>

                                {/* Admin Users List */}
                                <Card className="bg-slate-800/50 border-slate-700">
                                    <CardHeader>
                                        <CardTitle className="flex items-center space-x-2 text-white">
                                            <Users className="h-4 w-4 sm:h-5 sm:w-5" />
                                            <span>Admin Users List ({filteredUsers.length})</span>
                                        </CardTitle>
                                    </CardHeader>
                                    <CardContent>
                                        <div className="space-y-4 max-h-80 sm:max-h-96 overflow-y-scroll scrollbar-thin scrollbar-track-slate-800 scrollbar-thumb-slate-600">
                                            {filteredUsers.length === 0 ? (
                                                <div className="text-center py-8 text-slate-400">
                                                    <Settings className="mx-auto h-12 w-12 mb-4 opacity-50" />
                                                    <p>No admin users created yet</p>
                                                </div>
                                            ) : (
                                                filteredUsers.map((user) => (
                                                    <div key={user.id} className="bg-slate-700/50 rounded-lg p-3 sm:p-4 border border-slate-600">
                                                        <div className="flex items-start sm:items-center justify-between mb-3 flex-col sm:flex-row gap-2 sm:gap-0">
                                                            <div className="flex items-center space-x-3">
                                                                <div className="flex h-8 w-8 sm:h-10 sm:w-10 items-center justify-center rounded-full bg-gradient-to-r from-blue-500 to-green-500 text-sm sm:text-lg flex-shrink-0">
                                                                    {getUserIcon(user.userType)}
                                                                </div>
                                                                <div className="min-w-0">
                                                                    <h3 className="font-semibold text-white text-sm sm:text-base truncate">{`${user.firstName} ${user.lastName}`}</h3>
                                                                    <p className="text-xs sm:text-sm text-slate-400 truncate">{user.email}</p>
                                                                </div>
                                                            </div>
                                                            <div className="flex items-center space-x-2 self-end sm:self-auto">
                                                                <Badge variant="secondary" className="bg-green-500/20 text-green-400 text-xs">
                                                                    Active
                                                                </Badge>
                                                                <Button
                                                                    variant="destructive"
                                                                    size="sm"
                                                                    onClick={() => handleDeleteUser(user.userId, user.userType)}
                                                                    disabled={loading}
                                                                >
                                                                    <Trash2 className="h-3 w-3 sm:h-4 sm:w-4" />
                                                                </Button>
                                                            </div>
                                                        </div>
                                                        <div className="grid gap-2 grid-cols-1 sm:grid-cols-2 text-xs sm:text-sm">
                                                            <div className="truncate">
                                                                <span className="text-slate-400">Admin Type:</span>
                                                                <span className="ml-2 text-white">{user.userType.replace(/_/g, " ")}</span>
                                                            </div>
                                                            <div className="truncate">
                                                                <span className="text-slate-400">Email:</span>
                                                                <span className="ml-2 text-white">{user.email}</span>
                                                            </div>
                                                            <div className="truncate">
                                                                <span className="text-slate-400">Phone:</span>
                                                                <span className="ml-2 text-white">{user.phoneNumber || "N/A"}</span>
                                                            </div>
                                                            <div className="truncate">
                                                                <span className="text-slate-400">Status:</span>
                                                                <span className="ml-2 text-white">Active</span>
                                                            </div>
                                                        </div>
                                                    </div>
                                                ))
                                            )}
                                        </div>
                                    </CardContent>
                                </Card>
                            </div>
                        </TabsContent>
                    )}
                </Tabs>
            </main>
        </div>
    )
}
