"use client"

import { useState, useEffect, useRef } from "react"
import "./DSCEPortal.css"

const DSCEPortal = () => {
    const [dropdownOpen, setDropdownOpen] = useState(false)
    const [isLoaded, setIsLoaded] = useState(false)
    const dropdownRef = useRef(null)

    const adminOptions = [
        { key: "food-cart", label: "Food Cart Management", icon: "üçï" },
        { key: "canteen", label: "Canteen Management", icon: "üçΩÔ∏è" },
        { key: "stationery", label: "Stationery Management", icon: "üìö" },
        { key: "department", label: "Department Management", icon: "üè¢" },
    ]

    useEffect(() => {
        const timer = setTimeout(() => setIsLoaded(true), 200)
        return () => clearTimeout(timer)
    }, [])

    useEffect(() => {
        const handleClickOutside = (event) => {
            if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
                setDropdownOpen(false)
            }
        }

        document.addEventListener("mousedown", handleClickOutside)
        return () => document.removeEventListener("mousedown", handleClickOutside)
    }, [])

    const handlePortalClick = (portal) => {
        alert(`Redirecting to ${portal} Portal...`)
    }

    const handleAdminOption = (option) => {
        const optionNames = {
            "food-cart": "Food Cart Management",
            canteen: "Canteen Management",
            stationery: "Stationery Management",
            department: "Department Management",
        }
        alert(`Redirecting to ${optionNames[option]}...`)
        setDropdownOpen(false)
    }

    return (
        <div className="portal-container">
            {/* Background */}
            <div className="background-image"></div>
            <div className="overlay"></div>

            {/* Content */}
            <div className="content">
                {/* Welcome Section */}
                <div className={`welcome-section ${isLoaded ? "loaded" : ""}`}>
                    <h1 className="welcome-title">Welcome to DSCE Campus</h1>
                    <p className="welcome-subtitle">Access your portal and manage your academic journey</p>
                </div>

                {/* Main Portal Section */}
                <div className="portal-section">
                    {/* Logo */}
                    <div className={`logo-section ${isLoaded ? "loaded" : ""}`}>
                        <div className="logo-circle">
                            <div className="logo-text">
                                DSCE
                                <br />
                                LOGO
                            </div>
                        </div>
                    </div>

                    {/* Portal Buttons */}
                    <div className={`buttons-section ${isLoaded ? "loaded" : ""}`}>
                        <div className="portal-card">
                            <h2 className="card-title">Portal Access System</h2>

                            <div className="buttons-container">
                                {/* Student Portal */}
                                <button className="portal-button student-btn" onClick={() => handlePortalClick("Student")}>
                                    <span className="button-shine"></span>
                                    <div className="button-content">
                                        <span className="button-icon">üë®‚Äçüéì</span>
                                        <span>Student Portal</span>
                                    </div>
                                </button>

                                {/* Teacher Portal */}
                                <button className="portal-button teacher-btn" onClick={() => handlePortalClick("Teacher")}>
                                    <span className="button-shine"></span>
                                    <div className="button-content">
                                        <span className="button-icon">üë©‚Äçüè´</span>
                                        <span>Teacher Portal</span>
                                    </div>
                                </button>

                                {/* Admin Dropdown */}
                                <div className="dropdown-container" ref={dropdownRef}>
                                    <button className="portal-button admin-btn" onClick={() => setDropdownOpen(!dropdownOpen)}>
                                        <span className="button-shine"></span>
                                        <div className="button-content">
                                            <span className="button-icon">üîß</span>
                                            <span>Admin Panel</span>
                                            <span className={`dropdown-arrow ${dropdownOpen ? "open" : ""}`}>‚ñº</span>
                                        </div>
                                    </button>

                                    {/* Dropdown Menu */}
                                    {dropdownOpen && (
                                        <div className="dropdown-menu">
                                            {adminOptions.map((option, index) => (
                                                <div
                                                    key={option.key}
                                                    className="dropdown-item"
                                                    onClick={() => handleAdminOption(option.key)}
                                                    style={{ animationDelay: `${index * 0.1}s` }}
                                                >
                                                    <span className="dropdown-icon">{option.icon}</span>
                                                    <span>{option.label}</span>
                                                </div>
                                            ))}
                                        </div>
                                    )}
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default DSCEPortal
