// Main App Component
const FoodJuiceCartApp = () => {
    const [currentPage, setCurrentPage] = useState("menu")
    const [orderData, setOrderData] = useState(null)

    const handleSetCurrentPage = (page, data = null) => {
        setCurrentPage(page)
        if (data) {
            setOrderData(data)
        }
    }

    return (
        <CartProvider>
            {currentPage === "menu" && <FoodJuiceCartPage setCurrentPage={handleSetCurrentPage} />}
            {currentPage === "cart" && <CartPage setCurrentPage={handleSetCurrentPage} />}
            {currentPage === "payment" && <PaymentPage setCurrentPage={handleSetCurrentPage} orderData={orderData} />}
            {currentPage === "confirmation" && (
                <OrderConfirmationPage setCurrentPage={handleSetCurrentPage} orderData={orderData} />
            )}
        </CartProvider>
    )
}

export default FoodJuiceCartApp
