import React, { useState, useEffect } from "react";

const DSCEPortal = () => {
    const [isLoaded, setIsLoaded] = useState(false);
    const [backgroundImage, setBackgroundImage] = useState("");
    const [logoImage, setLogoImage] = useState("");
    const [adminDropdownOpen, setAdminDropdownOpen] = useState(false);

    // Load Images and logo (same logic as before)
    useEffect(() => {
        const loadImages = async () => {
            try {
                if (window.fs) {
                    // Background
                    try {
                        const bgImageData = await window.fs.readFile("image.jpg", {
                            encoding: "base64",
                        });
                        // Use backticks for template literals
                        setBackgroundImage(`url('data:image/jpeg;base64,${bgImageData}')`);
                    } catch (error) {
                        // fallback later
                    }
                    // Logo
                    try {
                        const logoImageData = await window.fs.readFile("logo.png", {
                            encoding: "base64",
                        });
                        setLogoImage(`data:image/png;base64,${logoImageData}`);
                    } catch (error) {
                        try {
                            const logoImageData = await window.fs.readFile("logo.jpg", {
                                encoding: "base64",
                            });
                            setLogoImage(`data:image/jpeg;base64,${logoImageData}`);
                        } catch (error2) {
                            // fallback to text
                        }
                    }
                }
                if (!backgroundImage) {
                    setBackgroundImage(
                        `url('https://images.unsplash.com/photo-1562774053-701939374585?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1486&q=80')`
                    );
                }
            } catch (error) {
                setBackgroundImage(
                    `url('https://images.unsplash.com/photo-1562774053-701939374585?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1486&q=80')`
                );
            }
            setTimeout(() => setIsLoaded(true), 100);
        };
        loadImages();
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []); // Only on mount

    // Dropdown close on outside click
    useEffect(() => {
        const handler = (e) => {
            if (!e.target.closest(".admin-dropdown")) {
                setAdminDropdownOpen(false);
            }
        };
        if (adminDropdownOpen) document.addEventListener("mousedown", handler);
        return () => document.removeEventListener("mousedown", handler);
    }, [adminDropdownOpen]);

    // Role selects
    const handleRoleSelection = (role) => {
        alert(`Redirecting to ${role.charAt(0).toUpperCase() + role.slice(1)} Portal...`);
        // navigation logic can go here
    };

    const handleAdminItemClick = (section) => {
        alert(`Redirecting to Admin - ${section}`);
        setAdminDropdownOpen(false);
        // navigation logic can go here
    };

    return (
        <div
            className="min-h-screen flex flex-col items-center justify-center relative px-2 sm:px-4"
            style={{
                background: `linear-gradient(rgba(0,0,0,0.52), rgba(0,0,0,0.32)), ${backgroundImage}`,
                backgroundSize: "cover",
                backgroundPosition: "center",
                backgroundAttachment: "fixed",
            }}
        >
            {/* Header */}
            <h1
                className="text-white text-2xl sm:text-3xl md:text-5xl lg:text-6xl font-bold text-center mb-8 md:mb-16 px-2 sm:px-4 animate-fade-in-down"
                style={{
                    textShadow: "2px 2px 4px rgba(0,0,0,0.7)",
                    letterSpacing: "2px",
                    animation: "fadeInDown 1s ease-out",
                }}
            >
                Welcome to DSCE Campus
            </h1>

            {/* Main Layout - Responsive */}
            <div
                className={`flex flex-col md:flex-row items-center justify-between w-full max-w-6xl px-2 sm:px-4 md:px-8 transition-all duration-800 ${isLoaded ? "opacity-100 translate-y-0" : "opacity-0 translate-y-12"
                    }`}
            >
                {/* Left: Logo */}
                <div className="flex items-center justify-center md:justify-start w-full md:w-1/2 mb-8 md:mb-0">
                    <div
                        className="w-28 h-28 xs:w-32 xs:h-32 sm:w-40 sm:h-40 md:w-48 md:h-48 lg:w-60 lg:h-60 rounded-full flex items-center justify-center relative overflow-hidden border-4 md:border-6 border-white border-opacity-20"
                        style={{
                            background: logoImage
                                ? "transparent"
                                : "linear-gradient(135deg, #667eea 0%, #764ba2 100%)",
                            boxShadow: "0 15px 30px rgba(0,0,0,0.3)",
                            animation: "fadeInLeft 1s ease-out",
                        }}
                    >
                        {logoImage ? (
                            <img
                                src={logoImage}
                                alt="DSCE Logo"
                                className="w-full h-full object-cover rounded-full"
                            />
                        ) : (
                            <>
                                <span className="relative z-10 text-white font-bold text-lg sm:text-2xl md:text-3xl lg:text-5xl">
                                    DSCE
                                </span>
                                {/* Shine effect over text */}
                                <div
                                    className="absolute -top-1/2 -left-1/2 w-full h-full transform rotate-45 animate-shine"
                                    style={{
                                        background:
                                            "linear-gradient(45deg, transparent, rgba(255,255,255,0.13), transparent)",
                                        width: "185%",
                                        height: "185%",
                                        animation: "shine 3s infinite",
                                    }}
                                />
                            </>
                        )}
                    </div>
                </div>

                {/* Right: Portal Selector */}
                <div className="flex items-center justify-center md:justify-end w-full md:w-1/2">
                    <div
                        className="bg-white bg-opacity-95 rounded-2xl p-4 xs:p-5 sm:p-6 md:p-8 text-center w-full max-w-xs xs:max-w-sm border border-white border-opacity-30"
                        style={{
                            boxShadow: "0 8px 24px rgba(0,0,0,0.2)",
                            backdropFilter: "blur(10px)",
                            animation: "fadeInRight 1s ease-out",
                        }}
                    >
                        <h2 className="text-gray-800 text-base xs:text-lg sm:text-xl lg:text-2xl font-semibold mb-1 xs:mb-2">
                            Portal Access
                        </h2>
                        <p className="text-gray-600 text-xs xs:text-sm md:text-base mb-5 xs:mb-6">
                            Select your role to continue
                        </p>

                        {/* Buttons - Responsive vertical */}
                        <div className="flex flex-col gap-3 items-center w-full">
                            {/* Student */}
                            <button
                                onClick={() => handleRoleSelection("student")}
                                className="w-full py-3 px-6 rounded-full text-white font-semibold text-xs xs:text-sm md:text-base transition-all duration-300 relative overflow-hidden transform hover:-translate-y-1 active:scale-95"
                                style={{
                                    background: "linear-gradient(45deg, #4facfe, #00f2fe)",
                                    boxShadow: "0 5px 12px rgba(79,172,254,0.29)",
                                }}
                                onMouseEnter={(e) => {
                                    e.currentTarget.style.boxShadow =
                                        "0 8px 18px rgba(79, 172, 254, 0.39)";
                                }}
                                onMouseLeave={(e) => {
                                    e.currentTarget.style.boxShadow =
                                        "0 5px 12px rgba(79,172,254,0.29)";
                                }}
                            >
                                <span className="mr-2 text-base">🎓</span>Student
                                <div className="absolute top-0 left-0 w-full h-full bg-gradient-to-r from-transparent via-white to-transparent opacity-20 transform -translate-x-full transition-transform duration-500 hover:translate-x-full" />
                            </button>

                            {/* Teacher */}
                            <button
                                onClick={() => handleRoleSelection("teacher")}
                                className="w-full py-3 px-6 rounded-full text-white font-semibold text-xs xs:text-sm md:text-base transition-all duration-300 relative overflow-hidden transform hover:-translate-y-1 active:scale-95"
                                style={{
                                    background: "linear-gradient(45deg, #43e97b, #38f9d7)",
                                    boxShadow: "0 5px 12px rgba(67,233,123,0.29)",
                                }}
                                onMouseEnter={(e) => {
                                    e.currentTarget.style.boxShadow =
                                        "0 8px 18px rgba(67, 233, 123, 0.39)";
                                }}
                                onMouseLeave={(e) => {
                                    e.currentTarget.style.boxShadow =
                                        "0 5px 12px rgba(67,233,123,0.29)";
                                }}
                            >
                                <span className="mr-2 text-base">👩‍🏫</span>Teacher
                                <div className="absolute top-0 left-0 w-full h-full bg-gradient-to-r from-transparent via-white to-transparent opacity-20 transform -translate-x-full transition-transform duration-500 hover:translate-x-full" />
                            </button>

                            {/* Admin with Dropdown */}
                            <div className="w-full relative admin-dropdown">
                                <button
                                    onClick={() => setAdminDropdownOpen((open) => !open)}
                                    className="w-full py-3 px-6 rounded-full text-white font-semibold text-xs xs:text-sm md:text-base transition-all duration-300 relative overflow-hidden transform hover:-translate-y-1 active:scale-95 flex items-center justify-center"
                                    style={{
                                        background: "linear-gradient(45deg, #fa709a, #fee140)",
                                        boxShadow: "0 5px 12px rgba(250,112,154,0.29)",
                                    }}
                                >
                                    <span className="mr-2 text-base">⚙</span>Admin
                                    <svg
                                        className={`ml-2 w-4 h-4 transition-transform ${adminDropdownOpen ? "rotate-180" : ""
                                            }`}
                                        fill="none"
                                        stroke="currentColor"
                                        strokeWidth={2}
                                        viewBox="0 0 24 24"
                                    >
                                        <path d="M19 9l-7 7-7-7" />
                                    </svg>
                                    <div className="absolute top-0 left-0 w-full h-full bg-gradient-to-r from-transparent via-white to-transparent opacity-20 transform -translate-x-full transition-transform duration-500 hover:translate-x-full" />
                                </button>
                                {adminDropdownOpen && (
                                    <div
                                        className="absolute left-0 right-0 mt-2 z-40 bg-white rounded-lg border border-gray-200 shadow-lg w-full min-w-[150px] animate-fade-in-down"
                                        style={{
                                            fontSize: "1rem",
                                            overflow: "hidden",
                                        }}
                                    >
                                        <button
                                            className="block w-full text-left px-4 py-2 text-gray-700 hover:bg-gray-100 transition"
                                            onClick={() => handleAdminItemClick("Stationary")}
                                        >
                                            Stationary
                                        </button>
                                        <button
                                            className="block w-full text-left px-4 py-2 text-gray-700 hover:bg-gray-100 transition"
                                            onClick={() => handleAdminItemClick("Food Court")}
                                        >
                                            Food Court
                                        </button>
                                        <button
                                            className="block w-full text-left px-4 py-2 text-gray-700 hover:bg-gray-100 transition"
                                            onClick={() => handleAdminItemClick("Canteen")}
                                        >
                                            Canteen
                                        </button>
                                        <button
                                            className="block w-full text-left px-4 py-2 text-gray-700 hover:bg-gray-100 transition"
                                            onClick={() => handleAdminItemClick("Department")}
                                        >
                                            Department
                                        </button>
                                    </div>
                                )}
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            {/* Custom Animations */}
            <style jsx>{`
        @keyframes fadeInDown {
          from {
            opacity: 0;
            transform: translateY(-50px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }
        @keyframes fadeInLeft {
          from {
            opacity: 0;
            transform: translateX(-100px);
          }
          to {
            opacity: 1;
            transform: translateX(0);
          }
        }
        @keyframes fadeInRight {
          from {
            opacity: 0;
            transform: translateX(100px);
          }
          to {
            opacity: 1;
            transform: translateX(0);
          }
        }
        @keyframes shine {
          0% {
            transform: translateX(-100%) translateY(-100%) rotate(45deg);
          }
          100% {
            transform: translateX(100%) translateY(100%) rotate(45deg);
          }
        }
        /* DROPDOWN entry animation */
        .animate-fade-in-down {
          animation: fadeInDown 0.3s;
        }
      `}</style>
        </div>
    );
};

export default DSCEPortal;
