"use client"
import { useState, useRef, useEffect } from "react"

// Icons as simple SVG components
const HomeIcon = () => (
    <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
        <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth={2}
            d="M3 12l2-2m0 0l7-7 7 7M5 10v10a1 1 0 001 1h3m10-11l2 2m-2-2v10a1 1 0 01-1 1h-3m-6 0a1 1 0 001-1v-4a1 1 0 011-1h2a1 1 0 011 1v4a1 1 0 001 1m-6 0h6"
        />
    </svg>
)

const BriefcaseIcon = () => (
    <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
        <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth={2}
            d="M21 13.255A23.931 23.931 0 0112 15c-3.183 0-6.22-.62-9-1.745M16 6V4a2 2 0 00-2-2h-4a2 2 0 00-2-2v2m8 0V6a2 2 0 012 2v6a2 2 0 01-2 2H8a2 2 0 01-2-2V8a2 2 0 012-2h8z"
        />
    </svg>
)

const ShoppingCartIcon = () => (
    <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
        <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth={2}
            d="M3 3h2l.4 2M7 13h10l4-8H5.4m0 0L7 13m0 0l-2.5 5M7 13l2.5 5m6-5v5a2 2 0 11-4 0v-5m4 0V8a2 2 0 10-4 0v5z"
        />
    </svg>
)

const CoffeeIcon = () => (
    <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
        <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth={2}
            d="M12 8v13m0-13V6a2 2 0 112 2h-2zm0 0V5.5A2.5 2.5 0 109.5 8H12zm-7 4h14M5 12a2 2 0 110-4h14a2 2 0 110 4M5 12v7a2 2 0 002 2h10a2 2 0 002-2v-7"
        />
    </svg>
)

const HelpCircleIcon = () => (
    <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
        <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth={2}
            d="M8.228 9c.549-1.165 2.03-2 3.772-2 2.21 0 4 1.343 4 3 0 1.4-1.278 2.575-3.006 2.907-.542.104-.994.54-.994 1.093m0 3h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z"
        />
    </svg>
)

const MenuIcon = () => (
    <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
    </svg>
)

const BellIcon = () => (
    <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
        <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth={2}
            d="M15 17h5l-1.405-1.405A2.032 2.032 0 0118 14.158V11a6.002 6.002 0 00-4-5.659V5a2 2 0 10-4 0v.341C7.67 6.165 6 8.388 6 11v3.159c0 .538-.214 1.055-.595 1.436L4 17h5m6 0v1a3 3 0 11-6 0v-1m6 0H9"
        />
    </svg>
)

const UserIcon = () => (
    <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
        <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth={2}
            d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z"
        />
    </svg>
)

const ChevronDownIcon = () => (
    <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
    </svg>
)

// Menu items for the sidebar
const menuItems = [
    { title: "Home", url: "/", icon: HomeIcon },
    { title: "Placements", url: "/StudentPlace", icon: BriefcaseIcon },
    { title: "Food Cart", url: "/cart", icon: ShoppingCartIcon },
    { title: "Canteen", url: "/Food", icon: CoffeeIcon },
    { title: "Help", url: "/Admin", icon: HelpCircleIcon },
]

// Sample student data
const studentData = {
    name: "Alex Johnson",
    studentId: "ST2024001",
    course: "Computer Science Engineering",
    year: "3rd Year",
    avatar: "/placeholder.svg?height=40&width=40",
}

// Custom hook for handling clicks outside (for dropdowns)
const useClickOutside = (ref, handler) => {
    useEffect(() => {
        const listener = (event) => {
            if (!ref.current || ref.current.contains(event.target)) {
                return
            }
            handler(event)
        }
        document.addEventListener("mousedown", listener)
        document.addEventListener("touchstart", listener)
        return () => {
            document.removeEventListener("mousedown", listener)
            document.removeEventListener("touchstart", listener)
        }
    }, [ref, handler])
}

// Sidebar Component
const Sidebar = ({ isOpen, activeItem, onItemClick }) => {
    return (
        <div
            className={`fixed left-0 top-0 h-full bg-gradient-to-b from-blue-600 to-purple-700 text-white transition-all duration-300 z-50 shadow-xl ${isOpen ? "w-72" : "w-20"
                }`}
        >
            <div className="p-6 border-b border-white/10">
                <h2 className={`text-xl font-bold transition-opacity duration-300 ${!isOpen && "opacity-0"}`}>
                    Student Portal
                </h2>
            </div>
            <nav className="p-4">
                {menuItems.map((item) => {
                    const IconComponent = item.icon
                    return (
                        <div
                            key={item.title}
                            className={`flex items-center p-3 mb-2 rounded-lg cursor-pointer transition-all duration-200 hover:bg-white/10 hover:translate-x-1 ${activeItem === item.title ? "bg-white/20 font-semibold" : "text-white/80"
                                }`}
                            onClick={() => onItemClick(item.title)}
                        >
                            <IconComponent />
                            <span className={`ml-3 transition-opacity duration-300 ${!isOpen && "opacity-0"}`}>{item.title}</span>
                        </div>
                    )
                })}
            </nav>
        </div>
    )
}

// Dropdown Component
const Dropdown = ({ trigger, children, isOpen, onToggle }) => {
    const dropdownRef = useRef(null)
    useClickOutside(dropdownRef, () => {
        if (isOpen) onToggle()
    })

    return (
        <div className="relative" ref={dropdownRef}>
            <div onClick={onToggle}>{trigger}</div>
            {isOpen && (
                <div className="absolute right-0 top-full mt-2 w-64 bg-white border border-gray-200 rounded-lg shadow-xl z-50 overflow-hidden">
                    {children}
                </div>
            )}
        </div>
    )
}

// Header Component
const Header = ({ onSidebarToggle, sidebarOpen }) => {
    const [notificationsOpen, setNotificationsOpen] = useState(false)
    const [profileOpen, setProfileOpen] = useState(false)

    const notificationsTrigger = (
        <button className="relative p-2 text-gray-600 hover:text-gray-900 hover:bg-gray-100 rounded-lg transition-colors">
            <BellIcon />
            <span className="absolute -top-1 -right-1 bg-red-500 text-white text-xs rounded-full w-5 h-5 flex items-center justify-center font-semibold">
                3
            </span>
        </button>
    )

    const profileTrigger = (
        <button className="flex items-center gap-3 p-2 text-gray-600 hover:text-gray-900 hover:bg-gray-100 rounded-lg transition-colors">
            <div className="w-8 h-8 bg-gradient-to-r from-blue-500 to-purple-600 rounded-full flex items-center justify-center text-white font-semibold text-sm">
                {studentData.name
                    .split(" ")
                    .map((n) => n[0])
                    .join("")}
            </div>
            <span className="font-semibold text-gray-900 hidden sm:block">{studentData.name}</span>
            <ChevronDownIcon />
        </button>
    )

    return (
        <header className="bg-white border-b border-gray-200 px-6 py-4 flex justify-between items-center sticky top-0 z-40 shadow-sm">
            <div className="flex items-center gap-4">
                <button
                    onClick={onSidebarToggle}
                    className="p-2 text-gray-600 hover:text-gray-900 hover:bg-gray-100 rounded-lg transition-colors"
                >
                    <MenuIcon />
                </button>
                <h1 className="text-2xl font-bold text-gray-900">Student Dashboard</h1>
            </div>
            <div className="flex items-center gap-2">
                <Dropdown
                    trigger={notificationsTrigger}
                    isOpen={notificationsOpen}
                    onToggle={() => setNotificationsOpen(!notificationsOpen)}
                >
                    <div className="p-4 bg-gray-50 border-b border-gray-200">
                        <h3 className="font-semibold text-gray-900">Notifications</h3>
                    </div>
                    <div className="p-4 hover:bg-gray-50 cursor-pointer border-b border-gray-100">
                        <div className="font-semibold text-gray-900">New placement opportunity</div>
                        <div className="text-sm text-gray-600">Software Engineer at TechCorp</div>
                    </div>
                    <div className="p-4 hover:bg-gray-50 cursor-pointer border-b border-gray-100">
                        <div className="font-semibold text-gray-900">Canteen menu updated</div>
                        <div className="text-sm text-gray-600">New items added for this week</div>
                    </div>
                    <div className="p-4 hover:bg-gray-50 cursor-pointer">
                        <div className="font-semibold text-gray-900">Assignment reminder</div>
                        <div className="text-sm text-gray-600">Data Structures assignment due tomorrow</div>
                    </div>
                </Dropdown>
                <Dropdown trigger={profileTrigger} isOpen={profileOpen} onToggle={() => setProfileOpen(!profileOpen)}>
                    <div className="p-4 bg-gray-50 border-b border-gray-200">
                        <h3 className="font-semibold text-gray-900">My Account</h3>
                    </div>
                    <div
                        className="p-4 hover:bg-gray-50 cursor-pointer flex items-center gap-2"
                        onClick={() => alert("Navigate to Student Details")}
                    >
                        <UserIcon />
                        Student Details
                    </div>
                    <div className="p-4 hover:bg-gray-50 cursor-pointer">Academic Records</div>
                    <div className="p-4 hover:bg-gray-50 cursor-pointer">Settings</div>
                    <div className="p-4 hover:bg-gray-50 cursor-pointer text-red-600">Log out</div>
                </Dropdown>
            </div>
        </header>
    )
}

// Main Content Component
const MainContent = () => {
    return (
        <main className="flex-1 p-8 bg-gray-50">
            {/* Welcome Section */}
            <div className="mb-8">
                <h2 className="text-3xl font-bold text-gray-900 mb-2">Welcome back, {studentData.name}!</h2>
                <p className="text-gray-600">
                    {studentData.course} • {studentData.year} • ID: {studentData.studentId}
                </p>
            </div>

            {/* Quick Stats Cards */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
