// Main Dashboard Component
const StudentDashboard = () => {
    const [sidebarOpen, setSidebarOpen] = useState(true)
    const [activeItem, setActiveItem] = useState("Home")
    const [isMobile, setIsMobile] = useState(false)

    useEffect(() => {
        const handleResize = () => {
            const mobile = window.innerWidth <= 768
            setIsMobile(mobile)
            if (mobile) {
                setSidebarOpen(false)
            }
        }

        handleResize()
        window.addEventListener("resize", handleResize)
        return () => window.removeEventListener("resize", handleResize)
    }, [])

    const handleItemClick = (itemTitle) => {
        setActiveItem(itemTitle)
        if (isMobile) setSidebarOpen(false)

        const selected = menuItems.find((item) => item.title === itemTitle)
        if (selected && selected.url) {
            window.location.href = selected.url
        }
    }

    return (
        <div className="flex min-h-screen bg-gray-50 font-sans">
            {isMobile && sidebarOpen && (
                <div className="fixed inset-0 bg-black bg-opacity-50 z-40" onClick={() => setSidebarOpen(false)} />
            )}
            <Sidebar isOpen={sidebarOpen} activeItem={activeItem} onItemClick={handleItemClick} />
            <div
                className={`flex-1 flex flex-col transition-all duration-300 ${sidebarOpen ? "ml-72" : "ml-20"} ${isMobile ? "ml-0" : ""}`}
            >
                <Header onSidebarToggle={() => setSidebarOpen(!sidebarOpen)} sidebarOpen={sidebarOpen} />
                <MainContent />
            </div>
        </div>
    )
}

export default StudentDashboard
