"use client"
import { useState, useEffect } from "react"

// Simple UI Components (now using Inline Styles for styling)
const Button = ({
  children,
  onClick,
  style = {}, // Added style prop
  variant = "default",
  size = "default",
  type = "button",
  disabled = false,
}) => {
  let buttonStyle = {
    display: "inline-flex",
    alignItems: "center",
    justifyContent: "center",
    borderRadius: "0.375rem", // rounded-md
    fontWeight: "500", // font-medium
    transitionProperty:
      "color, background-color, border-color, text-decoration-color, fill, stroke, opacity, box-shadow, transform, filter, backdrop-filter",
    transitionDuration: "150ms",
    transitionTimingFunction: "cubic-bezier(0.4, 0, 0.2, 1)",
    outline: "none",
    border: "none",
    cursor: "pointer",
    // Focus and disabled states cannot be directly inline, so omitted
  }
  const variants = {
    default: {
      backgroundColor: "#2563eb", // bg-blue-600
      color: "white", // text-white
    },
    destructive: {
      backgroundColor: "#dc2626", // bg-red-600
      color: "white", // text-white
    },
    ghost: {
      // Adjusted for dark background, hover states omitted
    },
  }
  const sizes = {
    default: {
      height: "2.5rem", // h-10
      paddingTop: "0.5rem", // py-2
      paddingBottom: "0.5rem", // py-2
      paddingLeft: "1rem", // px-4
      paddingRight: "1rem", // px-4
    },
    sm: {
      height: "2rem", // h-8
      paddingLeft: "0.75rem", // px-3
      paddingRight: "0.75rem", // px-3
      fontSize: "0.875rem", // text-sm
    },
    lg: {
      height: "3rem", // h-12
      paddingLeft: "2rem", // px-8
      paddingRight: "2rem", // px-8
    },
  }
  buttonStyle = { ...buttonStyle, ...variants[variant], ...sizes[size], ...style }
  if (disabled) {
    buttonStyle.opacity = "0.5"
    buttonStyle.pointerEvents = "none"
  }
  return (
    <button type={type} onClick={onClick} disabled={disabled} style={buttonStyle}>
      {children}
    </button>
  )
}

const Input = ({ style = {}, type = "text", placeholder = "", value = "", onChange, id, ...props }) => {
  const inputStyle = {
    display: "flex",
    height: "2.5rem", // h-10
    width: "100%", // w-full
    borderRadius: "0.75rem", // rounded-xl
    borderWidth: "2px", // border-2
    borderColor: "#334155", // border-[#334155]
    backgroundColor: "#1e293b", // bg-[#1e293b]
    paddingLeft: "0.75rem", // px-3
    paddingRight: "0.75rem", // px-3
    paddingTop: "0.5rem", // py-2
    paddingBottom: "0.5rem", // py-2
    fontSize: "0.875rem", // text-sm
    color: "#e2e8f0", // text-gray-200
    // Placeholder and focus styles cannot be directly inline, so omitted for full browser compatibility
    // Placeholder color applied via ::placeholder pseudo-element in actual CSS
    // Focus ring applied via :focus pseudo-element in actual CSS
    transitionProperty: "all",
    transitionDuration: "150ms",
    transitionTimingFunction: "cubic-bezier(0.4, 0, 0.2, 1)",
  }
  return (
    <input
      type={type}
      id={id}
      placeholder={placeholder}
      value={value}
      onChange={onChange}
      style={{ ...inputStyle, ...style }}
      {...props}
    />
  )
}

const Label = ({ children, htmlFor, style = {} }) => {
  const labelStyle = {
    fontSize: "0.875rem", // text-sm
    fontWeight: "500", // font-medium
    lineHeight: "normal", // leading-none
    color: "white", // text-white
    // peer-disabled styles cannot be directly inline
  }
  return (
    <label htmlFor={htmlFor} style={{ ...labelStyle, ...style }}>
      {children}
    </label>
  )
}

const Select = ({ children, value, onValueChange, style = {} }) => {
  const selectStyle = {
    display: "flex",
    height: "2.5rem", // h-10
    width: "100%", // w-full
    borderRadius: "0.75rem", // rounded-xl
    borderWidth: "2px", // border-2
    borderColor: "#334155", // border-[#334155]
    backgroundColor: "#1e293b", // bg-[#1e293b]
    paddingLeft: "0.75rem", // px-3
    paddingRight: "0.75rem", // px-3
    paddingTop: "0.5rem", // py-2
    paddingBottom: "0.5rem", // py-2
    fontSize: "0.875rem", // text-sm
    color: "#e2e8f0", // text-gray-200
    transitionProperty: "all",
    transitionDuration: "150ms",
    transitionTimingFunction: "cubic-bezier(0.4, 0, 0.2, 1)",
    appearance: "none", // appearance-none
    paddingRight: "2rem", // pr-8
    backgroundImage: `url("data:image/svg+xml;charset=UTF-8,%3Csvg%20xmlns%3D%22http%3A%2F%2Fwww.w3.org%2F2000%2Fsvg%22%20viewBox%3D%220%200%2020%2020%22%20fill%3D%22none%22%20stroke%3D%22%236b7280%22%20stroke-width%3D%221.5%22%20stroke-linecap%3D%22round%22%20stroke-linejoin%3D%22round%22%3E%3Cpolyline%20points%3D%226%209%2012%2015%2018%209%22%2F%3E%3C%2Fsvg%3E")`,
    backgroundRepeat: "no-repeat",
    backgroundPosition: "right 0.75rem center", // bg-right bg-origin-content (adjusted for padding)
    backgroundSize: "1rem", // roughly size of icon
  }
  return (
    <select value={value} onChange={(e) => onValueChange(e.target.value)} style={{ ...selectStyle, ...style }}>
      {children}
    </select>
  )
}

const SelectOption = ({ children, value }) => {
  return <option value={value}>{children}</option>
}

const Textarea = ({ style = {}, placeholder = "", value = "", onChange, rows = 3, id, ...props }) => {
  const textareaStyle = {
    display: "flex",
    width: "100%", // w-full
    borderRadius: "0.75rem", // rounded-xl
    borderWidth: "2px", // border-2
    borderColor: "#334155", // border-[#334155]
    backgroundColor: "#1e293b", // bg-[#1e293b]
    paddingLeft: "0.75rem", // px-3
    paddingRight: "0.75rem", // px-3
    paddingTop: "0.5rem", // py-2
    paddingBottom: "0.5rem", // py-2
    fontSize: "0.875rem", // text-sm
    color: "#e2e8f0", // text-gray-200
    resize: "vertical", // resize-none (changed to vertical as `none` is default and restricts resizing)
    transitionProperty: "all",
    transitionDuration: "150ms",
    transitionTimingFunction: "cubic-bezier(0.4, 0, 0.2, 1)",
    // Placeholder and focus styles cannot be directly inline
  }
  return (
    <textarea
      id={id}
      placeholder={placeholder}
      value={value}
      onChange={onChange}
      rows={rows}
      style={{ ...textareaStyle, ...style }}
      {...props}
    />
  )
}

const Table = ({ children, style = {} }) => {
  const tableWrapperStyle = {
    width: "100%",
    overflowX: "auto", // overflow-auto
    borderRadius: "0.5rem", // rounded-lg
    borderWidth: "1px", // border
    borderColor: "#374151", // border-[#374151]
  }
  const tableStyle = {
    width: "100%", // w-full
    captionSide: "bottom", // caption-bottom
    fontSize: "0.875rem", // text-sm
    backgroundColor: "#1e293b", // bg-[#1e293b]
  }
  return (
    <div style={{ ...tableWrapperStyle }}>
      <table style={{ ...tableStyle, ...style }}>{children}</table>
    </div>
  )
}

const TableHeader = ({ children }) => {
  const tableHeaderStyle = {
    borderBottomWidth: "1px", // [&_tr]:border-b
    borderBottomColor: "#374151", // border-[#374151]
  }
