export default function AdminDashboard() {
  // API Base URL - Update this to match your Tomcat deployment
  const API_BASE_URL = "http://localhost:8080/dsce-portal/api"

  // Students State
  const [students, setStudents] = useState([])
  const [studentForm, setStudentForm] = useState({
    name: "",
    usn: "",
    mobile: "",
    department: "",
    semester: "",
  })

  // Teachers State
  const [teachers, setTeachers] = useState([])
  const [teacherForm, setTeacherForm] = useState({
    name: "",
    mobile: "",
    department: "",
    subject: "",
    experience: "",
  })

  // Admin State
  const [admins, setAdmins] = useState([])
  const [adminForm, setAdminForm] = useState({
    name: "",
    phone: "",
    adminType: "",
  })

  // Placements State
  const [placements, setPlacements] = useState([])
  const [placementForm, setPlacementForm] = useState({
    companyName: "",
    position: "",
    description: "",
    requirements: "",
    salary: "",
    deadline: "",
    location: "",
  })

  const [sidebarOpen, setSidebarOpen] = useState(true)
  const [activeTab, setActiveTab] = useState("students")
  const [isLoading, setIsLoading] = useState(false)

  const departments = [
    "MCA",
    "MBA",
    "BCom",
    "BBA",
    "BCA",
    "Computer Science Engineering",
    "Mechanical Engineering",
    "Electrical Engineering",
    "Civil Engineering",
    "Electronics and Communication Engineering",
    "Information Science Engineering",
  ]

  const adminTypes = ["Food Cart Admin", "Stationery Admin", "Canteen Admin"]

  // API Helper Functions
  const apiCall = async (endpoint, method = "GET", data = null) => {
    try {
      const token = localStorage.getItem("departmentToken")
      const config = {
        method,
        headers: {
          "Content-Type": "application/json",
          ...(token && { Authorization: `Bearer ${token}` }),
        },
      }

      if (data) {
        config.body = JSON.stringify(data)
      }

      const response = await fetch(`${API_BASE_URL}${endpoint}`, config)

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`)
      }

      return await response.json()
    } catch (error) {
      console.error("API call failed:", error)
      throw error
    }
  }

  // Load data on component mount
  useEffect(() => {
    loadAllData()
  }, [])

  const loadAllData = async () => {
    setIsLoading(true)
    try {
      const [studentsData, teachersData, adminsData, placementsData] = await Promise.all([
        apiCall("/students"),
        apiCall("/teachers"),
        apiCall("/admins"),
        apiCall("/placements"),
      ])

      setStudents(studentsData || [])
      setTeachers(teachersData || [])
      setAdmins(adminsData || [])
      setPlacements(placementsData || [])
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to load data",
        variant: "destructive",
      })
    } finally {
      setIsLoading(false)
    }
  }

  // Student Functions
  const handleStudentSubmit = async (e) => {
    e.preventDefault()
    if (
      !studentForm.name ||
      !studentForm.usn ||
      !studentForm.mobile ||
      !studentForm.department ||
      !studentForm.semester
    ) {
      toast({
        title: "Error",
        description: "Please fill all student fields",
        variant: "destructive",
      })
      return
    }

    setIsLoading(true)
    try {
      const response = await apiCall("/students", "POST", studentForm)

      setStudents([...students, response.student])
      setStudentForm({
        name: "",
        usn: "",
        mobile: "",
        department: "",
        semester: "",
      })

      toast({
        title: "Success",
        description: `Student created successfully! Email: ${response.generatedEmail}, Password: ${response.generatedPassword}`,
      })
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to create student",
        variant: "destructive",
      })
    } finally {
      setIsLoading(false)
    }
  }

  const deleteStudent = async (id) => {
    setIsLoading(true)
    try {
      await apiCall(`/students/${id}`, "DELETE")
      setStudents(students.filter((student) => student.id !== id))
      toast({
        title: "Success",
        description: "Student deleted successfully",
      })
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to delete student",
        variant: "destructive",
      })
    } finally {
      setIsLoading(false)
    }
  }

  // Teacher Functions
  const handleTeacherSubmit = async (e) => {
    e.preventDefault()
    if (!teacherForm.name || !teacherForm.mobile || !teacherForm.department || !teacherForm.subject) {
      toast({
        title: "Error",
        description: "Please fill all teacher fields",
        variant: "destructive",
      })
      return
    }

    setIsLoading(true)
    try {
      const response = await apiCall("/teachers", "POST", teacherForm)

      setTeachers([...teachers, response.teacher])
      setTeacherForm({
        name: "",
        mobile: "",
        department: "",
        subject: "",
        experience: "",
      })

      toast({
        title: "Success",
        description: `Teacher created successfully! Email: ${response.generatedEmail}, Password: ${response.generatedPassword}`,
      })
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to create teacher",
        variant: "destructive",
      })
    } finally {
      setIsLoading(false)
    }
  }

  const deleteTeacher = async (id) => {
    setIsLoading(true)
    try {
      await apiCall(`/teachers/${id}`, "DELETE")
      setTeachers(teachers.filter((teacher) => teacher.id !== id))
      toast({
        title: "Success",
        description: "Teacher deleted successfully",
      })
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to delete teacher",
        variant: "destructive",
      })
    } finally {
      setIsLoading(false)
    }
  }

  // Admin Functions
  const handleAdminSubmit = async (e) => {
    e.preventDefault()
    if (!adminForm.name || !adminForm.phone || !adminForm.adminType) {
      toast({
        title: "Error",
        description: "Please fill all admin fields",
        variant: "destructive",
      })
      return
    }

    setIsLoading(true)
    try {
      const response = await apiCall("/admins", "POST", adminForm)

      setAdmins([...admins, response.admin])
      setAdminForm({
        name: "",
        phone: "",
        adminType: "",
      })

      toast({
        title: "Success",
        description: `Admin created successfully! Email: ${response.generatedEmail}, Password: ${response.generatedPassword}`,
      })
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to create admin",
        variant: "destructive",
      })
    } finally {
      setIsLoading(false)
    }
  }

  const deleteAdmin = async (id) => {
    setIsLoading(true)
    try {
      await apiCall(`/admins/${id}`, "DELETE")
      setAdmins(admins.filter((admin) => admin.id !== id))
      toast({
        title: "Success",
        description: "Admin deleted successfully",
      })
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to delete admin",
        variant: "destructive",
      })
    } finally {
      setIsLoading(false)
    }
  }

  // Placement Functions
  const handlePlacementSubmit = async (e) => {
    e.preventDefault()
    if (
      !placementForm.companyName ||
      !placementForm.position ||
      !placementForm.description ||
      !placementForm.deadline
    ) {
      toast({
        title: "Error",
        description: "Please fill all required placement fields",
        variant: "destructive",
      })
      return
    }

    setIsLoading(true)
    try {
      const response = await apiCall("/placements", "POST", placementForm)

      setPlacements([...placements, response])
      setPlacementForm({
        companyName: "",
        position: "",
        description: "",
        requirements: "",
        salary: "",
        deadline: "",
        location: "",
      })

      toast({
        title: "Success",
        description: `Placement notification sent to all ${students.length} students`,
      })
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to create placement notification",
        variant: "destructive",
      })
    } finally {
      setIsLoading(false)
    }
  }

  const deletePlacement = async (id) => {
    setIsLoading(true)
    try {
      await apiCall(`/placements/${id}`, "DELETE")
      setPlacements(placements.filter((placement) => placement.id !== id))
      toast({
        title: "Success",
        description: "Placement notification deleted",
      })
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to delete placement",
        variant: "destructive",
      })
    } finally {
      setIsLoading(false)
    }
  }

  return (
    // Main container background color
    <div style={{ minHeight: "100vh", backgroundColor: "#0f172a", color: "white" }}>
      {/* Sidebar */}
      <div
        style={{
          position: "fixed",
          left: 0,
          top: 0,
          height: "100%",
          zIndex: 30,
          transitionProperty: "width",
          transitionDuration: "300ms",
          transitionTimingFunction: "cubic-bezier(0.4, 0, 0.2, 1)",
          width: sidebarOpen ? "16rem" : "5rem", // w-64 : w-20
          background: "linear-gradient(135deg, #3b82f6 0%, #10b981 100%)",
          boxShadow: "0 10px 30px rgba(0, 0, 0, 0.3)",
        }}
      >
        <div
          style={{
            padding: "1.5rem",
            background: "rgba(255, 255, 255, 0.1)",
            backdropFilter: "blur(10px)",
            borderBottom: "1px solid rgba(255, 255, 255, 0.2)",
          }}
        >
          <h2 style={{ fontWeight: "bold", fontSize: "1.25rem", display: sidebarOpen ? "block" : "none" }}>
            Admin Portal
          </h2>
        </div>
        <nav style={{ marginTop: "1.5rem" }}>
          {[
            { id: "students", label: "Students", icon: Users },
            { id: "teachers", label: "Teachers", icon: GraduationCap },
            { id: "admins", label: "Admins", icon: Shield },
            { id: "placements", label: "Placements", icon: Briefcase },
          ].map((item) => {
            const IconComponent = item.icon
            const isActive = activeTab === item.id
            return (
              <div
                key={item.id}
                style={{
                  display: "flex",
                  alignItems: "center",
                  padding: "0.75rem",
                  cursor: "pointer",
                  color: "rgba(255, 255, 255, 0.8)",
                  transitionProperty: "all",
                  transitionDuration: "300ms",
                  transitionTimingFunction: "cubic-bezier(0.4, 0, 0.2, 1)",
                  borderRadius: "0.75rem",
                  marginLeft: "0.75rem",
                  marginRight: "0.75rem",
                  marginTop: "0.25rem",
                  marginBottom: "0.25rem",
                  backgroundColor: isActive ? "rgba(255, 255, 255, 0.2)" : "transparent",
                  color: isActive ? "white" : "rgba(255, 255, 255, 0.8)",
                  boxShadow: isActive
                    ? "0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06)"
                    : "none",
                  // Hover effects omitted
                }}
                onClick={() => setActiveTab(item.id)}
