import React, { useState, useEffect, useRef } from 'react';

const ContactAdminForm = () => {
    const [formData, setFormData] = useState({
        firstName: '',
        lastName: '',
        email: '',
        department: '',
        queryType: '',
        priority: 'low',
        subject: '',
        message: ''
    });

    const [charCount, setCharCount] = useState(0);
    const [isLoading, setIsLoading] = useState(false);
    const [errors, setErrors] = useState({});
    const [isVisible, setIsVisible] = useState(false);
    const messageRef = useRef(null);

    useEffect(() => {
        setTimeout(() => {
            setIsVisible(true);
        }, 100);
    }, []);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));

        if (errors[name]) {
            setErrors(prev => ({
                ...prev,
                [name]: false
            }));
        }

        if (name === 'message') {
            setCharCount(value.length);
            if (messageRef.current) {
                messageRef.current.style.height = 'auto';
                messageRef.current.style.height = Math.min(messageRef.current.scrollHeight, 200) + 'px';
            }
        }
    };

    const validateForm = () => {
        const newErrors = {};
        const requiredFields = ['firstName', 'lastName', 'email', 'queryType', 'subject', 'message'];

        requiredFields.forEach(field => {
            if (!formData[field].trim()) {
                newErrors[field] = true;
            }
        });

        if (formData.email && !/\S+@\S+\.\S+/.test(formData.email)) {
            newErrors.email = true;
        }

        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };

    const handleSubmit = () => {
        if (!validateForm()) {
            alert('Please fill in all required fields.');
            return;
        }

        setIsLoading(true);

        setTimeout(() => {
            setIsLoading(false);
            alert('Query submitted successfully! Our admin team will review your request and respond within 24-48 hours.');

            setFormData({
                firstName: '',
                lastName: '',
                email: '',
                department: '',
                queryType: '',
                priority: 'low',
                subject: '',
                message: ''
            });
            setCharCount(0);
            setErrors({});

            if (messageRef.current) {
                messageRef.current.style.height = 'auto';
            }
        }, 2000);
    };

    const getCharCountColor = () => {
        if (charCount > 900) return '#ff6b6b';
        if (charCount > 750) return '#ffa500';
        return '#888888';
    };

    const containerStyle = {
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
        background: 'linear-gradient(135deg, #0f0f0f 0%, #1a1a1a 50%, #000000 100%)',
        minHeight: '100vh',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        padding: '20px',
        margin: 0
    };

    const formContainerStyle = {
        background: 'rgba(25, 25, 25, 0.95)',
        backdropFilter: 'blur(10px)',
        border: '1px solid rgba(255, 255, 255, 0.1)',
        borderRadius: '20px',
        boxShadow: '0 20px 40px rgba(0, 0, 0, 0.5)',
        padding: '40px',
        width: '100%',
        maxWidth: '500px',
        transition: 'all 0.6s ease',
        opacity: isVisible ? 1 : 0,
        transform: isVisible ? 'translateY(0)' : 'translateY(30px)',
        ...(isLoading && { opacity: 0.7, pointerEvents: 'none' })
    };

    const inputStyle = {
        width: '100%',
        padding: '15px 20px',
        border: '2px solid #333333',
        borderRadius: '12px',
        fontSize: '1rem',
        transition: 'all 0.3s ease',
        background: '#1a1a1a',
        color: '#ffffff',
        fontFamily: 'inherit',
        outline: 'none'
    };

    const errorStyle = {
        borderColor: '#ff6b6b'
    };

    return (
        <div style={containerStyle}>
            <div style={formContainerStyle}>
                <div style={{ textAlign: 'center', marginBottom: '30px' }}>
                    <h1 style={{ color: '#ffffff', fontSize: '2rem', fontWeight: '600', marginBottom: '8px', margin: 0 }}>
                        Contact Admin
                    </h1>
                    <p style={{ color: '#cccccc', fontSize: '0.95rem', lineHeight: 1.5, margin: 0 }}>
                        Submit your query or request for account access. Our administrators will review and respond to your request.
                    </p>
                </div>

                <div>
                    <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '15px', marginBottom: '20px' }}>
                        <div>
                            <label style={{ display: 'block', color: '#dddddd', fontWeight: '500', marginBottom: '8px', fontSize: '0.9rem' }}>
                                First Name <span style={{ color: '#ff6b6b' }}>*</span>
                            </label>
                            <input
                                type="text"
                                name="firstName"
                                value={formData.firstName}
                                onChange={handleInputChange}
                                placeholder="Enter your first name"
                                style={{ ...inputStyle, ...(errors.firstName ? errorStyle : {}) }}
                            />
                        </div>
                        <div>
                            <label style={{ display: 'block', color: '#dddddd', fontWeight: '500', marginBottom: '8px', fontSize: '0.9rem' }}>
                                Last Name <span style={{ color: '#ff6b6b' }}>*</span>
                            </label>
                            <input
                                type="text"
                                name="lastName"
                                value={formData.lastName}
                                onChange={handleInputChange}
                                placeholder="Enter your last name"
                                style={{ ...inputStyle, ...(errors.lastName ? errorStyle : {}) }}
                            />
                        </div>
                    </div>

                    <div style={{ marginBottom: '20px' }}>
                        <label style={{ display: 'block', color: '#dddddd', fontWeight: '500', marginBottom: '8px', fontSize: '0.9rem' }}>
                            Email Address <span style={{ color: '#ff6b6b' }}>*</span>
                        </label>
                        <input
                            type="email"
                            name="email"
                            value={formData.email}
                            onChange={handleInputChange}
                            placeholder="Enter your email address"
                            style={{ ...inputStyle, ...(errors.email ? errorStyle : {}) }}
                        />
                    </div>

                    <div style={{ marginBottom: '20px' }}>
                        <label style={{ display: 'block', color: '#dddddd', fontWeight: '500', marginBottom: '8px', fontSize: '0.9rem' }}>
                            Department/Organization
                        </label>
                        <input
                            type="text"
                            name="department"
                            value={formData.department}
                            onChange={handleInputChange}
                            placeholder="Enter your department or organization"
                            style={inputStyle}
                        />
                    </div>

                    <div style={{ marginBottom: '20px' }}>
                        <label style={{ display: 'block', color: '#dddddd', fontWeight: '500', marginBottom: '8px', fontSize: '0.9rem' }}>
                            Query Type <span style={{ color: '#ff6b6b' }}>*</span>
                        </label>
                        <select
                            name="queryType"
                            value={formData.queryType}
                            onChange={handleInputChange}
                            style={{ ...inputStyle, cursor: 'pointer', ...(errors.queryType ? errorStyle : {}) }}
                        >
                            <option value="">Select query type</option>
                            <option value="account-access">Account Access Request</option>
                            <option value="technical-support">Technical Support</option>
                            <option value="feature-request">Feature Request</option>
                            <option value="bug-report">Bug Report</option>
                            <option value="general-inquiry">General Inquiry</option>
                            <option value="other">Other</option>
                        </select>
                    </div>

                    <div style={{ marginBottom: '20px' }}>
                        <label style={{ display: 'block', color: '#dddddd', fontWeight: '500', marginBottom: '8px', fontSize: '0.9rem' }}>
                            Priority Level
                        </label>
                        <div style={{ display: 'flex', gap: '15px', marginTop: '8px', flexWrap: 'wrap' }}>
                            {['low', 'medium', 'high', 'urgent'].map(priority => (
                                <div
                                    key={priority}
                                    style={{
                                        display: 'flex',
                                        alignItems: 'center',
                                        gap: '8px',
                                        cursor: 'pointer',
                                        padding: '8px 12px',
                                        borderRadius: '8px',
                                        transition: 'background-color 0.2s ease',
                                        backgroundColor: 'transparent'
                                    }}
                                    onClick={() => setFormData(prev => ({ ...prev, priority }))}
                                >
                                    <input
                                        type="radio"
                                        id={priority}
                                        name="priority"
                                        value={priority}
                                        checked={formData.priority === priority}
                                        onChange={handleInputChange}
                                        style={{ width: 'auto', margin: 0, cursor: 'pointer' }}
                                    />
                                    <label htmlFor={priority} style={{ margin: 0, cursor: 'pointer', fontSize: '0.9rem', color: '#dddddd' }}>
                                        {priority.charAt(0).toUpperCase() + priority.slice(1)}
                                    </label>
                                </div>
                            ))}
                        </div>
                    </div>

                    <div style={{ marginBottom: '20px' }}>
                        <label style={{ display: 'block', color: '#dddddd', fontWeight: '500', marginBottom: '8px', fontSize: '0.9rem' }}>
                            Subject <span style={{ color: '#ff6b6b' }}>*</span>
                        </label>
                        <input
                            type="text"
                            name="subject"
                            value={formData.subject}
                            onChange={handleInputChange}
                            placeholder="Brief subject of your query"
                            maxLength="100"
                            style={{ ...inputStyle, ...(errors.subject ? errorStyle : {}) }}
                        />
                    </div>

                    <div style={{ marginBottom: '20px' }}>
                        <label style={{ display: 'block', color: '#dddddd', fontWeight: '500', marginBottom: '8px', fontSize: '0.9rem' }}>
                            Message <span style={{ color: '#ff6b6b' }}>*</span>
                        </label>
                        <textarea
                            ref={messageRef}
                            name="message"
                            value={formData.message}
                            onChange={handleInputChange}
                            placeholder="Please describe your query in detail..."
                            maxLength="1000"
                            style={{
                                ...inputStyle,
                                minHeight: '120px',
                                resize: 'vertical',
                                ...(errors.message ? errorStyle : {})
                            }}
                        />
                        <div style={{ textAlign: 'right', fontSize: '0.8rem', marginTop: '5px', color: getCharCountColor() }}>
                            {charCount}/1000 characters
                        </div>
                    </div>

                    <button
                        onClick={handleSubmit}
                        disabled={isLoading}
                        style={{
                            width: '100%',
                            background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                            color: 'white',
                            border: 'none',
                            padding: '15px',
                            borderRadius: '12px',
                            fontSize: '1rem',
                            fontWeight: '600',
                            cursor: isLoading ? 'not-allowed' : 'pointer',
                            transition: 'all 0.3s ease',
                            marginBottom: '20px',
                            opacity: isLoading ? 0.7 : 1
                        }}
                    >
                        {isLoading ? 'Submitting Query...' : 'Submit Query'}
                    </button>
                </div>

                <div style={{ textAlign: 'center', marginTop: '20px' }}>
                    <a
                        href="#"
                        onClick={(e) => {
                            e.preventDefault();
                            alert('Redirecting to login page... (This is a demo)');
                        }}
                        style={{ color: '#667eea', textDecoration: 'none', fontSize: '0.9rem' }}
                    >
                        ‚Üê Back to Login
                    </a>
                </div>
            </div>
        </div>
    );
};

export default ContactAdminForm;
