import { useState } from 'react';
import { Home, Briefcase, ShoppingCart, Coffee, HelpCircle, User, Bell, ChevronRight } from 'lucide-react';

export default function StudentHomePage() {
    const [activeSection, setActiveSection] = useState('Home');
    const [showProfileDropdown, setShowProfileDropdown] = useState(false);
    const [showNotifications, setShowNotifications] = useState(false);
    const [notifications] = useState([
        {
            id: 1,
            type: 'job',
            title: 'New Job Opening',
            message: 'Software Developer position at TechCorp India',
            time: '2 hours ago',
            isNew: true
        },
        {
            id: 2,
            type: 'interview',
            title: 'Interview Scheduled',
            message: 'Interview for Data Analyst role at DataTech Solutions on March 15, 2024',
            time: '5 hours ago',
            isNew: true
        },
        {
            id: 3,
            type: 'job',
            title: 'Job Application Update',
            message: 'Your application for Frontend Developer at WebCorp has been shortlisted',
            time: '1 day ago',
            isNew: false
        }
    ]);

    const sidebarItems = [
        { name: 'Home', icon: Home },
        { name: 'Placements', icon: Briefcase },
        { name: 'Food Cart', icon: ShoppingCart },
        { name: 'Canteen', icon: Coffee },
        { name: 'Help', icon: HelpCircle }
    ];

    const handleSectionClick = (sectionName) => {
        setActiveSection(sectionName);
        setShowNotifications(false); // Close notifications when switching sections
    };

    const handleProfileClick = () => {
        setShowProfileDropdown(!showProfileDropdown);
        setShowNotifications(false); // Close notifications when opening profile
    };

    const navigateToProfile = () => {
        alert('Navigating to Student Details...');
        setShowProfileDropdown(false);
    };

    const handleNotificationClick = () => {
        setShowNotifications(!showNotifications);
    };

    return (
        <div className="min-h-screen bg-gray-50 flex">
            {/* Left Sidebar */}
            <div className="w-64 bg-white shadow-lg">
                <div className="p-6 border-b border-gray-200">
                    <h2 className="text-xl font-bold text-gray-800">Student Portal</h2>
                </div>
                <nav className="mt-6">
                    {sidebarItems.map((item) => {
                        const IconComponent = item.icon;
                        return (
                            <button
                                key={item.name}
                                onClick={() => handleSectionClick(item.name)}
                                className={`w-full flex items-center px-6 py-3 text-left hover:bg-blue-50 transition-colors duration-200 ${activeSection === item.name
                                    ? 'bg-blue-100 border-r-4 border-blue-500 text-blue-700'
                                    : 'text-gray-700 hover:text-blue-600'
                                    }`}
                            >
                                <IconComponent className="w-5 h-5 mr-3" />
                                <span className="font-medium">{item.name}</span>
                                <ChevronRight className="w-4 h-4 ml-auto opacity-50" />
                            </button>
                        );
                    })}
                </nav>
            </div>

            {/* Main Content Area */}
            <div className="flex-1 flex flex-col">
                {/* Header */}
                <header className="bg-white shadow-sm border-b border-gray-200 px-6 py-4">
                    <div className="flex justify-between items-center">
                        <div className="flex-1">
                            <h1 className="text-2xl font-semibold text-gray-800">
                                {activeSection}
                            </h1>
                        </div>

                        {/* Right side icons */}
                        <div className="flex items-center space-x-4">
                            {/* Notifications */}
                            <div className="relative">
                                <button
                                    onClick={handleNotificationClick}
                                    className="relative p-2 text-gray-600 hover:text-gray-800 hover:bg-gray-100 rounded-full transition-colors duration-200"
                                >
                                    <Bell className="w-6 h-6" />
                                    {notifications.filter(n => n.isNew).length > 0 && (
                                        <span className="absolute -top-1 -right-1 bg-red-500 text-white text-xs rounded-full w-5 h-5 flex items-center justify-center">
                                            {notifications.filter(n => n.isNew).length}
                                        </span>
                                    )}
                                </button>

                                {/* Notifications Dropdown */}
                                {showNotifications && (
                                    <div className="absolute right-0 mt-2 w-80 bg-white rounded-lg shadow-lg border border-gray-200 py-2 z-20 max-h-96 overflow-y-auto">
                                        <div className="px-4 py-2 border-b border-gray-200">
                                            <h3 className="text-sm font-semibold text-gray-800">Notifications from Admin</h3>
                                        </div>

                                        {notifications.map((notification) => (
                                            <div key={notification.id} className={`px-4 py-3 hover:bg-gray-50 transition-colors duration-200 border-b border-gray-100 last:border-b-0 ${notification.isNew ? 'bg-blue-50' : ''}`}>
                                                <div className="flex items-start space-x-3">
                                                    <div className={`p-2 rounded-full ${notification.type === 'job' ? 'bg-green-100' : 'bg-blue-100'}`}>
                                                        {notification.type === 'job' ? (
                                                            <Briefcase className={`w-4 h-4 ${notification.type === 'job' ? 'text-green-600' : 'text-blue-600'}`} />
                                                        ) : (
                                                            <User className="w-4 h-4 text-blue-600" />
                                                        )}
                                                    </div>
                                                    <div className="flex-1 min-w-0">
                                                        <div className="flex items-center justify-between">
                                                            <p className="text-sm font-medium text-gray-800 truncate">
                                                                {notification.title}
                                                            </p>
                                                            {notification.isNew && (
                                                                <span className="w-2 h-2 bg-red-500 rounded-full flex-shrink-0"></span>
                                                            )}
                                                        </div>
                                                        <p className="text-sm text-gray-600 mt-1 line-clamp-2">
                                                            {notification.message}
                                                        </p>
                                                        <p className="text-xs text-gray-500 mt-1">
                                                            {notification.time}
                                                        </p>
                                                    </div>
                                                </div>
                                            </div>
                                        ))}

                                        <div className="px-4 py-2 border-t border-gray-200">
                                            <button className="text-sm text-blue-600 hover:text-blue-800 font-medium">
                                                View All Notifications
                                            </button>
                                        </div>
                                    </div>
                                )}
                            </div>

                            {/* Profile Dropdown */}
                            <div className="relative">
                                <button
                                    onClick={handleProfileClick}
                                    className="flex items-center space-x-3 p-2 text-gray-600 hover:text-gray-800 hover:bg-gray-100 rounded-lg transition-colors duration-200"
                                >
                                    {/* Student Photo */}
                                    <div className="w-8 h-8 bg-gradient-to-br from-blue-500 to-purple-600 rounded-full flex items-center justify-center text-white text-sm font-semibold">
                                        JD
                                    </div>
                                    <span className="text-sm font-medium">My Profile</span>
                                </button>

                                {showProfileDropdown && (
                                    <div className="absolute right-0 mt-2 w-48 bg-white rounded-lg shadow-lg border border-gray-200 py-2 z-10">
                                        <button
                                            onClick={navigateToProfile}
                                            className="w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 transition-colors duration-200"
                                        >
                                            Student Details
                                        </button>
                                        <button
                                            onClick={() => setShowProfileDropdown(false)}
                                            className="w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 transition-colors duration-200"
                                        >
                                            Settings
                                        </button>
                                        <hr className="my-1" />
                                        <button
                                            onClick={() => setShowProfileDropdown(false)}
                                            className="w-full text-left px-4 py-2 text-sm text-red-600 hover:bg-red-50 transition-colors duration-200"
                                        >
                                            Logout
                                        </button>
                                    </div>
                                )}
                            </div>
                        </div>
                    </div>
                </header>

                {/* Main Content */}
                <main className="flex-1">
                    {activeSection === 'Home' && (
                        <div className="h-full flex items-center justify-center">
                            {/* Full Page Welcome Section */}
                            <div className="bg-gradient-to-br from-blue-600 via-purple-600 to-indigo-700 w-full h-full flex items-center justify-center text-white relative overflow-hidden">
                                {/* Background decoration */}
                                <div className="absolute inset-0 opacity-10">
                                    <div className="absolute top-10 left-10 w-32 h-32 bg-white rounded-full"></div>
                                    <div className="absolute bottom-20 right-20 w-24 h-24 bg-white rounded-full"></div>
                                    <div className="absolute top-1/2 right-10 w-16 h-16 bg-white rounded-full"></div>
                                </div>

                                <div className="text-center z-10">
                                    <h1 className="text-6xl md:text-7xl font-bold mb-6 animate-fade-in">Welcome Back!</h1>
                                    <p className="text-3xl md:text-4xl opacity-90 mb-4">John Doe</p>
                                    <p className="text-xl text-blue-100 mb-8">Student ID: 2024001</p>
                                    <div className="text-lg text-blue-200">
                                        <p>Today's Date: {new Date().toLocaleDateString()}</p>
                                    </div>
                                </div>
                            </div>
                        </div>
                    )}

                    {activeSection !== 'Home' && (
                        <div className="text-center py-12">
                            <h2 className="text-2xl font-semibold text-gray-800 mb-4">{activeSection}</h2>
                            <p className="text-gray-600">This section is under development. Content for {activeSection} will be displayed here.</p>
                        </div>
                    )}
                </main>
            </div>
        </div>
    );
}
