"use client";

import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { GraduationCap, Users, Settings, ChevronDown } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card } from "@/components/ui/card";
import {
    DropdownMenu,
    DropdownMenuContent,
    DropdownMenuItem,
    DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";

interface AdminOption {
    key: string;
    label: string;
    icon: string;
}

const adminOptions: AdminOption[] = [
    { key: "food-cart", label: "Food Cart Management", icon: "🍕" },
    { key: "canteen", label: "Canteen Management", icon: "🍽️" },
    { key: "stationery", label: "Stationery Management", icon: "📚" },
    { key: "department", label: "Department Management", icon: "🏢" },
];

const DSCEPortal = () => {
    const [isLoaded, setIsLoaded] = useState(false);

    useEffect(() => {
        const timer = setTimeout(() => setIsLoaded(true), 200);
        return () => clearTimeout(timer);
    }, []);

    const handlePortalAccess = (portal: string) => {
        // Replace with actual navigation logic
        console.log(`Navigating to ${portal} portal...`);
        // Example: router.push(`/${portal}`)
    };

    const handleAdminOption = (option: string) => {
        const optionNames: Record<string, string> = {
            "food-cart": "Food Cart Management",
            canteen: "Canteen Management",
            stationery: "Stationery Management",
            department: "Department Management",
        };
        console.log(`Navigating to ${optionNames[option]}...`);
        // Example: router.push(`/admin/${option}`)
    };

    return (
        <div className="min-h-screen relative overflow-hidden">
            {/* Background with overlay */}
            <div
                className="fixed inset-0 bg-cover bg-center bg-no-repeat"
                style={{
                    backgroundImage: "url('/placeholder.svg?height=1080&width=1920')",
                }}
            />
            <div className="fixed inset-0 bg-gradient-to-br from-slate-900/90 via-slate-800/85 to-slate-900/90" />

            {/* Main content */}
            <div className="relative z-10 min-h-screen flex flex-col items-center justify-center p-4">
                <motion.div
                    initial={{ opacity: 0, y: -50 }}
                    animate={{ opacity: isLoaded ? 1 : 0, y: isLoaded ? 0 : -50 }}
                    transition={{ duration: 0.8, ease: "easeOut" }}
                    className="text-center mb-12"
                >
                    <h1 className="text-4xl md:text-5xl lg:text-6xl font-bold text-white mb-4 bg-gradient-to-r from-blue-400 to-emerald-400 bg-clip-text text-transparent">
                        Welcome to DSCE Campus
                    </h1>
                    <p className="text-slate-300 text-lg md:text-xl max-w-2xl mx-auto">
                        Access your portal and manage your academic journey
                    </p>
                </motion.div>

                <div className="w-full max-w-6xl mx-auto">
                    {/* Desktop Layout */}
                    <div className="hidden lg:flex items-center justify-center gap-16">
                        <LogoSection isLoaded={isLoaded} />
                        <PortalButtons
                            isLoaded={isLoaded}
                            onPortalAccess={handlePortalAccess}
                            onAdminOption={handleAdminOption}
                        />
                    </div>

                    {/* Mobile Layout */}
                    <div className="lg:hidden flex flex-col items-center gap-8">
                        <LogoSection isLoaded={isLoaded} mobile />
                        <PortalButtons
                            isLoaded={isLoaded}
                            onPortalAccess={handlePortalAccess}
                            onAdminOption={handleAdminOption}
                            mobile
                        />
                    </div>
                </div>
            </div>
        </div>
    );
};

interface LogoSectionProps {
    isLoaded: boolean;
    mobile?: boolean;
}

const LogoSection = ({ isLoaded, mobile = false }: LogoSectionProps) => {
    const size = mobile ? "w-40 h-40 md:w-48 md:h-48" : "w-64 xl:w-80 h-64 xl:h-80";
    const textSize = mobile ? "text-xl md:text-2xl" : "text-3xl xl:text-4xl";

    return (
        <motion.div
            initial={{ opacity: 0, x: mobile ? 0 : -100, y: mobile ? -50 : 0 }}
            animate={{
                opacity: isLoaded ? 1 : 0,
                x: isLoaded ? 0 : mobile ? 0 : -100,
                y: isLoaded ? 0 : mobile ? -50 : 0,
            }}
            transition={{ duration: 1, ease: "easeOut", delay: 0.2 }}
            className="flex-shrink-0"
        >
            <motion.div
                whileHover={{ scale: 1.05, rotate: 3 }}
                whileTap={{ scale: 0.95 }}
                className={`${size} rounded-full flex items-center justify-center shadow-2xl cursor-pointer bg-gradient-to-br from-blue-500 via-blue-600 to-emerald-500 hover:from-blue-400 hover:to-emerald-400 transition-all duration-300`}
            >
                <div className={`text-white ${textSize} font-bold text-center drop-shadow-lg`}>
                    DSCE
                    <br />
                    LOGO
                </div>
            </motion.div>
        </motion.div>
    );
};

interface PortalButtonsProps {
    isLoaded: boolean;
    onPortalAccess: (portal: string) => void;
    onAdminOption: (option: string) => void;
    mobile?: boolean;
}

const PortalButtons = ({
    isLoaded,
    onPortalAccess,
    onAdminOption,
    mobile = false,
}: PortalButtonsProps) => {
    const containerClass = mobile ? "w-full max-w-md mx-auto" : "flex-1 max-w-2xl";
    const padding = mobile ? "p-6 md:p-8" : "p-8 xl:p-10";

    return (
        <motion.div
            initial={{ opacity: 0, x: mobile ? 0 : 100, y: mobile ? 50 : 0 }}
            animate={{
                opacity: isLoaded ? 1 : 0,
                x: isLoaded ? 0 : mobile ? 0 : 100,
                y: isLoaded ? 0 : mobile ? 50 : 0,
            }}
            transition={{ duration: 1, ease: "easeOut", delay: 0.4 }}
            className={containerClass}
        >
            <Card className={`bg-slate-900/95 backdrop-blur-md border-slate-600 ${padding} shadow-2xl`}>
                <h2
                    className={`text-white font-semibold mb-8 text-center ${mobile ? "text-xl md:text-2xl" : "text-2xl xl:text-3xl"
                        }`}
                >
                    Portal Access System
                </h2>

                <div className="space-y-4">
                    <PortalButton
                        icon={<GraduationCap className="w-6 h-6" />}
                        label="Student Portal"
                        onClick={() => onPortalAccess("student")}
                        gradient="from-blue-500 to-emerald-500"
                        delay={0.6}
                    />

                    <PortalButton
                        icon={<Users className="w-6 h-6" />}
                        label="Teacher Portal"
                        onClick={() => onPortalAccess("teacher")}
                        gradient="from-emerald-500 to-blue-500"
                        delay={0.7}
                    />

                    <AdminDropdown onAdminOption={onAdminOption} delay={0.8} />
                </div>
            </Card>
        </motion.div>
    );
};

interface PortalButtonProps {
