    label: string;
    onClick: () => void;
    gradient: string;
    delay: number;
}

const PortalButton = ({ icon, label, onClick, gradient, delay }: PortalButtonProps) => {
    return (
        <motion.div initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }} transition={{ duration: 0.6, delay }}>
            <Button
                onClick={onClick}
                className={`w-full h-16 text-lg font-semibold bg-gradient-to-r ${gradient} hover:shadow-lg hover:shadow-blue-500/25 transition-all duration-300 group relative overflow-hidden`}
                size="lg"
            >
                <motion.div className="absolute inset-0 bg-gradient-to-r from-transparent via-white/20 to-transparent -translate-x-full group-hover:translate-x-full transition-transform duration-700" />
                <div className="flex items-center gap-3 relative z-10">
                    {icon}
                    <span className="uppercase tracking-wide">{label}</span>
                </div>
            </Button>
        </motion.div>
    );
};

interface AdminDropdownProps {
