    delay: number;
}

const AdminDropdown = ({ onAdminOption, delay }: AdminDropdownProps) => {
    return (
        <motion.div initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }} transition={{ duration: 0.6, delay }}>
            <DropdownMenu>
                <DropdownMenuTrigger asChild>
                    <Button
                        className="w-full h-16 text-lg font-semibold bg-gradient-to-r from-blue-500 to-emerald-500 hover:shadow-lg hover:shadow-blue-500/25 transition-all duration-300 group relative overflow-hidden"
                        size="lg"
                    >
                        <motion.div className="absolute inset-0 bg-gradient-to-r from-transparent via-white/20 to-transparent -translate-x-full group-hover:translate-x-full transition-transform duration-700" />
                        <div className="flex items-center gap-3 relative z-10">
                            <Settings className="w-6 h-6" />
                            <span className="uppercase tracking-wide">Admin Panel</span>
                            <ChevronDown className="w-4 h-4 ml-auto transition-transform group-data-[state=open]:rotate-180" />
                        </div>
                    </Button>
                </DropdownMenuTrigger>

                <DropdownMenuContent className="w-full min-w-[300px] bg-slate-800 border-slate-600 shadow-xl" align="center">
                    <AnimatePresence>
