                            <motion.div
                                key={option.key}
                                initial={{ opacity: 0, x: -20 }}
                                animate={{ opacity: 1, x: 0 }}
                                transition={{ duration: 0.3, delay: index * 0.1 }}
                            >
                                <DropdownMenuItem
                                    onClick={() => onAdminOption(option.key)}
                                    className="flex items-center gap-3 p-4 text-slate-300 hover:text-white hover:bg-gradient-to-r hover:from-blue-500/20 hover:to-emerald-500/20 cursor-pointer transition-all duration-200"
