import React, { useState, useEffect, useRef } from 'react';

const DSCEPortal = () => {
    const [dropdownOpen, setDropdownOpen] = useState(false);
    const [isLoaded, setIsLoaded] = useState(false);
    const adminButtonRef = useRef(null);
    const dropdownRef = useRef(null);

    useEffect(() => {
        const timer = setTimeout(() => {
            setIsLoaded(true);
        }, 200);
        return () => clearTimeout(timer);
    }, []);

    const handleStudentClick = () => {
        alert('Redirecting to Student Portal...');
    };

    const handleTeacherClick = () => {
        alert('Redirecting to Teacher Portal...');
    };

    const toggleAdminDropdown = () => {
        setDropdownOpen(!dropdownOpen);

        // Scroll to show dropdown when opening
        if (!dropdownOpen && adminButtonRef.current) {
            setTimeout(() => {
                const buttonRect = adminButtonRef.current.getBoundingClientRect();
                const viewportHeight = window.innerHeight;
                const dropdownHeight = 250; // Approximate dropdown height
                const buffer = 20; // Extra space buffer

                // Check if dropdown would be cut off at bottom of screen
                if (buttonRect.bottom + dropdownHeight + buffer > viewportHeight) {
                    // Calculate scroll position to center the admin section
                    const scrollTarget = window.pageYOffset + buttonRect.top - (viewportHeight / 2) + (dropdownHeight / 2);

                    window.scrollTo({
                        top: Math.max(0, scrollTarget),
                        behavior: 'smooth'
                    });
                }
            }, 100); // Small delay to allow dropdown to start opening
        }
    };

    const handleAdminOption = (option) => {
        const optionNames = {
            'food-cart': 'Food Cart Management',
            'canteen': 'Canteen Management',
            'stationery': 'Stationery Management',
            'department': 'Department Management',
            'library': 'Library Management',
            'hostel': 'Hostel Management',
            'transport': 'Transport Management',
            'finance': 'Finance Management',
            'hr': 'HR Management',
            'inventory': 'Inventory Management'
        };

        alert(`Redirecting to ${optionNames[option]}...`);
        setDropdownOpen(false);
    };

    const handleClickOutside = (event) => {
        if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
            setDropdownOpen(false);
        }
    };

    useEffect(() => {
        document.addEventListener('click', handleClickOutside);
        return () => {
            document.removeEventListener('click', handleClickOutside);
        };
    }, []);

    const styles = {
        container: {
            minHeight: '100vh',
            position: 'relative',
            fontFamily: 'system-ui, -apple-system, sans-serif',
            overflow: 'hidden'
        },
        backgroundImage: {
            position: 'fixed',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            backgroundImage: "url('https://images.unsplash.com/photo-1562774053-701939374585?ixlib=rb-4.0.3&auto=format&fit=crop&w=1920&q=80')",
            backgroundSize: 'cover',
            backgroundPosition: 'center',
            backgroundRepeat: 'no-repeat'
        },
        overlay: {
            position: 'fixed',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            background: 'rgba(15, 23, 42, 0.8)',
            zIndex: 10
        },
        portalContainer: {
            position: 'relative',
            zIndex: 20,
            minHeight: '100vh',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center',
            padding: window.innerWidth >= 1024 ? '2rem' : '1rem'
        },
        welcomeSection: {
            width: '100%',
            maxWidth: '1280px',
            margin: '0 auto',
            marginBottom: window.innerWidth >= 1024 ? '3rem' : '2rem',
            opacity: isLoaded ? 1 : 0,
            transform: isLoaded ? 'translateY(0)' : 'translateY(-48px)',
            transition: 'all 1s ease-out'
        },
        welcomeTitle: {
            color: 'white',
            fontSize: window.innerWidth >= 1280 ? '3.75rem' : window.innerWidth >= 1024 ? '3rem' : window.innerWidth >= 768 ? '2.25rem' : '1.875rem',
            fontWeight: 'bold',
            textAlign: 'center',
            textShadow: '2px 2px 8px rgba(0, 0, 0, 0.7)',
            margin: 0
        },
        mainContent: {
            width: '100%',
            maxWidth: '1280px',
            margin: '0 auto'
        },
        desktopLayout: {
            display: window.innerWidth >= 1024 ? 'flex' : 'none',
            alignItems: 'center',
            justifyContent: 'space-between',
            gap: window.innerWidth >= 1280 ? '5rem' : '4rem',
            width: '100%'
        },
        logoSectionDesktop: {
            flexShrink: 0,
            opacity: isLoaded ? 1 : 0,
            transform: isLoaded ? 'translateX(0)' : 'translateX(-96px)',
            transition: 'all 1s ease-out'
        },
        logoCircleDesktop: {
            width: window.innerWidth >= 1280 ? '320px' : '256px',
            height: window.innerWidth >= 1280 ? '320px' : '256px',
            borderRadius: '50%',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            background: 'linear-gradient(135deg, #3b82f6, #10b981)',
            boxShadow: '0 20px 40px rgba(59, 130, 246, 0.4)',
            transition: 'transform 0.3s ease-in-out',
            overflow: 'hidden',
            cursor: 'pointer'
        },
        logoTextDesktop: {
            color: 'white',
            fontSize: window.innerWidth >= 1280 ? '2.25rem' : '1.875rem',
            fontWeight: 'bold',
            textAlign: 'center',
            textShadow: '0 2px 4px rgba(0, 0, 0, 0.3)',
            lineHeight: 1.2
        },
        buttonsSectionDesktop: {
            flexShrink: 0,
            width: '100%',
            maxWidth: '512px',
            background: 'rgba(15, 23, 42, 0.95)',
            backdropFilter: 'blur(12px)',
            borderRadius: '1.5rem',
            padding: window.innerWidth >= 1280 ? '2.5rem' : '2rem',
            border: '1px solid #475569',
            boxShadow: '0 25px 50px rgba(0, 0, 0, 0.3)',
            opacity: isLoaded ? 1 : 0,
            transform: isLoaded ? 'translateX(0)' : 'translateX(96px)',
            transition: 'all 1s ease-out'
        },
        cardTitle: {
            color: 'white',
            fontSize: window.innerWidth >= 1280 ? '1.875rem' : '1.5rem',
            fontWeight: 600,
            marginBottom: '2rem',
            textAlign: 'center',
            margin: '0 0 2rem 0'
        },
        buttonsContainer: {
            display: 'flex',
            flexDirection: 'column',
            gap: '1.25rem'
        },
        portalButton: {
            position: 'relative',
            overflow: 'hidden',
            padding: window.innerWidth >= 1280 ? '1.5rem' : '1.25rem',
            fontSize: window.innerWidth >= 1280 ? '1.25rem' : '1.125rem',
            fontWeight: 600,
            border: 'none',
            borderRadius: '1rem',
            cursor: 'pointer',
            transition: 'all 0.3s ease-in-out',
            color: 'white',
            textTransform: 'uppercase',
            letterSpacing: '0.05em',
            minHeight: window.innerWidth >= 1280 ? '80px' : '72px',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            width: '100%'
        },
        studentButton: {
            background: 'linear-gradient(135deg, #3b82f6, #10b981)',
            boxShadow: '0 8px 20px rgba(59, 130, 246, 0.4)'
        },
        teacherButton: {
            background: 'linear-gradient(135deg, #10b981, #3b82f6)',
            boxShadow: '0 8px 20px rgba(16, 185, 129, 0.4)'
        },
        adminButton: {
            background: 'linear-gradient(135deg, #3b82f6, #10b981)',
            boxShadow: '0 8px 20px rgba(59, 130, 246, 0.4)'
        },
        buttonShine: {
            position: 'absolute',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            background: 'linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.3), transparent)',
            transform: 'translateX(-100%)',
            transition: 'transform 0.5s ease'
        },
        dropdownContainer: {
            position: 'relative',
            width: '100%'
        },
        dropdownArrow: {
            marginLeft: '0.75rem',
            transition: 'transform 0.3s ease',
            transform: dropdownOpen ? 'rotate(180deg)' : 'rotate(0deg)'
        },
        dropdownMenu: {
            position: 'absolute',
            top: '100%',
            left: 0,
            right: 0,
            background: '#1e293b',
            borderRadius: '1rem',
            border: '1px solid #475569',
            marginTop: '0.5rem',
            zIndex: 50,
            boxShadow: '0 15px 35px rgba(0, 0, 0, 0.3)',
            opacity: dropdownOpen ? 1 : 0,
            visibility: dropdownOpen ? 'visible' : 'hidden',
            transform: dropdownOpen ? 'translateY(0) scale(1)' : 'translateY(-12px) scale(0.95)',
            transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
            transformOrigin: 'top center',
            // Scrolling styles
            maxHeight: '300px',
            overflowY: 'auto',
            overflowX: 'hidden'
        },
        dropdownItem: {
            padding: window.innerWidth >= 1280 ? '1.25rem 1.5rem' : '1rem 1.5rem',
            color: '#cbd5e1',
            cursor: 'pointer',
            transition: 'all 0.3s ease',
            fontWeight: 500,
            fontSize: window.innerWidth >= 1280 ? '1.125rem' : '1rem',
            display: 'flex',
            alignItems: 'center',
            gap: '0.75rem'
        },
        mobileLayout: {
            display: window.innerWidth >= 1024 ? 'none' : 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            gap: window.innerWidth >= 768 ? '3rem' : '2rem'
        },
        logoSectionMobile: {
            opacity: isLoaded ? 1 : 0,
            transform: isLoaded ? 'translateY(0)' : 'translateY(-96px)',
            transition: 'all 1s ease-out'
        },
        logoCircleMobile: {
            width: window.innerWidth >= 768 ? '224px' : '160px',
            height: window.innerWidth >= 768 ? '224px' : '160px',
            borderRadius: '50%',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            background: 'linear-gradient(135deg, #3b82f6, #10b981)',
            boxShadow: '0 20px 40px rgba(59, 130, 246, 0.4)',
            transition: 'transform 0.3s ease-in-out',
            overflow: 'hidden',
            cursor: 'pointer'
        },
        logoTextMobile: {
            color: 'white',
            fontSize: window.innerWidth >= 768 ? '1.5rem' : '1.25rem',
            fontWeight: 'bold',
            textAlign: 'center',
            textShadow: '0 2px 4px rgba(0, 0, 0, 0.3)',
            lineHeight: 1.2
        },
        buttonsSectionMobile: {
            width: '100%',
            maxWidth: '448px',
            margin: '0 auto',
            background: 'rgba(15, 23, 42, 0.95)',
            backdropFilter: 'blur(12px)',
            borderRadius: '1.5rem',
            padding: window.innerWidth >= 768 ? '2rem' : '1.5rem',
            border: '1px solid #475569',
            boxShadow: '0 25px 50px rgba(0, 0, 0, 0.3)',
            opacity: isLoaded ? 1 : 0,
            transform: isLoaded ? 'translateY(0)' : 'translateY(96px)',
            transition: 'all 1s ease-out'
        },
        cardTitleMobile: {
            color: 'white',
            fontSize: window.innerWidth >= 768 ? '1.5rem' : '1.25rem',
            fontWeight: 600,
            marginBottom: window.innerWidth >= 768 ? '2rem' : '1.5rem',
            textAlign: 'center',
            margin: `0 0 ${window.innerWidth >= 768 ? '2rem' : '1.5rem'} 0`
        },
        buttonsContainerMobile: {
            display: 'flex',
            flexDirection: 'column',
            gap: window.innerWidth >= 768 ? '1.25rem' : '1rem'
        },
        portalButtonMobile: {
            position: 'relative',
            overflow: 'hidden',
            padding: window.innerWidth >= 768 ? '1.25rem' : '1rem',
            fontSize: window.innerWidth >= 768 ? '1.125rem' : '1rem',
            fontWeight: 600,
            border: 'none',
            borderRadius: '1rem',
            cursor: 'pointer',
            transition: 'all 0.3s ease-in-out',
            color: 'white',
            textTransform: 'uppercase',
            letterSpacing: '0.05em',
            minHeight: window.innerWidth >= 768 ? '68px' : '60px',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            width: '100%'
        },
        dropdownMenuMobile: {
            position: 'absolute',
            top: '100%',
            left: 0,
            right: 0,
            background: '#1e293b',
            borderRadius: '1rem',
            border: '1px solid #475569',
            marginTop: '0.5rem',
            zIndex: 50,
            boxShadow: '0 15px 35px rgba(0, 0, 0, 0.3)',
            opacity: dropdownOpen ? 1 : 0,
            visibility: dropdownOpen ? 'visible' : 'hidden',
            transform: dropdownOpen ? 'translateY(0) scale(1)' : 'translateY(-12px) scale(0.95)',
            transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
            transformOrigin: 'top center',
            // Mobile scrolling styles
            maxHeight: '250px',
            overflowY: 'auto',
            overflowX: 'hidden'
        },
        dropdownItemMobile: {
            padding: window.innerWidth >= 768 ? '1rem 1.5rem' : '0.75rem 1rem',
            color: '#cbd5e1',
            cursor: 'pointer',
            transition: 'all 0.3s ease',
            fontWeight: 500,
            fontSize: window.innerWidth >= 768 ? '1rem' : '0.875rem',
            display: 'flex',
            alignItems: 'center',
            gap: '0.75rem'
        },
        // Scroll indicator styles
        scrollIndicator: {
            position: 'absolute',
            bottom: '8px',
            right: '12px',
            color: '#64748b',
            fontSize: '0.75rem',
            opacity: dropdownOpen ? 1 : 0,
            transition: 'opacity 0.3s ease',
            pointerEvents: 'none',
            zIndex: 51
        }
    };

    // Add custom scrollbar styles to dropdown menus
    const dropdownScrollbarStyle = `
    .dropdown-menu::-webkit-scrollbar {
      width: 6px;
    }
    .dropdown-menu::-webkit-scrollbar-track {
      background: #1e293b;
      border-radius: 3px;
    }
    .dropdown-menu::-webkit-scrollbar-thumb {
      background: #475569;
      border-radius: 3px;
    }
    .dropdown-menu::-webkit-scrollbar-thumb:hover {
      background: #64748b;
    }
    .dropdown-menu {
      scroll-behavior: smooth;
    }
  `;

    useEffect(() => {
        // Add scrollbar styles to document
        const style = document.createElement('style');
        style.textContent = dropdownScrollbarStyle;
        document.head.appendChild(style);

        return () => {
            document.head.removeChild(style);
        };
    }, []);

    const handleButtonHover = (e, isHover, shadowColor) => {
        if (isHover) {
            e.currentTarget.style.transform = 'translateY(-3px)';
            e.currentTarget.style.boxShadow = `0 12px 25px ${shadowColor}`;
            const shine = e.currentTarget.querySelector('.button-shine');
            if (shine) shine.style.transform = 'translateX(100%)';
        } else {
            e.currentTarget.style.transform = 'translateY(0)';
            e.currentTarget.style.boxShadow = `0 8px 20px ${shadowColor}`;
            const shine = e.currentTarget.querySelector('.button-shine');
            if (shine) shine.style.transform = 'translateX(-100%)';
        }
    };

    const handleLogoHover = (e, isHover) => {
        if (isHover) {
            e.currentTarget.style.transform = 'scale(1.05) rotate(6deg)';
            e.currentTarget.style.boxShadow = '0 25px 50px rgba(59, 130, 246, 0.6)';
        } else {
            e.currentTarget.style.transform = 'scale(1) rotate(0deg)';
            e.currentTarget.style.boxShadow = '0 20px 40px rgba(59, 130, 246, 0.4)';
        }
    };

    const handleDropdownItemHover = (e, isHover) => {
        if (isHover) {
            e.currentTarget.style.background = 'linear-gradient(90deg, #3b82f6, #10b981)';
            e.currentTarget.style.color = 'white';
            e.currentTarget.style.transform = 'translateX(4px)';
        } else {
            e.currentTarget.style.background = 'transparent';
            e.currentTarget.style.color = '#cbd5e1';
            e.currentTarget.style.transform = 'translateX(0)';
        }
    };

    // Extended admin options for scrolling demonstration
    const adminOptions = [
        { key: 'food-cart', label: '🍕 Food Cart Management' },
        { key: 'canteen', label: '🍽 Canteen Management' },
        { key: 'stationery', label: '📚 Stationery Management' },
        { key: 'department', label: '🏢 Department Management' },
        { key: 'library', label: '📖 Library Management' },
        { key: 'hostel', label: '🏠 Hostel Management' },
        { key: 'transport', label: '🚌 Transport Management' },
        { key: 'finance', label: '💰 Finance Management' },
        { key: 'hr', label: '👥 HR Management' },
        { key: 'inventory', label: '📦 Inventory Management' }
    ];

    return (
        <div style={styles.container}>
            {/* Background Image */}
            <div style={styles.backgroundImage} />

            {/* Dark Overlay */}
            <div style={styles.overlay} />

            {/* Portal Container */}
            <div style={styles.portalContainer}>

                {/* Welcome Message */}
                <div style={styles.welcomeSection}>
                    <h1 style={styles.welcomeTitle}>
                        Welcome to DSCE Campus
                    </h1>
                </div>

                {/* Main Content Container */}
                <div style={styles.mainContent}>

                    {/* Desktop Layout */}
                    <div style={styles.desktopLayout}>

                        {/* Logo Section - Desktop */}
                        <div style={styles.logoSectionDesktop}>
                            <div
                                style={styles.logoCircleDesktop}
                                onMouseEnter={(e) => handleLogoHover(e, true)}
                                onMouseLeave={(e) => handleLogoHover(e, false)}
                            >
                                <div style={styles.logoTextDesktop}>
                                    DSCE<br />LOGO
                                </div>
                            </div>
                        </div>

                        {/* Buttons Section - Desktop */}
                        <div style={styles.buttonsSectionDesktop}>
                            <h2 style={styles.cardTitle}>
                                Portal Access System
                            </h2>

                            <div style={styles.buttonsContainer}>
                                {/* Student Button */}
                                <button
                                    onClick={handleStudentClick}
                                    style={{ ...styles.portalButton, ...styles.studentButton }}
                                    onMouseEnter={(e) => handleButtonHover(e, true, 'rgba(59, 130, 246, 0.6)')}
                                    onMouseLeave={(e) => handleButtonHover(e, false, 'rgba(59, 130, 246, 0.4)')}
                                >
                                    <span className="button-shine" style={styles.buttonShine} />
                                    👨‍🎓 Student Portal
                                </button>

                                {/* Teacher Button */}
                                <button
                                    onClick={handleTeacherClick}
                                    style={{ ...styles.portalButton, ...styles.teacherButton }}
                                    onMouseEnter={(e) => handleButtonHover(e, true, 'rgba(16, 185, 129, 0.6)')}
                                    onMouseLeave={(e) => handleButtonHover(e, false, 'rgba(16, 185, 129, 0.4)')}
                                >
                                    <span className="button-shine" style={styles.buttonShine} />
                                    👩‍🏫 Teacher Portal
                                </button>

                                {/* Admin Dropdown */}
                                <div className="dropdown-container" style={styles.dropdownContainer} ref={dropdownRef}>
                                    <button
                                        ref={adminButtonRef}
                                        onClick={toggleAdminDropdown}
                                        style={{ ...styles.portalButton, ...styles.adminButton }}
                                        onMouseEnter={(e) => handleButtonHover(e, true, 'rgba(59, 130, 246, 0.6)')}
                                        onMouseLeave={(e) => handleButtonHover(e, false, 'rgba(59, 130, 246, 0.4)')}
                                    >
                                        <span className="button-shine" style={styles.buttonShine} />
                                        🔧 Admin Panel
                                        <span style={styles.dropdownArrow}>
                                            ▼
                                        </span>
                                    </button>

                                    {/* Dropdown Menu with Scrolling */}
                                    <div className="dropdown-menu" style={styles.dropdownMenu}>
                                        {adminOptions.map((item, index, array) => (
                                            <div
                                                key={item.key}
                                                onClick={() => handleAdminOption(item.key)}
                                                style={{
                                                    ...styles.dropdownItem,
                                                    borderBottom: index === array.length - 1 ? 'none' : '1px solid #475569',
                                                    borderRadius: index === 0 ? '1rem 1rem 0 0' : index === array.length - 1 ? '0 0 1rem 1rem' : '0'
                                                }}
                                                onMouseEnter={(e) => handleDropdownItemHover(e, true)}
                                                onMouseLeave={(e) => handleDropdownItemHover(e, false)}
                                            >
                                                {item.label}
                                            </div>
                                        ))}
                                    </div>

                                    {/* Scroll Indicator */}
                                    <div style={styles.scrollIndicator}>
                                        ↕ Scroll
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    {/* Mobile/Tablet Layout */}
                    <div style={styles.mobileLayout}>

                        {/* Logo Section - Mobile */}
                        <div style={styles.logoSectionMobile}>
                            <div
                                style={styles.logoCircleMobile}
                                onMouseEnter={(e) => handleLogoHover(e, true)}
                                onMouseLeave={(e) => handleLogoHover(e, false)}
                            >
                                <div style={styles.logoTextMobile}>
                                    DSCE<br />LOGO
                                </div>
                            </div>
                        </div>

                        {/* Buttons Section - Mobile */}
                        <div style={styles.buttonsSectionMobile}>
                            <h2 style={styles.cardTitleMobile}>
                                Portal Access System
                            </h2>

                            <div style={styles.buttonsContainerMobile}>
                                {/* Student Button - Mobile */}
                                <button
                                    onClick={handleStudentClick}
                                    style={{ ...styles.portalButtonMobile, ...styles.studentButton }}
                                >
                                    <span style={styles.buttonShine} />
                                    👨‍🎓 Student Portal
                                </button>

                                {/* Teacher Button - Mobile */}
                                <button
                                    onClick={handleTeacherClick}
                                    style={{ ...styles.portalButtonMobile, ...styles.teacherButton }}
                                >
                                    <span style={styles.buttonShine} />
                                    👩‍🏫 Teacher Portal
                                </button>

                                {/* Admin Dropdown - Mobile */}
                                <div className="dropdown-container" style={styles.dropdownContainer} ref={dropdownRef}>
                                    <button
                                        ref={adminButtonRef}
                                        onClick={toggleAdminDropdown}
                                        style={{ ...styles.portalButtonMobile, ...styles.adminButton }}
                                    >
                                        <span style={styles.buttonShine} />
                                        🔧 Admin Panel
                                        <span style={styles.dropdownArrow}>
                                            ▼
                                        </span>
                                    </button>

                                    {/* Dropdown Menu - Mobile with Scrolling */}
                                    <div className="dropdown-menu" style={styles.dropdownMenuMobile}>
                                        {adminOptions.map((item, index, array) => (
                                            <div
                                                key={item.key}
                                                onClick={() => handleAdminOption(item.key)}
                                                style={{
                                                    ...styles.dropdownItemMobile,
                                                    borderBottom: index === array.length - 1 ? 'none' : '1px solid #475569',
                                                    borderRadius: index === 0 ? '1rem 1rem 0 0' : index === array.length - 1 ? '0 0 1rem 1rem' : '0'
                                                }}
                                                onMouseEnter={(e) => handleDropdownItemHover(e, true)}
                                                onMouseLeave={(e) => handleDropdownItemHover(e, false)}
                                            >
                                                {item.label}
                                            </div>
                                        ))}
                                    </div>

                                    {/* Scroll Indicator - Mobile */}
                                    <div style={styles.scrollIndicator}>
                                        ↕ Scroll
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default DSCEPortal;
