import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const RoleSelection = () => {
    const navigate = useNavigate();
    const [dropdownOpen, setDropdownOpen] = useState(false);
    const [isLoaded, setIsLoaded] = useState(false);

    useEffect(() => {
        setTimeout(() => setIsLoaded(true), 100);
    }, []);

    const handleTeacherClick = () => {
        navigate('/teacher/login');
    };

    const toggleAdminDropdown = () => {
        setDropdownOpen(!dropdownOpen);
    };

    const handleAdminOption = (option) => {
        const optionNames = {
            'food-cart': 'Food Cart Management',
            'canteen': 'Canteen Management',
            'stationery': 'Stationery Management',
            'department': 'Department Management'
        };

        alert(`Redirecting to ${optionNames[option]}...`);
        setDropdownOpen(false);
        navigate(`/admin/${option}`);
    };

    return (
        <div className="min-h-screen bg-gray-900 text-white p-5 md:p-10 overflow-hidden">
            {/* Desktop Layout */}
            <div className="hidden lg:flex items-start justify-center gap-16 xl:gap-20">
                {/* Left Side - DSCE Logo */}
                <div className={`flex-shrink-0 self-start transition-all duration-1000 ease-out ${isLoaded ? 'opacity-100 translate-x-0' : 'opacity-0 -translate-x-24'}`}>
                    <div className="w-64 xl:w-80 h-64 xl:h-80 rounded-full overflow-hidden shadow-lg border-4 border-white">
                        <img src="/images/dsce.png" alt="DSCE Logo" className="w-full h-full object-cover" />
                    </div>
                    <h2 className="text-center text-xl mt-4 font-semibold">DSCE</h2>
                </div>

                {/* Right Side - Buttons */}
                <div className={`flex flex-col gap-6 flex-grow transition-all duration-1000 ease-out ${isLoaded ? 'opacity-100 translate-x-0' : 'opacity-0 translate-x-24'}`}>
                    <h1 className="text-4xl xl:text-5xl font-bold mb-6 text-center">Portal Access System</h1>

                    {/* Teacher Button */}
                    <button
                        onClick={handleTeacherClick}
                        className="group relative overflow-hidden p-5 xl:p-6 text-lg xl:text-xl font-semibold border-none rounded-2xl cursor-pointer transition-all duration-300 ease-in-out text-white uppercase tracking-wide min-h-[72px] xl:min-h-[80px] flex items-center justify-center w-full"
                        style={{
                            background: 'linear-gradient(135deg, #10b981, #3b82f6)',
                            boxShadow: '0 8px 20px rgba(16, 185, 129, 0.4)'
                        }}
                        onMouseEnter={(e) => {
                            e.target.style.transform = 'translateY(-3px)';
                            e.target.style.boxShadow = '0 12px 25px rgba(16, 185, 129, 0.6)';
                        }}
                        onMouseLeave={(e) => {
                            e.target.style.transform = 'translateY(0)';
                            e.target.style.boxShadow = '0 8px 20px rgba(16, 185, 129, 0.4)';
                        }}
                    >
                        <span className="absolute inset-0 bg-gradient-to-r from-transparent via-white/30 to-transparent -translate-x-full group-hover:translate-x-full transition-transform duration-500" />
                        👩‍🏫 Teacher Portal
                    </button>

                    {/* Admin Dropdown */}
                    <div className="dropdown-container relative w-full">
                        <button
                            onClick={toggleAdminDropdown}
                            className="group relative overflow-hidden p-5 xl:p-6 text-lg xl:text-xl font-semibold border-none rounded-2xl cursor-pointer transition-all duration-300 ease-in-out text-white uppercase tracking-wide min-h-[72px] xl:min-h-[80px] flex items-center justify-center w-full"
                            style={{
                                background: 'linear-gradient(135deg, #3b82f6, #10b981)',
                                boxShadow: '0 8px 20px rgba(59, 130, 246, 0.4)'
                            }}
                            onMouseEnter={(e) => {
                                e.target.style.transform = 'translateY(-3px)';
                                e.target.style.boxShadow = '0 12px 25px rgba(59, 130, 246, 0.6)';
                            }}
                            onMouseLeave={(e) => {
                                e.target.style.transform = 'translateY(0)';
                                e.target.style.boxShadow = '0 8px 20px rgba(59, 130, 246, 0.4)';
                            }}
                        >
                            <span className="absolute inset-0 bg-gradient-to-r from-transparent via-white/30 to-transparent -translate-x-full group-hover:translate-x-full transition-transform duration-500" />
                            🔧 Admin Panel
                            <span className={`ml-3 transition-transform duration-300 ${dropdownOpen ? 'rotate-180' : ''}`}>
                                ▼
                            </span>
                        </button>

                        {/* Dropdown Menu */}
                        <div className={`absolute top-full left-0 right-0 bg-slate-800 rounded-2xl border border-slate-600 mt-2 z-50 transition-all duration-300 ${dropdownOpen
                            ? 'opacity-100 visible translate-y-0'
                            : 'opacity-0 invisible -translate-y-3'
                            }`}
                            style={{ boxShadow: '0 15px 35px rgba(0, 0, 0, 0.3)' }}>
                            {[
                                { key: 'food-cart', label: '🍕 Food Cart Management' },
                                { key: 'canteen', label: '🍽 Canteen Management' },
                                { key: 'stationery', label: '📚 Stationery Management' },
                                { key: 'department', label: '🏢 Department Management' }
                            ].map((item, index, array) => (
                                <div
                                    key={item.key}
                                    onClick={() => handleAdminOption(item.key)}
                                    className={`px-4 md:px-6 py-3 md:py-4 text-slate-300 cursor-pointer transition-all duration-300 font-medium text-sm md:text-base hover:bg-gradient-to-r hover:from-blue-500 hover:to-emerald-500 hover:text-white active:bg-gradient-to-r active:from-blue-500 active:to-emerald-500 active:text-white ${index === 0 ? 'rounded-t-2xl' : ''
                                        } ${index === array.length - 1 ? 'rounded-b-2xl' : 'border-b border-slate-600'
                                        }`}
                                >
                                    {item.label}
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            </div>

            {/* Mobile View */}
            <div className="lg:hidden flex flex-col items-center justify-center gap-10">
                {/* Logo */}
                <div className="w-48 h-48 rounded-full overflow-hidden border-4 border-white shadow-lg">
                    <img src="/images/dsce.png" alt="DSCE Logo" className="w-full h-full object-cover" />
                </div>
                <h2 className="text-xl font-semibold">DSCE</h2>

                <h1 className="text-3xl font-bold text-center">Portal Access System</h1>

                {/* Teacher Button */}
                <button
                    onClick={handleTeacherClick}
                    className="group relative overflow-hidden p-4 md:p-5 text-base md:text-lg font-semibold border-none rounded-2xl cursor-pointer transition-all duration-300 ease-in-out text-white uppercase tracking-wide min-h-[60px] md:min-h-[68px] flex items-center justify-center w-full"
                    style={{
                        background: 'linear-gradient(135deg, #10b981, #3b82f6)',
                        boxShadow: '0 8px 20px rgba(16, 185, 129, 0.4)'
                    }}
                >
                    <span className="absolute inset-0 bg-gradient-to-r from-transparent via-white/30 to-transparent -translate-x-full group-hover:translate-x-full transition-transform duration-500" />
                    👩‍🏫 Teacher Portal
                </button>

                {/* Admin Button */}
                <div className="dropdown-container relative w-full">
                    <button
                        onClick={toggleAdminDropdown}
                        className="group relative overflow-hidden p-4 md:p-5 text-base md:text-lg font-semibold border-none rounded-2xl cursor-pointer transition-all duration-300 ease-in-out text-white uppercase tracking-wide min-h-[60px] md:min-h-[68px] flex items-center justify-center w-full"
                        style={{
                            background: 'linear-gradient(135deg, #3b82f6, #10b981)',
                            boxShadow: '0 8px 20px rgba(59, 130, 246, 0.4)'
                        }}
                    >
                        <span className="absolute inset-0 bg-gradient-to-r from-transparent via-white/30 to-transparent -translate-x-full group-active:translate-x-full transition-transform duration-500" />
                        🔧 Admin Panel
                        <span className={`ml-3 transition-transform duration-300 ${dropdownOpen ? 'rotate-180' : ''}`}>
                            ▼
                        </span>
                    </button>

                    {/* Dropdown - Mobile */}
                    <div className={`absolute top-full left-0 right-0 bg-slate-800 rounded-2xl border border-slate-600 mt-2 z-50 transition-all duration-300 ${dropdownOpen
                        ? 'opacity-100 visible translate-y-0'
                        : 'opacity-0 invisible -translate-y-3'
                        }`}
                        style={{ boxShadow: '0 15px 35px rgba(0, 0, 0, 0.3)' }}>
                        {[
                            { key: 'food-cart', label: '🍕 Food Cart Management' },
                            { key: 'canteen', label: '🍽 Canteen Management' },
                            { key: 'stationery', label: '📚 Stationery Management' },
                            { key: 'department', label: '🏢 Department Management' }
                        ].map((item, index, array) => (
                            <div
                                key={item.key}
                                onClick={() => handleAdminOption(item.key)}
                                className={`px-4 py-3 text-slate-300 cursor-pointer transition-all duration-300 font-medium text-sm md:text-base hover:bg-gradient-to-r hover:from-blue-500 hover:to-emerald-500 hover:text-white ${index === 0 ? 'rounded-t-2xl' : ''
                                    } ${index === array.length - 1 ? 'rounded-b-2xl' : 'border-b border-slate-600'
                                    }`}
                            >
                                {item.label}
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default RoleSelection;
