"use client"

import { useState } from "react"
import { useCart } from "./CartContext"

const FoodPage = () => {
    const [selectedCategory, setSelectedCategory] = useState("south-indian")
    const [showAlert, setShowAlert] = useState(false)
    const [alertMessage, setAlertMessage] = useState("")

    const { addToCart, getCartCount } = useCart()
    const cartCount = getCartCount()

    // Food categories
    const categories = [
        { id: "south-indian", name: "South Indian Meals", icon: "ü•ò" },
        { id: "north-indian", name: "North Indian Meals", icon: "üçõ" },
        { id: "chinese", name: "Chinese Food", icon: "ü•¢" },
        { id: "snacks", name: "Snacks and Chats", icon: "üçü" },
        { id: "beverages", name: "Drinks and Beverages", icon: "ü•§" },
    ]

    // Food items for each category
    const foodItems = {
        "south-indian": [
            {
                id: 1,
                name: "Traditional Thali",
                description: "Rice + Sambar + Rasam + 2 Vegetables + Curd + Pickle + Papad",
                price: 150,
                category: "South Indian Meals",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 2,
                name: "Masala Dosa Combo",
                description: "Masala Dosa + Coconut Chutney + Sambar + Filter Coffee",
                price: 120,
                category: "South Indian Meals",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
