        headerTitle: {
            fontSize: "1.5rem",
            fontWeight: "bold",
            color: "white",
            margin: 0,
        },
        cartContainer: {
            position: "relative",
            background: "rgba(255, 255, 255, 0.2)",
            borderRadius: "50%",
            width: "40px",
            height: "40px",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            cursor: "pointer",
            transition: "all 0.3s ease",
