            padding: "1rem 1.5rem",
            cursor: "pointer",
            transition: "all 0.3s ease",
            borderLeft: "4px solid transparent",
            display: "flex",
            alignItems: "center",
            gap: "0.75rem",
        },
        categoryItemActive: {
            background: "linear-gradient(90deg, rgba(59, 130, 246, 0.2), rgba(16, 185, 129, 0.2))",
            borderLeftColor: "#60a5fa",
            color: "#60a5fa",
        },
        categoryIcon: {
            fontSize: "1.25rem",
        },
        categoryName: {
            fontSize: "0.95rem",
            fontWeight: 500,
        },
        contentArea: {
            flex: 1,
            padding: "2rem",
            overflowY: "auto",
        },
        itemsGrid: {
            display: "grid",
            gap: "1.5rem",
