        foodImage: {
            width: "120px",
            height: "120px",
            borderRadius: "0.75rem",
            objectFit: "cover",
            flexShrink: 0,
        },
        foodDetails: {
            flex: 1,
            display: "flex",
            flexDirection: "column",
            justifyContent: "space-between",
        },
        foodHeader: {
            display: "flex",
            alignItems: "flex-start",
            justifyContent: "space-between",
            marginBottom: "0.5rem",
        },
        foodName: {
            fontSize: "1.25rem",
            fontWeight: "bold",
            color: "white",
            margin: 0,
        },
        foodDescription: {
            color: "#cbd5e1",
            fontSize: "0.95rem",
            lineHeight: 1.5,
            marginBottom: "1rem",
        },
        foodFooter: {
            display: "flex",
            alignItems: "center",
            justifyContent: "space-between",
        },
        foodPrice: {
            fontSize: "1.5rem",
