"use client"
import { useState, useRef, useEffect } from "react"
import "./dashboard.css"

// Icons as simple SVG components
const HomeIcon = () => (
    <svg className="sidebar-menu-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
        <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth={2}
            d="M3 12l2-2m0 0l7-7 7 7M5 10v10a1 1 0 001 1h3m10-11l2 2m-2-2v10a1 1 0 01-1 1h-3m-6 0a1 1 0 001-1v-4a1 1 0 011-1h2a1 1 0 011 1v4a1 1 0 001 1m-6 0h6"
        />
    </svg>
)

const BriefcaseIcon = () => (
    <svg className="sidebar-menu-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
        <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth={2}
            d="M21 13.255A23.931 23.931 0 0112 15c-3.183 0-6.22-.62-9-1.745M16 6V4a2 2 0 00-2-2h-4a2 2 0 00-2-2v2m8 0V6a2 2 0 012 2v6a2 2 0 01-2 2H8a2 2 0 01-2-2V8a2 2 0 012-2h8z"
        />
    </svg>
)

const ShoppingCartIcon = () => (
    <svg className="sidebar-menu-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
        <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth={2}
            d="M3 3h2l.4 2M7 13h10l4-8H5.4m0 0L7 13m0 0l-2.5 5M7 13l2.5 5m6-5v5a2 2 0 11-4 0v-5m4 0V8a2 2 0 10-4 0v5z"
        />
    </svg>
)

const CoffeeIcon = () => (
    <svg className="sidebar-menu-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
        <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth={2}
            d="M12 8v13m0-13V6a2 2 0 112 2h-2zm0 0V5.5A2.5 2.5 0 109.5 8H12zm-7 4h14M5 12a2 2 0 110-4h14a2 2 0 110 4M5 12v7a2 2 0 002 2h10a2 2 0 002-2v-7"
        />
    </svg>
)

const HelpCircleIcon = () => (
    <svg className="sidebar-menu-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
        <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth={2}
            d="M8.228 9c.549-1.165 2.03-2 3.772-2 2.21 0 4 1.343 4 3 0 1.4-1.278 2.575-3.006 2.907-.542.104-.994.54-.994 1.093m0 3h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z"
        />
    </svg>
)

const MenuIcon = () => (
    <svg width="20" height="20" fill="none" stroke="currentColor" viewBox="0 0 24 24">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
    </svg>
)

const BellIcon = () => (
    <svg width="20" height="20" fill="none" stroke="currentColor" viewBox="0 0 24 24">
        <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth={2}
            d="M15 17h5l-1.405-1.405A2.032 2.032 0 0118 14.158V11a6.002 6.002 0 00-4-5.659V5a2 2 0 10-4 0v.341C7.67 6.165 6 8.388 6 11v3.159c0 .538-.214 1.055-.595 1.436L4 17h5m6 0v1a3 3 0 11-6 0v-1m6 0H9"
        />
    </svg>
)

const UserIcon = () => (
    <svg width="16" height="16" fill="none" stroke="currentColor" viewBox="0 0 24 24">
        <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth={2}
            d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z"
        />
    </svg>
)

const ChevronDownIcon = () => (
    <svg width="16" height="16" fill="none" stroke="currentColor" viewBox="0 0 24 24">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
    </svg>
)

// Menu items for the sidebar
const menuItems = [
    { title: "Home", url: "/", icon: HomeIcon },
    { title: "Placements", url: "/placements", icon: BriefcaseIcon },
    { title: "Food Cart", url: "/cart", icon: ShoppingCartIcon },
    { title: "Canteen", url: "/food", icon: CoffeeIcon },
    { title: "Help", url: "/help", icon: HelpCircleIcon },
]

// Sample student data
const studentData = {
    name: "Alex Johnson",
    studentId: "ST2024001",
    course: "Computer Science Engineering",
    year: "3rd Year",
    avatar: "/placeholder.svg?height=40&width=40",
}

// Custom hook for handling clicks outside
const useClickOutside = (ref, handler) => {
    useEffect(() => {
        const listener = (event) => {
            if (!ref.current || ref.current.contains(event.target)) {
                return
            }
            handler(event)
        }
        document.addEventListener("mousedown", listener)
        document.addEventListener("touchstart", listener)
        return () => {
            document.removeEventListener("mousedown", listener)
            document.removeEventListener("touchstart", listener)
        }
    }, [ref, handler])
}

// Sidebar Component
const Sidebar = ({ isOpen, activeItem, onItemClick }) => {
    return (
        <div className={`sidebar ${!isOpen ? "collapsed" : ""}`}>
            <div className="sidebar-header">
                <h2 className="sidebar-title">Student Portal</h2>
            </div>
            <nav className="sidebar-menu">
                {menuItems.map((item) => {
                    const IconComponent = item.icon
                    return (
                        <div
                            key={item.title}
                            className={`sidebar-menu-item ${activeItem === item.title ? "active" : ""}`}
                            onClick={() => onItemClick(item.title)}
                        >
                            <IconComponent />
                            <span>{item.title}</span>
                        </div>
                    )
                })}
            </nav>
        </div>
    )
}

// Dropdown Component
const Dropdown = ({ trigger, children, isOpen, onToggle }) => {
    const dropdownRef = useRef(null)
    useClickOutside(dropdownRef, () => {
        if (isOpen) onToggle()
    })

    return (
        <div className="dropdown" ref={dropdownRef}>
            <div onClick={onToggle}>{trigger}</div>
            {isOpen && <div className="dropdown-content">{children}</div>}
        </div>
    )
}

// Header Component
const Header = ({ onSidebarToggle, sidebarOpen }) => {
    const [notificationsOpen, setNotificationsOpen] = useState(false)
    const [profileOpen, setProfileOpen] = useState(false)

    const notificationsTrigger = (
        <button className="btn btn-ghost">
            <BellIcon />
            <span className="badge">3</span>
        </button>
    )

    const profileTrigger = (
        <button className="btn btn-ghost profile-btn">
            <div className="avatar">
                {studentData.name
                    .split(" ")
                    .map((n) => n[0])
                    .join("")}
            </div>
            <span className="profile-name">{studentData.name}</span>
            <ChevronDownIcon />
        </button>
    )

    return (
        <header className="header">
            <div className="header-left">
                <button className="sidebar-toggle" onClick={onSidebarToggle}>
                    <MenuIcon />
                </button>
                <h1 className="header-title">Student Dashboard</h1>
            </div>
            <div className="header-right">
                <Dropdown
                    trigger={notificationsTrigger}
                    isOpen={notificationsOpen}
                    onToggle={() => setNotificationsOpen(!notificationsOpen)}
                >
                    <div className="dropdown-header">Notifications</div>
                    <div className="dropdown-item">
                        <div>
                            <div className="notification-title">New placement opportunity</div>
                            <div className="notification-subtitle">Software Engineer at TechCorp</div>
                        </div>
                    </div>
                    <div className="dropdown-item">
                        <div>
                            <div className="notification-title">Canteen menu updated</div>
                            <div className="notification-subtitle">New items added for this week</div>
                        </div>
                    </div>
                    <div className="dropdown-item">
                        <div>
                            <div className="notification-title">Assignment reminder</div>
                            <div className="notification-subtitle">Data Structures assignment due tomorrow</div>
                        </div>
                    </div>
                </Dropdown>
                <Dropdown trigger={profileTrigger} isOpen={profileOpen} onToggle={() => setProfileOpen(!profileOpen)}>
                    <div className="dropdown-header">My Account</div>
                    <div className="dropdown-item" onClick={() => alert("Navigate to Student Details")}>
                        <UserIcon />
                        Student Details
                    </div>
                    <div className="dropdown-item">Academic Records</div>
                    <div className="dropdown-item">Settings</div>
                    <div className="dropdown-item">Log out</div>
                </Dropdown>
            </div>
        </header>
    )
}

// Main Content Component
const MainContent = () => {
    return (
        <main className="content-area">
            {/* Welcome Section */}
            <div className="welcome-section">
                <h2 className="welcome-title">Welcome back, {studentData.name}!</h2>
                <p className="welcome-subtitle">
                    {studentData.course} • {studentData.year} • ID: {studentData.studentId}
                </p>
            </div>

            {/* Quick Stats Cards */}
            <div className="cards-grid">
                <div className="card">
                    <div className="card-header">
                        <div className="card-title">Attendance</div>
                        <UserIcon />
                    </div>
                    <div className="card-value">85%</div>
                    <div className="card-description">+2% from last month</div>
                </div>
                <div className="card">
                    <div className="card-header">
                        <div className="card-title">CGPA</div>
                        <BriefcaseIcon />
                    </div>
                    <div className="card-value">8.7</div>
                    <div className="card-description">Current semester</div>
                </div>
                <div className="card">
                    <div className="card-header">
                        <div className="card-title">Assignments</div>
                        <ShoppingCartIcon />
                    </div>
                    <div className="card-value">3</div>
                    <div className="card-description">Pending submissions</div>
                </div>
                <div className="card">
                    <div className="card-header">
                        <div className="card-title">Credits</div>
                        <CoffeeIcon />
                    </div>
                    <div className="card-value">142</div>
                    <div className="card-description">Out of 160 required</div>
                </div>
            </div>

            {/* Recent Activities */}
            <div className="activities-card">
                <div className="activities-header">
                    <h3 className="activities-title">Recent Activities</h3>
                    <p className="activities-description">Your latest academic and campus activities</p>
                </div>
                <div className="activities-list">
                    <div className="activity-item">
                        <div className="activity-dot blue"></div>
                        <div className="activity-content">
                            <div className="activity-title">Submitted Data Structures Assignment</div>
                            <div className="activity-time">2 hours ago</div>
                        </div>
                    </div>
                    <div className="activity-item">
                        <div className="activity-dot green"></div>
                        <div className="activity-content">
                            <div className="activity-title">Attended Machine Learning Lecture</div>
                            <div className="activity-time">Yesterday</div>
                        </div>
                    </div>
                    <div className="activity-item">
                        <div className="activity-dot orange"></div>
                        <div className="activity-content">
                            <div className="activity-title">Applied for Software Engineer Internship</div>
                            <div className="activity-time">3 days ago</div>
                        </div>
                    </div>
                </div>
            </div>
        </main>
    )
}

// Main Dashboard Component
const StudentDashboard = () => {
    const [sidebarOpen, setSidebarOpen] = useState(true)
    const [activeItem, setActiveItem] = useState("Home")
    const [isMobile, setIsMobile] = useState(false)

    useEffect(() => {
        const handleResize = () => {
            const mobile = window.innerWidth <= 768
            setIsMobile(mobile)
            if (mobile) {
                setSidebarOpen(false)
            }
        }

        handleResize()
        window.addEventListener("resize", handleResize)
        return () => window.removeEventListener("resize", handleResize)
    }, [])

    const handleSidebarToggle = () => {
        setSidebarOpen(!sidebarOpen)
    }

    const handleItemClick = (item) => {
        setActiveItem(item)
        if (isMobile) {
            setSidebarOpen(false)
        }

        // Navigate to food cart when clicked
        if (item === "Food Cart") {
            window.location.href = "/food"
        }
    }

    return (
        <div className="dashboard-container">
            {isMobile && sidebarOpen && <div className="overlay" onClick={() => setSidebarOpen(false)} />}
            <Sidebar isOpen={sidebarOpen} activeItem={activeItem} onItemClick={handleItemClick} />
            <div className="main-content">
                <Header onSidebarToggle={handleSidebarToggle} sidebarOpen={sidebarOpen} />
                <MainContent />
            </div>
        </div>
    )
}

export default StudentDashboard
