"use client"

import { useState } from "react"
import { User, MapPin, Phone, Package, DollarSign, Home } from "lucide-react"
import { useNavigate } from "react-router-dom"
// Main Vendor Dashboard App
const FoodVendorDashboard = () => {

    const navigate = useNavigate()
    const [currentPage, setCurrentPage] = useState("orders")
    const [orders, setOrders] = useState([
        {
            id: "DSCE123456",
            customerName: "Rahul Sharma",
            studentId: "4NM21CS001",
            phone: "+91 9876543210",
            email: "rahul@dsce.edu.in",
            items: [
                { name: "Fresh Orange Juice", quantity: 2, price: 45, category: "Fresh Fruit Juices" },
                { name: "Samosa Chat", quantity: 1, price: 60, category: "Snacks and Chats" },
            ],
            subtotal: 150,
            tax: 8,
            total: 158,
            status: "pending",
            orderTime: "2025-01-31T10:30:00",
            estimatedTime: 15,
        },
        {
            id: "DSCE123457",
            customerName: "Priya Patel",
            studentId: "4NM21CS002",
            phone: "+91 8765432109",
            email: "priya@dsce.edu.in",
            items: [
                { name: "Mango Smoothie", quantity: 1, price: 70, category: "Smoothies & Shakes" },
                { name: "Veg Fried Rice Combo", quantity: 1, price: 170, category: "Chinese Food" },
            ],
            subtotal: 240,
            tax: 12,
            total: 252,
            status: "preparing",
            orderTime: "2025-01-31T10:25:00",
            estimatedTime: 8,
        },
        {
            id: "DSCE123458",
            customerName: "Amit Kumar",
            studentId: "4NM21CS003",
            phone: "+91 7654321098",
            email: "amit@dsce.edu.in",
            items: [
                { name: "Filter Coffee", quantity: 3, price: 30, category: "Hot & Cold Beverages" },
                { name: "Pav Bhaji", quantity: 2, price: 80, category: "Snacks and Chats" },
            ],
            subtotal: 250,
            tax: 13,
            total: 263,
            status: "ready",
            orderTime: "2025-01-31T10:20:00",
            estimatedTime: 0,
        },
        {
            id: "DSCE123459",
            customerName: "Sneha Reddy",
            studentId: "4NM21CS004",
            phone: "+91 6543210987",
            email: "sneha@dsce.edu.in",
            items: [{ name: "Chocolate Milkshake", quantity: 1, price: 80, category: "Smoothies & Shakes" }],
            subtotal: 80,
            tax: 4,
            total: 84,
            status: "completed",
            orderTime: "2025-01-31T10:15:00",
            estimatedTime: 0,
        },
    ])

    const [vendorInfo] = useState({
        name: "DSCE Food Court Vendor",
        stall: "Stall A1",
        phone: "+91 9988776655",
        email: "vendor@dsce.edu.in",
    })

    const updateOrderStatus = (orderId, newStatus) => {
        setOrders((prev) =>
            prev.map((order) =>
                order.id === orderId
                    ? { ...order, status: newStatus, estimatedTime: newStatus === "ready" ? 0 : order.estimatedTime }
                    : order,
            ),
        )
    }

    const updateEstimatedTime = (orderId, time) => {
        setOrders((prev) => prev.map((order) => (order.id === orderId ? { ...order, estimatedTime: time } : order)))
    }

    const getOrderStats = () => {
        const stats = {
            pending: orders.filter((o) => o.status === "pending").length,
            preparing: orders.filter((o) => o.status === "preparing").length,
            ready: orders.filter((o) => o.status === "ready").length,
            completed: orders.filter((o) => o.status === "completed").length,
            totalRevenue: orders.filter((o) => o.status === "completed").reduce((sum, o) => sum + o.total, 0),
        }
        return stats
    }

    return (
        <>
            {currentPage === "orders" && (
                <OrdersPage
                    orders={orders}
                    vendorInfo={vendorInfo}
                    updateOrderStatus={updateOrderStatus}
                    updateEstimatedTime={updateEstimatedTime}
                    getOrderStats={getOrderStats}
                    setCurrentPage={setCurrentPage}
                />
            )}
        </>
    )
}

// Orders Management Page
const OrdersPage = ({ orders, vendorInfo, updateOrderStatus, updateEstimatedTime, getOrderStats, setCurrentPage }) => {
    const [selectedFilter, setSelectedFilter] = useState("all")
    const [selectedOrder, setSelectedOrder] = useState(null)
    const [showNotification, setShowNotification] = useState(false)
    const [notificationMessage, setNotificationMessage] = useState("")

    const stats = getOrderStats()

    // Filter orders by status - treating pending, preparing, ready as "received" and completed as "delivered"
    const receivedOrders = orders.filter((order) => ["pending", "preparing", "ready"].includes(order.status))
    const deliveredOrders = orders.filter((order) => order.status === "completed")

    const getStatusColor = (status) => {
        switch (status) {
            case "pending":
                return "#f59e0b"
            case "preparing":
                return "#3b82f6"
            case "ready":
                return "#10b981"
            case "completed":
                return "#6b7280"
            default:
                return "#6b7280"
        }
    }

    const getStatusIcon = (status) => {
        switch (status) {
            case "pending":
                return "⏳"
            case "preparing":
                return "👨‍🍳"
            case "ready":
                return "✅"
            case "completed":
                return "📦"
            default:
                return "📋"
        }
    }

    const handleStatusUpdate = (orderId, newStatus) => {
        updateOrderStatus(orderId, newStatus)
        setNotificationMessage(`Order ${orderId} marked as ${newStatus}`)
        setShowNotification(true)
        setTimeout(() => setShowNotification(false), 3000)
    }

    const formatTime = (timeString) => {
        return new Date(timeString).toLocaleTimeString("en-IN", {
            hour: "2-digit",
            minute: "2-digit",
        })
    }

    const getTimeAgo = (timeString) => {
        const now = new Date()
        const orderTime = new Date(timeString)
        const diffMs = now - orderTime
        const diffMins = Math.floor(diffMs / 60000)

        if (diffMins < 1) return "Just now"
        if (diffMins < 60) return `${diffMins}m ago`
        const diffHours = Math.floor(diffMins / 60)
        return `${diffHours}h ${diffMins % 60}m ago`
    }

    const OrderCard = ({ order, showDeliverButton = false, showRevertButton = false }) => (
        <div
            style={{
                background: "rgba(30, 41, 59, 0.8)",
                backdropFilter: "blur(12px)",
                borderRadius: "1rem",
                padding: "1.5rem",
                border: "1px solid #475569",
                marginBottom: "1.5rem",
                transition: "all 0.3s ease",
            }}
        >
            <div className="order-header">
                <div>
                    <div className="order-id">Order #{order.id}</div>
                    <div className="order-time">
                        {formatTime(order.orderTime)} • {getTimeAgo(order.orderTime)}
                    </div>
                </div>
                <div
                    className="order-status"
                    style={{
                        backgroundColor: getStatusColor(order.status),
                        color: "white",
                    }}
                >
                    {getStatusIcon(order.status)} {order.status.charAt(0).toUpperCase() + order.status.slice(1)}
                </div>
            </div>

            <div className="customer-info">
                <User size={16} />
                <span>{order.customerName}</span>
                <span>({order.studentId})</span>
                <Phone size={14} />
                <span>{order.phone}</span>
            </div>

            <div className="order-items">
                <h4 style={{ color: "#60a5fa", marginBottom: "0.75rem", display: "flex", alignItems: "center", gap: "0.5rem" }}>
                    <Package size={16} /> Order Items ({order.items.length})
                </h4>
