                <div
                    style={{
                        display: "flex",
                        justifyContent: "space-between",
                        paddingTop: "0.5rem",
                        borderTop: "2px solid #60a5fa",
                    }}
                >
                    <span style={{ color: "#60a5fa", fontWeight: "bold", fontSize: "1.1rem" }}>Total:</span>
                    <span style={{ color: "#60a5fa", fontWeight: "bold", fontSize: "1.1rem" }}>₹{order.total}/-</span>
                </div>
                <div style={{ marginTop: "0.5rem", textAlign: "right" }}>
                    <span
                        style={{
                            background: "rgba(16, 185, 129, 0.2)",
                            color: "#10b981",
                            padding: "0.25rem 0.75rem",
                            borderRadius: "1rem",
                            fontSize: "0.8rem",
                            fontWeight: "bold",
                        }}
                    >
                        Payment: {order.paymentMethod}
                    </span>
                </div>
            </div>

            {/* Action Buttons */}
            <div style={{ display: "flex", justifyContent: "flex-end", gap: "1rem" }}>
                {showDeliverButton && (
                    <button
                        onClick={() => markAsDelivered(order.id)}
                        style={{
                            background: "linear-gradient(135deg, #10b981, #059669)",
                            border: "none",
                            borderRadius: "2rem",
                            padding: "0.75rem 1.5rem",
                            color: "white",
                            fontSize: "0.9rem",
                            fontWeight: "bold",
                            cursor: "pointer",
                            transition: "all 0.3s ease",
                            textTransform: "uppercase",
                            letterSpacing: "0.05em",
                        }}
                    >
                        Mark as Delivered ✅
                    </button>
                )}
                {showRevertButton && (
                    <button
                        onClick={() => markAsPlaced(order.id)}
                        style={{
                            background: "linear-gradient(135deg, #f59e0b, #d97706)",
                            border: "none",
                            borderRadius: "2rem",
                            padding: "0.75rem 1.5rem",
                            color: "white",
                            fontSize: "0.9rem",
                            fontWeight: "bold",
                            cursor: "pointer",
                            transition: "all 0.3s ease",
                            textTransform: "uppercase",
                            letterSpacing: "0.05em",
                        }}
                    >
                        Mark as Pending ⏳
                    </button>
                )}
            </div>
        </div>
    )

    const totalRevenue = orders
        .filter((order) => order.status === "delivered")
        .reduce((sum, order) => sum + order.total, 0)

    const styles = {
        container: {
            minHeight: "100vh",
            background: "linear-gradient(135deg, #0f172a, #1e293b)",
            fontFamily: "system-ui, -apple-system, sans-serif",
            color: "white",
        },
        header: {
            background: "linear-gradient(135deg, #3b82f6, #10b981)",
            padding: "1rem",
            display: "flex",
            alignItems: "center",
            justifyContent: "space-between",
            boxShadow: "0 4px 12px rgba(0, 0, 0, 0.3)",
            position: "sticky",
            top: 0,
            zIndex: 50,
        },
        headerTitle: {
            fontSize: "1.5rem",
            fontWeight: "bold",
            color: "white",
            margin: 0,
        },
        homeButton: {
            background: "rgba(255, 255, 255, 0.2)",
            border: "none",
            borderRadius: "50%",
            width: "40px",
            height: "40px",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            cursor: "pointer",
            color: "white",
            fontSize: "1.25rem",
            transition: "all 0.3s ease",
        },
        statsContainer: {
            maxWidth: "1200px",
            margin: "2rem auto",
            padding: "0 2rem",
            display: "grid",
            gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))",
            gap: "1.5rem",
        },
        statCard: {
            background: "rgba(30, 41, 59, 0.8)",
            backdropFilter: "blur(12px)",
            borderRadius: "1rem",
            padding: "1.5rem",
            border: "1px solid #475569",
            display: "flex",
            alignItems: "center",
            gap: "1rem",
        },
        statIcon: {
            width: "50px",
            height: "50px",
            borderRadius: "50%",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            fontSize: "1.5rem",
        },
        statContent: {
            flex: 1,
        },
        statLabel: {
            color: "#cbd5e1",
            fontSize: "0.9rem",
            margin: "0 0 0.25rem 0",
        },
        statValue: {
            color: "white",
            fontSize: "1.5rem",
            fontWeight: "bold",
            margin: 0,
        },
        mainContent: {
            maxWidth: "1200px",
            margin: "0 auto",
            padding: "0 2rem",
            display: "grid",
            gridTemplateColumns: "1fr 1fr",
            gap: "2rem",
        },
        sectionContainer: {
            background: "rgba(30, 41, 59, 0.8)",
            backdropFilter: "blur(12px)",
            borderRadius: "1rem",
            border: "1px solid #475569",
            padding: "2rem",
            height: "fit-content",
        },
        sectionTitle: {
            color: "#60a5fa",
            fontSize: "1.5rem",
            fontWeight: "bold",
            marginBottom: "1.5rem",
            display: "flex",
            alignItems: "center",
            gap: "0.5rem",
        },
        emptyState: {
            textAlign: "center",
            padding: "3rem",
            color: "#94a3b8",
        },
        emptyIcon: {
            fontSize: "4rem",
            marginBottom: "1rem",
        },
        emptyText: {
            fontSize: "1.25rem",
            marginBottom: "0.5rem",
        },
    }

    return (
        <div style={styles.container}>
            {/* Header */}
            <header style={styles.header}>
                <button style={styles.homeButton} onClick={() => navigate('/')}>
                    <Home size={20} />
                </button>
                <h1 style={styles.headerTitle}>DSCE Canteen - Admin Dashboard</h1>
                <div
                    style={{
                        textAlign: "right",
                        color: "white",
                    }}
                >
                    <div style={{ fontSize: "0.9rem", opacity: 0.9 }}>{"Today's Revenue"}</div>
                    <div style={{ fontSize: "1.25rem", fontWeight: "bold" }}>₹{totalRevenue}/-</div>
                </div>
            </header>

            {/* Stats Cards */}
            <div style={styles.statsContainer}>
                <div style={styles.statCard}>
                    <div
                        style={{
                            ...styles.statIcon,
                            background: "linear-gradient(135deg, #3b82f6, #1d4ed8)",
                        }}
                    >
                        <Package size={24} />
                    </div>
                    <div style={styles.statContent}>
                        <p style={styles.statLabel}>Total Orders</p>
                        <p style={styles.statValue}>{orders.length}</p>
                    </div>
                </div>

                <div style={styles.statCard}>
                    <div
                        style={{
                            ...styles.statIcon,
                            background: "linear-gradient(135deg, #8b5cf6, #7c3aed)",
                        }}
                    >
                        <DollarSign size={24} />
                    </div>
                    <div style={styles.statContent}>
                        <p style={styles.statLabel}>Revenue Today</p>
                        <p style={styles.statValue}>₹{totalRevenue}/-</p>
                    </div>
                </div>
            </div>

            {/* Main Content */}
            <div style={styles.mainContent}>
                {/* Orders Received Section */}
                <div style={styles.sectionContainer}>
                    <h2 style={styles.sectionTitle}>📋 Orders Received ({placedOrders.length})</h2>
                    {placedOrders.length === 0 ? (
                        <div style={styles.emptyState}>
                            <div style={styles.emptyIcon}>📋</div>
                            <div style={styles.emptyText}>No Pending Orders</div>
                            <p>New orders will appear here when placed</p>
                        </div>
                    ) : (
                        placedOrders.map((order) => <OrderCard key={order.id} order={order} showDeliverButton={true} />)
                    )}
                </div>

                {/* Orders Delivered Section */}
                <div style={styles.sectionContainer}>
                    <h2 style={styles.sectionTitle}>✅ Orders Delivered ({deliveredOrders.length})</h2>
                    {deliveredOrders.length === 0 ? (
                        <div style={styles.emptyState}>
                            <div style={styles.emptyIcon}>✅</div>
                            <div style={styles.emptyText}>No Delivered Orders</div>
                            <p>Completed orders will appear here</p>
                        </div>
                    ) : (
                        deliveredOrders.map((order) => <OrderCard key={order.id} order={order} showRevertButton={true} />)
                    )}
                </div>
            </div>
            <div style={{ height: "2rem" }}></div>
        </div>
    )
}

export default FoodVendorDashboard
