import { useState } from "react";
import { Eye, EyeOff, User, Lock, AlertCircle, CheckCircle } from 'lucide-react';

export default function Login() {
    const [formData, setFormData] = useState({
        email: "",
        password: "",
    });
    const [showPassword, setShowPassword] = useState(false);
    const [message, setMessage] = useState({ type: "", text: "" });
    const [isLoading, setIsLoading] = useState(false);

    const handleInputChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value,
        });
    };

    const showMessage = (type, text) => {
        setMessage({ type, text });
        setTimeout(() => setMessage({ type: "", text: "" }), 3000);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setIsLoading(true);

        setTimeout(() => {
            if (formData.email === "admin@dsce.edu" && formData.password === "admin123") {
                showMessage("success", "Login successful! Redirecting to dashboard...");
                setTimeout(() => {
                    alert(
                        "Welcome to Stationary Admin Dashboard!\n\nDashboard Features:\n• View & manage inventory\n• Update product catalog\n• Track sales analytics\n• Customer orders\n• Revenue reports"
                    );
                }, 2000);
            } else {
                showMessage("error", "Invalid credentials. Please check your email and password.");
            }
            setIsLoading(false);
        }, 1500);
    };

    const handleForgotPassword = () => {
        alert(
            "Password Reset Request\n\nPlease contact DSCE IT Support:\n📧 support@dsce.edu\n📞 +91-80-28440384\n\nInclude your admin ID for faster assistance."
        );
    };

    // Inline styles object
    const styles = {
        container: {
            minHeight: "100vh",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            padding: "16px",
            background: "#0f172a",
            fontFamily: "system-ui, -apple-system, sans-serif",
        },
        backgroundPattern: {
            position: "absolute",
            inset: "0",
            opacity: 0.05,
            backgroundImage: "radial-gradient(circle at 25% 25%, #3b82f6 0%, transparent 50%), radial-gradient(circle at 75% 75%, #10b981 0%, transparent 50%)",
        },
        formContainer: {
            width: "100%",
            maxWidth: "448px",
            position: "relative",
            zIndex: 10,
        },
        card: {
            borderRadius: "16px",
            boxShadow: "0 25px 50px -12px rgba(0, 0, 0, 0.25)",
            overflow: "hidden",
            background: "#0f172a",
            border: "1px solid #1e293b",
        },
        header: {
            padding: "32px 32px 24px",
            textAlign: "center",
        },
        logoContainer: {
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            marginBottom: "16px",
        },
        logo: {
            width: "64px",
            height: "64px",
            borderRadius: "50%",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            fontSize: "24px",
            background: "linear-gradient(135deg, #3b82f6 0%, #10b981 100%)",
        },
        title: {
            fontSize: "24px",
            fontWeight: "bold",
            color: "white",
            marginBottom: "8px",
            margin: "0",
        },
        subtitle: {
            color: "#94a3b8",
            fontSize: "14px",
            margin: "0",
        },
        form: {
            padding: "0 32px 32px",
        },
        fieldContainer: {
            marginBottom: "24px",
        },
        label: {
            display: "block",
            fontSize: "14px",
            fontWeight: "500",
            color: "#cbd5e1",
            marginBottom: "8px",
        },
        inputContainer: {
            position: "relative",
        },
        input: {
            width: "100%",
            paddingLeft: "48px",
            paddingRight: "16px",
            paddingTop: "16px",
            paddingBottom: "16px",
            borderRadius: "12px",
            color: "white",
            background: "#1e293b",
            border: "1px solid #334155",
            fontSize: "16px",
            transition: "all 0.3s",
            outline: "none",
            boxSizing: "border-box",
        },
        inputPassword: {
            paddingRight: "48px",
        },
        icon: {
            position: "absolute",
            left: "16px",
            top: "50%",
            transform: "translateY(-50%)",
            color: "#94a3b8",
        },
        eyeButton: {
            position: "absolute",
            right: "16px",
            top: "50%",
            transform: "translateY(-50%)",
            background: "none",
            border: "none",
            color: "#94a3b8",
            cursor: "pointer",
            padding: "4px",
        },
        submitButton: {
            width: "100%",
            padding: "16px",
            borderRadius: "12px",
            color: "white",
            fontWeight: "600",
            fontSize: "18px",
            background: "linear-gradient(135deg, #3b82f6 0%, #10b981 100%)",
            border: "none",
            cursor: "pointer",
            transition: "all 0.3s",
            marginBottom: "16px",
        },
        submitButtonDisabled: {
            opacity: 0.7,
            cursor: "not-allowed",
        },
        forgotPassword: {
            textAlign: "center",
            marginBottom: "24px",
        },
        forgotPasswordButton: {
            background: "none",
            border: "none",
            color: "#94a3b8",
            fontSize: "14px",
            cursor: "pointer",
            textDecoration: "underline",
        },
        demoCredentials: {
            padding: "16px",
            borderRadius: "8px",
            border: "1px solid #374151",
            background: "rgba(30, 41, 59, 0.5)",
        },
        demoTitle: {
            fontSize: "12px",
            color: "#94a3b8",
            marginBottom: "8px",
            margin: "0 0 8px 0",
        },
        demoText: {
            fontSize: "12px",
            color: "#cbd5e1",
            margin: "4px 0",
        },
        footer: {
            textAlign: "center",
            marginTop: "24px",
        },
        footerText: {
            fontSize: "12px",
            color: "#64748b",
            margin: "0",
        },
        message: {
            margin: "0 32px 24px",
            padding: "16px",
            borderRadius: "8px",
            display: "flex",
            alignItems: "center",
            gap: "12px",
            fontSize: "14px",
        },
        messageSuccess: {
            background: "rgba(34, 197, 94, 0.2)",
            border: "1px solid rgba(34, 197, 94, 0.3)",
            color: "#4ade80",
        },
        messageError: {
            background: "rgba(239, 68, 68, 0.2)",
            border: "1px solid rgba(239, 68, 68, 0.3)",
            color: "#f87171",
        },
        spinner: {
            width: "20px",
            height: "20px",
            border: "2px solid rgba(255, 255, 255, 0.3)",
            borderTop: "2px solid white",
            borderRadius: "50%",
            animation: "spin 1s linear infinite",
            marginRight: "8px",
        },
        loadingContainer: {
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
        },
    };

    return (
        <div style={styles.container}>
            <style>
                {`
                @keyframes spin {
                    0% { transform: rotate(0deg); }
                    100% { transform: rotate(360deg); }
                }
                input::placeholder {
                    color: #94a3b8;
                }
                button:hover {
                    transform: scale(1.02);
                }
                input:focus {
                    border-color: #3b82f6;
                    box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1);
                }
                `}
            </style>

            <div style={styles.backgroundPattern}></div>

            <div style={styles.formContainer}>
                <div style={styles.card}>
                    <div style={styles.header}>
                        <div style={styles.logoContainer}>
                            <div style={styles.logo}>📝</div>
                        </div>
                        <h1 style={styles.title}>Stationary</h1>
                        <p style={styles.subtitle}>Admin Login Portal</p>
                    </div>

                    {message.text && (
                        <div
                            style={{
                                ...styles.message,
                                ...(message.type === "success" ? styles.messageSuccess : styles.messageError),
                            }}
                        >
                            {message.type === "success" ? <CheckCircle size={18} /> : <AlertCircle size={18} />}
                            <span>{message.text}</span>
                        </div>
                    )}

                    <form onSubmit={handleSubmit} style={styles.form}>
                        <div style={styles.fieldContainer}>
                            <label style={styles.label}>Admin Email</label>
                            <div style={styles.inputContainer}>
                                <User style={styles.icon} size={18} />
                                <input
                                    type="email"
                                    name="email"
                                    value={formData.email}
                                    onChange={handleInputChange}
                                    placeholder="Enter your admin email"
                                    required
                                    style={styles.input}
                                />
                            </div>
                        </div>

                        <div style={styles.fieldContainer}>
                            <label style={styles.label}>Password</label>
                            <div style={styles.inputContainer}>
                                <Lock style={styles.icon} size={18} />
                                <input
                                    type={showPassword ? "text" : "password"}
                                    name="password"
                                    value={formData.password}
                                    onChange={handleInputChange}
                                    placeholder="Enter your password"
                                    required
                                    style={{ ...styles.input, ...styles.inputPassword }}
                                />
                                <button
                                    type="button"
                                    onClick={() => setShowPassword(!showPassword)}
                                    style={styles.eyeButton}
                                >
                                    {showPassword ? <EyeOff size={18} /> : <Eye size={18} />}
                                </button>
                            </div>
                        </div>

                        <button
                            type="submit"
                            disabled={isLoading}
                            style={{
                                ...styles.submitButton,
                                ...(isLoading ? styles.submitButtonDisabled : {}),
                            }}
                        >
                            {isLoading ? (
                                <div style={styles.loadingContainer}>
                                    <div style={styles.spinner}></div>
                                    Signing In...
                                </div>
                            ) : (
                                "Sign In to Admin Portal"
                            )}
                        </button>

                        <div style={styles.forgotPassword}>
                            <button
                                type="button"
                                onClick={handleForgotPassword}
                                style={styles.forgotPasswordButton}
                            >
                                Forgot your password?
                            </button>
                        </div>

                        <div style={styles.demoCredentials}>
                            <p style={styles.demoTitle}>Demo Credentials:</p>
                            <p style={styles.demoText}>📧 admin@dsce.edu</p>
                            <p style={styles.demoText}>🔒 admin123</p>
                        </div>
                    </form>
                </div>

                <div style={styles.footer}>
                    <p style={styles.footerText}>© 2024 Stationary. All rights reserved.</p>
                </div>
            </div>
        </div>
    );
}
