"use client"

import { useState, useRef, useEffect } from "react"
import '../Student/dashboard.css';
// Icons as simple SVG components
const HomeIcon = () => (
    <svg style={{ width: '20px', height: '20px' }} fill="none" stroke="currentColor" viewBox="0 0 24 24">
        <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth={2}
            d="M3 12l2-2m0 0l7-7 7 7M5 10v10a1 1 0 001 1h3m10-11l2 2m-2-2v10a1 1 0 01-1 1h-3m-6 0a1 1 0 001-1v-4a1 1 0 011-1h2a1 1 0 011 1v4a1 1 0 001 1m-6 0h6"
        />
    </svg>
)

const ShoppingCartIcon = () => (
    <svg style={{ width: '20px', height: '20px' }} fill="none" stroke="currentColor" viewBox="0 0 24 24">
        <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth={2}
            d="M3 3h2l.4 2M7 13h10l4-8H5.4m0 0L7 13m0 0l-2.5 5M7 13l2.5 5m6-5v5a2 2 0 11-4 0v-5m4 0V8a2 2 0 10-4 0v5z"
        />
    </svg>
)

const CoffeeIcon = () => (
    <svg style={{ width: '20px', height: '20px' }} fill="none" stroke="currentColor" viewBox="0 0 24 24">
        <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth={2}
            d="M12 8v13m0-13V6a2 2 112 2h-2zm0 0V5.5A2.5 2.5 0 109.5 8H12zm-7 4h14M5 12a2 2 0 110-4h14a2 2 0 110 4M5 12v7a2 2 0 002 2h10a2 2 0 002-2v-7"
        />
    </svg>
)

const HelpCircleIcon = () => (
    <svg style={{ width: '20px', height: '20px' }} fill="none" stroke="currentColor" viewBox="0 0 24 24">
        <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth={2}
            d="M8.228 9c.549-1.165 2.03-2 3.772-2 2.21 0 4 1.343 4 3 0 1.4-1.278 2.575-3.006 2.907-.542.104-.994.54-.994 1.093m0 3h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z"
        />
    </svg>
)

const MenuIcon = () => (
    <svg width="20" height="20" fill="none" stroke="currentColor" viewBox="0 0 24 24">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
    </svg>
)

const BellIcon = () => (
    <svg width="20" height="20" fill="none" stroke="currentColor" viewBox="0 0 24 24">
        <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth={2}
            d="M15 17h5l-1.405-1.405A2.032 2.032 0 0118 14.158V11a6.002 6.002 0 00-4-5.659V5a2 2 0 10-4 0v.341C7.67 6.165 6 8.388 6 11v3.159c0 .538-.214 1.055-.595 1.436L4 17h5m6 0v1a3 3 0 11-6 0v-1m6 0H9"
        />
    </svg>
)

const UserIcon = () => (
    <svg width="16" height="16" fill="none" stroke="currentColor" viewBox="0 0 24 24">
        <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth={2}
            d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z"
        />
    </svg>
)

const ChevronDownIcon = () => (
    <svg width="16" height="16" fill="none" stroke="currentColor" viewBox="0 0 24 24">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
    </svg>
)

const BookIcon = () => (
    <svg style={{ width: '20px', height: '20px' }} fill="none" stroke="currentColor" viewBox="0 0 24 24">
        <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth={2}
            d="M12 6.253v13m0-13C10.832 5.477 9.246 5 7.5 5S4.168 5.477 3 6.253v13C4.168 18.477 5.754 18 7.5 18s3.332.477 4.5 1.253m0-13C13.168 5.477 14.754 5 16.5 5c1.747 0 3.332.477 4.5 1.253v13C19.832 18.477 18.246 18 16.5 18c-1.746 0-3.332.477-4.5 1.253"
        />
    </svg>
)

// Menu items for the sidebar
const menuItems = [
    { title: "Home", url: "/", icon: HomeIcon },
    { title: "Food Cart", url: "/cart", icon: ShoppingCartIcon },
    { title: "Canteen", url: "/Food", icon: CoffeeIcon },
    { title: "Help", url: "/Contact", icon: HelpCircleIcon },
]

// Sample teacher data
const teacherData = {
    name: "Dr. Sarah Wilson",
    teacherId: "EMP2024001",
    department: "Computer Science Engineering",
    designation: "Associate Professor",
    avatar: "/placeholder.svg?height=40&width=40",
}

// Custom hook for handling clicks outside (for dropdowns)
const useClickOutside = (ref, handler) => {
    useEffect(() => {
        const listener = (event) => {
            if (!ref.current || ref.current.contains(event.target)) {
                return
            }
            handler(event)
        }
        document.addEventListener("mousedown", listener)
        document.addEventListener("touchstart", listener)
        return () => {
            document.removeEventListener("mousedown", listener)
            document.removeEventListener("touchstart", listener)
        }
    }, [ref, handler])
}

// Sidebar Component
const Sidebar = ({ isOpen, activeItem, onItemClick }) => {
    const sidebarStyle = {
        position: 'fixed',
        left: 0,
        top: 0,
        height: '100vh',
        width: isOpen ? '280px' : '70px',
        backgroundColor: '#1e293b',
        borderRight: '1px solid #334155',
        transition: 'width 0.3s ease',
        zIndex: 1000,
        overflow: 'hidden'
    }

    const headerStyle = {
        padding: '1.5rem 1rem',
        borderBottom: '1px solid #334155'
    }

    const titleStyle = {
        color: '#ffffff',
        fontSize: '1.25rem',
        fontWeight: '600',
        opacity: isOpen ? 1 : 0,
        transition: 'opacity 0.3s ease'
    }

    const menuStyle = {
        padding: '1rem 0'
    }

    const menuItemStyle = (isActive) => ({
        display: 'flex',
        alignItems: 'center',
        padding: '0.75rem 1rem',
        margin: '0.25rem 0.5rem',
        borderRadius: '0.5rem',
        cursor: 'pointer',
        color: isActive ? '#3b82f6' : '#cbd5e1',
        backgroundColor: isActive ? '#1e40af20' : 'transparent',
        transition: 'all 0.2s ease',
        gap: '0.75rem'
    })

    const menuItemSpanStyle = {
        opacity: isOpen ? 1 : 0,
        transition: 'opacity 0.3s ease',
        fontSize: '0.875rem',
        fontWeight: '500'
    }

    return (
        <div style={sidebarStyle}>
            <div style={headerStyle}>
                <h2 style={titleStyle}>Teacher Portal</h2>
            </div>
            <nav style={menuStyle}>
                {menuItems.map((item) => {
                    const IconComponent = item.icon
                    const isActive = activeItem === item.title
                    return (
                        <div
                            key={item.title}
                            style={menuItemStyle(isActive)}
                            onClick={() => onItemClick(item.title)}
                            onMouseEnter={(e) => {
                                if (!isActive) {
                                    e.target.style.backgroundColor = '#334155'
                                }
                            }}
                            onMouseLeave={(e) => {
                                if (!isActive) {
                                    e.target.style.backgroundColor = 'transparent'
                                }
                            }}
                        >
                            <IconComponent />
                            <span style={menuItemSpanStyle}>{item.title}</span>
                        </div>
                    )
                })}
            </nav>
        </div>
    )
}

// Dropdown Component
const Dropdown = ({ trigger, children, isOpen, onToggle }) => {
    const dropdownRef = useRef(null)
    useClickOutside(dropdownRef, () => {
        if (isOpen) onToggle()
    })

    const dropdownStyle = {
        position: 'relative',
        display: 'inline-block'
    }

    const dropdownContentStyle = {
        position: 'absolute',
        right: 0,
        top: '100%',
        marginTop: '0.5rem',
        backgroundColor: '#ffffff',
        border: '1px solid #e2e8f0',
        borderRadius: '0.5rem',
        boxShadow: '0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05)',
        minWidth: '200px',
        zIndex: 1000
    }

    return (
        <div style={dropdownStyle} ref={dropdownRef}>
            <div onClick={onToggle}>{trigger}</div>
            {isOpen && <div style={dropdownContentStyle}>{children}</div>}
        </div>
    )
}

// Header Component
const Header = ({ onSidebarToggle, sidebarOpen }) => {
    const [notificationsOpen, setNotificationsOpen] = useState(false)
    const [profileOpen, setProfileOpen] = useState(false)

    const headerStyle = {
        position: 'fixed',
        top: 0,
        left: sidebarOpen ? '280px' : '70px',
        right: 0,
        height: '70px',
        backgroundColor: '#1e293b',
        borderBottom: '1px solid #334155',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        padding: '0 2rem',
        transition: 'left 0.3s ease',
        zIndex: 999
    }

    const headerLeftStyle = {
        display: 'flex',
        alignItems: 'center',
        gap: '1rem'
    }

    const toggleButtonStyle = {
        backgroundColor: 'transparent',
        border: 'none',
        color: '#cbd5e1',
        cursor: 'pointer',
        padding: '0.5rem',
        borderRadius: '0.375rem',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center'
    }

    const titleStyle = {
        color: '#ffffff',
        fontSize: '1.5rem',
        fontWeight: '600',
        margin: 0
    }

    const headerRightStyle = {
        display: 'flex',
        alignItems: 'center',
        gap: '1rem'
    }

    const buttonStyle = {
        backgroundColor: 'transparent',
        border: 'none',
        color: '#cbd5e1',
        cursor: 'pointer',
        padding: '0.5rem',
        borderRadius: '0.375rem',
        display: 'flex',
        alignItems: 'center',
        gap: '0.5rem',
        position: 'relative'
    }

    const badgeStyle = {
        position: 'absolute',
        top: '0',
        right: '0',
        backgroundColor: '#ef4444',
        color: '#ffffff',
        fontSize: '0.75rem',
        padding: '0.125rem 0.375rem',
        borderRadius: '9999px',
        minWidth: '1.25rem',
        height: '1.25rem',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center'
    }

    const avatarStyle = {
        width: '32px',
        height: '32px',
        borderRadius: '50%',
        backgroundColor: '#3b82f6',
        color: '#ffffff',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        fontSize: '0.75rem',
        fontWeight: '600'
    }

    const dropdownHeaderStyle = {
        padding: '0.75rem 1rem',
        borderBottom: '1px solid #e2e8f0',
        fontSize: '0.875rem',
        fontWeight: '600',
        color: '#374151'
    }

    const dropdownItemStyle = {
        padding: '0.75rem 1rem',
        cursor: 'pointer',
        fontSize: '0.875rem',
        color: '#374151',
        display: 'flex',
        alignItems: 'center',
        gap: '0.5rem',
        borderBottom: '1px solid #f3f4f6'
    }

    const notificationsTrigger = (
        <button style={buttonStyle}>
            <BellIcon />
            <span style={badgeStyle}>3</span>
        </button>
    )

    const profileTrigger = (
        <button style={buttonStyle}>
            <div style={avatarStyle}>
                {teacherData.name
                    .split(" ")
                    .map((n) => n[0])
                    .join("")}
            </div>
            <span style={{ color: '#ffffff' }}>{teacherData.name}</span>
            <ChevronDownIcon />
        </button>
    )

    return (
        <header style={headerStyle}>
            <div style={headerLeftStyle}>
                <button style={toggleButtonStyle} onClick={onSidebarToggle}>
                    <MenuIcon />
                </button>
                <h1 style={titleStyle}>Teacher Dashboard</h1>
            </div>
            <div style={headerRightStyle}>
                <Dropdown
                    trigger={notificationsTrigger}
                    isOpen={notificationsOpen}
                    onToggle={() => setNotificationsOpen(!notificationsOpen)}
                >
                    <div style={dropdownHeaderStyle}>Notifications</div>
                    <div style={dropdownItemStyle}>
                        <div>
                            <div style={{ fontWeight: '500' }}>New class schedule update</div>
                            <div style={{ fontSize: '0.75rem', color: '#6b7280' }}>Data Structures class moved to Room 301</div>
                        </div>
                    </div>
                    <div style={dropdownItemStyle}>
                        <div>
                            <div style={{ fontWeight: '500' }}>Assignment submissions</div>
                            <div style={{ fontSize: '0.75rem', color: '#6b7280' }}>15 new submissions to review</div>
                        </div>
                    </div>
                    <div style={dropdownItemStyle}>
                        <div>
                            <div style={{ fontWeight: '500' }}>Faculty meeting reminder</div>
                            <div style={{ fontSize: '0.75rem', color: '#6b7280' }}>Department meeting tomorrow at 2 PM</div>
                        </div>
                    </div>
                </Dropdown>

                <Dropdown trigger={profileTrigger} isOpen={profileOpen} onToggle={() => setProfileOpen(!profileOpen)}>
                    <div style={dropdownHeaderStyle}>My Account</div>
                    <div style={dropdownItemStyle} onClick={() => alert("Navigate to Teacher Profile")}>
                        <UserIcon />
                        Teacher Profile
                    </div>
                    <div style={dropdownItemStyle}>Academic Records</div>
                    <div style={dropdownItemStyle}>Settings</div>
                    <div style={dropdownItemStyle}>Log out</div>
                </Dropdown>
            </div>
        </header>
    )
}

// Main Content Component
const MainContent = ({ sidebarOpen }) => {
    const [currentTime, setCurrentTime] = useState(new Date())

    // Update time every second
    useEffect(() => {
        const timer = setInterval(() => setCurrentTime(new Date()), 1000)
        return () => clearInterval(timer)
    }, [])

    const contentStyle = {
        marginLeft: sidebarOpen ? '280px' : '70px',
        marginTop: '70px',
        padding: '2rem',
        minHeight: 'calc(100vh - 70px)',
        background: 'linear-gradient(135deg, #0f172a 0%, #1e293b 50%, #0f172a 100%)',
        transition: 'margin-left 0.3s ease'
    }

    const welcomeSectionStyle = {
        marginBottom: '3rem',
        textAlign: 'center',
        position: 'relative'
    }

    const welcomeTitleStyle = {
        color: '#ffffff',
        fontSize: '3rem',
        fontWeight: '700',
        margin: '0 0 1rem 0',
        background: 'linear-gradient(135deg, #3b82f6 0%, #10b981 100%)',
        WebkitBackgroundClip: 'text',
        WebkitTextFillColor: 'transparent',
        backgroundClip: 'text',
        animation: 'fadeInUp 1s ease-out'
    }

    const welcomeSubtitleStyle = {
        color: '#cbd5e1',
        fontSize: '1.2rem',
        margin: '0 0 1rem 0',
        animation: 'fadeInUp 1s ease-out 0.2s both'
    }

    const timeStyle = {
        color: '#94a3b8',
        fontSize: '1rem',
        background: 'rgba(59, 130, 246, 0.1)',
        padding: '0.5rem 1rem',
        borderRadius: '2rem',
        display: 'inline-block',
        border: '1px solid rgba(59, 130, 246, 0.2)',
        animation: 'fadeInUp 1s ease-out 0.4s both'
    }

    return (
        <>
            <style>{`
        @keyframes fadeInUp {
          from {
            opacity: 0;
            transform: translateY(30px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }
      `}</style>

            <main style={contentStyle}>
                {/* Welcome Section */}
                <div style={welcomeSectionStyle}>
                    <h2 style={welcomeTitleStyle}>Welcome back, {teacherData.name}!</h2>
                    <p style={welcomeSubtitleStyle}>
                        {teacherData.department}  {teacherData.designation}  ID: {teacherData.teacherId}
                    </p>
                    <div style={timeStyle}>
                        {currentTime.toLocaleString('en-US', {
                            weekday: 'long',
                            year: 'numeric',
                            month: 'long',
                            day: 'numeric',
                            hour: '2-digit',
                            minute: '2-digit',
                            second: '2-digit'
                        })}
                    </div>
                </div>
            </main>
        </>
    )
}

// Main Dashboard Component
const TeacherDashboard = () => {
    const [sidebarOpen, setSidebarOpen] = useState(true)
    const [activeItem, setActiveItem] = useState("Home")
    const [isMobile, setIsMobile] = useState(false)

    useEffect(() => {
        const handleResize = () => {
            const mobile = window.innerWidth <= 768
            setIsMobile(mobile)
            if (mobile) {
                setSidebarOpen(false)
            }
        }

        handleResize()
        window.addEventListener("resize", handleResize)
        return () => window.removeEventListener("resize", handleResize)
    }, [])

    // Sidebar click handler that navigates to correct page
    const handleItemClick = (itemTitle) => {
        setActiveItem(itemTitle)
        if (isMobile) setSidebarOpen(false)

        // Grab URL from the menuItems config
        const selected = menuItems.find((item) => item.title === itemTitle)
        if (selected && selected.url) {
            window.location.href = selected.url
        }
    }

    const overlayStyle = {
        position: 'fixed',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        backgroundColor: 'rgba(0, 0, 0, 0.5)',
        zIndex: 999
    }

    const containerStyle = {
        fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif',
        minHeight: '100vh',
        backgroundColor: '#0f172a'
    }

    return (
        <div style={containerStyle}>
            {isMobile && sidebarOpen && <div style={overlayStyle} onClick={() => setSidebarOpen(false)} />}
            <Sidebar isOpen={sidebarOpen} activeItem={activeItem} onItemClick={handleItemClick} />
            <Header onSidebarToggle={() => setSidebarOpen(!sidebarOpen)} sidebarOpen={sidebarOpen} />
            <MainContent sidebarOpen={sidebarOpen} />
        </div>
    )
}

export default TeacherDashboard
