export default function AdminDashboard() {
    // Students State
    const [students, setStudents] = useState([])
    const [studentForm, setStudentForm] = useState({
        name: "",
        usn: "",
        email: "",
        mobile: "",
        department: "",
        semester: "",
    })

    // Teachers State
    const [teachers, setTeachers] = useState([])
    const [teacherForm, setTeacherForm] = useState({
        name: "",
        email: "",
        mobile: "",
        department: "",
        subject: "",
        experience: "",
    })

    // Admin State
    const [admins, setAdmins] = useState([])
    const [adminForm, setAdminForm] = useState({
        name: "",
        email: "",
        phone: "",
        adminType: "",
    })

    // Placements State
    const [placements, setPlacements] = useState([])
    const [placementForm, setPlacementForm] = useState({
        companyName: "",
        position: "",
        description: "",
        requirements: "",
        salary: "",
        deadline: "",
        location: "",
    })

    const [sidebarOpen, setSidebarOpen] = useState(true)
    const [activeTab, setActiveTab] = useState("students")

    const departments = [
        "MCA",
        "MBA",
        "BCom",
        "BBA",
        "BCA",
        "Computer Science Engineering",
        "Mechanical Engineering",
        "Electrical Engineering",
        "Civil Engineering",
        "Electronics and Communication Engineering",
        "Information Science Engineering",
    ]

    const adminTypes = ["Food Cart Admin", "Stationary Admin", "Canteen Admin"]

    // Student Functions
    const handleStudentSubmit = (e) => {
        e.preventDefault()
        if (
            !studentForm.name ||
            !studentForm.usn ||
            !studentForm.email ||
            !studentForm.mobile ||
            !studentForm.department ||
            !studentForm.semester
        ) {
            toast({
                title: "Error",
                description: "Please fill all student fields",
                variant: "destructive",
            })
            return
        }
        const newStudent = {
            id: Date.now(),
            ...studentForm,
        }
        setStudents([...students, newStudent])
        setStudentForm({
            name: "",
            usn: "",
            email: "",
            mobile: "",
            department: "",
            semester: "",
        })
        toast({
            title: "Success",
            description: "Student added successfully",
        })
    }

    const deleteStudent = (id) => {
        setStudents(students.filter((student) => student.id !== id))
        toast({
            title: "Success",
            description: "Student deleted successfully",
        })
    }

    // Teacher Functions
    const handleTeacherSubmit = (e) => {
        e.preventDefault()
        if (
            !teacherForm.name ||
            !teacherForm.email ||
            !teacherForm.mobile ||
            !teacherForm.department ||
            !teacherForm.subject
        ) {
            toast({
                title: "Error",
                description: "Please fill all teacher fields",
                variant: "destructive",
            })
            return
        }
        const newTeacher = {
            id: Date.now(),
            ...teacherForm,
        }
        setTeachers([...teachers, newTeacher])
        setTeacherForm({
            name: "",
            email: "",
            mobile: "",
            department: "",
            subject: "",
            experience: "",
        })
        toast({
            title: "Success",
            description: "Teacher added successfully",
        })
    }

    const deleteTeacher = (id) => {
        setTeachers(teachers.filter((teacher) => teacher.id !== id))
        toast({
            title: "Success",
            description: "Teacher deleted successfully",
        })
    }

    // Admin Functions
    const handleAdminSubmit = (e) => {
        e.preventDefault()
        if (!adminForm.name || !adminForm.email || !adminForm.phone || !adminForm.adminType) {
            toast({
                title: "Error",
                description: "Please fill all admin fields",
                variant: "destructive",
            })
            return
        }
        const newAdmin = {
            id: Date.now(),
            ...adminForm,
        }
        setAdmins([...admins, newAdmin])
        setAdminForm({
            name: "",
            email: "",
            phone: "",
            adminType: "",
        })
        toast({
            title: "Success",
            description: "Admin account created successfully",
        })
    }

    const deleteAdmin = (id) => {
        setAdmins(admins.filter((admin) => admin.id !== id))
        toast({
            title: "Success",
            description: "Admin deleted successfully",
        })
    }

    // Placement Functions
    const handlePlacementSubmit = (e) => {
        e.preventDefault()
        if (
            !placementForm.companyName ||
            !placementForm.position ||
            !placementForm.description ||
            !placementForm.deadline
        ) {
            toast({
                title: "Error",
                description: "Please fill all required placement fields",
