                        </div>
                    )}

                    {/* Placements Tab */}
                    {activeTab === "placements" && (
                        <div style={{ display: "flex", flexDirection: "column", gap: "1.5rem" }}>
                            <div
                                style={{
                                    padding: "1.5rem",
                                    backgroundColor: "#1e293b",
                                    borderRadius: "1rem",
                                    boxShadow: "0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05)",
                                    border: "1px solid #374151",
                                }}
                            >
                                <div style={{ display: "flex", alignItems: "center", gap: "0.5rem", marginBottom: "1rem" }}>
                                    <Send style={{ height: "1.25rem", width: "1.25rem", color: "#fb923c" }} />
                                    <h3 style={{ fontSize: "1.25rem", fontWeight: "600", color: "white" }}>
                                        Create Placement Notification
                                    </h3>
                                </div>
                                <form
                                    onSubmit={handlePlacementSubmit}
                                    style={{
                                        display: "grid",
                                        gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))",
                                        gap: "1rem",
                                    }}
                                >
                                    <div style={{ display: "flex", flexDirection: "column", gap: "0.5rem" }}>
                                        <Label htmlFor="company-name">Company Name</Label>
                                        <Input
                                            id="company-name"
                                            value={placementForm.companyName}
                                            onChange={(e) => setPlacementForm({ ...placementForm, companyName: e.target.value })}
                                            placeholder="Enter company name"
                                        />
                                    </div>
                                    <div style={{ display: "flex", flexDirection: "column", gap: "0.5rem" }}>
                                        <Label htmlFor="position">Position</Label>
                                        <Input
                                            id="position"
                                            value={placementForm.position}
                                            onChange={(e) => setPlacementForm({ ...placementForm, position: e.target.value })}
                                            placeholder="Enter position"
                                        />
                                    </div>
                                    <div style={{ display: "flex", flexDirection: "column", gap: "0.5rem" }}>
                                        <Label htmlFor="salary">Salary Package</Label>
                                        <Input
                                            id="salary"
                                            value={placementForm.salary}
                                            onChange={(e) => setPlacementForm({ ...placementForm, salary: e.target.value })}
                                            placeholder="Enter salary package"
                                        />
                                    </div>
                                    <div style={{ display: "flex", flexDirection: "column", gap: "0.5rem" }}>
                                        <Label htmlFor="location">Location</Label>
                                        <Input
                                            id="location"
                                            value={placementForm.location}
                                            onChange={(e) => setPlacementForm({ ...placementForm, location: e.target.value })}
                                            placeholder="Enter job location"
                                        />
                                    </div>
                                    <div style={{ display: "flex", flexDirection: "column", gap: "0.5rem" }}>
                                        <Label htmlFor="deadline">Application Deadline</Label>
                                        <Input
                                            id="deadline"
                                            type="date"
                                            value={placementForm.deadline}
                                            onChange={(e) => setPlacementForm({ ...placementForm, deadline: e.target.value })}
                                        />
                                    </div>
                                    <div
                                        style={{ display: "flex", flexDirection: "column", gap: "0.5rem", gridColumn: "span 2 / span 2" }}
                                    >
                                        <Label htmlFor="description">Job Description</Label>
                                        <Textarea
                                            id="description"
                                            value={placementForm.description}
                                            onChange={(e) => setPlacementForm({ ...placementForm, description: e.target.value })}
                                            placeholder="Enter job description"
                                            rows={3}
                                        />
                                    </div>
                                    <div
                                        style={{ display: "flex", flexDirection: "column", gap: "0.5rem", gridColumn: "span 2 / span 2" }}
                                    >
                                        <Label htmlFor="requirements">Requirements</Label>
                                        <Textarea
                                            id="requirements"
                                            value={placementForm.requirements}
                                            onChange={(e) => setPlacementForm({ ...placementForm, requirements: e.target.value })}
                                            placeholder="Enter job requirements"
                                            rows={3}
                                        />
                                    </div>
                                    <div style={{ gridColumn: "span 2 / span 2" }}>
                                        <button
                                            type="submit"
                                            style={{
                                                width: "100%",
                                                display: "flex",
                                                alignItems: "center",
                                                justifyContent: "center",
                                                gap: "0.5rem",
                                                borderRadius: "0.75rem",
                                                color: "white",
                                                fontWeight: "600",
                                                paddingTop: "0.75rem",
                                                paddingBottom: "0.75rem",
                                                paddingLeft: "1.5rem",
                                                paddingRight: "1.5rem",
                                                cursor: "pointer",
                                                transitionProperty: "transform, box-shadow",
                                                transitionDuration: "300ms",
                                                transitionTimingFunction: "cubic-bezier(0.4, 0, 0.2, 1)",
                                                background: "linear-gradient(135deg, #3b82f6 0%, #10b981 100%)",
                                                border: "none",
                                            }}
                                        >
                                            <Send style={{ height: "1rem", width: "1rem" }} />
                                            Send Notification to All Students ({students.length})
                                        </button>
                                    </div>
                                </form>
                            </div>

                            <div
                                style={{
                                    padding: "1.5rem",
                                    backgroundColor: "#1e293b",
                                    borderRadius: "1rem",
                                    boxShadow: "0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05)",
                                    border: "1px solid #374151",
                                }}
                            >
                                <h3 style={{ fontSize: "1.25rem", fontWeight: "600", color: "white", marginBottom: "1rem" }}>
                                    Placement Notifications
                                </h3>
                                <div style={{ display: "flex", flexDirection: "column", gap: "1rem" }}>
                                    {placements.map((placement) => (
                                        <div
                                            key={placement.id}
                                            style={{
                                                backgroundColor: "#0f172a",
                                                padding: "1rem",
                                                borderRadius: "0.75rem",
                                                boxShadow: "0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06)",
                                                border: "1px solid #374151",
                                                position: "relative",
                                            }}
                                        >
                                            <div
                                                style={{
                                                    display: "flex",
                                                    justifyContent: "space-between",
                                                    alignItems: "flex-start",
                                                    marginBottom: "0.5rem",
                                                }}
                                            >
                                                <div>
                                                    <h4 style={{ fontSize: "1.125rem", fontWeight: "600", color: "white" }}>
                                                        {placement.companyName}
                                                    </h4>
                                                    <p style={{ color: "#93c5fd", fontSize: "0.875rem" }}>{placement.position}</p>
                                                    <p style={{ color: "#9ca3af", fontSize: "0.75rem", marginTop: "0.25rem" }}>
                                                        Posted: {placement.createdAt}
                                                    </p>
                                                </div>
                                                <Button
                                                    variant="destructive"
                                                    size="sm"
                                                    onClick={() => deletePlacement(placement.id)}
                                                    style={{ display: "flex", alignItems: "center", gap: "0.25rem" }}
                                                >
                                                    <Trash2 style={{ height: "1rem", width: "1rem" }} />
                                                </Button>
                                            </div>
                                            <div
                                                style={{
                                                    display: "grid",
                                                    gridTemplateColumns: "repeat(auto-fit, minmax(150px, 1fr))", // Simplified responsiveness
                                                    columnGap: "1rem",
                                                    fontSize: "0.875rem",
                                                    marginBottom: "0.5rem",
                                                }}
                                            >
                                                <div style={{ color: "#d1d5db" }}>
                                                    <span style={{ fontWeight: "500", color: "white" }}>Salary:</span> {placement.salary || "N/A"}
                                                </div>
                                                <div style={{ color: "#d1d5db" }}>
                                                    <span style={{ fontWeight: "500", color: "white" }}>Location:</span>{" "}
                                                    {placement.location || "N/A"}
                                                </div>
                                                <div style={{ color: "#d1d5db" }}>
                                                    <span style={{ fontWeight: "500", color: "white" }}>Deadline:</span> {placement.deadline}
                                                </div>
                                            </div>
                                            <div style={{ marginTop: "0.5rem" }}>
                                                <p style={{ fontSize: "0.875rem", color: "#d1d5db" }}>{placement.description}</p>
                                            </div>
                                            {placement.requirements && (
                                                <div style={{ marginTop: "0.5rem" }}>
                                                    <span style={{ fontWeight: "500", fontSize: "0.875rem", color: "white" }}>Requirements:</span>
                                                    <p style={{ fontSize: "0.875rem", color: "#d1d5db" }}>{placement.requirements}</p>
                                                </div>
                                            )}
                                        </div>
                                    ))}
                                    {placements.length === 0 && (
                                        <div
                                            style={{
                                                textAlign: "center",
                                                paddingTop: "2rem",
                                                paddingBottom: "2rem",
                                                color: "#9ca3af",
                                                backgroundColor: "#1e293b",
                                                borderBottomLeftRadius: "0.5rem",
                                                borderBottomRightRadius: "0.5rem",
                                            }}
                                        >
                                            No placement notifications sent yet
                                        </div>
                                    )}
                                </div>
                            </div>
                        </div>
                    )}
                </div>
            </div>
        </div>
    )
}
