"use client"

import { useState, useEffect, useCallback } from "react"
import {
    Briefcase,
    FileText,
    Calendar,
    MapPin,
    Clock,
    DollarSign,
    Building,
    User,
    Phone,
    Mail,
    HelpCircle,
    X,
    Search,
    Filter,
    CheckCircle,
    Eye,
    CalendarIcon,
    FileCheck,
    Timer,
    ArrowRight,
    ArrowLeft,
    Hourglass,
} from "lucide-react"

const PlacementPortal = () => {
    const [activeSection, setActiveSection] = useState("apply")
    const [showHelp, setShowHelp] = useState(false)
    const [showJobDetails, setShowJobDetails] = useState(false)
    const [showAssessment, setShowAssessment] = useState(false)
    const [selectedJob, setSelectedJob] = useState(null)
    const [selectedInterview, setSelectedInterview] = useState(null)
    const [searchTerm, setSearchTerm] = useState("")

    // Assessment state
    const [currentQuestion, setCurrentQuestion] = useState(0)
    const [answers, setAnswers] = useState({})
    const [timeLeft, setTimeLeft] = useState(1800) // 30 minutes in seconds
    const [assessmentStarted, setAssessmentStarted] = useState(false)
    const [assessmentCompleted, setAssessmentCompleted] = useState(false)
    const [appliedJobs, setAppliedJobs] = useState([
        {
            id: 1,
            title: "Frontend Developer",
            company: "WebSolutions Ltd.",
            appliedDate: "2024-01-15",
            status: "Under Review",
            statusColor: { backgroundColor: "#f59e0b", color: "#ffffff" },
            nextStep: "Technical Assessment",
            nextStepDate: "2024-02-10",
            nextStepTime: "2:00 PM",
            details:
                "Your application is currently being reviewed by our technical team. You will receive an email with assessment details within 2-3 business days.",
            waitingMessage: null,
        },
        {
            id: 2,
            title: "Backend Developer",
            company: "ServerTech Inc.",
            appliedDate: "2024-01-10",
            status: "Assessment Completed",
            statusColor: { backgroundColor: "#3b82f6", color: "#ffffff" },
            nextStep: "Waiting for Next Round",
            nextStepDate: null,
            nextStepTime: null,
            details:
                "You have successfully completed the technical assessment. Our team is currently evaluating your performance and will notify you about the next round within 5-7 business days.",
            waitingMessage: "Results under evaluation. Next round details will be shared soon.",
        },
        {
            id: 3,
            title: "Full Stack Developer",
            company: "DevCorp",
            appliedDate: "2024-01-08",
            status: "Interview Scheduled",
            statusColor: { backgroundColor: "#10b981", color: "#ffffff" },
            nextStep: "Technical Interview",
            nextStepDate: "2024-02-12",
            nextStepTime: "11:00 AM",
            details:
                "Congratulations! You have been selected for the technical interview round. Please prepare for system design and coding challenges.",
            waitingMessage: null,
        },
        {
            id: 4,
            title: "Mobile App Developer",
            company: "AppTech Solutions",
            appliedDate: "2024-01-05",
            status: "Waiting for HR Round",
            statusColor: { backgroundColor: "#f59e0b", color: "#ffffff" },
            nextStep: "HR Interview",
            nextStepDate: "TBD",
            nextStepTime: "TBD",
            details:
                "You have successfully cleared the technical rounds. HR team will contact you soon to schedule the final interview round.",
            waitingMessage: "Waiting for HR team to schedule final interview. Expected timeline: 3-5 business days.",
        },
        {
            id: 5,
            title: "DevOps Engineer",
            company: "CloudTech Inc.",
            appliedDate: "2024-01-03",
            status: "Rejected",
            statusColor: { backgroundColor: "#ef4444", color: "#ffffff" },
            nextStep: "Application Closed",
            nextStepDate: null,
            nextStepTime: null,
            details:
                "Thank you for your interest. Unfortunately, we have decided to move forward with other candidates. We encourage you to apply for future openings.",
            waitingMessage: null,
        },
    ])

    // Aptitude questions
    const aptitudeQuestions = [
        {
            id: 1,
            question: "If a train travels 120 km in 2 hours, what is its average speed?",
            options: ["50 km/h", "60 km/h", "70 km/h", "80 km/h"],
            correct: 1,
            type: "quantitative",
        },
        {
            id: 2,
            question: "Complete the series: 2, 6, 12, 20, 30, ?",
            options: ["40", "42", "44", "46"],
            correct: 1,
            type: "logical",
        },
        {
            id: 3,
            question: "Choose the word that is most similar to 'METICULOUS':",
            options: ["Careless", "Precise", "Quick", "Loud"],
            correct: 1,
            type: "verbal",
        },
        {
            id: 4,
            question: "If 5x + 3 = 23, what is the value of x?",
            options: ["3", "4", "5", "6"],
            correct: 1,
            type: "quantitative",
        },
        {
            id: 5,
            question: "Which number comes next in the sequence: 1, 4, 9, 16, 25, ?",
            options: ["30", "32", "36", "40"],
            correct: 2,
            type: "logical",
        },
        {
            id: 6,
            question: "Choose the antonym of 'ABUNDANT':",
            options: ["Plentiful", "Scarce", "Rich", "Full"],
            correct: 1,
            type: "verbal",
        },
        {
            id: 7,
            question: "A rectangle has length 15 cm and width 8 cm. What is its area?",
            options: ["120 sq cm", "130 sq cm", "140 sq cm", "150 sq cm"],
            correct: 0,
            type: "quantitative",
        },
        {
            id: 8,
            question: "If all roses are flowers and some flowers are red, which statement is definitely true?",
            options: ["All roses are red", "Some roses are red", "No roses are red", "Some roses may be red"],
            correct: 3,
            type: "logical",
        },
        {
            id: 9,
            question: "Choose the word that best completes: 'Book is to Library as Car is to ____'",
            options: ["Road", "Garage", "Driver", "Engine"],
            correct: 1,
            type: "verbal",
        },
        {
            id: 10,
            question: "What is 25% of 80?",
            options: ["15", "20", "25", "30"],
            correct: 1,
            type: "quantitative",
        },
    ]

    const handleSubmitAssessment = useCallback(() => {
        setAssessmentCompleted(true)
        setAssessmentStarted(false)

        let score = 0
        aptitudeQuestions.forEach((question) => {
            if (answers[question.id] === question.correct) {
                score++
            }
        })
        const percentage = Math.round((score / aptitudeQuestions.length) * 100)

        setTimeout(() => {
            alert(
                `ðŸŽ‰ Assessment Completed!\n\nYour Score: ${score}/${aptitudeQuestions.length} (${percentage}%)\n\nThank you for taking the assessment. Results will be shared within 2-3 business days.`,
            )
            setShowAssessment(false)

            setAppliedJobs((prev) =>
                prev.map((job) =>
                    job.company === selectedInterview?.company
                        ? {
                            ...job,
                            status: "Assessment Completed",
                            statusColor: { backgroundColor: "#3b82f6", color: "#ffffff" },
                            nextStep: "Waiting for Next Round",
                            nextStepDate: null,
                            nextStepTime: null,
                            details:
                                "You have successfully completed the technical assessment. Our team is currently evaluating your performance and will notify you about the next round within 5-7 business days.",
                            waitingMessage: "Results under evaluation. Next round details will be shared soon.",
                        }
                        : job,
                ),
            )
        }, 1000)
    }, [answers, selectedInterview?.company, setAppliedJobs])

    // Timer effect
    useEffect(() => {
        let timer
        if (assessmentStarted && !assessmentCompleted && timeLeft > 0) {
            timer = setInterval(() => {
                setTimeLeft((prev) => {
                    if (prev <= 1) {
                        handleSubmitAssessment()
                        return 0
                    }
                    return prev - 1
                })
            }, 1000)
        }
        return () => clearInterval(timer)
    }, [assessmentStarted, assessmentCompleted, timeLeft, handleSubmitAssessment])

    // Format time
    const formatTime = (seconds) => {
        const mins = Math.floor(seconds / 60)
        const secs = seconds % 60
        return `${mins.toString().padStart(2, "0")}:${secs.toString().padStart(2, "0")}`
    }

    // Sample job data
    const availableJobs = [
        {
            id: 1,
            title: "Software Engineer",
            company: "TechCorp Inc.",
            location: "Bangalore, India",
            salary: "â‚¹8-12 LPA",
            type: "Full-time",
            description: "Join our dynamic team to build cutting-edge software solutions.",
            requirements: ["React", "Node.js", "JavaScript", "Problem Solving"],
            posted: "2 days ago",
            logo: "ðŸ¢",
        },
        {
            id: 2,
            title: "Data Scientist",
            company: "DataTech Solutions",
            location: "Mumbai, India",
            salary: "â‚¹10-15 LPA",
            type: "Full-time",
            description: "Analyze complex data sets to drive business insights.",
            requirements: ["Python", "Machine Learning", "SQL", "Statistics"],
            posted: "1 day ago",
            logo: "ðŸ“Š",
        },
        {
            id: 3,
            title: "UI/UX Designer",
            company: "DesignHub",
            location: "Pune, India",
            salary: "â‚¹6-9 LPA",
            type: "Full-time",
            description: "Create beautiful and intuitive user experiences.",
            requirements: ["Figma", "Adobe XD", "User Research", "Prototyping"],
            posted: "3 days ago",
            logo: "ðŸŽ¨",
        },
    ]

    // State for applied jobs

    // State to track which jobs have been applied to
    const [appliedJobIds, setAppliedJobIds] = useState([])

    const interviews = [
        {
            id: 1,
            title: "Technical Interview",
            company: "TechGiant Corp.",
            date: "2024-02-05",
            time: "10:00 AM",
            type: "Aptitude Assessment",
            interviewer: "John Smith",
            status: "Scheduled",
            round: "Round 1",
            description: "Online aptitude test covering quantitative, logical, and verbal reasoning",
        },
        {
            id: 2,
            title: "HR Interview",
            company: "InnovateLabs",
            date: "2024-02-03",
            time: "2:00 PM",
            type: "In-person",
            interviewer: "Sarah Johnson",
            status: "Completed",
            round: "Final Round",
            description: "Final round discussion with HR team",
        },
    ]

    const sidebarItems = [
        { id: "apply", label: "Apply Jobs", icon: Briefcase },
        { id: "applied", label: "Applied Jobs", icon: FileText },
        { id: "interview", label: "Interviews", icon: Calendar },
    ]

    // Function to handle job application
    const handleApplyJob = (job) => {
        if (appliedJobIds.includes(job.id)) {
            alert("You have already applied for this job!")
            return
        }

        alert(
            `âœ… Application Submitted Successfully!\n\nJob: ${job.title}\nCompany: ${job.company}\n\nYour application has been submitted and is under review. You will be notified about the next steps via email.`,
        )

        const newAppliedJob = {
            id: appliedJobs.length + job.id + 100,
            title: job.title,
            company: job.company,
            appliedDate: new Date().toISOString().split("T")[0],
            status: "Under Review",
            statusColor: { backgroundColor: "#f59e0b", color: "#ffffff" },
            nextStep: "Initial Screening",
            nextStepDate: "2024-02-15",
            nextStepTime: "11:00 AM",
            details:
                "Your application has been received and is currently under review. Our HR team will contact you within 3-5 business days for the initial screening process.",
            waitingMessage: null,
        }

        setAppliedJobs((prev) => [newAppliedJob, ...prev])
        setAppliedJobIds((prev) => [...prev, job.id])

        setTimeout(() => {
            setActiveSection("applied")
        }, 1000)
    }

    // Function to handle view details
    const handleViewDetails = (job) => {
        setSelectedJob(job)
        setShowJobDetails(true)
    }

    // Function to start assessment
    const handleJoinInterview = (interview) => {
        if (interview.type === "Aptitude Assessment") {
            setSelectedInterview(interview)
            setShowAssessment(true)
            setCurrentQuestion(0)
            setAnswers({})
            setTimeLeft(1800)
            setAssessmentStarted(false)
            setAssessmentCompleted(false)
        } else {
            alert(`Joining ${interview.type} for ${interview.title} at ${interview.company}`)
        }
    }

    // Function to start assessment
    const startAssessment = () => {
        setAssessmentStarted(true)
    }

    // Function to handle answer selection
    const handleAnswerSelect = (questionId, answerIndex) => {
        setAnswers((prev) => ({
            ...prev,
            [questionId]: answerIndex,
        }))
    }

    // Function to go to next question
    const nextQuestion = () => {
        if (currentQuestion < aptitudeQuestions.length - 1) {
            setCurrentQuestion((prev) => prev + 1)
        }
    }

    // Function to go to previous question
    const prevQuestion = () => {
        if (currentQuestion > 0) {
            setCurrentQuestion((prev) => prev - 1)
        }
    }

    const JobCard = ({ job }) => {
        const isApplied = appliedJobIds.includes(job.id)
        return (
            <div
                style={{
                    backgroundColor: "#1e293b",
                    borderRadius: "12px",
                    boxShadow: "0 4px 6px rgba(0, 0, 0, 0.3)",
                    border: "1px solid #334155",
                    padding: "24px",
                    marginBottom: "16px",
                    transition: "all 0.3s ease",
                }}
                onMouseOver={(e) => {
                    e.currentTarget.style.transform = "translateY(-2px)"
                    e.currentTarget.style.boxShadow = "0 8px 25px rgba(59, 130, 246, 0.15)"
                }}
                onMouseOut={(e) => {
                    e.currentTarget.style.transform = "translateY(0)"
                    e.currentTarget.style.boxShadow = "0 4px 6px rgba(0, 0, 0, 0.3)"
                }}
            >
                <div
                    style={{ display: "flex", alignItems: "flex-start", justifyContent: "space-between", marginBottom: "16px" }}
                >
                    <div style={{ display: "flex", alignItems: "flex-start", gap: "16px", flex: 1 }}>
                        <div
                            style={{
                                width: "48px",
                                height: "48px",
                                background: "linear-gradient(135deg, #3b82f6, #10b981)",
                                borderRadius: "8px",
                                display: "flex",
                                alignItems: "center",
                                justifyContent: "center",
                                fontSize: "20px",
                                flexShrink: 0,
                            }}
                        >
                            {job.logo}
                        </div>
                        <div style={{ flex: 1 }}>
                            <div
                                style={{ display: "flex", alignItems: "center", justifyContent: "space-between", marginBottom: "8px" }}
                            >
                                <h3 style={{ fontSize: "20px", fontWeight: "600", color: "#ffffff", margin: 0 }}>{job.title}</h3>
                                <span
