                            <div style={{ display: "flex", alignItems: "center", color: "#cbd5e1", marginBottom: "12px" }}>
                                <Building style={{ width: "16px", height: "16px", marginRight: "4px" }} />
                                <span style={{ fontSize: "14px" }}>{job.company}</span>
                            </div>
                            <div
                                style={{
                                    display: "flex",
                                    alignItems: "center",
                                    gap: "24px",
                                    fontSize: "14px",
                                    color: "#e2e8f0",
                                    marginBottom: "16px",
                                }}
                            >
                                <div style={{ display: "flex", alignItems: "center" }}>
                                    <MapPin style={{ width: "16px", height: "16px", marginRight: "4px" }} />
