                            <p
                                style={{
                                    color: "#cbd5e1",
                                    fontSize: "14px",
                                    lineHeight: "1.5",
                                    marginBottom: "16px",
                                    margin: "0 0 16px 0",
                                }}
                            >
                                {job.description}
                            </p>
                            <div style={{ display: "flex", flexWrap: "wrap", gap: "8px" }}>
                                {job.requirements.map((skill, index) => (
                                    <span
                                        key={index}
                                        style={{
                                            backgroundColor: "#334155",
                                            color: "#e2e8f0",
                                            padding: "4px 12px",
                                            borderRadius: "20px",
                                            fontSize: "12px",
                                            fontWeight: "500",
